INSERT INTO `goodpractices` (`cle`, `Family`, `Id`, `Planet`, `People`, `Prosperity`, `Recommendation`, `Criteria`, `Justification`, `Stage`, `Tests1`, `Tests2`, `Tests3`, `Correspondence`, `Link`, `Type`, `Difficulty`, `Goal`, `Incontournable`, `Tags`, `Actors`, `Indicators_`, `IndicatorsX`, `IndicatorsY`, `Priority`, `Recurrence`, `UseCase`, `Example`, `Limite`, `Automatizable`, `AutomatizationScript`, `AutomatizationLevel`, `AutomatizationInformation`) VALUES
(1, 'STRATEGIE', 'STR-1', NULL, NULL, NULL, 'Définir et valider les besoins et les enjeux du projet afin d\'anticiper les impacts', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'ODD#12', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(2, 'STRATEGIE', 'STR-1.01', 'A', 'A', 'B', 'Définir et valider les besoins et les enjeux du projet afin d\'anticiper les impacts', 'Les réunions sont-elles limitées au strict minimum, en privilégiant le distanciel, lorsque les réunions nécessitent des déplacements physiques ?', 'Les trajets sont sources d\'émissions de Gaz à Effet de Serre (GES) et ont un impact sur l\'organisation personnelle des collaborateurs.', 'N/A', 'Les outils existent-ils dans l\'entreprise pour permettre les réunions en distanciel dans de bonnes conditions ?', 'Toutes les réunions prévues sont-elles utiles ?', NULL, NULL, NULL, 'RECO', '*', NULL, NULL, '#GOV ; #HR ; #FIN', 'ProjectMngr ; Manager ;  HR ; ITMngr ; Staff', '(X / Y ) * 100', 'Meeting Distance', 'Meeting total', 'High', 'Once', 'La fiche de cadrage projet défini les réunions et les distances de déplacements de tous les participants', NULL, NULL, NULL, NULL, NULL, NULL),
(3, 'STRATEGIE', 'STR-1.02', 'A', 'A', 'B', 'Définir et valider les besoins et les enjeux du projet afin d\'anticiper les impacts', 'Le télétravail est-il autorisé, sans limites, aux collaborateurs qui le souhaitent et dont l\'activité le permet ?', 'Outre la limitation des transports quotidiens et leurs impacts environnementaux, les temps perdus lors des trajets peuvent étre considérables. Les outils et l\'organisation doivent permettre en revanche de lutter contre la rupture de lien social qui peut s\'installer avec le télétravail.', 'N/A', 'L\'entreprise offre-t-elle un cadre au télétravail ?', 'L\'organisation du projet permet-elle aux collaborateurs qui le souhaitent et dont le métier le permet de télétravailler, selon un rythme choisi par chaque collaborateur ?', NULL, NULL, NULL, 'RECO', '*', NULL, NULL, '#METH ; #GOV ; #HR ; #FIN ; #LEG', 'Manager ; RSE/NR ; HR ; ITMngr', '(X / Y ) * 100', 'Propositions de télétravail', 'Postes elligibles', 'Medium', 'Once', 'La stratégie RH précise les conditions et modalités du télétravail', NULL, NULL, NULL, NULL, NULL, NULL),
(4, 'STRATEGIE', 'STR-1.C01', 'B', 'A', 'B', 'Définir et valider les besoins et les enjeux du projet afin d\'anticiper les impacts', 'L\'entreprise est-elle ouverte é une démarche NR qui pourrait fédérer les énergies autour d\'un projet novateur ?', 'Pour étre efficace, la démarche NR doit étre présente dans la stratégie de l\'organisation et associer l\'ensemble des processus de décision dans l\'objectif de réduire les impacts des services numériques.', 'N/A', 'Les managers ont-ils les mains libres pour initier la démarche NR dans les projets ?', NULL, NULL, NULL, NULL, 'CONSEIL', 'N/A', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(5, 'STRATEGIE', 'STR-1.C02', 'B', 'B', 'B', 'Définir et valider les besoins et les enjeux du projet afin d\'anticiper les impacts', 'Est-ce que les stratégies RSE et NR sont associées et systématiquement mises en oeuvre dans tous les projets ?', 'Le traitement NR est une démarche systémique si la stratégie NR est prise en compte dans tous les projets. Comme c\'est le plus souvent le cas pour la RSE le gain est beaucoup plus significatif que lorsque les éléments NR sont traités ponctuellement', 'Conception', 'Quelle proportion des projets embarquent des exigences NR ?', 'Quelle est l\'objectif de proportion de projets avec des exigences NR ? A quelle échéance ?', NULL, NULL, NULL, 'CONSEIL', 'N/A', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(6, 'STRATEGIE', 'STR-1.03', 'B', 'A', 'B', 'Définir et valider les besoins et les enjeux du projet afin d\'anticiper les impacts', 'Comment faire du NR plus qu\'un objectif de communication ? Est-ce que des résultats concrets sont associés é la démarche NR ? (pas de Greenwashing)', 'Les énergies et budgets dépensés pour les aspects purement communication (greenwashing) peuvent étre conséquents pour aucun résultat tangible. Avec les mémes investissements il est tout é fait possible d\'obtenir des résultats significatifs sur lesquels la communication sera de plus, plus efficace.', 'Réalisation', 'Les exigences NR sont-elles prises en compte par toutes les équipes projets ?', NULL, NULL, NULL, NULL, 'RECO', '*', NULL, NULL, '#COM ; #VALUE ; #GOV ; #FIN', 'RSE/NR ;', '(X / Y ) * 100', 'Oui = 100 ; Partiel = 50 ; Non = 0', '100', 'Medium', 'Once', 'Stratégie RSE / NR - schéma directeur', NULL, NULL, NULL, NULL, NULL, NULL),
(7, 'STRATEGIE', 'STR-1.04', 'A', 'B', 'A', 'Définir et valider les besoins et les enjeux du projet afin d\'anticiper les impacts', 'Le besoin métier est-il exprimé ?', 'Les incertitudes poussent é extrapoler les besoins souvent au delé des attentes réelles ce qui est nuisible pour les impacts environnementaux', 'Conception', 'Les besoins métiers sont-ils évalués de maniére impartiale par rapport é des usages ?', NULL, NULL, NULL, NULL, 'RECO', '*', NULL, 'INCONTOURNABLE', '#VOL ; #USE ; #VALUE ; #METH ; #FIN', 'ProductOwner ;', '(X / Y ) * 100', 'Expression rédigée et validée = 100 ; Expression formalisée = 75 ; Tacite = 30 ; Non = 0', '100', 'High', 'OnUpdate', 'La documentation de collecte et expression des besoins est formalisée, validée et partagée', NULL, NULL, NULL, NULL, NULL, NULL),
(8, 'STRATEGIE', 'STR-1.05', 'B', 'A', 'B', 'Définir et valider les besoins et les enjeux du projet afin d\'anticiper les impacts', 'La cible des utilisateurs (principale et secondaire) est-elle définie, pour préciser le besoin métier ?', 'Pour répondre justement (ni trop, ni trop peu) aux attentes utilisateurs, il est indispensable de connaétre les cibles et leurs comportements., afin de ne pas surcharger les applicatifs, ni les appauvrir au point qu\'ils ne répondent plus aux attentes légitimes. Les utilisateurs secondaires ont des besoins souvent plus simples. Sans l\'identification de cette catégorie d\'utilisateur, il est difficile de dimensionner correctement le service.', 'Conception', 'Les profils utilisateurs sont-ils décrits avec les USE CASE correspondant ?', NULL, NULL, NULL, NULL, 'RECO', '*', NULL, NULL, '#USE ;  #VALUE', 'Dev ; Designer ; ProjectMngr ; ProductOwner', NULL, NULL, NULL, 'High', 'OnUpdate', 'Fiche de cadrage projet', NULL, NULL, NULL, NULL, NULL, NULL),
(9, 'STRATEGIE', 'STR-1.06', 'B', 'C', 'C', 'Définir et valider les besoins et les enjeux du projet afin d\'anticiper les impacts', 'Les matériels utilisateurs sont-ils identifiés ?', 'Certaines utilisations peuvent étre contraintes par les équipements des utilisateurs, pour permettre un choix plus large d\'équipements méme anciens et limiter les renouvellements de matériels. Il est important de connaétre les profils de matériel que les utilisateurs vont pouvoir employer, aujourd\'hui et demain.', 'Conception', 'Chaque USE CASE est-il associé é la liste des équipements matériels nécessaires é sa mise en oeuvre ?', NULL, NULL, NULL, NULL, 'RECO', '**', NULL, NULL, '#USE ;  #VALUE ; #OBSO', 'Dev ; Designer ; ProjectMngr ; ProductOwner', NULL, NULL, NULL, 'High', 'OnUpdate', 'Dossier Technique', NULL, NULL, NULL, NULL, NULL, NULL),
(10, 'STRATEGIE', 'STR-1.07', 'A', 'B', 'A', 'Définir et valider les besoins et les enjeux du projet afin d\'anticiper les impacts', 'Léensemble des produits et services sont-ils indispensables en ligne ?', 'Certaines fonctionnalités sont proposées en mode connecté alors qu\'aucune donnée distante ou aucun processus de calcul lourd n\'est requis, dans ces cas une solution locale (hors ligne) est efficace d\'un point de vue environnemental avec moins de circulation d\'information et moins d\'utilisation des réseaux. En revanche, si le mode hors ligne nécessite des duplications d\'informations, il ne se justifie que si les accés réseaux sont intermittents et posent des difficultés d\'utilisation du service.', 'Conception', 'Le produit/service peut-il étre envisagé sans étre déployé en ligne, avec un résultat similaire pour l\'entreprise ?', NULL, NULL, NULL, NULL, 'RECO', '*', NULL, NULL, '#USE ; #COM ; #VALUE', 'ProductOwner ;', '(X / Y ) * 100', 'Abordée = 100 ; Partielle = 50 ; Non traitée = 0', '100', 'Medium', 'Once', 'La fiche de cadrage projet précise les produits et services associés et leurs modes d\'opération (en ligne, en personne, ...)', NULL, NULL, NULL, NULL, NULL, NULL),
(11, 'STRATEGIE', 'STR-1.08', 'A', 'B', 'A', 'Définir et valider les besoins et les enjeux du projet afin d\'anticiper les impacts', 'Est ce que je peux proposer de couvrir les besoins autrement, en évitant le digital avec un moyen plus respectueux de léenvironnement ?', 'Certains processus manuels simples peuvent étre une alternative au tout numérique, méme au sein d\'un service numérique. Dans le cadre d\'un service numérique la tendance naturelle est d\'y intégrer toutes les étapes sans se poser la question de l\'efficacité environnementale.', 'Conception', 'Le produit/service peut-il étre envisagé sans étre numérique, avec un résultat similaire pour l\'entreprise ?', NULL, NULL, NULL, NULL, 'RECO', '*', NULL, NULL, '#USE ; #COM ; #VALUE', 'ProjectMngr ; ProductOwner ;', '(X / Y ) * 100', 'Questions formalisées', 'Besoins clients', 'Medium', 'Once', 'La fiche de cadrage projet précise pour chaque besoin une évaluation d\'impact', NULL, NULL, NULL, NULL, NULL, NULL),
(12, 'STRATEGIE', 'STR-1.09', 'A', 'B', 'B', 'Définir et valider les besoins et les enjeux du projet afin d\'anticiper les impacts', 'La solution retenue est-elle la plus respectueuse de l\'environnement? Sinon, ce choix est-il justifié par le besoin utilisateur?', 'Le nouveau service peut apporter une meilleure efficacité environnementale, mais il peut aussi participer é la création de dette environnementale supplémentaire, l\'ensemble des 3 dimensions (3P) doivent pouvoir montrer un bénéfice sinon le positionnement du service peut étre é revoir.', 'Conception', 'Avez-vous exprimé des scénarios alternatifs et évalué l\'impact NR de chacun ?', 'Le choix final est-il guidé par la performance NR ?', NULL, NULL, NULL, 'RECO', '**', NULL, NULL, '#USE ;  #COM ; #VALUE', 'Dev ; Designer ; ProjectMngr ; ProductOwner', '(X / Y ) * 100', 'Oui = 100 ; Partiel = 50 ; Non = 0', '100', 'Medium', 'OnUpdate', 'Une analyse d\'impact est définie pour permettre d\'arbitrer les impacts vis é vis de la couverture des besoins', NULL, NULL, NULL, NULL, NULL, NULL),
(13, 'STRATEGIE', 'STR-1.C03', 'A', 'B', 'A', 'Définir et valider les besoins et les enjeux du projet afin d\'anticiper les impacts', 'Le modéle économique de ce service prend-il en compte l\'éco-conception ?', 'Lorsque le NR est en dehors du modéle économique il est difficile de concilier l\'activité économique et les impacts du NR, et cela tourne le plus souvent é des arbitrages oé la prosperityabilité immédiate est privilégiée face aux 3P', 'Conception', 'Est-ce que l\'adaptation du modéle économique est envisagée pour prendre en compte l\'éco-conception?', NULL, NULL, NULL, NULL, 'CONSEIL', 'N/A', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(14, 'STRATEGIE', 'STR-1.C04', 'B', 'B', 'B', 'Définir et valider les besoins et les enjeux du projet afin d\'anticiper les impacts', 'Les aspects performances, accessibilité, sécurité et sobriété sont-ils pris en compte ensemble ?', 'Dans la majorité des cas, une meilleure performance technique est aussi synonyme d\'une meilleure efficacité environnementale, la consommation de ressources de calcul est plus faible, les volumes de transferts sont plus restreints, les algorithmes sont plus efficaces.', 'Conception', 'Quelles régles de décision sont précisées pour optimiser mon service numérique en mixant sobriété et performance ?', NULL, NULL, NULL, NULL, 'CONSEIL', 'N/A', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(15, 'STRATEGIE', 'STR-1.10', 'B', 'B', 'A', 'Définir et valider les besoins et les enjeux du projet afin d\'anticiper les impacts', 'Les gains NR sont-ils évalués en préalable du projet ?', 'Dans le cas de refonte de services, il est plus simple d\'estimer les gains attendus par le nouveau service en référence au service actuel. Pour un service nouveau, l\'étude préalable déterminera des hypothéses de gains qui devront étre affinées au cours du projet. L\'affichage des gains attendus est un marqueur important pour convaincre l\'ensemble des acteurs du bien fondé de la démarche', 'Conception', 'Avez-vous chiffré le bénéfice environnemental du projet en faisant de lééco-conception ou en décidant d\'éviter le numérique ?', NULL, NULL, NULL, NULL, 'RECO', '**', NULL, NULL, '#USE ; #MAINT ; #COM ; #VALUE ; #DEBT ; #METH ;', 'ProjectMngr ; ProductOwner ; Manager ; ITMngr', '(X / Y ) * 100', 'Formalisés = 100 ; prévus = 75 ; Identifiés = 50 ; Non = 0', '100', 'Medium', 'Once', 'Analyse d\'impact', NULL, NULL, NULL, NULL, NULL, NULL),
(16, 'STRATEGIE', 'STR-1.C05', 'B', 'B', 'B', 'Définir et valider les besoins et les enjeux du projet afin d\'anticiper les impacts', 'Le NR est-il inscrit dans la stratégie et les objectifs de l\'organisation ?', 'Afin de pouvoir installer une démarche NR dans les projets, il est indispensable que tous les décideurs adhérent é cette démarche, c\'est nécessairement le cas si le NR est inscrit dans la stratégie de l\'organisation. Dans le cas contraire, il sera difficile de mener une démarche NR, car en permanence il faudra trouver des justifications pour convaincre de poursuivre. Le projet en serait impacté donc peréu comme moins efficace \"é cause du NR\"', 'Conception', 'Est-ce que l\'ensemble des instances de décision sont partantes pour développer un service numérique éco-conéu dans votre organisation é ce moment-lé ? La solidité financiére de l\'organisation permet-elle d\'innover ?', NULL, NULL, NULL, NULL, 'CONSEIL', 'N/A', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(17, 'STRATEGIE', 'STR-1.C06', 'B', 'B', 'B', 'Définir et valider les besoins et les enjeux du projet afin d\'anticiper les impacts', 'L\'attente du marché, clients, fournisseurs, partenaires est-elle en phase avec une démarche NR ?', 'Les différents secteurs d\'activité présentent des sensibilités variables face au NR, et é périmétre identique certaines organisations ou catégories de parties prenantes ont des aspirations variables. Le fait de s\'engager dans une démarche NR et de l\'exposer est plus simple si cela correspond é une préoccupation des acteurs concernés. La communication et l\'expression de la démarche NR sera quelque peu différente en fonction de la maturité du marché cible.', 'Conception', 'Avez-vous réalisé une étude de marché pour valider les attentes des parties prenantes vis é vis du NR ?', 'Avez-vous anticipé l\'évolution de ces attentes, pour proposer un service NR méme si les demandes ne sont pas complétement formalisées au début du projet, mais évoluent au cours de la vie du service ?', NULL, NULL, NULL, 'CONSEIL', 'N/A', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(18, 'STRATEGIE', 'STR-1.C07', 'B', 'B', 'A', 'Définir et valider les besoins et les enjeux du projet afin d\'anticiper les impacts', 'Les enjeux et bénéfices attendus sont-ils bien identifiés ?', 'Pour capitaliser sur l\'innovation apportée par la prise en compte du NR il est important de déterminer les bénéfices de la démarche, que ce soit d\'un point de vue financier, ou d\'avantage compétitif ou de notoriété ou de capacité de communication et reconnaissance d\'étre un acteur engagé pour réduire les impacts du numérique.', 'Conception', 'Avez-vous chiffré les gains financiers (réduction des coéts, augmentation CA) et non financiers (communication, environnement, innovation, se différencier de la concurrence) ?', NULL, NULL, NULL, NULL, 'CONSEIL', 'N/A', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(19, 'STRATEGIE', 'STR-1.C08', 'B', 'A', 'B', 'Définir et valider les besoins et les enjeux du projet afin d\'anticiper les impacts', 'L\'organisation est-elle en mesure de faire progresser son niveau de maturité vis é vis de lééco conception et de son périmétre, avec la réalisation de projets NR ?', 'La démarche NR doit étre présente dans toutes les étapes de réalisation et cela passe nécessairement par une culture NR au sein de l\'organisation. La maturité augmentera progressivement avec les pratiques.', 'Conception', 'Quelle proportion des acteurs du projet est sensibilisée ou formée au NR ?', 'Les processus, outils et méthodes utilisés ont-ils été adaptés pour prendre en compte les enjeux NR ?', NULL, NULL, NULL, 'CONSEIL', 'N/A', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(20, 'STRATEGIE', 'STR-1.C09', 'B', 'B', 'B', 'Définir et valider les besoins et les enjeux du projet afin d\'anticiper les impacts', 'Un audit de l\'existant est-il disponible pour en améliorer les performances NR ?', 'Les principaux travers d\'un service existant par rapport aux 3P peut utilement servir de socle pour déterminer les axes de performance NR é améliorer avec des quick wins et des adaptations plus profondes.', 'Revalorisation', 'Avez-vous besoin de réaliser un audit de léexistant afin d\'améliorer le projet ou devez-vous repartir é zéro ?', NULL, NULL, NULL, NULL, 'CONSEIL', 'N/A', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(21, 'STRATEGIE', 'STR-1.11', 'B', 'B', 'A', 'Définir et valider les besoins et les enjeux du projet afin d\'anticiper les impacts', 'Les budgets financiers pour les aspects NR du projet sont-ils évalués ?', 'Le NR va générer une nouvelle catégorie d\'exigences qui devront étre prises en compte dans les démarches projet, et demanderont des lignes budgétaires clairement identifiées. Bien que souvent les projets NR soient plus efficaces, moins \"boulimiques\" de technologies \"é la mode\" et de fonctionnalités inutiles, les charges projet en final sont diminuées.', 'Acquisition', 'Est ce que jéai les moyens matériels ? Mes équipements sont-ils é renouveler ou é acheter et quelle sera la maniére la plus écologique de le faire ? Les moyens humains sont-ils identifiés pour chaque phase du projet ? Les moyens financiers pour réaliser le projet en éco-conception sont-ils alloués ?', NULL, NULL, NULL, NULL, 'RECO', '*', NULL, NULL, '#COM ; #VALUE ; #GOV ; #FIN ;', 'Manager ; ITMngr', '(X / Y ) * 100', 'Formalisés = 100 ; prévus = 75 ; Identifiés = 50 ; Non = 0', '100', 'High', 'Once', 'Affectation des budgets', NULL, NULL, NULL, NULL, NULL, NULL),
(22, 'STRATEGIE', 'STR-1.12', 'B', 'C', 'B', 'Définir et valider les besoins et les enjeux du projet afin d\'anticiper les impacts', 'Des équipements matériels sont-ils é renouveler ou é acheter pour le projet, les équipements supplémentaires nécessaires sont-ils mis é disposition en gardant en téte les principes du NR ?', 'Le lancement de nouveaux projets est souvent l\'occasion de renouveler ou compléter les équipements techniques. Dans le principe de la démarche NR, il serait plutét l\'occasion de challenger une meilleure utilisation des équipements disponibles et mettre en place toutes les solutions possibles pour les préserver.', 'Acquisition', 'La politique d\'achat/renouvellement de matériels prend-elle en compte l\'efficacité NR ?', NULL, NULL, NULL, NULL, 'RECO', '*', NULL, NULL, '#BUY ; #FIN ; #OBSO', 'ProjectMngr ; ProductOwner ;  ITMngr', '(X / Y ) * 100', 'Formalisés = 100 ; prévus = 75 ; Identifiés = 50 ; Non = 0', '100', 'High', 'Once', 'Gestion de configuration', NULL, NULL, NULL, NULL, NULL, NULL),
(23, 'STRATEGIE', 'STR-1.13', 'A', 'A', 'B', 'Définir et valider les besoins et les enjeux du projet afin d\'anticiper les impacts', 'Mes ressources humaines (Identifiées pour chaque phase) sont-elles disponibles ?', 'L\'efficacité de la démarche NR ne peut étre atteinte sans la connaissance des enjeux NR par les personnes qui prendront part au projet.', 'N/A', 'Les sensibilisations/formations NR nécessaires sont-elles budgetées et planifiées ?', NULL, NULL, NULL, NULL, 'RECO', '*', NULL, NULL, '#COM ; #VALUE ; #METH ; #HR ;', 'ProjectMngr ; RSE/NR ; HR ; ITMngr', '(X / Y ) * 100', 'Formalisés = 100 ; Identifiés = 50 ; Non = 0', '100', 'High', 'Once', 'La fiche de cadrage projet précise les profils de tous les membres de l\'équipe.', NULL, NULL, NULL, NULL, NULL, NULL),
(24, 'STRATEGIE', 'STR-1.C10', 'B', 'B', 'B', 'Définir et valider les besoins et les enjeux du projet afin d\'anticiper les impacts', 'Les sponsors et éventuelles résistances de la démarche NR sont-ils identifiés ?', 'Lorsque la démarche NR est peu installée dans le modéle opérationnel de l\'organisation, les luttes d\'influence vont parfois décider de l\'avenir du NR. Pour faciliter l\'adoption, les freins doivent étre identifiés et les arguments pour vaincre les résistances doivent étre préparés en avance de phase et mis é disposition des sponsors de cette démarche.', 'N/A', 'Avez-vous anticipé les résistances en exposant les arguments par rapport aux freins é la mise en place de la démarche : obstacles prévisibles, motivation, mauvaise communication interne, management peu impliqué, autres projets lourds et prioritaires, investissements lourds et récents, réglementation,... ?', NULL, NULL, NULL, NULL, 'CONSEIL', 'N/A', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(25, 'STRATEGIE', 'STR-1.14', 'B', 'A', 'B', 'Définir et valider les besoins et les enjeux du projet afin d\'anticiper les impacts', 'Les objectifs NR sont-ils exposés clairement et partagés par toutes les parties prenantes, internes ou externes é l\'organisation ?', 'Plusieurs acteurs, y compris externes é l\'organisation, peuvent étre impliqués dans le projet. La perméabilité de la démarche NR vers toutes les parties prenantes peut permettre d\'identifier / activer des leviers que des acteurs n\'auraient peut-étre pas spontanément exprimés.', 'Conception', 'Avez-vous mis en place une communication formelle des objectifs NR, accessible é tous les acteurs ?', 'La constitution des objectifs NR est-elle documentée pour permettre son exposition aux acteurs impliqués ?', NULL, NULL, NULL, 'RECO', '*', NULL, NULL, '#COM ; #GOV ; #HR', 'ProjectMngr ; Manager ; RSE/NR ; HR ; ITMngr', '(X / Y ) * 100', 'Informations diffusées', 'Parties prenantes', 'High', 'Once', 'Fiche projet', NULL, NULL, NULL, NULL, NULL, NULL),
(26, 'STRATEGIE', 'STR-1.15', 'B', 'B', 'A', 'Définir et valider les besoins et les enjeux du projet afin d\'anticiper les impacts', 'Les délais de mise en oeuvre sont-ils envisagés ?', 'La gestion des délais projet est toujours au centre des préoccupations. Pour que le NR ne soit pas l\'élément pointé du doigt il faut que les délais (en plus ou en moins) liés é cette démarche soient exprimés. Le délai projet n\'intervient qu\'une fois dans la vie du service, le gain ou la dette NR du projet va se multiplier tout au long du cycle de vie du projet.', 'Conception', 'Avez-vous chiffré les bénéfices/sur-coéts de la démarche NR sur le planning projet ?', NULL, NULL, NULL, NULL, 'RECO', '*', NULL, NULL, '#METH ;', 'ProjectMngr ; ITMngr ;', '(X / Y ) * 100', 'Formalisés = 100 ; Identifiés = 50 ; Non = 0', '100', 'Medium', 'Once', 'Fiche de cadrage projet', NULL, NULL, NULL, NULL, NULL, NULL),
(27, 'STRATEGIE', 'STR-1.16', 'A', 'A', 'A', 'Définir et valider les besoins et les enjeux du projet afin d\'anticiper les impacts', 'Le besoin couvert par le service numérique s\'inscrit-il dans l\'un des objectifs de développement durable?', 'Le questionnement de l\'utilité du service numérique par rapport é l\'un ou plusieurs des 17 ODD est capital, afin d\'éviter la production de services numériques qui ne soient pas porteurs de valeurs.', 'Conception', 'Le service répond-t-il é au moins l\'un des Objectifs de Développement Durable (ODD) ?', NULL, NULL, NULL, NULL, 'RECO', '*', NULL, 'INCONTOURNABLE', '#USE ; #COM ; #VALUE ; #METH ; #GOV ;', 'ProjectMngr ; ProductOwner ; RSE/NR ;', '(X / Y ) * 100', 'Au moins 1 ODD couvert = 100 ; 0 ODD = 0', '100', 'High', 'Once', 'Fiche de cadrage projet', NULL, NULL, 'Y', '#INSTRUMENTATION', '***', 'Tags ODD dans le document de cardage'),
(28, 'STRATEGIE', 'STR-2', NULL, NULL, NULL, 'Prévoir, préparer et valider les disponibilités des ressources humaines du Numérique Responsable', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'ODD#12', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(29, 'STRATEGIE', 'STR-2.01', 'A', 'A', 'B', 'Prévoir, préparer et valider les disponibilités des ressources humaines du Numérique Responsable', 'A-t-on les personnes avec les compétences techniques les plus pertinentes pour chaque élément du projet ?', 'La démarche NR requiert avant tout une pratique métier affirmée. Il est difficile d\'optimiser des éléments techniques lorsque la pratique n\'est pas suffisamment ancrée. A l\'inverse les optimisations NR sont parfois \"contre-intuitives\" et une bonne maétrise du NR et des 3P est indispensable.', 'N/A', 'Combien d\'acteurs dans chaque étape du projet ont une formation NR ?', NULL, NULL, NULL, NULL, 'RECO', '*', NULL, NULL, '#METH ; #HR ;', 'ProjectMngr ; Manager ; RSE/NR ; HR ; ITMngr', '(X / Y ) * 100', 'Formés au NR', 'Membre équipe', 'High', 'Periodic', 'La fiche de cadrage projet précise pour chaque profil des membres de l\'équipe, les compétences nécessaires et les besoins de ressources externes', NULL, NULL, NULL, NULL, NULL, NULL),
(30, 'STRATEGIE', 'STR-2.02', 'A', 'A', 'B', 'Prévoir, préparer et valider les disponibilités des ressources humaines du Numérique Responsable', 'Les référents NR sont-ils listés et la liste communiquée aux acteurs du projet?', 'Le NR adresse un trés large périmétre, qu\'il est difficile d\'appréhender complétement dans chaque phase projet. Il est indispensable que les professionnels intervenant sur le projet puissent s\'appuyer é tous moments sur des personnes référence pour les assister dans les meilleures pratiques é déployer.', 'N/A', 'Quels sont les référents officiels du projet en terme d\'éco-conception ? en interne ou en externe ?', NULL, NULL, NULL, NULL, 'RECO', '*', NULL, NULL, '#COM ; #METH ; #HR', 'ProjectMngr ; Manager ; RSE/NR ; HR ; ITMngr', '(X / Y ) * 100', 'Formalisés = 100 ; Identifiés = 50 ; Non = 0', '100', 'High', 'Once', 'La fiche de cadrage projet désigne le référent NR du projet.', NULL, NULL, NULL, NULL, NULL, NULL),
(31, 'STRATEGIE', 'STR-2.03', 'A', 'A', 'A', 'Prévoir, préparer et valider les disponibilités des ressources humaines du Numérique Responsable', 'Une stratégie pour sensibiliser les collégues/partenaires/clients au NR est-elle mise en place avec des étapes permettant de faire progresser les pratiques en continu ?', 'L\'adoption du NR est une démarche globale, qui ne doit pas étre freinée par la capacité de tout faire tout de suite, elle s\'inscrit dans une progression qui s\'installe au fur et é mesure des pratiques.', 'N/A', 'Le plan de formation NR est-il mis en place et suivi ?', NULL, NULL, NULL, NULL, 'RECO', '*', NULL, NULL, '#METH ; #GOV', 'ProjectMngr ; Manager ; RSE/NR ; HR ; ITMngr', '(X / Y ) * 100', 'Formalisés = 100 ; Identifiés = 50 ; Non = 0', '100', 'High', 'Periodic', 'Le plan de formation intégre les diffusions de sensibilisations et formations aux aspects NR.', NULL, NULL, NULL, NULL, NULL, NULL),
(32, 'STRATEGIE', 'STR-2.04', 'A', 'A', 'B', 'Prévoir, préparer et valider les disponibilités des ressources humaines du Numérique Responsable', 'Les parties prenantes internes et externes sont-elles sensibilisées, voire formées/certifiées au NR ?', 'Chaque partie prenante é besoin d\'une information efficace sur son domaine d\'expertise et d\'action, seul un plan de formation adapté peut apporter cette efficacité en amont des projets', 'N/A', 'Quel budget formation est alloué par type de profil ?', NULL, NULL, NULL, NULL, 'RECO', '*', NULL, 'INCONTOURNABLE', '#COM ; #METH', 'ProjectMngr ; Manager ; RSE/NR ; HR ; ITMngr', '(X / Y ) * 100', 'Sensibilisations NR', 'Membre équipe', 'High', 'Periodic', 'Le plan de formation intégre les diffusions de sensibilisations et formations aux aspects NR. La communication externe propage les enjeux environnementaux du numérique.', NULL, NULL, NULL, NULL, NULL, NULL),
(33, 'STRATEGIE', 'STR-2.C01', 'B', 'A', 'B', 'Prévoir, préparer et valider les disponibilités des ressources humaines du Numérique Responsable', 'Les équipes ont-elle été questionnées en amont du lancement du projet, l\'impact a-t-il été évalué et communiqué?', 'Lorsque le NR est intégré dans la démarche projet, tous les éléments d\'optimisation des projets sont mis en oeuvre, notamment les adaptations des processus avec la prise en compte des retours d\'expérience sont naturels et totalement adaptés au NR', 'Revalorisation', 'Les retours des équipes sont-ils revus et discutés pour améliorer les processus NR ,', NULL, NULL, NULL, NULL, 'CONSEIL', 'N/A', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(34, 'STRATEGIE', 'STR-2.C02', 'B', 'A', 'B', 'Prévoir, préparer et valider les disponibilités des ressources humaines du Numérique Responsable', 'Chaque partie-prenante interne et externe a-t-elle la latitude de prendre des décisions pouvant influer sur l\'impact du Numérique Responsable ?', 'Les décisions projets qui peuvent avoir un impact important sur l\'environnement doivent pouvoir étre prises rapidement. Pour cela il est indispensable de définir l\'espace de décision de chaque acteur et les mécanismes de validation. En l\'absence de cette clarification, des décisions erratiques, méme pour de bonnes raisons, pourraient intervenir et décrédibiliser la démarche NR, ou entraéner une forme d\'auto-censure limitant les bénéfices NR', 'N/A', 'Le circuit et l\'autonomie de décision ayant un impact NR est-il défini pour chaque acteur ?', NULL, NULL, NULL, NULL, 'CONSEIL', 'N/A', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(35, 'STRATEGIE', 'STR-2.C03', 'A', 'B', 'A', 'Prévoir, préparer et valider les disponibilités des ressources humaines du Numérique Responsable', 'Mes cibles peuvent-elles adhérer é ma démarche NR et de quelle maniére ?', 'La démarche NR est mise en place pour le bien de toute la communauté, il serait dommage que cette prise en compte reste confidentielle et ne participe pas é la prise de conscience collective de l\'impact des services numériques', 'Revalorisation', 'Avez-vous prévu un plan de communication pour évangéliser sur les aspects NR ?', NULL, NULL, NULL, NULL, 'CONSEIL', 'N/A', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(36, 'STRATEGIE', 'STR-2.05', 'B', 'B', 'A', 'Prévoir, préparer et valider les disponibilités des ressources humaines du Numérique Responsable', 'Le projet avec une démarche NR va t-il réellement répondre aux besoins de mes clients ?', 'Une démarche NR appliquée sur un service numérique peut dans certains cas générer un peu d\'inconfort / de disruption pour les utilisateurs dans leurs pratiques. Un mécanisme d\'accompagnement ou de communication peut aisément lever ces points. Et apporter une valorisation importante.', 'Conception', 'Mesurez-vous les satisfactions clients sur les aspects NR ?', NULL, NULL, NULL, NULL, 'RECO', '*', NULL, NULL, '#USE ; #VALUE', 'ProjectMngr ; Manager ; RSE/NR ; ITMngr', '(X / Y ) * 100', 'Abordée = 100 ; Partielle = 50 ; Non traitée = 0', '100', 'Medium', 'Once', 'Fiche de cadrage projet', NULL, NULL, NULL, NULL, NULL, NULL),
(37, 'STRATEGIE', 'STR-2.C04', 'B', 'A', 'B', 'Prévoir, préparer et valider les disponibilités des ressources humaines du Numérique Responsable', 'Les aspects Numérique Responsable sont-ils propagés et entretenus sur l\'ensemble des acteurs ?', 'Obtenir et maintenir l\'engagement des acteurs est un point important pour que le NR devienne la norme de fait.', 'Utilisation', 'Quelle est votre fréquence de communication NR pour chaque profil de partie prenante ?', NULL, NULL, NULL, NULL, 'CONSEIL', 'N/A', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(38, 'STRATEGIE', 'STR-3', NULL, NULL, NULL, 'Déterminer et planifier la capacité de suivre et capitaliser sur les composantes Numérique Responsable des projets', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'ODD#12', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(39, 'STRATEGIE', 'STR-3.01', 'A', 'C', 'B', 'Déterminer et planifier la capacité de suivre et capitaliser sur les composantes Numérique Responsable des projets', 'Chacune des phases du cycle de vie (démarrage, maturité, arrét) sont-elles identifiées avec des actions spécifiques ?', 'La démarche ACV est une base de la prise en compte du NR dans la conception des services numériques. Elle permet d\'associer des actions é chaque étape du cycle de vie.', 'Revalorisation', 'Avez-vous listé toutes les actions associées au NR pour les différentes étapes projet ?', NULL, NULL, NULL, NULL, 'RECO', '*', NULL, NULL, '#USE ; #MAINT ; #COM ; #VALUE ; #DEBT ; #METH', 'Dev ; Designer ; ProjectMngr ; ProductOwner ; RSE/NR', '(X / Y ) * 100', 'Formalisés = 100 ; Identifiés = 50 ; Non = 0', '100', 'Low', 'OnUpdate', 'L\'analyse d\'impact détermne les actions de réduction des impacts avec les acteurs concernés', NULL, NULL, NULL, NULL, NULL, NULL),
(40, 'STRATEGIE', 'STR-3.02', 'A', 'B', 'B', 'Déterminer et planifier la capacité de suivre et capitaliser sur les composantes Numérique Responsable des projets', 'Les données ACV sont-elles réutilisées dans le cas d\'une adaptation d\'un service existant ?', 'Les analyses ACV peuvent étre réalisées sur des services existants afin de les rendre plus efficaces dans le cas d\'une refonte ou adaptation d\'un service existant', 'Revalorisation', 'Avez-vous mis é disposition des équipes projet les ACV précédentes ?', 'L\'ACV de ce projet est-elle rendue disponible pour d\'autres projets ?', NULL, NULL, NULL, 'RECO', '**', NULL, NULL, '#USE ; #MAINT ; #COM ; #VALUE ; #DEBT ; #METH', 'Dev ; Designer ; ProjectMngr ; ProductOwner ; RSE/NR', '(X / Y ) * 100', 'Formalisés = 100 ; Identifiés = 50 ; Non = 0', '100', 'Low', 'OnUpdate', 'Une ACV minimale (\"screening\") est disponible pour le service existant', NULL, NULL, NULL, NULL, NULL, NULL),
(41, 'STRATEGIE', 'STR-3.03', 'B', 'B', 'A', 'Déterminer et planifier la capacité de suivre et capitaliser sur les composantes Numérique Responsable des projets', 'Si je décide de refaire mon service, quéest-ce que je conserve de léancien et comment je le nettoie de la toile ?', 'Une bonne optimisation des enjeux NR passe aussi par la réutilisation des éléments qui ont été conéus avec une prise en compte du NR afin d\'éviter de dupliquer certains éléments ou d\'avoir des composants en double emploi, ou tout simplement inactifs, qui ne seraient maintenus que parce que l\'effort de nettoyage n\'a pas été pris en compte.', 'Revalorisation', 'Les  parties réutilisables de l\'ancien site sont-elles décrites ?', 'Une stratégie globale d\'archivage des sites/services numériques existe-t-elle dans l\'entreprise ?', NULL, NULL, NULL, 'RECO', '*', NULL, NULL, '#MAINT ; #DEBT', 'ProjectMngr ; ITMngr ;', '(X / Y ) * 100', 'Volume réutilsé', 'Volume service', 'Medium', 'OnUpdate', 'Analyse d\'impact', NULL, NULL, NULL, NULL, NULL, NULL),
(42, 'STRATEGIE', 'STR-3.04', 'B', 'B', 'A', 'Déterminer et planifier la capacité de suivre et capitaliser sur les composantes Numérique Responsable des projets', 'Est-ce que je peux recycler des briques logicielles ou du contenu existant ? Est-ce que je vais créer de la valeur réutilisable/valorisable dans d\'autres projets ?', 'La conception modulaire est une pratique métier trés largement employée. Le NR peut tirer un bénéfice de ces méthodes lorsque chaque composant modulaire est bien décrit et que ses atouts ou travers sont bien identifiés.', 'Revalorisation', 'Existe-t-il un référentiel de briques logicielles partagées (authentification, base de compte utilisateurs, API génériques d\'interconnections, etc.) ?', 'Une stratégie globale de mutualisation/capitalisation des réalisations inter-projets est-elle en place dans l\'entreprise ?', NULL, NULL, NULL, 'RECO', '*', NULL, NULL, '#MAINT ; #DEBT ; #METH', 'ProjectMngr ;  ITMngr ;', '(X / Y ) * 100', 'Volume réutilsé', 'Fonctionnalités', 'Medium', 'OnUpdate', 'Fiche de cadrage projet', NULL, NULL, NULL, NULL, NULL, NULL),
(43, 'STRATEGIE', 'STR-3.C01', 'B', 'C', 'B', 'Déterminer et planifier la capacité de suivre et capitaliser sur les composantes Numérique Responsable des projets', 'Comment sont gérées les phases de fin de vie, dés le début ou é la fin d\'un projet ?', 'Lors de la conception, chaque partie composant le projet peut étre amenée é avoir une durée de vie différente. Il est important que l\'ensemble des procédure de fin de vie soient exprimées pour que l\'empreinte des composants inutilisés et arrétés soient nulle.', 'Fin de Vie', 'Avez-vous listé l\'ensemble des procédures de dé-provisionnement ?', 'La modularité de la conception permettra-t-elle un déprovisionnement partiel, avec l\'abandon de fonctionnalités obsolétes mais conservation de l\'implémentation de certains processus métiers toujours utiles ?', NULL, NULL, NULL, 'CONSEIL', 'N/A', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(44, 'STRATEGIE', 'STR-3.C02', 'C', 'C', 'A', 'Déterminer et planifier la capacité de suivre et capitaliser sur les composantes Numérique Responsable des projets', 'La prise de conscience des \"coéts de non-qualité\" est-elle partagée par toutes les parties prenantes internes ?', 'La qualité du logiciel produit é un impact direct sur les aspects NR. En effet, les défauts générent de multiples livraisons de nouvelles versions successives, les anomalies entraénent des productions de traces d\'anomalies importantes, et des fonctionnalités plus consommatrices de ressources techniques, parfois pour un résultat non atteint, donc des ressources ont été gaspillées pour ne produire aucun résultat.', 'Administration', 'Les défauts de qualités sont-ils consignés, analysés et traités aussi sur l\'axe NR ?', NULL, NULL, NULL, NULL, 'CONSEIL', 'N/A', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(45, 'STRATEGIE', 'STR-3.05', 'A', 'B', 'B', 'Déterminer et planifier la capacité de suivre et capitaliser sur les composantes Numérique Responsable des projets', 'Lors d\'un audit, les éléments suivants sont-ils appréhendés ? A quel moment je géche de l\'énergie dans mon service ? Quels sont les pages/parcours utilisateurs en trop ou inutiles qui devraient donc étre nettoyées ?', 'Lorsque le parcours utilisateur est court, moins de composants sont mis en oeuvre, les flux sont plus réduits, la compréhension du service est plus simple, donc les aspects NR sont mieux pris en compte.', 'Utilisation', 'La consommation d\'énergie est-elle mesurée pour chaque parcours-type du site ?', NULL, NULL, NULL, NULL, 'RECO', '**', NULL, NULL, '#PERF ; #VOL ; #USE ; #VALUE ;', 'ProjectMngr ; ITMngr ; Staff', '(X / Y ) * 100', 'Parcours analysés', 'Parcours user', 'Medium', 'OnUpdate', 'L\'audit se base sur la cartographie des parcours / branches du service, pour analyser les volumes et charges d\'utilisation générées et déterminer les améliorations nécesaires', NULL, NULL, NULL, NULL, NULL, NULL),
(46, 'STRATEGIE', 'STR-3.06', 'A', 'B', 'A', 'Déterminer et planifier la capacité de suivre et capitaliser sur les composantes Numérique Responsable des projets', 'Les indicateurs pour mesurer l\'efficacité de ma démarche NR sont-ils identifiés et suivis ?', 'La mesure de certains éléments est un objectif qui permet de valoriser le résultat d\'une démarche NR. La recherche d\'indicateurs trop complexes peut s\'avérer contre-productive, il vaut mieux s\'attacher é des données fiables et marquantes et faire évoluer le nombre et la précision des indicateurs au fur et é mesure des gains de maturité de l\'organisation et des acteurs internes', 'Administration', 'Quels sont les indicateurs définis pour mesurer l\'efficacité de ma démarche NR?', NULL, NULL, NULL, NULL, 'RECO', '**', NULL, NULL, '#PERF ; #VOL ; #SEC ; #USE ; #METH ; #GOV', 'ProjectMngr ; ProductOwner ; Manager ; RSE/NR ; ITMngr ;', '(X / Y ) * 100', 'Formalisés = 100 ; Identifiés = 50 ; Non = 0', '100', 'High', 'Periodic', 'Les extensions des navigateurs (ex: CHROME : extension GreenIT) sont utilisés et exposés pour valider l\'efficacité.', NULL, NULL, NULL, NULL, NULL, NULL),
(47, 'STRATEGIE', 'STR-3.C03', 'B', 'B', 'A', 'Déterminer et planifier la capacité de suivre et capitaliser sur les composantes Numérique Responsable des projets', 'Les outils pour mesurer l\'efficacité de ma démarche NR sont-ils identifiés et communiqués ?', 'Permettre aux acteurs de valider les résultats obtenus par la prise en compte du NR est important car il permet é chacun de s\'approprier cette démarche et de situer ses actions dans cet objectif avec des outils adaptés é chaque acteur', 'Administration', 'Quels sont les outils sélectionnés pour mesurer l\'efficacité de ma démarche NR ?', NULL, NULL, NULL, NULL, 'CONSEIL', 'N/A', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(48, 'STRATEGIE', 'STR-3.07', 'B', 'B', 'B', 'Déterminer et planifier la capacité de suivre et capitaliser sur les composantes Numérique Responsable des projets', 'La durée de vie du projet est-elle définie ?', 'Un service numérique ne peut plus étre conéu pour l\'éternité, il est étroitement lié aux environnements socio-économiques. A cette fin, il doit pouvoir étre remis en cause réguliérement. Ceci présente aussi l\'avantage de ne pas cumuler de dettes techniques et de rester au fait des avancées et pratiques les plus efficaces dans les domaines NR qui évoluent trés rapidement.', 'Fin de Vie', 'Avez-vous estimé la période durant laquelle le service sera utile ?', NULL, NULL, NULL, NULL, 'RECO', '*', NULL, NULL, '#PERF ; #VOL ; #SEC ; #USE', 'ProjectMngr ; ProductOwner ; Manager ; ITMngr ;', NULL, NULL, NULL, 'Medium', 'Once', 'Fiche de cadrage projet', NULL, NULL, NULL, NULL, NULL, NULL),
(49, 'STRATEGIE', 'STR-3.C04', 'B', 'A', 'A', 'Déterminer et planifier la capacité de suivre et capitaliser sur les composantes Numérique Responsable des projets', 'Est ce que les expériences des projets passés sont prises en compte ?', 'Beaucoup de projets au sein d\'une organisation ont des principes communs, il est nécessaire de pouvoir s\'appuyer sur d\'autres projets pour capitaliser sur les gains et bénéfices des démarches NR du passé, et de les enrichir avec les nouvelles connaissances ou pratiques acquises.', 'Revalorisation', 'Combien de post-mortem de projets avez-vous de disponibles pour capitaliser sur les réalisations précédentes ?', NULL, NULL, NULL, NULL, 'CONSEIL', 'N/A', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(50, 'STRATEGIE', 'STR-3.C05', 'B', 'A', 'A', 'Déterminer et planifier la capacité de suivre et capitaliser sur les composantes Numérique Responsable des projets', 'Est-il prévu de capitaliser l\'expérience de ce projet pour le futur?', 'Les méthodologies projet utilisent fréquemment les post-mortem pour apprendre des réalisations passées et progresser. Le NR doit apparaétre dans ces thématiques.', 'Revalorisation', 'Quelle production documentaire est associée au projet pour les aspects NR ?', NULL, NULL, NULL, NULL, 'CONSEIL', 'N/A', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(51, 'STRATEGIE', 'STR-3.C06', 'B', 'B', 'A', 'Déterminer et planifier la capacité de suivre et capitaliser sur les composantes Numérique Responsable des projets', 'Les indicateurs couvrent-ils les éléments pour mesurer, évaluer et estimer le niveau de maturité NR, le piloter, léaméliorer et le contréler ?', 'La démarche NR est itérative, elle doit s\'insérer dans un processus d\'amélioration continue.', 'Administration', 'Les sources de données des indicateurs sont-elles fiables ?', NULL, NULL, NULL, NULL, 'CONSEIL', 'N/A', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(52, 'STRATEGIE', 'STR-3.08', 'A', 'A', 'B', 'Déterminer et planifier la capacité de suivre et capitaliser sur les composantes Numérique Responsable des projets', 'La conformité numérique responsable est-elle exposée ?', 'Un service numérique concu en respectant les recommandations du numérique responsable doit exposer les acquis et faiblesses identfiés. Dans une démarche similaire é celle déployée pour l\'accessibilité avec la \"déclaration d\'accessibilité\".', 'Conception', 'Une déclaration de conception responsable est-elle disponible pour le service numérique ?', NULL, NULL, NULL, NULL, 'RECO', '*', NULL, 'INCONTOURNABLE', '#USE ; #COM ; #VALUE ; #METH ; #GOV ;', 'ProjectMngr ; ProductOwner ; RSE/NR ;', '(X / Y ) * 100', 'Document validé et exposé = 100 ; Partiel = 50 ; Prévu = 25 ; Non = 0', '100', 'High', 'Once', 'Document \"déclaration de conception responsable\"', NULL, NULL, NULL, NULL, NULL, NULL),
(53, 'STRATEGIE', 'STR-4', NULL, NULL, NULL, 'Prévoir, préparer et valider les éléments techniques du projet pour la conformité avec le Numérique Responsable', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'ODD#10 ; ODD#12 ; ODD#13', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(54, 'STRATEGIE', 'STR-4.C01', 'B', 'B', 'B', 'Prévoir, préparer et valider les éléments techniques du projet pour la conformité avec le Numérique Responsable', 'En dehors des éléments constituant le service numérique, quels sont les services annexes nécessaires : technologie tiers / open source, hébergement vert, agence digitale éco-responsable ... ?', 'Les phases de design, de développements, de production ont des impacts sur les aspects NR qui doivent étre abordés dans toutes les ressources associées au projet.', 'Acquisition', 'L\'annexe technique du projet est-elle formalisée ?', NULL, NULL, NULL, NULL, 'CONSEIL', 'N/A', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(55, 'STRATEGIE', 'STR-4.C02', 'A', 'B', 'B', 'Prévoir, préparer et valider les éléments techniques du projet pour la conformité avec le Numérique Responsable', 'Chaque fonction du service est-elle appréhendée en regard de son importance dans le service ?', 'Les fonctionnalités les plus importantes sont parfois moins consommatrices de ressources que des fonctions marginales ce qui n\'a pas de sens dans une démarche NR cohérente.', 'Conception', 'Avez-vous appliqué une démarche 80/20 pour chaque fonction en rapport avec ses impacts environnementaux et son importance ?', NULL, NULL, NULL, NULL, 'CONSEIL', 'N/A', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(56, 'STRATEGIE', 'STR-4.01', 'C', 'C', 'B', 'Prévoir, préparer et valider les éléments techniques du projet pour la conformité avec le Numérique Responsable', 'Est-ce qu\'une donnée réglementée (santé, bancaire, ...)  est utilisée dans l\'ensemble du systéme ?', 'L\'exposition de l\'utilisateur doit pouvoir étre strictement encadrée. Au delé de la réglementation, c\'est aussi la dimension humaine du NR qui est en jeu', 'Conception', 'Chaque donnée réglementée est-elle justifiée et identifiée ?', NULL, NULL, NULL, NULL, 'RECO', '*', NULL, NULL, '#SEC ; #USE ; #LEG', 'ProductOwner ;', '(X / Y ) * 100', 'Formalisés = 100 ; Identifiés = 50 ; Non = 0', '100', 'High', 'Once', 'Analyse de risques', NULL, NULL, NULL, NULL, NULL, NULL),
(57, 'STRATEGIE', 'STR-4.02', 'C', 'C', 'B', 'Prévoir, préparer et valider les éléments techniques du projet pour la conformité avec le Numérique Responsable', 'Des données personnelles au sens du RGPD sont-elles utilisées ?', 'L\'utilisateur vous confie des données. Pour prendre en considération l\'aspect PEOPLE, et ne pas s\'exposer é des sanctions onéreuses, la démarche NR incite é fortement limiter ces données.', 'Conception', 'Chaque donnée personnelle est-elle répertoriée et gérée ?', NULL, NULL, NULL, NULL, 'RECO', '*', NULL, NULL, '#SEC ; #USE ; #LEG', 'ProductOwner ;', NULL, NULL, NULL, 'High', 'Once', 'Analyse de risques', NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `goodpractices` (`cle`, `Family`, `Id`, `Planet`, `People`, `Prosperity`, `Recommendation`, `Criteria`, `Justification`, `Stage`, `Tests1`, `Tests2`, `Tests3`, `Correspondence`, `Link`, `Type`, `Difficulty`, `Goal`, `Incontournable`, `Tags`, `Actors`, `Indicators_`, `IndicatorsX`, `IndicatorsY`, `Priority`, `Recurrence`, `UseCase`, `Example`, `Limite`, `Automatizable`, `AutomatizationScript`, `AutomatizationLevel`, `AutomatizationInformation`) VALUES
(58, 'STRATEGIE', 'STR-4.C03', 'B', 'B', 'B', 'Prévoir, préparer et valider les éléments techniques du projet pour la conformité avec le Numérique Responsable', 'Est ce que le choix de solutions Open Source est privilégié ?', 'Une solution Open Source n\'est pas un gage d\'efficacité NR et les différentes alternatives doivent étre challengées. Mais l\'open source permet de limiter les ambitions et mainmises de certains acteurs dont les pratiques économiques et sociales sont opaques.', 'Acquisition', 'De quelles métriques NR disposez-vous pour les solutions Open Source envisagées ?', NULL, NULL, NULL, NULL, 'CONSEIL', 'N/A', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(59, 'STRATEGIE', 'STR-4.C04', 'B', 'B', 'B', 'Prévoir, préparer et valider les éléments techniques du projet pour la conformité avec le Numérique Responsable', 'Les outils intégrés (CMS, frameworks, ...), ou les développements internes, apportent-ils des éléments positifs vis é vis de la démarche NR ?', 'Dans les choix BUY/BUILD la dimension NR doit pouvoir étre évaluée et projetée dans le temps pour s\'assurer que les choix sont suffisamment évolutifs pour permettre de gagner en conformité NR au fil du temps. Et pas uniquement sur la phase de développement / intégration.', 'Acquisition', 'De quelles métriques NR disposez-vous pour les solutions déployées en interne ?', 'De quelles métriques NR disposez-vous pour les solutions intégrées ?', NULL, NULL, NULL, 'CONSEIL', 'N/A', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(60, 'STRATEGIE', 'STR-4.C05', 'B', 'B', 'B', 'Prévoir, préparer et valider les éléments techniques du projet pour la conformité avec le Numérique Responsable', 'Quelle est la technologie adhérant aux principes NR la plus appropriée é mes besoins ?', 'Le choix technologique doit étre dicté par des critéres NR, plutét que par l\'attrait technologique de la nouveauté é la mode. La liste des critéres doit étre déterminée par un collectif pas exclusivement composé des techniciens qui vont les utiliser afin d\'avoir une vision plus large.', 'Acquisition', 'Avez-vous une grille de critéres d\'évaluation des alternatives technologiques disponibles ?', NULL, NULL, NULL, NULL, 'CONSEIL', 'N/A', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(61, 'STRATEGIE', 'STR-4.C06', 'B', 'B', 'A', 'Prévoir, préparer et valider les éléments techniques du projet pour la conformité avec le Numérique Responsable', 'Dans le cas d\'un développement interne (CMS, Framework, ...), quelle est la pertinence de le retravailler sous l\'angle d\'une optimisation NR, si la démarche NR est peu maétrisée et le projet déjé avancé ?', 'Les outils sélectionnés pour les développements doivent permettre d\'intégrer les principes NR sans qu\'une refonte complexe ne soit nécessaire. La complexité écarte de fait les bonnes volontés et écarte les personnes les moins aguerries, ce qui donne au projet une forme élitiste peu compatible avec les principes NR.', 'Acquisition', 'Le coét de mise é niveau vis-é-vis du NR des solutions déployées en interne est-il chiffré ?', NULL, NULL, NULL, NULL, 'CONSEIL', 'N/A', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(62, 'STRATEGIE', 'STR-4.C07', 'B', 'B', 'A', 'Prévoir, préparer et valider les éléments techniques du projet pour la conformité avec le Numérique Responsable', 'Comment choisir les solutions techniques les plus propices au NR ?', 'Un choix de base technique doit prendre en compte les aspects techniques mais aussi les orientations des fournisseurs et la facilité de déployer la production réalisée é partir de ce socle en limitant les impacts environnementaux. Les critéres permettant la sélection de ce type de composants doivent étre clairement définis.', 'Acquisition', 'Avez-vous listé les régles qui permettent d\'arbitrer les choix techniques, par exemple : la charge de formation par rapport au gain NR, les aspects économiques moyen et long terme par rapport au gain NR, la performance technique et le positionnement NR de la solution et du fournisseur?', NULL, NULL, NULL, NULL, 'CONSEIL', 'N/A', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(63, 'SPECIFICATIONS', 'SPC-1', NULL, NULL, NULL, 'Organiser les ressources humaines projets pour permettre la prise en compte de la démarche NR', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'ODD#12', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(64, 'SPECIFICATIONS', 'SPC-1.01', 'A', 'A', 'B', 'Organiser les ressources humaines projets pour permettre la prise en compte de la démarche NR', 'Les enjeux NR du projet ont-ils été communiqués é l\'équipe dés l\'origine ?', 'La démarche NR est mieux prise en compte lorsqu\'elle s\'insére par un processus systémique et qu\'elle est exposée comme direction é suivre pour tous les acteurs.', 'N/A', 'Combien de personnes sensibilisées / formées au NR dans l\'équipe projet ?', NULL, NULL, NULL, NULL, 'RECO', '*', NULL, 'INCONTOURNABLE', '#COM ; #METH ; #GOV ; #HR', 'ProjectMngr ; ProductOwner ; Manager ; RSE/NR ;  ITMngr', '(X / Y ) * 100', 'Enjeux NR communiqués', 'Membre équipe', 'High', 'Once', 'La fiche projet précise les objectifs et attendus NR', NULL, NULL, NULL, NULL, NULL, NULL),
(65, 'SPECIFICATIONS', 'SPC-1.02', 'A', 'A', 'B', 'Organiser les ressources humaines projets pour permettre la prise en compte de la démarche NR', 'Les référents NR de l\'organisation sont-ils identifiés ?', 'Le NR nécessite une forte connaissance qui doit étre portée par des personnes qui sont les mieux adaptées.', 'N/A', 'Y a-t-il un référent NR permanent ou dédié au projet ?', NULL, NULL, NULL, NULL, 'RECO', '*', NULL, NULL, '#COM ; #METH ; #GOV ; #HR', 'RSE/NR ; HR ;', '(X / Y ) * 100', 'Formalisés = 100 ; Identifiés = 50 ; Non = 0', '100', 'High', 'Once', 'La fiche de cadrage projet désigne le référent NR du projet.', NULL, NULL, NULL, NULL, NULL, NULL),
(66, 'SPECIFICATIONS', 'SPC-1.C01', 'A', 'B', 'A', 'Organiser les ressources humaines projets pour permettre la prise en compte de la démarche NR', 'Quelle approche éditoriale est prévue pour sensibiliser les clients au NR ?', 'La communication vers les utilisateurs et clients est aussi un engagement de l\'entreprise pour réduire ses impacts environnementaux et se démarquer de la concurrence en affichant son ambition et ses résultats NR.', 'Utilisation', 'La communication NR vers les clients est-elle définie ?', NULL, NULL, NULL, NULL, 'CONSEIL', 'N/A', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(67, 'SPECIFICATIONS', 'SPC-1.03', 'B', 'A', 'B', 'Organiser les ressources humaines projets pour permettre la prise en compte de la démarche NR', 'Chaque entité de l\'équipe Projet dispose-t-elle des compétences NR ?', 'Chacune des étapes projet requiert des connaissances spécifiques pour les aspects NR, il est important que toutes les étapes soient couvertes afin que les résultats NR soient cohérents tout au long de la vie du service.', 'N/A', 'Combien de personnes sont sensibilisées et formées au NR ?', NULL, NULL, NULL, NULL, 'RECO', '*', NULL, 'INCONTOURNABLE', '#COM ; #METH ; #HR ;', 'ProjectMngr ; ProductOwner ; Manager ; ITMngr ;', '(X / Y ) * 100', 'Plan de formation NR = 100 ; Mise é dispo de ressources NR = 50 ; Non = 0', '100', 'High', 'OnUpdate', 'Plan de formation adapté é chacun des profils', NULL, NULL, NULL, NULL, NULL, NULL),
(68, 'SPECIFICATIONS', 'SPC-1.04', 'A', 'B', 'B', 'Organiser les ressources humaines projets pour permettre la prise en compte de la démarche NR', 'Les données ACV sont-elles réutilisées dans le cas d\'une adaptation d\'un service existant ?', 'Les analyses ACV peuvent étre réalisées sur des services existants afin de les rendre plus efficaces dans le cas d\'une refonte ou adaptation d\'un service existant. Les spécifications et exigences NR issues des ACV précédentes doivent étre reprises et adaptées si besoin.', 'Revalorisation', 'Avez-vous mis é disposition des équipes projet les ACV précédentes ?', 'L\'ACV de ce projet est-elle rendue disponible pour d\'autres projets ?', 'L\'ACV de ce projet sera-t-elle réalisée ?', NULL, NULL, 'RECO', '**', NULL, NULL, '#USE ; #MAINT ; #COM ; #VALUE ; #DEBT ; #METH', 'Dev ; Designer ; ProjectMngr ; ProductOwner ; RSE/NR', '(X / Y ) * 100', 'Formalisés = 100 ; Identifiés = 50 ; Non = 0', '100', 'Low', 'OnUpdate', 'Une ACV minimale (\"screening\") est disponible pour le service existant', NULL, NULL, NULL, NULL, NULL, NULL),
(69, 'SPECIFICATIONS', 'SPC-1.C02', 'B', 'A', 'B', 'Organiser les ressources humaines projets pour permettre la prise en compte de la démarche NR', 'Chaque partie-prenante a-t-elle la latitude de prendre des décisions pouvant influer sur l\'impact environnemental ?', 'Les spécifications et les USER STORY peuvent évoluer au fil du temps, ces évolutions doivent systématiquement prendre en compte les aspects NR. Les adaptations sont le plus souvent le fait des métiers. Les impacts environnementaux doivent pouvoir aussi avoir cette latitude avec des chemins de validation pré-définis.', 'Réalisation', 'Le circuit et l\'autonomie de décision ayant un impact NR est-il défini pour chaque acteur ?', NULL, NULL, NULL, NULL, 'CONSEIL', 'N/A', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(70, 'SPECIFICATIONS', 'SPC-2', NULL, NULL, NULL, 'Inclure les utilisateurs dans les ressources projet NR', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'ODD#12', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(71, 'SPECIFICATIONS', 'SPC-2.C01', 'B', 'B', 'A', 'Inclure les utilisateurs dans les ressources projet NR', 'La définition des besoins est-elle réalisée en consultant les utilisateurs finaux ?', 'Lorsque les besoins utilisateurs sont extrapolés é partir d\'analyses et d\'objectifs de l\'organisation, cela peut conduire é des expressions de besoin plus larges que celles dont les utilisateurs vont réellement se servir et le service sera plus consommateur que nécessaire.', 'Conception', 'La maétrise d\'ouvrage du projet contient-elle un utilisateur témoin ou un représentant maétrisant le métier ?', NULL, NULL, NULL, NULL, 'CONSEIL', 'N/A', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(72, 'SPECIFICATIONS', 'SPC-2.C02', 'A', 'B', 'A', 'Inclure les utilisateurs dans les ressources projet NR', 'Transmettez-vous une pédagogie NR aux utilisateurs ?', 'Les utilisations des services sont une source de consommation de ressources. Si les utilisateurs sont incités et guidés pour réduire leurs impacts avec des démarches pédagogiques, cela rend l\'ensemble du service plus efficace pour réduire son impact.', 'Conception', 'Un utilisateur témoin avec une forte coloration NR est-il inclus dans la démarche projet ?', 'Un utilisateur témoin dédié au projet est-il formé au NR ?', NULL, NULL, NULL, 'CONSEIL', 'N/A', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(73, 'SPECIFICATIONS', 'SPC-2.C03', 'B', 'A', 'A', 'Inclure les utilisateurs dans les ressources projet NR', 'Les aspects NR sont-ils propagés et entretenus sur l\'ensemble des acteurs ?', 'Lorsque chacun des acteurs a un accés simple é des marqueurs permettant d\'évaluer l\'impact environnemental de ses actions et choix, il peut adapter ses pratiques pour rendre le service plus efficace et responsable.', 'Réalisation', 'Quels sont les marqueurs et étapes de suivis définis ?', NULL, NULL, NULL, NULL, 'CONSEIL', 'N/A', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(74, 'SPECIFICATIONS', 'SPC-2.01', 'B', 'B', 'B', 'Inclure les utilisateurs dans les ressources projet NR', 'Les profils cibles sont-ils définis ?', 'Pour exprimer clairement les objectifs NR dans chacune des spécifications il est indispensable de situer les profils cibles et de leur associer des objectifs NR précis.', 'Conception', 'Les objectifs NR sont-ils qualifiés pour chaque profil cible?', NULL, NULL, NULL, NULL, 'RECO', '**', NULL, NULL, '#USE ;  #COM ; #VALUE', 'Dev ; Designer ; ProjectMngr ; ProductOwner ; RSE/NR', NULL, NULL, NULL, 'Medium', 'OnUpdate', 'Fiche de cadrage projet', NULL, NULL, NULL, NULL, NULL, NULL),
(75, 'SPECIFICATIONS', 'SPC-2.02', 'B', 'B', 'B', 'Inclure les utilisateurs dans les ressources projet NR', 'Les aspects numérique responsable sont-ils propagés et entretenus sur l\'ensemble des profils utilisateurs ?', 'Les phases d\'utilisation sont un moyen de communiquer et entretenir le lien sur les aspects NR avec les utilisateurs, c\'est aussi une démarche qui offre un canal de communication différent et démarquant vis é vis de la concurrence en impliquant les utilisateurs et les rendant acteurs du service proposé.', 'Utilisation', 'Quelle est votre fréquence de communication NR pour chaque profil utilisateur ?', NULL, NULL, NULL, NULL, 'RECO', '*', NULL, NULL, '#COM ; #VALUE', 'ProductOwner ; RSE/NR', NULL, NULL, NULL, 'Low', 'Periodic', 'Plan de formation', NULL, NULL, NULL, NULL, NULL, NULL),
(76, 'SPECIFICATIONS', 'SPC-3', NULL, NULL, NULL, 'Organiser la méthodologie projet pour permettre d\'appliquer une démarche NR', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'ODD#12', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(77, 'SPECIFICATIONS', 'SPC-3.C01', 'B', 'B', 'B', 'Organiser la méthodologie projet pour permettre d\'appliquer une démarche NR', 'Les étapes de revue NR sont-elles incluses dans la méthodologie ?', 'Les objectifs NR doivent étre clairement définis et la gestion projet doit traiter le suivi de ces objectifs avec des revues au méme titre que les autres catégories d\'exigences, en associant les acteurs aptes é arbitrer, justifier et valider les décisions', 'N/A', 'Comment et qui arbitre les conflits ? (par exemple : Budget et NR)', NULL, NULL, NULL, NULL, 'CONSEIL', 'N/A', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(78, 'SPECIFICATIONS', 'SPC-3.01', 'A', 'B', 'B', 'Organiser la méthodologie projet pour permettre d\'appliquer une démarche NR', 'Des fonctionnalités (User Story) concernent-elles uniquement les aspects NR ?', 'Le projet doit définir des exigences NR (Exigences Non Fonctionnelles - NFR) ou des User Stories spécifiques qui vont introduire des orientations NR dans la conception. Ces éléments seront pris en charge au méme titre que les autres NFR (sécurité, accessibilité par exemple) et garantiront que les aspects NR seront pris en considération dans toutes les étapes du projet.', 'Conception', 'Quel est l\'objectif fixé au départ du projet ?', NULL, NULL, NULL, NULL, 'RECO', '*', NULL, NULL, '#USE ; #MAINT ; #METH ;', 'Dev ; Designer ; ProjectMngr ; ProductOwner', '(X / Y ) * 100', 'Nombre US NR', 'Nombre US NFR', 'Medium', 'OnUpdate', 'Des Tags/Labels NR sont utilisés pour identifier tous les aspects NR pris en compte dans le Backlog', NULL, NULL, 'Y', '#INSTRUMENTATION', '**', 'Tag NR dans le backlog'),
(79, 'SPECIFICATIONS', 'SPC-3.02', 'B', 'B', 'B', 'Organiser la méthodologie projet pour permettre d\'appliquer une démarche NR', 'Quelle proportion des fonctionnalités (User Story) ont une composante NR ?', 'Certaines fonctionnalités peuvent étre conéues avec des impacts NR trés différents. En l\'absence de clarification, la solution choisie sera le plus souvent pilotée par la facilité ou l\'appétence technique, ce qui s\'oppose en général é la performance NR. Le seul moyen de guider les décisions est d\'introduire des points d\'attention sur les aspects NR dans la majorité des User Stories.', 'Conception', 'Une exigence NR est-elle justifiée plutét qu\'un aspect NR dans une exigence plus générale ?', NULL, NULL, NULL, NULL, 'RECO', '*', NULL, 'INCONTOURNABLE', '#USE ; #MAINT ; #METH ;', 'Dev ; Designer ; ProjectMngr ; ProductOwner', '(X / Y ) * 100', 'Nombre US avec info NR', 'Nombre US', 'High', 'OnUpdate', 'Le Backlog utilise des Tags/Labels NR pour identifier les user stories avec des éléments NR', NULL, NULL, 'Y', '#INSTRUMENTATION', '**', 'Tag NR dans le backlog'),
(80, 'SPECIFICATIONS', 'SPC-3.C02', 'B', 'B', 'B', 'Organiser la méthodologie projet pour permettre d\'appliquer une démarche NR', 'Quelle est la fréquence de revue/validation des éléments NR, sur un mode similaire aux revues et validation \"Sécurité\" ?', 'La prise en compte des aspects NR fait au démarrage de la conception peut étre respectée mais dans un mode itératif il est possible de s\'en écarter graduellement. Les revues du respect NR sont indispensables pour conserver la cohérence du projet.', 'Réalisation', 'Le processus de revue est-il formalisé ?', 'Les référents NR sont-ils inclus dans les revues ?', NULL, NULL, NULL, 'CONSEIL', 'N/A', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(81, 'SPECIFICATIONS', 'SPC-3.03', 'B', 'B', 'B', 'Organiser la méthodologie projet pour permettre d\'appliquer une démarche NR', 'Chaque fonctionnalité est-elle évaluée par rapport aux usages ?', 'Les User Story / Use Case doivent étre spécifiés par rapport é des usages afin de ne pas aller vers une inflation de fonctionnalités qui seraient superflues par rapport aux besoins réels des utilisateurs. Proposer des fonctionnalités marginales va engendrer une empreinte environnementale, un coét et une incompréhension du service supplémentaire non justifiés.', 'Conception', 'Les fonctionnalités attendues par l\'utilisateur et leur périmétre d\'action sont-ils définis afin de poser un cadre aux interactions et ne pas  aller dans le superflu ?', NULL, NULL, NULL, NULL, 'RECO', '*', NULL, NULL, '#USE ; #MAINT ; #VALUE ; #METH ;', 'Dev ; Designer ; ProjectMngr ; ProductOwner', NULL, NULL, NULL, 'High', 'OnUpdate', 'Definition of Done', NULL, NULL, NULL, NULL, NULL, NULL),
(82, 'SPECIFICATIONS', 'SPC-3.C03', 'C', 'B', 'C', 'Organiser la méthodologie projet pour permettre d\'appliquer une démarche NR', 'L\'utilisation d\'intégration continue est-elle généralisée ?', 'Du point de vue NR, le fait de capitaliser sur les acquis et de fournir un modéle technique qui garanti ces acquis, évite les anomalies et surcoéts de non qualité.', 'Déploiement', 'Est-ce que la compléxité du service est encadrée / suivie dans le processus d\'intégration continue ?', NULL, NULL, NULL, NULL, 'CONSEIL', 'N/A', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(83, 'SPECIFICATIONS', 'SPC-3.04', 'C', 'B', 'C', 'Organiser la méthodologie projet pour permettre d\'appliquer une démarche NR', 'Les processus d\'intégration continue ne générent-ils pas des bréches dans la prise en compte des aspects NR ?', 'Les différentes phases de l\'intégration continue permettent de piloter les processus avec des attendus définis. L\'une de ces étapes d\'intégration doit valider le respect des impacts NR.', 'Déploiement', 'Chaque étape du cycle de vie prend-elle en compte les aspects NR?', NULL, NULL, NULL, NULL, 'RECO', '**', NULL, NULL, '#MAINT ; #METH', 'Dev ; ProjectMngr ; ProductOwner', '(X / Y ) * 100', 'Formalisés = 100 ; Identifiés = 50 ; Non = 0', '100', 'Low', 'OnUpdate', 'Dossier technique', NULL, NULL, NULL, NULL, NULL, NULL),
(84, 'SPECIFICATIONS', 'SPC-3.C04', 'B', 'B', 'B', 'Organiser la méthodologie projet pour permettre d\'appliquer une démarche NR', 'Les exigences non fonctionnelles (User Story) sont-elles traitées de la méme maniére pour les aspects NR, sécurité et accessibilité ?', 'Les catégories d\'exigences Non Fonctionnelles (NFR) interviennent de maniére transverse dans la gestion de projet car les dépendances sont le plus souvent associées é des comportements que l\'on ne peut que rarement lier é une seule action. C\'est aussi le cas du NR, une décision ponctuelle efficace peut étre rendue caduque par des actions en amont ou en aval.', 'Réalisation', 'Les mécanismes de validation sont-ils similaires pour la sécurité et les aspects NR ?', NULL, NULL, NULL, NULL, 'CONSEIL', 'N/A', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(85, 'SPECIFICATIONS', 'SPC-3.05', 'B', 'B', 'B', 'Organiser la méthodologie projet pour permettre d\'appliquer une démarche NR', 'L\'état des lieux des données d\'entrée est-il mis é disposition afin d\'étre réutilisé dans d\'autres projets?', 'Les principes NR sont stables et les bonnes pratiques déployées sont directement utilisables dans d\'autres contextes projet, d\'autant plus que les projets au sein d\'une organisation ont beaucoup de similarités, que ce soit dans les méthodes, les objectifs ou les périmétres fonctionnels. Dans la phase de spécification, collecter, préparer et produire ces éléments est un gage d\'efficacité et de fiabilité pour augmenter la maturité de l\'organisation dans ses pratiques NR.', 'Revalorisation', 'Quelles sont les données utilisées en entrée et produites pour les autres projets ?', 'Existe-t-il une cartographie applicative et une cartographie des processus (et des données) exploitables dans l\'entreprise ? Seront-elles bien mises é jour dans le cadre du projet ?', 'La cellule d\'urbanisation du SI de l\'entreprise a-t-elle été sollicitée ?', NULL, NULL, 'RECO', '**', NULL, NULL, '#PERF ; #VOL ; #SEC ; #USE ; #MAINT ;  #DEBT ; #METH', 'Dev ;ProjectMngr ; ProductOwner ; ITMngr', NULL, NULL, NULL, 'Medium', 'OnUpdate', 'Documentation technique', NULL, NULL, NULL, NULL, NULL, NULL),
(86, 'SPECIFICATIONS', 'SPC-4', NULL, NULL, NULL, 'Construire le périmétre du projet et valider la pertinence de chaque fonctionnalité', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'ODD#10 ; ODD#12 ; ODD#13', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(87, 'SPECIFICATIONS', 'SPC-4.01', 'A', 'B', 'A', 'Construire le périmétre du projet et valider la pertinence de chaque fonctionnalité', 'Les exigences NR sont elles associées é toutes les fonctionnalités produit ?', 'Chaque fonctionnalité mise en oeuvre doit disposer d\'une prise en compte du NR pour que l\'ensemble du service soit conéu avec le respect le plus complet des principes NR, un aspect non couvert pouvant annuler tout ou partie des bénéfices acquis par ailleurs. Il est plus efficace d\'avoir un traitement équilibré sur l\'ensemble, plutét que viser l\'excellence sur une partie et laisser des trous conséquents sur d\'autres.', 'Conception', 'Le produit est-il validé comme nécessaire par rapport é son impact environnemental ?', NULL, NULL, NULL, NULL, 'RECO', '*', NULL, NULL, '#USE ; #MAINT ; #VALUE ; #METH ;', 'Dev ; Designer ; ProjectMngr ; ProductOwner', '(X / Y ) * 100', 'Exigences NR', 'Fonctionnalités', 'High', 'OnUpdate', 'Des Tags/Labels NR sont utilisés pour identifier tous les aspects NR pris en compte dans le Backlog', NULL, NULL, NULL, NULL, NULL, NULL),
(88, 'SPECIFICATIONS', 'SPC-4.02', 'B', 'B', 'B', 'Construire le périmétre du projet et valider la pertinence de chaque fonctionnalité', 'Chaque fonction principale ou secondaire du service est-elle appréhendée en regard de son importance dans le service ?', 'La cohérence NR pousse é s\'assurer que les fonctions secondaires n\'ont pas un impact environnemental plus important que les fonctions principales. Les spécifications doivent clairement identifier les fonctions principales et secondaires et leurs impacts du point de vue NR.', 'Conception', 'Les régles de validation des impacts NR des fonctions sont-elles décrites ?', NULL, NULL, NULL, NULL, 'RECO', '**', NULL, NULL, '#USE ; #MAINT ; #VALUE ; #METH ;', 'Dev ; Designer ; ProjectMngr ; ProductOwner', NULL, NULL, NULL, 'Medium', 'OnUpdate', 'Analyse d\'impact', NULL, NULL, NULL, NULL, NULL, NULL),
(89, 'SPECIFICATIONS', 'SPC-4.03', 'B', 'B', 'A', 'Construire le périmétre du projet et valider la pertinence de chaque fonctionnalité', 'Toutes les exigences projet (performance, sécurité, accessibilité, NR) sont-elles associées é chaque fonctionnalité ?', 'Le panorama complet des exigences non fonctionnelles (Sécurité, Accessibilité, NR) permet de faciliter les arbitrages et de déterminer l\'axe qui sera choisi lorsque des éléments présentent des conflits. Le conflit est difficilement traitable localement, seule la vision globale permettra de garantir que l\'équilibre est respecté sur l\'ensemble du projet.', 'Conception', 'Les régles de priorité sont-elles définies et arbitrées en cas de conflit ?', 'La priorité est-elle donnée au NR s\'il ne remet pas en cause la sécurité ?', NULL, NULL, NULL, 'RECO', '*', NULL, NULL, '#SEC ; #USE ; #MAINT ; #METH ; #LEG', 'Dev ; Designer ; ProjectMngr ; ProductOwner ; ITMngr', '(X / Y ) * 100', 'Exigences NR', 'Fonctionnalités avec NFR', 'High', 'OnUpdate', 'Backlog - Tag/Label NR', NULL, NULL, NULL, NULL, NULL, NULL),
(90, 'SPECIFICATIONS', 'SPC-4.04', 'B', 'A', 'B', 'Construire le périmétre du projet et valider la pertinence de chaque fonctionnalité', 'Les cas d\'exclusion de chaque fonctionnalité sont-ils adressés et des alternatives proposées ?', 'Certaines fonctionnalités non retenues peuvent néanmoins avoir un intérét et pousser é trouver des alternatives de remplacement. Ces solutions intermédiaires respectent les mémes processus projet en terme de NR.', 'Acquisition', 'Les coéts et bénéfices des alternatives sont-ils évalués ?', NULL, NULL, NULL, NULL, 'RECO', '**', NULL, NULL, '#PERF ; #VOL ; #SEC ; #USE ; #MAINT ;  #DEBT ; #METH ; #FIN', 'Dev ;ProjectMngr ; ProductOwner ; ITMngr', NULL, NULL, NULL, 'Medium', 'OnUpdate', 'Analyse d\'impact', NULL, NULL, NULL, NULL, NULL, NULL),
(91, 'SPECIFICATIONS', 'SPC-4.C01', 'A', 'B', 'B', 'Construire le périmétre du projet et valider la pertinence de chaque fonctionnalité', 'La nécessité d\'une fonctionnalité est-elle validée en rapport avec son impact NR ?', 'La sélection des fonctionnalités spécifiées jugées indispensables peut étre remis en cause dans sa forme initiale par rapport aux impacts NR. Dans ce cas les coéts / bénéfices devront prendre en compte ces oppositions et faire l\'objet d\'une adaptation pour faire converger les 2 approches : nécessité fonctionnelle et impact NR.', 'Conception', 'L\'arbitrage entre les gains NR et les coéts du projets sont-ils clairement identifiés ?', NULL, NULL, NULL, NULL, 'CONSEIL', 'N/A', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(92, 'SPECIFICATIONS', 'SPC-4.05', 'B', 'A', 'B', 'Construire le périmétre du projet et valider la pertinence de chaque fonctionnalité', 'Quelles alternatives sont proposées pour traiter une fonctionnalité non retenue ?', 'Des solutions alternatives peuvent étre déployées sachant qu\'elles ne sont pas les plus efficaces d\'un point de vue NR mais apportent une premiére étape plus efficace par rapport é la solution n\'incluant aucune considération NR. Ces pistes alternatives doivent étre transitoires et faire l\'objet de planification de revue afin d\'augmenter graduellement leur conformité NR.', 'Acquisition', 'Chaque fonctionnalité retirée a-t-elle une alternative disponible, si la fonctionnalité est utile ?', NULL, NULL, NULL, NULL, 'RECO', '*', NULL, NULL, '#VOL ; #SEC ; #USE ; #MAINT ; #DEBT ; #METH ; #FIN', 'Dev ; Designer ; ProjectMngr ; ProductOwner ; ITMngr', NULL, NULL, NULL, 'Medium', 'OnUpdate', 'Analyse d\'impact', NULL, NULL, NULL, NULL, NULL, NULL),
(93, 'SPECIFICATIONS', 'SPC-4.C02', 'C', 'A', 'B', 'Construire le périmétre du projet et valider la pertinence de chaque fonctionnalité', 'Les orientations du service ne sont-elles pas basées sur les effets d\'addiction artificiels ?', 'La sur-consommation des usages du service implique la création d\'une dépendance de l\'utilisateur vis é vis du service, et augmente artificiellement les charges d\'utilisation du service, donc l\'impact environnemental. Ces effets d\'addiction sont dommageables pour le respect des principes NR.', 'Utilisation', 'La matrice d\'évaluation de captologie est-elle appliquée ?', NULL, NULL, NULL, NULL, 'CONSEIL', 'N/A', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(94, 'SPECIFICATIONS', 'SPC-4.C03', 'B', 'A', 'A', 'Construire le périmétre du projet et valider la pertinence de chaque fonctionnalité', 'Les interfaces utilisateurs et techniques sont-elles conéues pour étre efficaces du point de vue NR ?', 'Le lien entre l\'utilisateur et le service peut générer des parcours complexes, longs, avec des volumes de saisie ou d\'informations présentées trés élevés. Si la gestion des interfaces est efficace pour l\'utilisateur, elle sera le plus souvent aussi efficace du point de vue NR', 'Utilisation', 'Le coét NR des interfaces est-il évalué ?', NULL, NULL, NULL, NULL, 'CONSEIL', 'N/A', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(95, 'SPECIFICATIONS', 'SPC-4.06', 'C', 'A', 'A', 'Construire le périmétre du projet et valider la pertinence de chaque fonctionnalité', 'Les aspects réglementaires de l\'accessibilité sont-ils pris en compte ?', 'Le non-respect des réglementations est tout d\'abord illégal et peut entraéner des pénalités importantes. Il coupe aussi des utilisateurs, méme hors du cadre du handicap, de l\'accés au service.', 'Utilisation', 'Est-ce que la conformité au RGAA est garantie ?', NULL, NULL, 'Loi 2005', NULL, 'RECO', '**', NULL, NULL, '#USE ; #MAINT ; #METH ; #LEG', 'Dev ; Designer ; ProjectMngr ; ProductOwner', NULL, NULL, NULL, 'High', 'OnUpdate', 'Analyse de risques', NULL, NULL, NULL, NULL, NULL, NULL),
(96, 'SPECIFICATIONS', 'SPC-4.07', 'C', 'B', 'A', 'Construire le périmétre du projet et valider la pertinence de chaque fonctionnalité', 'Le service est-il conforme au RGPD ?', 'Lors des spécifications, si de nouvelles collections de données sont prévues, elles doivent systématiquement faire l\'objet d\'une évaluation pour les données é caractére personnelle, étre associé au registre de traitement et au PIA, en lien avec le DPO. Les aspects de confiance et respect des utilisateurs est l\'une des dimensions du NR.', 'Utilisation', 'Les documents RGPD du projet sont-ils réalisés et validés par le DPO ?', NULL, NULL, NULL, NULL, 'RECO', '**', NULL, NULL, '#SEC ; #USE ; #MAINT ; #METH ; #LEG', 'Dev ; Designer ; ProjectMngr ; ProductOwner', '(X / Y ) * 100', 'Formalisés = 100 ; Identifiés = 50 ; Non = 0', '100', 'High', 'OnUpdate', 'Documentation : Informations légales', NULL, NULL, NULL, NULL, NULL, NULL),
(97, 'SPECIFICATIONS', 'SPC-4.08', 'C', 'B', 'B', 'Construire le périmétre du projet et valider la pertinence de chaque fonctionnalité', 'La sécurité est-elle adaptée é la valeur de mes données ?', 'La sécurisation des données nécessite souvent des éléments redondants, des méthodes de sauvegardes et de duplications qui générent une empreinte environnementale supplémentaire. Certaines catégories de données justifient pleinement ces dispositions, mais pour d\'autres l\'extréme sécurisation n\'a pas de sens, si ce n\'est la facilité de traiter de la méme maniére toutes les informations. Le niveau de sécurisation, é partir du moment oé il présente un surcoét environnemental, doit étre en rapport avec la donnée.', 'Administration', 'Les régles de sécurité sont-elles associées é des audits réguliers ?', NULL, NULL, NULL, NULL, 'RECO', '**', NULL, NULL, '#SEC ; #USE ; #MAINT ; #METH ; #LEG', 'Dev ; Designer ; ProjectMngr ; ProductOwner ; ITMngr', '(X / Y ) * 100', 'Formalisés = 100 ; Identifiés = 50 ; Non = 0', '100', 'High', 'OnUpdate', 'Analyse de risques', NULL, NULL, NULL, NULL, NULL, NULL),
(98, 'SPECIFICATIONS', 'SPC-4.C04', 'B', 'B', 'B', 'Construire le périmétre du projet et valider la pertinence de chaque fonctionnalité', 'Les solutions techniques retenues sont-elles pilotées par l\'usage plutét que par l\'intérét technique ?', 'Les fonctions techniques ont souvent besoin d\'exprimer leurs compétences et leur suivi des avancées technologiques en choisissant les solutions les plus récentes et définies dans leur écosystéme comme les plus avancées. Cela pousse é faire des choix d\'implémentation guidés par l\'attrait technologique plutét que par l\'efficacité notamment NR. Réorienter les pratiques techniques sur les défis NR est une bonne pratique pour conserver l\'intérét technique des professionnels et leur permettre de progresser avec des résultats concrets.', 'Acquisition', 'Toutes les technologies utilisées ont-elles une justification validée et partagée ?', NULL, NULL, NULL, NULL, 'CONSEIL', 'N/A', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(99, 'SPECIFICATIONS', 'SPC-4.09', 'B', 'A', 'B', 'Construire le périmétre du projet et valider la pertinence de chaque fonctionnalité', 'L\'évaluation de la valeur fonctionnelle de chaque interaction utilisateur est-elle réalisée ?', 'Chaque fois que l\'utilisateur interagit avec le service, un nombre important de mécanismes \"invisibles\" se déclenchent. Chacun de ces éléments, en s\'exécutant, consomme des ressources. Pour réduire cette consommation, outre les principes d\'optimisation des algorithmes, il est plus utile et souvent plus facile de limiter le nombre de ces interactions.', 'Réalisation', 'Est-ce que la fonctionnalité est indispensable ?', 'Une analyse MoSCoW a-t-elle été réalisée (Must/Should/Could/Would) ?', NULL, NULL, NULL, 'RECO', '*', NULL, NULL, '#USE ; #VALUE ;', 'Dev ; Designer ; ProjectMngr ; ProductOwner', '(X / Y ) * 100', 'Validations', 'Interactions User', 'High', 'OnUpdate', 'Definition of Done', NULL, NULL, NULL, NULL, NULL, NULL),
(100, 'SPECIFICATIONS', 'SPC-4.10', 'A', 'A', 'B', 'Construire le périmétre du projet et valider la pertinence de chaque fonctionnalité', 'L\'impact de la couverture de la fonctionnalité peut-elle étre réduite par la solution technique envisagée ?', 'Certaines fonctionnalités sont nativement offertes par des composants techniques, mais ces composants ne se limitent que trés rarement au seul périmétre recherché et fournissent beaucoup d\'autres fonctionnalités qui peuvent ne pas étre utiles. Le fait de pouvoir réduire un composant technique aux seuls apports réellement utiles est un bénéfice NR important.', 'Acquisition', 'Les technologies alternatives ont-elles été testées ?', 'Est-ce que la solution technique envisagée pour couvrir la fonctionnalité peut étre réduite ?', NULL, NULL, NULL, 'RECO', '*', NULL, NULL, '#USE ; #MAINT ; #DEBT ;', 'Dev ; Designer ; ProjectMngr ; ProductOwner', '(X / Y ) * 100', 'Analysée = 100 ; présumée = 50 ; Non = 0', 'Fonctionnalités', 'Medium', 'OnUpdate', 'L\'analyse d\'impact détermne les actions de réduction des impacts avec les acteurs concernés', NULL, NULL, NULL, NULL, NULL, NULL),
(101, 'SPECIFICATIONS', 'SPC-5', NULL, NULL, NULL, 'Préparer le suivi du cycle de vie du projet et des indicateurs de performance / bénéfice NR', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'ODD#12', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(102, 'SPECIFICATIONS', 'SPC-5.01', 'B', 'B', 'A', 'Préparer le suivi du cycle de vie du projet et des indicateurs de performance / bénéfice NR', 'Avez-vous identifié des indicateurs de performance pour le suivi du cycle de vie ?', 'Les indicateurs NR universels ne sont pas vraiment disponibles, cela peut pousser é passer énormément d\'énergie et de ressources é définir les meilleurs indicateurs. Ce qui représente une surcharge dans le projet qui peut conduire é une remise en cause du traitement des impacts NR dans les projets. Il serait dommage que la crédibilité des bénéfices NR soient dégradés par la recherche de points de mesure. La fiabilité d\'un indicateur est dicté par la fiabilité de la donnée qui permet de le calculer, il vaut mieux démarrer avec des indicateurs sans doute moins ambitieux mais justes et peu onéreux en terme de charge projet.', 'Administration', 'Quels indicateurs sont mis en place, et comment les données sur lesquels ces indicateurs sont basés sont-elles fiabilisées ?', NULL, NULL, NULL, NULL, 'RECO', '**', NULL, NULL, '#PERF ; #VOL ; #SEC ; #USE ; #COM ; #VALUE ;  #GOV ;  #FIN', 'ProjectMngr ; ProductOwner ; Manager ; RSE/NR ; HR ; ITMngr', NULL, NULL, NULL, 'High', 'Periodic', 'Analyse de bénéfices', NULL, NULL, NULL, NULL, NULL, NULL),
(103, 'SPECIFICATIONS', 'SPC-5.02', 'B', 'C', 'B', 'Préparer le suivi du cycle de vie du projet et des indicateurs de performance / bénéfice NR', 'Dans le processus d\'achat (Appel d\'offre) des exigences d\'engagement NR sont-elles demandées aux prestataires externes ?', 'Un projet est rarement réalisé avec un périmétre couvert é 100% au sein de l\'organisation, nombre de ressources externes sont mobilisées au cours du projet. Les spécifications permettent d\'identifier ces ressources nécessaires et leur associer des exigences NR pour permettre de sélectionner les prestataires en fonctions des contraintes techniques, de solvabilité économique et de NR.', 'Acquisition', 'Les exigences NR des prestataires sont-elles définies ?', NULL, NULL, NULL, NULL, 'RECO', '*', NULL, 'INCONTOURNABLE', '#COM ; #VALUE ; #GOV ; #BUY', 'Manager ; RSE/NR ; HR ; ITMngr', '(X / Y ) * 100', 'Exigences NR dans AO', 'Appel d\'offres', 'High', 'OnUpdate', 'Les procédures d\'achat contiennent des éléments de sélection liées aux enjeux NR', NULL, NULL, NULL, NULL, NULL, NULL),
(104, 'SPECIFICATIONS', 'SPC-5.03', 'B', 'B', 'B', 'Préparer le suivi du cycle de vie du projet et des indicateurs de performance / bénéfice NR', 'La gestion des impacts des mises é jour logicielles sur la mise en obsolescence des matériels est-elle évaluée ?', 'Dans un développement itératif et tout au long de la vie du service des mises é jour seront appliquées. Chacune de ces adaptations doit permettre de continuer é utiliser les matériels actuels et ne pas ajouter de contraintes artificielles qui améneraient é saturer les capacités de traitement des terminaux utilisateurs, ni introduire de mise é jour technique qui générerait une incompatibilité, nécessiterait / pousserait é un renouvellement des équipements.', 'Maintenance', 'Les impacts sur les composants matériels des mises é jour logicielles sont-ils tracés ?', 'Est-ce que la mise é jour du logiciel entraéne une obsolescence de certains matériels pour l\'utilisation du logiciel, au regard du cycle de vie du service estimé au lancement du projet ?', NULL, NULL, NULL, 'RECO', '*', NULL, NULL, '#MAINT ; #VALUE ; #DEBT ;', 'Dev ; Designer ; ProjectMngr ; ProductOwner', '(X / Y ) * 100', 'Etude obsolescence', 'Mises é jour', 'Low', 'OnUpdate', 'Analyse d\'impact', NULL, NULL, NULL, NULL, NULL, NULL),
(105, 'SPECIFICATIONS', 'SPC-5.04', 'B', 'B', 'B', 'Préparer le suivi du cycle de vie du projet et des indicateurs de performance / bénéfice NR', 'Les équipements actuels des utilisateurs sont-ils évalués ?', 'Les spécifications doivent définir les gammes de terminaux que le service doit supporter afin que les utilisateurs avec des périphériques plus anciens puissent accéder au service dans les meilleures conditions. Les incompatibilités matérielles sont une source de souhaits / impératifs de renouvellement de terminaux avant que celui-ci ne soit réellement hors d\'usage. Le NR participe é prolonger la durée de vie des équipements.', 'Utilisation', 'La plage des équipements utilisateurs valide est-elle disponible ?', 'Les utilisateurs peuvent-ils accéder au produit avec leur équipement actuel ? Des solutions de replis sont-elles décrites ?', NULL, NULL, NULL, 'RECO', '*', NULL, NULL, '#USE ; #MAINT ; #VALUE ; #DEBT ; #OBSO', 'Dev ; Designer ; ProjectMngr ; ProductOwner', '(X / Y ) * 100', 'Evaluations', 'Matériels identifiés', 'Medium', 'OnUpdate', 'Fiche de cadrage', NULL, NULL, NULL, NULL, NULL, NULL),
(106, 'SPECIFICATIONS', 'SPC-5.05', 'B', 'A', 'B', 'Préparer le suivi du cycle de vie du projet et des indicateurs de performance / bénéfice NR', 'Les revues de projet prennent-elles en compte les validations des exigences NR ?', 'Le cadrage et la phase de spécification prévoient des livraisons intermédiaires, sous forme de POC, de MVP, de BETA. Chacune de ces livraisons intermédiaires est une occasion de revoir et valider la couverture des exigences NR', 'Revalorisation', 'Le processus d\'arbitrage des conflits est-il partagé par tous les acteurs ?', 'Quelle est la fréquence de revue ?', NULL, NULL, NULL, 'RECO', '*', NULL, NULL, '#VALUE ; #METH ;', 'ProjectMngr ; ProductOwner', '(X / Y ) * 100', 'Composantes NR dans les revues', 'Revues de projets', 'High', 'OnUpdate', 'Definition of Done', NULL, NULL, NULL, NULL, NULL, NULL),
(107, 'SPECIFICATIONS', 'SPC-5.06', 'B', 'B', 'B', 'Préparer le suivi du cycle de vie du projet et des indicateurs de performance / bénéfice NR', 'Lors de chaque nouvelle mise en production, une revue du respect des enjeux NR est-elle effectuée ?', 'La mise en production ne doit pas étre considérée comme l\'étape ultime de la vie d\'un projet dans le respect des principes NR. Cette opération de mise en production permet de statuer sur les engagements NR définis et remplis au cours du projet et de valider que ces éléments seront suivis pendant toute la phase active du service, et pris en compte lors du retrait du service en fin de vie.', 'Maintenance', 'Les anomalies de revues sont-elles consignées et réutilisées ?', NULL, NULL, NULL, NULL, 'RECO', '*', NULL, NULL, '#MAINT ; #VALUE ; #METH ;', 'Dev ; Designer ; ProjectMngr ; ProductOwner', '(X / Y ) * 100', 'Revue NR', 'Mise en production', 'High', 'OnUpdate', 'Definition of Done', NULL, NULL, NULL, NULL, NULL, NULL),
(108, 'SPECIFICATIONS', 'SPC-5.07', 'A', 'B', 'A', 'Préparer le suivi du cycle de vie du projet et des indicateurs de performance / bénéfice NR', 'Les données et procédures spécifiées ont-elles toutes une information de fin de vie ?', 'Les spécifications font apparaétre des collections de données des procédures asynchrones ou des téches périodiques indispensables au bon fonctionnement du service. Ces éléments sont le plus souvent invisibles et en l\'absence de spécifications explicites des processus de retrait ils sont rarement pris en compte dans les fins d\'exploitation d\'un service. Chacun de ces éléments doit porter sa propre information de fin de vie.', 'Fin de Vie', 'La catalogue des procédures et données est-il é jour ?', NULL, NULL, NULL, NULL, 'RECO', '*', NULL, 'INCONTOURNABLE', '#VOL ; #VALUE ;', 'Dev ; Designer ; ProjectMngr ; ProductOwner', '(X / Y ) * 100', 'Formalisés = 100 ; prévus = 75 ; Identifiés = 50 ; Non = 0', '100', 'Medium', 'OnUpdate', 'Chaque élément référencé dans le Dossier Technique porte une information de fin de vie', NULL, NULL, 'Y', '#INSTRUMENTATION', '***', 'Tag EnD Of Life dans les US'),
(109, 'SPECIFICATIONS', 'SPC-5.08', 'A', 'B', 'A', 'Préparer le suivi du cycle de vie du projet et des indicateurs de performance / bénéfice NR', 'Est-ce que le produit cible é une durée de vie déterminée ?', 'Les services répondent é une opportunité et sont créés pour traiter cette demande. Dans un environnement technique et économique trés mouvant et imprévisible, ils ont rarement une espérance de vie infinie. Il est utile d\'un point de vue NR de décider en préalable du terme de l\'exploitation du service pour éviter qu\'il ne subsiste ad vitam aeternam alors que l\'objet du service n\'existe plus.', 'Fin de Vie', 'Le produit a t\'il une \"date d\'expiration\" ?', NULL, NULL, 'GreenIT 85', NULL, 'RECO', '*', NULL, NULL, '#VOL ; #VALUE ;', 'ProjectMngr ; ProductOwner ; Manager ; ITMngr', '(X / Y ) * 100', 'Formalisés = 100 ; prévus = 75 ; Identifiés = 50 ; Non = 0', '100', 'Medium', 'Once', 'La fiche de cadrage projet fait apparaitre explicitement la durée de vie estimée du service.', NULL, NULL, NULL, NULL, NULL, NULL),
(110, 'SPECIFICATIONS', 'SPC-5.C01', 'C', 'B', 'A', 'Préparer le suivi du cycle de vie du projet et des indicateurs de performance / bénéfice NR', 'Les charges, bénéfices, risques sont-ils suivis ?', 'La mise en production d\'un service respectant les principes NR est un atout important pour une organisation. Pour l\'ensemble des parties-prenantes, le bilan de la conduite du projet et du traitement des enjeux NR est un moyen supplémentaire de capitaliser sur les bénéfices de l\'opération et d\'évaluer sereinement les risques et charges pris en compte dans le projet afin de faciliter les prises en compte NR dans les futurs projets.', 'Administration', 'Est-ce qu\'une rétrospective est prévue pour évaluer les gains environnementaux ?', NULL, NULL, NULL, NULL, 'CONSEIL', 'N/A', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(111, 'SPECIFICATIONS', 'SPC-5.C02', 'B', 'B', 'A', 'Préparer le suivi du cycle de vie du projet et des indicateurs de performance / bénéfice NR', 'Un plan de suivi des risques est-il déployé ?', 'Certains choix techniques sont orientés par l\'angle NR, et des adaptations techniques sont réalisées pour plus de conformité NR. Cela peut introduire des risques supplémentaires (délais, difficultés, anomalies). Ces risques doivent étre suivis et le plan de traitement des risques doit prendre en considération ces éléments associés aux choix NR.', 'Déploiement', 'Le plan de suivi des risques intégre-t-il le NR ?', 'Est-ce que les choix techniques faits pour le NR ont introduit des risques supplémentaires ?', NULL, NULL, NULL, 'CONSEIL', 'N/A', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(112, 'SPECIFICATIONS', 'SPC-5.C03', 'C', 'C', 'A', 'Préparer le suivi du cycle de vie du projet et des indicateurs de performance / bénéfice NR', 'Un gain de notoriété et de capitalisation est-il associé aux aspects NR du projet ?', 'La conduite d\'un projet NR a un retentissement au delé des sphéres techniques et métiers du projet, pouvant permettre é l\'organisation d\'afficher une affinité forte avec les contraintes environnementales qui est valorisable dans les opérations de communications institutionnelles ou informelles. Ces éléments peuvent étre chiffrés dans les budgets projet.', 'Utilisation', 'Les bénéfices non financiers sont-ils évalués ?', 'Les équipes bénéficient-elles d\'une montée en compétence sur les sujets NR ?', NULL, NULL, NULL, 'CONSEIL', 'N/A', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(113, 'SPECIFICATIONS', 'SPC-5.09', 'B', 'C', 'B', 'Préparer le suivi du cycle de vie du projet et des indicateurs de performance / bénéfice NR', 'Les fonctions secondaires ont-elles aussi un impact moindre sur l\'impact environnemental ?', '80% des usages sont traités par 20% des fonctionnalités. Sans étre une régle immuable, c\'est trés souvent le cas. Il ne faut pas tomber dans le travers ou les 20% d\'usages marginaux consomment 80% des impacts environnementaux du projet.', 'Conception', 'Les impacts environnementaux de chaque fonctionnalité sont-ils évalués par rapport é la nature et l\'importance de la fonctionnalité ?', NULL, NULL, NULL, NULL, 'RECO', '**', NULL, NULL, '#USE ; #MAINT ; #VALUE ; #METH ;', 'Dev ; Designer ; ProjectMngr ; ProductOwner', '(X / Y ) * 100', 'Formalisés = 100 ; prévus = 75 ; Identifiés = 50 ; Non = 0', '100', 'Medium', 'OnUpdate', 'CHROME : Console dev/Network', NULL, NULL, NULL, NULL, NULL, NULL),
(114, 'SPECIFICATIONS', 'SPC-6', NULL, NULL, NULL, 'Déterminer l\'environnement de production dans lequel le projet sera construit et déployé en accord avec les principes NR', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'ODD#9 ; ODD#12 ; ODD#13', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(115, 'SPECIFICATIONS', 'SPC-6.01', 'B', 'C', 'B', 'Déterminer l\'environnement de production dans lequel le projet sera construit et déployé en accord avec les principes NR', 'Les fournisseurs de solutions et prestataires de service sont-ils sélectionnés sur leur capacité é respecter un cahier des charges NR ?', 'Les spécifications mettent en évidence des besoins de prestations externalisées ou des relations avec des fournisseurs de services. Dans les régles de sélection des fournisseurs de services ou de produits externes, les aspects NR doivent étre pris en compte de la méme maniére que si le service ou produit était pris en charge en interne. Le cahier des charges doit exposer en plus des considérations techniques, budgétaires et de respect des normes de sécurité les principes NR que le fournisseur doit remplir.', 'Acquisition', 'Les indicateurs NR des fournisseurs sont-ils pris en compte lors de la sélection des prestataires ?', NULL, NULL, NULL, NULL, 'RECO', '**', NULL, NULL, '#COM ; #VALUE ; #GOV ; #BUY', 'ProjectMngr ; ProductOwner ; Manager ; ITMngr', '(X / Y ) * 100', 'Composantes NR', 'Achats externes', 'High', 'Periodic', 'Procédures d\'achat', NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `goodpractices` (`cle`, `Family`, `Id`, `Planet`, `People`, `Prosperity`, `Recommendation`, `Criteria`, `Justification`, `Stage`, `Tests1`, `Tests2`, `Tests3`, `Correspondence`, `Link`, `Type`, `Difficulty`, `Goal`, `Incontournable`, `Tags`, `Actors`, `Indicators_`, `IndicatorsX`, `IndicatorsY`, `Priority`, `Recurrence`, `UseCase`, `Example`, `Limite`, `Automatizable`, `AutomatizationScript`, `AutomatizationLevel`, `AutomatizationInformation`) VALUES
(116, 'SPECIFICATIONS', 'SPC-6.C01', 'B', 'B', 'B', 'Déterminer l\'environnement de production dans lequel le projet sera construit et déployé en accord avec les principes NR', 'Les aspects déplacements physiques sont-ils évalués lors de la sélection de prestataires, des solutions techniques et de l\'organisation des acteurs internes intervenant sur le projet ?', 'L\'impact environnemental des déplacements physiques est trés visible et facilement estimable. Lors des interactions avec des partenaires, la distance, le nombre des déplacements doit étre évalué et restreint. La définition d\'un budget déplacement pour l\'ensemble du projet peut apporter plus de contréle sur cet aspect et promouvoir chaque fois que cela est possible des points distanciels plutét que présentiels.', 'Acquisition', 'Quel budget de déplacement physique est alloué au projet ?', NULL, NULL, NULL, NULL, 'CONSEIL', 'N/A', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(117, 'SPECIFICATIONS', 'SPC-6.C02', 'C', 'A', 'A', 'Déterminer l\'environnement de production dans lequel le projet sera construit et déployé en accord avec les principes NR', 'Les intervenants des prestataires disposent-ils tous des mémes bénéfices sociaux et conditions de travail ?', 'Dans les dimensions NR les aspects humains et économiques occupent une place importante. S\'assurer que tous les intervenants y compris chez les prestataires (et ses propres sous-traitants et fournisseurs) utilisés pour le projet disposent des mémes cadres socio-économiques est nécessaire pour que la cohérence des efforts NR ne soit pas anéantie par un choix qui aurait pu étre évité.', 'Acquisition', 'Le prestataire utilise-t-il des ressources humaines hors du territoire national ou avec des contrats spécifiques ?', NULL, NULL, NULL, NULL, 'CONSEIL', 'N/A', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(118, 'SPECIFICATIONS', 'SPC-6.02', 'A', 'C', 'A', 'Déterminer l\'environnement de production dans lequel le projet sera construit et déployé en accord avec les principes NR', 'Est-ce que les postes de travail font l\'objet d\'une gestion de cycle de vie ?', 'Chacun des acteurs internes va mobiliser des équipements techniques pour gérer et développer le projet. Les postes de travail de l\'ensemble de l\'équipe sont un point relativement simple é adresser du point de vue NR. Avec le suivi du cycle de vie des équipements, l\'utilisation de matériels avec labels environnementaux, le pilotage des alimentations électriques du poste de travail en fonction de la présence / absence au poste.', 'Utilisation', 'Les postes de travail de développement et d\'intégration/tests sont-ils choisis et suivis vis-é-vis de leurs impacts environnementaux ?', NULL, NULL, NULL, NULL, 'RECO', '**', NULL, NULL, '#PERF ; #VOL ; #SEC ; #USE ; #MAINT ;  #DEBT ; #GOV ; #BUY ; #FIN ; #OBSO', 'Manager ; RSE/NR ;ITMngr ;', '(X / Y ) * 100', 'Formalisés = 100 ; prévus = 75 ; Identifiés = 50 ; Non = 0', '100', 'Medium', 'Periodic', 'Une gestion de configuration couvre l\'ensemble des équipements des postes de travail avec consolidation des indicateurs de cycle de vie', NULL, NULL, NULL, NULL, NULL, NULL),
(119, 'SPECIFICATIONS', 'SPC-6.03', 'B', 'B', 'B', 'Déterminer l\'environnement de production dans lequel le projet sera construit et déployé en accord avec les principes NR', 'Un espace commun a-t-il été mis é disposition du projet pour le partage des ressources ? (ex : limiter les PJ mail, duplication de documents...)', 'Lors du suivi projet beaucoup de documents sont produits, revues, validés. La duplication de documents, les transferts vers des destinataires multiples est une source de consommation excessive de ressources. Le plus souvent l\'utilisation d\'un espace partagé avec la mise en place de régles d\'accés permet d\'éviter ces impacts environnementaux.', 'Utilisation', 'Les documents de travail n\'existe-t-il qu\'en un seul exemplaire partagé par tous les intéressés ?', NULL, NULL, NULL, NULL, 'RECO', '*', NULL, NULL, '#VOL ; #SEC ; #COM ; #METH ;', 'Dev ; Designer ; ProjectMngr ; ProductOwner', '(X / Y ) * 100', 'Systématique = 100 ; Conseillé = 50 : Non = 0', '100', 'High', 'Once', 'Dossier technique', NULL, NULL, NULL, NULL, NULL, NULL),
(120, 'SPECIFICATIONS', 'SPC-6.04', 'B', 'B', 'B', 'Déterminer l\'environnement de production dans lequel le projet sera construit et déployé en accord avec les principes NR', 'Est-ce que les ressources/composants disponibles pour le projet ont une information concernant la conformité NR ?', 'Les développements modulaires sont mis en place dans les projets pour faciliter la réutilisation et permettre d\'assembler des composants validés ce qui se traduit par des gains d\'efficacité et de fiabilité. Cette pratique est basée sur les descriptions des interfaces (entrées-sorties) de ces composants, de leurs cadres d\'utilisation et de leurs dépendances. En plus de ces éléments techniques les aspects et bénéfices NR implémentés dans le composant sont utiles pour capitaliser en plus des aspects techniques sur les aspects NR.', 'Revalorisation', 'Chaque composant développé dispose-t-il d\'une note d\'impact NR ?', NULL, NULL, NULL, NULL, 'RECO', '*', NULL, NULL, '#USE ; #MAINT ; #VALUE ; #METH ;', 'Dev ; Designer ; ProjectMngr ; ProductOwner', '(X / Y ) * 100', 'Information NR', 'Composants projet', 'High', 'OnUpdate', 'Dossier technique', NULL, NULL, NULL, NULL, NULL, NULL),
(121, 'SPECIFICATIONS', 'SPC-6.05', 'B', 'C', 'B', 'Déterminer l\'environnement de production dans lequel le projet sera construit et déployé en accord avec les principes NR', 'Dans les ressources techniques du projet est-ce que les gestions de dépendances sont prises en compte, pour limiter l\'obésité des solutions techniques employées?', 'La majorité des projets est une suite d\'intégration de composants avec des développements d\'interfaces spécifiques entre ces composants. Bien souvent les composants issus de bibliothéques publiques (open source ou commerciales) apportent un ensemble de fonctionnalités qui ne sont que rarement toutes utilisées et drainent des dépendances vis-é-vis d\'autres composants avec les mémes caractéristiques. Au final le projet agrége un volume de bibliothéque important alors qu\'une petite partie est réellement utilisée. Privilégier les composants dont les dépendances et fonctionnalités sont pilotables par rapport aux besoins est efficace du point de vue NR.', 'Réalisation', 'Un outil de suivi des dépendances de composants est-il employé ?', NULL, NULL, NULL, NULL, 'RECO', '**', NULL, NULL, '#VOL ; #USE ; #MAINT ; #VALUE ; #DEBT ; #METH  ; #OBSO', 'Dev ; Designer ; ProjectMngr ; ProductOwner', '(X / Y ) * 100', 'Analyses de dépendances', 'Solutions techniques', 'High', 'OnUpdate', 'CHROME : Console dev / COVERAGE', NULL, NULL, 'Y', '#SCRIPT', '*', 'Outils contréle de dépendances'),
(122, 'SPECIFICATIONS', 'SPC-6.C03', 'C', 'C', 'B', 'Déterminer l\'environnement de production dans lequel le projet sera construit et déployé en accord avec les principes NR', 'Dans les choix de conceptions (fonctionnelles et techniques), est-ce que la création, la mise é jour ou la fin de vie d\'un domaine du service numérique a un impact réduit sur les autres domaines du service numérique?', 'Les spécifications du projet peuvent s\'appuyer sur des éléments déjé employés pour d\'autres services. Ces dépendances entre services sont efficaces d\'un point de vue gestion du projet mais peuvent étre pénalisantes lorsqu\'elles imposent le maintien de composants partagés moins efficaces d\'un point de vue NR, surtout lorsque les services utilisant ces ressources sont destinés é étre arrétés é des périodes différentes.', 'Revalorisation', 'La publication des interfaces entre les composants est-elle systématique ?', NULL, NULL, NULL, NULL, 'CONSEIL', 'N/A', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(123, 'SPECIFICATIONS', 'SPC-6.C04', 'C', 'A', 'B', 'Déterminer l\'environnement de production dans lequel le projet sera construit et déployé en accord avec les principes NR', 'L\'implémentation de nouveaux services sous contraintes réglementaires (santé, bancaire, ...) sont-ils nécessaires et le cas échéant s\'appuient-ils sur un socle de service disponible ?', 'La pratique des professionnels utilisant des données réglementées est guidé par la sécurité des données et par la réduction de l\'impact d\'utilisation du service pour rester centré sur l\'enjeu principal. Les spécifications doivent prendre en compte ces éléments et réutiliser le maximum d\'éléments habituels de ces types de services consolidés sous forme d\'un socle de service.', 'Conception', 'Combien de données réglementées (santé, bancaire, ...) sont nécessaires ?', 'Le socle de service est-il évalué par rapport é sa conformité réglementaire (certification) et utilisabilité (accessibilité, sécurité) ?', NULL, NULL, NULL, 'CONSEIL', 'N/A', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(124, 'SPECIFICATIONS', 'SPC-6.06', 'B', 'B', 'A', 'Déterminer l\'environnement de production dans lequel le projet sera construit et déployé en accord avec les principes NR', 'Quels sont les KPIs définis ?', 'Les métiers et le management ont l\'habitude de piloter l\'activité avec une gamme d\'indicateurs business. Le suivi de la performance NR va aussi nécessiter des indicateurs dédiés pour suivre les impacts. Pour la détermination des indicateurs, des plages de valeurs de performance doivent étre définis de maniére itérative et suivre l\'augmentation de maturité de l\'organisation dans l\'appréhension de ces données afin que les processus de décisions puissent se mettre en place pour effectuer un pilotage des aspects NR.', 'Administration', 'Les plages de valeurs des KPIs sont-elles définies ?', NULL, NULL, NULL, NULL, 'RECO', '***', NULL, NULL, '#COM ; #VALUE ; #GOV ;', 'ProjectMngr ; ProductOwner ; Manager ; ITMngr', '(X / Y ) * 100', 'KPIs NR', 'KPI', 'High', 'Periodic', 'Analyse de bénéfices', NULL, NULL, NULL, NULL, NULL, NULL),
(125, 'UX/UI', 'UX/UI-1', NULL, NULL, NULL, 'Intégrer le Numérique Responsable dans les objectifs majeurs du projet', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'ODD#5 ; ODD#8 ; ODD#9 ; ODD#10 ; ODD#12 ; ODD#13', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(126, 'UX/UI', 'UX/UI-1.01', 'B', 'A', 'B', 'Intégrer le Numérique Responsable dans les objectifs majeurs du projet', 'Dans les méthodes d\'idéation, l\'ensemble des parties prenantes est-il pris en compte dans toute sa dimension (humaine) ?', 'Mettre l\'Utilisateur au centre de la réflexion est la garantie de couvrir les besoins essentiels de vos cibles et d\'avancer dans le bon sens. Par Utilisateurs, nous entendons les usagers finaux du service ou du produit mais également les différentes parties prenantes du projet. Considérer vos utilisateurs avec une dimension humaine et empathique permet d\'élargir le prisme de votre réflexion et d\'aller au-delé des aspects économiques et technologiques du projet.', 'Conception', 'Une cartographie des parties prenantes a-t-elle été réalisée ?   Une priorisation des parties prenantes est-elle faite ?', 'Les différentes parties prenantes sont-elles intégrées dans les différents outils : expérience et service Map, cartes UX, shadowing, workshops, etc. ) ?  Une approche Human-Centered Design est-elle utilisée ?', 'L\'approche User centric est-elle présente ?', 'SL : Stakeholders map template   SL : Human-Centered Design', 'SL : https://diytoolkit.org/media/People-and-Connections-map-Size-A4.pdf   SL : https://www.designkit.org/methods  https://www.ideo.com/post/design-kit', 'RECO', '***', NULL, 'INCONTOURNABLE', '#COM ;  #METH ;', 'Designer ; ProductOwner ;', '(X / Y ) * 100', 'Formalisés = 100 ; prévus = 75 ; Identifiés = 50 ; Non = 0', '100', 'High', 'OnUpdate', 'Le panel de profils d\'utilisateurs couvre l\'ensemble des aspects humains que le NR doit adresser pour éviter la fracture numérique', NULL, NULL, NULL, NULL, NULL, NULL),
(127, 'UX/UI', 'UX/UI-1.02', 'A', 'B', 'B', 'Intégrer le Numérique Responsable dans les objectifs majeurs du projet', 'La planéte est-elle prise en compte dans les méthodes d\'idéation pour intégrer la dimension écologique (planet centric design) ?', 'Chaque décision de conception a un impact. Si les problématiques environnementales et écologiques ne sont pas au coeur de l\'analyse, aucun objectif de maétrise ou de réduction des impacts ne sera pris en compte et donc respecté.', 'Conception', 'Le toolkit planet centric est-il connu et utilisé ?', NULL, NULL, 'Planet Centric toolkit', 'https://planetcentricdesign.com/', 'RECO', '***', NULL, 'INCONTOURNABLE', '#COM ;  #METH ;', 'Designer ; ProductOwner ;', '(X / Y ) * 100', 'Formalisés = 100 ; prévus = 75 ; Identifiés = 50 ; Non = 0', '100', 'High', 'OnUpdate', 'Une démarche systémique / Planet Centric est mise en place.', NULL, NULL, NULL, NULL, NULL, NULL),
(128, 'UX/UI', 'UX/UI-1.03', 'B', 'B', 'A', 'Intégrer le Numérique Responsable dans les objectifs majeurs du projet', 'Les plus-values extra-financiéres du numérique responsable sont-elles valorisées dans le business model ?', 'Le Numérique Responsable (NR) peut-étre considéré comme un réel vecteur de développement économique au sein de votre organisation. Intégrer la dimension NR de faéon pérenne é votre business model permettra d\'éviter des arbitrages permanents sur sa valorisation et son intégration é vos futurs projets.', 'Utilisation', 'Les valeurs humaines, écologiques et économiques se retrouvent-elles dans le business model canvas et sont-elles validées par toutes les parties prenantes  ?', 'Un \"Evidence planning\" est-il utilisé pour mettre en valeur les différents gains et coéts des 3P ?', NULL, 'ND: voir les templates (naissants) de sustainable business canvas ?  Sl : Evidence planning', 'SL : https://www.designkit.org/methods/business-model-canvas   SL : https://diytoolkit.org/media/Evidence-Planning-A4.pdf', 'RECO', '***', NULL, 'INCONTOURNABLE', '#COM ; #VALUE ; #FIN ;', 'ProjectMngr ; ProductOwner ; Manager ; ITMngr ;', '(X / Y ) * 100', 'Formalisés = 100 ; prévus = 75 ; Identifiés = 50 ; Non = 0', '100', 'High', 'Once', 'les analyses de bénéfices couvrent les aspects NR avec leurs retombées', NULL, NULL, NULL, NULL, NULL, NULL),
(129, 'UX/UI', 'UX/UI-1.C01', 'B', 'B', 'B', 'Intégrer le Numérique Responsable dans les objectifs majeurs du projet', 'Un process NR est-il prévu au niveau de la gouvernance projet ?', 'Une démarche NR doit étre systémique, sur l\'ensemble du cycle de vie du projet, et donc portée par tous les acteurs du développement.', 'Réalisation', 'Des revues et un suivi transverse des aspects NR sont-ils mis en place ? A quelle fréquence sont planifiées les revues NR ?', 'Un RACI est-il prévu ?', NULL, 'voir si un template est dispo ?', NULL, 'CONSEIL', 'N/A', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(130, 'UX/UI', 'UX/UI-1.04', 'C', 'B', 'C', 'Intégrer le Numérique Responsable dans les objectifs majeurs du projet', 'Les améliorations NR sont-elles  identifiées et systématiquement planifiées ?', 'Le principe d\'amélioration continue permet d\'augmenter la performance du projet et donc de limiter son impact.', 'Maintenance', 'La revue projet est-elle réalisée et permet-elle d\'améliorer les impacts sur les 3ps ?', 'Utiliser un backlog des améliorations identifiées', 'Un post mortem est-il systématiquement mis en place ?', 'liens vers méthodo de pratique post mortem', NULL, 'RECO', '**', NULL, NULL, '#MAINT ; #VALUE ; #METH ;', 'Dev ; Designer ; ProductOwner ;', '(X / Y ) * 100', 'Composantes NR', 'Améliorations', 'High', 'OnUpdate', 'Backlog', NULL, NULL, NULL, NULL, NULL, NULL),
(131, 'UX/UI', 'UX/UI-1.05', 'B', 'B', 'A', 'Intégrer le Numérique Responsable dans les objectifs majeurs du projet', 'Pour les aspects NR les responsables projet (chef de projet, product owner ....) ont-ils les mémes prérogatives que les aspects métiers?', 'Pour que le sujet NR soit suivi, il doit étre incarné par une personne identifiée, responsable, qui diffuse les enjeux é chaque intervenant métier et prend part aux décisions. Ce réle peut étre défini dans ses objectifs personnels ou fiche de poste.', 'Réalisation', 'Quel est le niveau de décision du responsable projet pour les aspects NR ?', 'Le responsable du projet a-t-il présenté le référentiel NR choisi pour le projet ?', 'Tous les protagonistes ont-ils bien le méme niveau d\'information sur les différents réles quant é la responsabilité NR ?', NULL, NULL, 'RECO', '**', NULL, NULL, '#VALUE ; #METH ; #HR ;', 'Dev ; Designer ; ProductOwner ;', '(X / Y ) * 100', 'Formalisés = 100 ; Arbitrés = 75 ; Possibles = 50 ; Non = 0', '100', 'High', 'Periodic', 'Plan de formation', NULL, NULL, NULL, NULL, NULL, NULL),
(132, 'UX/UI', 'UX/UI-1.06', 'B', 'B', 'A', 'Intégrer le Numérique Responsable dans les objectifs majeurs du projet', 'La production des livrables (production documentaire comprise) est-elle utilisée en amont du projet et est-elle produite pour permettre d\'étre réutilisée dans les projets suivants ?', 'Une base de connaissances et de compétences NR va émerger au fur et é mesure des projets. Il est important de capitaliser sur l\'expérience acquise et de diffuser les bonnes pratiques. Cela permettra é vos équipes de développer des automatismes et de réutiliser des solutions qui ont fait leurs preuves sur de futurs projets.', 'Revalorisation', 'La démarche qualité est-elle appliquée é tous les éléments de communication du produit ?', 'Un process de low fidelity est-il mis en oeuvre en préalable du high fidelity ?', NULL, NULL, NULL, 'RECO', '**', NULL, NULL, '#VOL ; #SEC ; #USE ; #MAINT ; #DEBT ; #METH ; #FIN', 'Dev ; Designer ; ProjectMngr ; ProductOwner ; ITMngr', NULL, NULL, NULL, 'Low', 'OnUpdate', 'Documentation technique', NULL, NULL, NULL, NULL, NULL, NULL),
(133, 'UX/UI', 'UX/UI-1.C02', 'B', 'B', 'A', 'Intégrer le Numérique Responsable dans les objectifs majeurs du projet', 'Un budget sur les axes 3Ps est-il associé et suivi lors du projet ?', 'Suivant le niveau de maturité de l\'organisation, la prise en compte du NR peut engendrer un coét et un temps de réflexion qui doivent étre prévus et associés aux bénéfices attendus. Les postes et les temps alloués au NR sur le budget doivent étre validés, visibles et communiqués de maniére précise é toutes les parties prenantes du projet.', 'Administration', 'Les missions UX-UI liées au NR sont-elles clairement détaillées et communiquées dans le budget ? Des indicateurs incluant les 3P sont-ils mis en place pour suivre le budget du projet ?', 'Le budget NR est-il validé au niveau projet ?', 'Un budget carbone est-il alloué au projet et au service, pour tout le cycle de vie ?', 'Systemic Design', 'https://www.systemicdesigntoolkit.org/download', 'CONSEIL', 'N/A', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(134, 'UX/UI', 'UX/UI-2', NULL, NULL, NULL, 'Sensibiliser les parties prenantes internes et externes au Numérique Responsable', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'ODD#10 ; ODD#12 ; ODD#13', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(135, 'UX/UI', 'UX/UI-2.01', 'B', 'A', 'A', 'Sensibiliser les parties prenantes internes et externes au Numérique Responsable', 'Les parties prenantes sont-elles sensibilisées au NR ?', 'Pour adhérer, il faut comprendre. Le NR n\'est pas une discipline technique. Les principes du NR doivent étre mis en oeuvre dans des pratiques métiers et les comportements de chacun des acteurs. Toutes les parties prenantes du projet doivent étre sensibilisées au NR pour agir é leur niveau.', 'N/A', 'Un partage de la connaissance et une sensibilisation vers les parties prenantes sont-ils en place ?', 'Un support de sensibilisation adaptable aux différents projets et aux différents niveaux de connaissance de l\'ensemble des parties prenantes est-il disponible ?', NULL, 'partage des référentiels NR prévoir au planning du temps (et un budget ? ) de sensibilisation', NULL, 'RECO', '*', NULL, 'INCONTOURNABLE', '#COM ; #GOV ; #HR ;', 'RSE/NR ; HR ;', '(X / Y ) * 100', 'Sensibilisations NR', 'Membre équipe', 'High', 'Periodic', 'Le plan de formation intégre les diffusions de sensibilisations et formations aux aspects NR.', NULL, NULL, NULL, NULL, NULL, NULL),
(136, 'UX/UI', 'UX/UI-2.02', 'B', 'B', 'B', 'Sensibiliser les parties prenantes internes et externes au Numérique Responsable', 'Les différentes étapes du projet sont-elles identifiées avec leur process de validation, afin de faire adhérer les parties prenantes é la démarche itérative ?', 'La démarche NR est itérative et parfaitement adaptée aux processus Agiles, ce qui impose aussi des mécanismes de contréles et de validation performants et formalisés.', 'Réalisation', 'Comment sont validées les étapes projets pour le NR ?', 'Disposez-vous de Sustainable Decision Point ?', NULL, NULL, NULL, 'RECO', '**', NULL, NULL, '#COM ; #VALUE ; #METH ;', 'Dev ; Designer ; ProductOwner ;', '(X / Y ) * 100', 'Formalisés = 100 ; prévus = 75 ; Identifiés = 50 ; Non = 0', '100', 'Medium', 'OnUpdate', 'Definition of Done', NULL, NULL, NULL, NULL, NULL, NULL),
(137, 'UX/UI', 'UX/UI-2.C01', 'A', 'A', 'B', 'Sensibiliser les parties prenantes internes et externes au Numérique Responsable', 'Avez-vous mis en place un systéme de présentation du suivi d\'impacts environnementaux, pour l\'exploitation du service et pour chaque utilisateur ?', 'L\'engagement des utilisateurs va susciter l\'adhésion é la démarche NR, c\'est un aspect important de la propagation des valeurs et résultats de l\'organisation. Le fait de communiquer aux utilisateurs des éléments trés visuels facilite leur adhésion et la compréhension de vos engagements.', 'Administration', 'Un compteur écologique est-il proposé é l\'utilisateur de maniére didactique ?', 'Un compteur de la consommation électrique est-il mis en place pour l\'usage du service (par session par ex.) ?', 'Un compteur de temps de visionnage est-il mis en place pour l\'usage du service (par session par ex.) ?  Le poids des fichiers é télécharger est-il systématiquement indiqué afin de sensibiliser é l\'impact de la fonctionnalité ?', NULL, NULL, 'CONSEIL', 'N/A', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(138, 'UX/UI', 'UX/UI-2.C02', 'B', 'A', 'A', 'Sensibiliser les parties prenantes internes et externes au Numérique Responsable', 'Quelles données sont disponibles é la communication concernant le respect des normes et préconisations NR sur le service ?', 'Un certain nombre de ressources sont disponibles pour afficher le niveau de complétion et adhésion au NR. Cela se traduit par des badges, des labels, des normes... permettant é l\'utilisateur d\'identifier les organisations qui font les efforts les plus sensibles sur les enjeux sociaux et environnementaux.', 'Acquisition', 'Les labels et badges é atteindre par l\'applicatif sont-ils fixés en début de projet et affichés dans le service ?', 'Le service est-il certifié et/ou répond-t-il é une normalisation (ISO 14000, 14040...) ?', 'Un outil de communication est-il disponible ?', 'eco index', 'http://www.ecoindex.fr/', 'CONSEIL', 'N/A', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(139, 'UX/UI', 'UX/UI-2.C03', 'B', 'B', 'B', 'Sensibiliser les parties prenantes internes et externes au Numérique Responsable', 'Pour permettre aux utilisateurs d\'étre acteurs dans la réduction de leurs impacts, ont-ils accés é des références qui les invitent é des pratiques NR  ?', 'Le comportement de l\'utilisateur reste l\'un des points sur lequel l\'organisation n\'a pas de contréle. Malgré tous les bénéfices attendus d\'une conception responsable de services numériques, la pratique de l\'utilisateur peut soit démultiplier les effets positifs, soit les réduire dans des proportions importantes. Communiquer des éléments qui permettent é l\'utilisateur de prendre conscience de ces actions pour qu\'il réduise son impact est indispensable.', 'Utilisation', 'Des éco-tips  et les données d\'utilisation sont-ils accessibles par l\'utilisateur lors de l\'utilisation du service ?', 'Les settings é impact NR du service sont-ils clairement identifiables et facilement accessibles par l\'utilisateur ?', 'Proposez-vous des guidelines orientées NR ? Des solutions alternatives NR permettent-elles de prioriser les choix utilisateurs ?', 'schéma pluri-annuel d\'accessibilité + déclaration d\'accessibilité', 'exemple : YT (pause) et discord (pause)', 'CONSEIL', 'N/A', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(140, 'UX/UI', 'UX/UI-3', NULL, NULL, NULL, 'Rendre disponible et accessible le service numérique au plus grand nombre d\'utilisateurs', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'ODD#5 ; ODD#8 ; ODD#10', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(141, 'UX/UI', 'UX/UI-3.C01', 'C', 'A', 'B', 'Rendre disponible et accessible le service numérique au plus grand nombre d\'utilisateurs', 'Les freins é l\'utilisation du numérique (accessibilité, techniques, territoriaux) sont-ils pris en compte ?', 'Afin déétre responsable, le numérique doit en tout premier lieu étre disponible et accessible (En France, 14% de la population n\'a pas d\'accés é internet ou pas de compétences numériques (source : Vie-publique.fr / DILA  Source: ICT Surveys - INSEE), car en France plus d\'un usager sur 3 manque de compétences numériques de base. C\'est l\'objet de la dimension humaine du NR, qui couvre les freins é l\'utilisation du numérique pour certains publics en fonction de capacités é percevoir les éléments (accessibilité) et les accés et la qualité des réseaux.', 'Conception', 'La constitution des personas prend elle en compte l\'intégralité des cas d\'inclusion numérique ?  Les usagers en difficulté sont-ils repérés é l\'aide des personnas et accompagnés durant tout le cycle de vie du service via des tests utilisateurs ou onboarding formations ?', 'En cas de conflit comment les élements sont-ils arbitrés ?', 'L\'accés aux technologies et é la qualité du débit d\'internet est-il bien pris en compte dés la phase de conception ?   Avez-vous défini un objectif au taux d\'accessibilité de votre service ?   Le service répond-t-il aux recommandations et obligations d\'accessibilité et rempli-t-il les critéres d\'un label d\'accessibilité / normes ?', 'SL : Rapport défenseur des droits : Dématérialisation et inégalités d\'accés aux services publics', 'SL : https://www.defenseurdesdroits.fr/sites/default/files/atoms/files/rapport-dematerialisation_num-accessible-03.04.19.pdf', 'CONSEIL', 'N/A', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(142, 'UX/UI', 'UX/UI-3.01', 'C', 'A', 'B', 'Rendre disponible et accessible le service numérique au plus grand nombre d\'utilisateurs', 'Quelles solutions sont déployées pour lutter contre l\'illectronisme et simplifier les usages et services ?', 'Réduire les inégalités déaccés au service numérique afin de néexclure aucun utilisateur, ces cas d\'exclusion peuvent étre fortement limités en prenant en compte trés tét dans la conception les disparités sociales, générationnelles et territoriales.', 'Conception', 'La courbe d\'apprentissage de l\'utilisation et de la compréhension du service est-elle gérée ?', 'L\'UX Content de l\'applicatif est-il géré, la prise en main du service a-t-elle été facilitée ?', 'Quel est le taux de satisfaction/réussite de l\'usage du service par tous les usagers séparément ?   Les outils d\'accessibilité existants et déjé utilisés sont-ils identifiés ?', 'GT contenus Microsoft : Design for guidance    Rapport INSEE sur l\'illectronisme', 'https://msdesignstorage.blob.core.windows.net/microsoftdesign/inclusive/InclusiveDesign_DesigningForGuidance.pdf   https://www.insee.fr/fr/statistiques/4241397', 'RECO', '**', NULL, NULL, '#USE ; #VALUE', 'Dev ; Designer ; ProductOwner ;', '(X / Y ) * 100', 'Formalisés = 100 ; prévus = 75 ; Identifiés = 50 ; Non = 0', '100', 'Low', 'OnUpdate', 'Tests utilisateurs', NULL, NULL, 'Y', '#SCRIPT', '**', 'Outils contréle de vocabulaire complexité des paragraphes'),
(143, 'UX/UI', 'UX/UI-3.02', 'B', 'A', 'B', 'Rendre disponible et accessible le service numérique au plus grand nombre d\'utilisateurs', 'Les critéres technologiques, sociaux, physiques, temporels qui permettent é vos utilisateurs d\'atteindre leur but et limiter la fracture numérique, sont-ils pris en compte ?', 'Traiter les cas d\'exclusion du numérique est la premiére étape incontournable, mais cela ne garantit pas que l\'utilisateur va pouvoir atteindre ses buts aisément par l\'utilisation du service. Ces aspects doivent étre adressés en facilitant le parcours de l\'utilisateur.', 'Conception', 'Les usagers en difficultés quant é l\'usage du service sont-ils bien pris en compte et représentés, ainsi que leur problématique, via des outils objectifs (personas, filatures, échelles de questions, entretiens, univers narratifs...) ?', 'Comment sont collectées les difficultés des utilisateurs ?', NULL, 'SL : Filature template   SL : Echelle de questions  SL : Guide d\'entretien  SL : Univers narratif', 'SL : https://fr.diytoolkit.org/tools/filature/  SL : https://fr.diytoolkit.org/media/echelle-de-questions.pdf  SL : https://fr.diytoolkit.org/media/guide-dentretien.pdf  SL : https://fr.diytoolkit.org/media/univers-narratif.pdf', 'RECO', '**', NULL, NULL, '#USE ; #VALUE ; #LEG', 'Dev ; Designer ; ProductOwner ;', '(X / Y ) * 100', 'Formalisés = 100 ; prévus = 75 ; Identifiés = 50 ; Non = 0', '100', 'Low', 'OnUpdate', 'Analyse d\'impact', NULL, NULL, NULL, NULL, NULL, NULL),
(144, 'UX/UI', 'UX/UI-3.03', 'A', 'A', 'A', 'Rendre disponible et accessible le service numérique au plus grand nombre d\'utilisateurs', 'Le service fonctionne-t-il avec des connexions faibles ?', 'Les environnements de développement et de tests sont souvent performants ce qui masque les impacts de réseaux avec des performances faibles. Afin de ne pas rendre inutilisable le service dans des conditions ou les débits réseaux sont réduits la démarche NR doit prendre en compte une conception adaptée aux territoires ou localisations dont les accés réseaux sont dégradés', 'Utilisation', 'Un mode dit \"Low\" est-il é envisager ?', 'Le service peut-il étre testé avec des débits restreints ?', 'Une perte de connexion au cours du service est-elle prise en compte avec une segmentation du service évitant é l\'utilisateur de refaire un parcours complet?', NULL, NULL, 'RECO', '***', NULL, NULL, '#PERF ; #VOL ;  #USE  ; #OBSO', 'Dev ; Designer ; ProductOwner ;', '(X / Y ) * 100', 'Formalisés = 100 ; prévus = 75 ; Identifiés = 50 ; Non = 0', '100', 'High', 'Periodic', 'Les outils et consoles de développement des navigateurs sont utilisés pour simuler des réseaux avec des bandes passantes réduites (ex: FIREFOX : Console / Limitation bande passante)', NULL, NULL, 'Y', '#SCRIPT', '*', 'Outils simulateurs'),
(145, 'UX/UI', 'UX/UI-3.04', 'C', 'A', 'A', 'Rendre disponible et accessible le service numérique au plus grand nombre d\'utilisateurs', 'Est-ce que les mécanismes d\'accés au service d\'assistance sont disponibles et adaptés é tout public ?', 'Les capacités d\'interactions entre l\'utilisateur et le service peut étre améliorées avec des mécanismes d\'assistance en ligne qui doivent respecter les mémes exigences NR que l\'ensemble du service. L\'accessibilité doit étre pensée pour le service ainsi que pour les solutions d\'aide.', 'Utilisation', 'Les modes d\'accés é l\'assistance ont-ils été testés avec les publics concernés, des difficultés ont-elles été identifiées ?', 'Des formations (Onboardings) sont-elles prévues et accessibles facilement par les utilisateurs en difficulté face é l\'usage du service ?   Une assistance humaine est-elle prévue en cas de difficultés ?', 'Plusieurs modalités d\'accés au service sont-elles prévues via des parcours utilisateurs, user journey, experience map différents ?', NULL, NULL, 'RECO', '*', NULL, NULL, '#USE ;', 'Dev ; Designer ; ProductOwner ;', '(X / Y ) * 100', 'Formalisés = 100 ; prévus = 75 ; Identifiés = 50 ; Non = 0', '100', 'Low', 'OnUpdate', 'Tests utilisateurs', NULL, NULL, NULL, NULL, NULL, NULL),
(146, 'UX/UI', 'UX/UI-3.C02', 'C', 'A', 'A', 'Rendre disponible et accessible le service numérique au plus grand nombre d\'utilisateurs', 'Un fall-back humain a-t-il été mis en place, pour accompagner la prise en main du service ?', 'La charge de l\'assistance aux utilisateurs est le plus souvent déléguée é un automate, afin de s\'affranchir des contraintes de disponibilité et ne pas générer de temps d\'attente pour des réponses fréquemment posées. Le contact avec un automate peut étre frustrant et doit pouvoir se transformer en contact humain en respectant aussi tous les critéres NR.', 'Utilisation', 'Aprés combien de tentatives infructueuses l\'utilisateur est-il mis en relation avec un opérateur humain ?', 'Plusieurs formats d\'échanges sont-ils envisagés (parlé, écrit, langue des signes, parlé complété, chat, présentiel, braille)  ?', 'Les différentes typologies de cibles (handicap, illectronisme ...) ont-elles bien été prises en compte ?', NULL, NULL, 'CONSEIL', 'N/A', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(147, 'UX/UI', 'UX/UI-3.05', 'A', 'A', 'B', 'Rendre disponible et accessible le service numérique au plus grand nombre d\'utilisateurs', 'Une stratégie de compatibilité avec les terminaux et versions logicielles obsolétes est-elle définie ?', 'Les avancées technologiques poussent é augmenter en permanence les exigences matérielles, ce qui réduit le parc de périphériques sur lesquels un service peut s\'exécuter. Cet aspect écarte certains utilisateurs et pousse au renouvellement d\'équipements encore en état de fonctionner avec l\'impact environnemental associé.', 'Utilisation', 'La liste des terminaux est-elle élargie aux terminaux cibles secondaires ?   Existe-t-il des exigences particuliéres en terme d\'expérience utilisateur (habitudes é une interface existante, é un terminal donné) ?', 'Vos personae sont ils qualifiés au niveau des devices et OS associés?', 'Est-ce que la démarche responsive et l\'application de la démarche mobile first sont mises en oeuvre ?', NULL, NULL, 'RECO', '***', NULL, 'INCONTOURNABLE', '#PERF ; #USE ; #DEBT  ; #OBSO', 'Dev ; Designer ; ProductOwner ;', '(X / Y ) * 100', 'Formalisés = 100 ; prévus = 75 ; Identifiés = 50 ; Non = 0', '100', 'High', 'Periodic', 'Chaque élément référencé dans le Dossier Technique porte une information de degrés de compatibilité', NULL, NULL, 'Y', '#SCRIPT', '**', 'Outils simulateurs terminaux'),
(148, 'UX/UI', 'UX/UI-3.C03', 'B', 'B', 'A', 'Rendre disponible et accessible le service numérique au plus grand nombre d\'utilisateurs', 'Dans la définition des personae, les versions cibles de devices, OS et browsers ont-ils été pris en compte ?', 'Le secteur d\'activité, le profil des utilisateurs permet le plus souvent de qualifier le parc matériel qui sera employé pour le service. Les matériels sont déterminés par leurs capacités techniques (puissance de calcul, mémoire, capacité réseau, capteurs, taille et qualité d\'écran) mais aussi par les systémes d\'exploitation qu\'ils peuvent accueillir. Chaque contrainte que le service impose réduit le nombre de postes qui pourront l\'utiliser et donc exclut des publics.', 'Conception', 'Quelle date maximale de compatibilité est définie, permettant de déterminer les éléments techniques de vétusté du parc informatique des cibles ?', 'Les coéts engendrés par la couverture attendue sont-ils chiffrés et acceptés ?', 'Les données analytics des cibles sont-elles intégrées ?', NULL, NULL, 'CONSEIL', 'N/A', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(149, 'UX/UI', 'UX/UI-4', NULL, NULL, NULL, 'Permettre aux personnes en situation de handicap ou souffrant de déficiences, d\'accéder et d\'utiliser efficacement le service numérique', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'ODD#3 ; ODD#8 ; ODD#10', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(150, 'UX/UI', 'UX/UI-4.01', 'C', 'A', 'A', 'Permettre aux personnes en situation de handicap ou souffrant de déficiences, d\'accéder et d\'utiliser efficacement le service numérique', 'Dés la conception, le service répond-t-il aux normes d\'accessibilité en vigueur (au minimum) ?', 'Au delé des aspects purement réglementaires, l\'anticipation de la prise en compte de l\'accessibilité est nécessaire pour assurer une couverture de ce sujet durant toute la durée de vie du service. Anticiper les futurs points de réglements et normes permet de pérenniser le service en limitant les besoins de mises é jour dictées par l\'environnement légal.', 'Conception', 'Quel est le niveau d\'accessibilité visé et mesuré ?', 'Des personas représentatifs des différents handicaps et déficiences sont-ils présents ?', 'Des tests (contrastes, lisibilité, polices usabilité), effectués avec des outils ou des personnes en situation de handicap, sont-ils prévus ?', 'Loi 2005 SL : Inclusive design Toolkit Univertsity of cambridge SL Microsoft Inclusive design toolkit Référentiel RGAA : niv.A, niv.AA et niv.AAA', 'http://www.inclusivedesigntoolkit.com/tools_simulation/  https://www.microsoft.com/design/inclusive/', 'RECO', '**', NULL, 'INCONTOURNABLE', '#USE ; #VALUE ; #LEG', 'Dev ; Designer ; ProductOwner ;', '(X / Y ) * 100', 'Formalisés = 100 ; prévus = 75 ; Identifiés = 50 ; Non = 0', '100', 'High', 'OnUpdate', 'Les aspects reglementaires et légaux sont exprimés, validés et partagés', NULL, NULL, NULL, NULL, NULL, NULL),
(151, 'UX/UI', 'UX/UI-4.02', 'C', 'B', 'B', 'Permettre aux personnes en situation de handicap ou souffrant de déficiences, d\'accéder et d\'utiliser efficacement le service numérique', 'Tout type d\'utilisateur peut-il savoir facilement oé il se trouve dans le service, par la mise en place d\'un systéme de navigation inclusif ?', 'Plus le service est facile d\'utilisation et bien construit, moins l\'utilisateur passera de temps é trouver l\'information qu\'il cherche, ce qui réduira les navigations parasites et diminuera la consommation de ressources et d\'énergie', 'Utilisation', 'Un repére de navigation (fil d\'ariane ou équivalent) est-il présent ?', 'Le plan du site est-il facilement accessible ? Le champ de recherche est-il présent et correctement positionné et indiqué ? Le titre de page est-il explicite ?', 'Avez-vous suivi les bonnes pratiques de navigation sur les apps ? Les outils facilitant l\'accessibilité relative aux terminaux sont-ils connus et utilisés (voiceover over et talkback) ?', 'Référentiel RGAA : niv.A, niv.AA et niv.AAA https://developer.apple.com/design/human-interface-guidelines/ios/app-architecture/navigation/', NULL, 'RECO', '*', NULL, NULL, '#USE ; #VALUE ;', 'Dev ; Designer ; ProductOwner ;', '(X / Y ) * 100', 'Formalisés = 100 ; prévus = 75 ; Identifiés = 50 ; Non = 0', '100', 'Low', 'OnUpdate', 'Navigateur / Touches Clavier : TAB / SPACE / ENTER', NULL, NULL, NULL, NULL, NULL, NULL),
(152, 'UX/UI', 'UX/UI-5', NULL, NULL, NULL, '\"Less is More\" : Concentrez vous sur les fonctionnalités essentielles et simplifiez votre interface', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'ODD#10 ; ODD#12 ; ODD#13', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(153, 'UX/UI', 'UX/UI-5.C01', 'A', 'B', 'B', '\"Less is More\" : Concentrez vous sur les fonctionnalités essentielles et simplifiez votre interface', 'Une sobriété visuelle est-elle mise en place, pour limiter les ressources énergétiques et l\'impact sur la dégradation matérielle des composants visuels, sonores et tactiles constitutifs de l\'interface ?', 'Des éléments/composants visuels, sonores et tactiles peuvent étre mis en avant dans une démarche purement esthétique et/ou marketing mais n\'ont que trés peu d\'intérét pour l\'utilisateur. La simplification de ces éléments apportera une meilleure ergonomie et un usage adapté é chacun ainsi qu\'une réduction de l\'impact énergétique.', 'Réalisation', 'La charte graphique est-elle éco-compatible ? La charte graphique respecte-t-elle les normes d\'accessibilité ?', 'Une liste d\'impacts énergétiques a-t-elle été réalisée ?  Des objectifs de réduction d\'impact sont-ils mis en place et partagés avec toute l\'équipe ?', 'Les designers en charge sont-ils formés au design visuel responsable ?', NULL, NULL, 'CONSEIL', 'N/A', NULL, 'INCONTOURNABLE', '#USE ; #VALUE ;', 'Dev ; Designer ; ProductOwner ;', '(X / Y ) * 100', 'Formalisés = 100 ; prévus = 75 ; Identifiés = 50 ; Non = 0', '100', 'High', 'OnUpdate', 'Un module dark mode est systématiquement proposé', NULL, NULL, NULL, NULL, NULL, NULL),
(154, 'UX/UI', 'UX/UI-5.01', 'A', 'A', 'B', '\"Less is More\" : Concentrez vous sur les fonctionnalités essentielles et simplifiez votre interface', 'Est-ce que les fonctionnalités sont utilisées par l\'utilisateur ?', 'Selon des études (cast software et standish group), 70% des fonctionnalités ne sont jamais ou rarement utilisées. (ref 115 bonnes pratiques)  Le nombre de fonctionnalités disponibles dans un service numérique impactent directement la consommation de ressources et l\'impact environnemental, en créant des fonctionnalités de peu d\'utilité. Ce sont des sources de création de dettes environnementales totalement inutiles. Les fonctionnalités augmentent aussi le volume du service, qui introduisent des délais de chargements, des espaces de stockage supplémentaires.', 'Utilisation', 'Les fonctionnalités proposées sont-elles attendues et utilisées par vos cibles et apportent-elles une réelle valeur é votre produit ?', 'Mesurer l\'usage de chaque feature et limiter votre produit au juste besoin des utilisateurs', 'Dans le respect des recommandations d\'utilisation des outils de tracking, avez-vous mis en place des outils de suivi ?', '- Méthode MoscoW', NULL, 'RECO', '**', NULL, 'INCONTOURNABLE', '#VOL ; #USE ; #VALUE ; #METH ;', 'Dev ; Designer ; ProductOwner ;', '(X / Y ) * 100', 'Validations d\'utilisation', 'Fonctionnalités', 'High', 'Periodic', 'Les parcours utilisateurs sont simulés et validés pour identifier les utilisations de fonctionnalités. Des données sont identifiées et collectées (analytics / sondes) en production.', NULL, NULL, NULL, NULL, NULL, NULL),
(155, 'UX/UI', 'UX/UI-5.02', 'C', 'B', 'B', '\"Less is More\" : Concentrez vous sur les fonctionnalités essentielles et simplifiez votre interface', 'Les prises de décision sont-elles justifiées et soutenues par des indicateurs fiables sur les usages des fonctionnalités tout au long du cycle de vie du service ?', 'La supervision des usages permet de valider les estimations faites en début de conception et d\'identifier les pistes d\'amélioration. Mais ce principe se base sur des sources de données fiables.', 'Administration', 'Quelles données sont collectées, é quelle fréquence ?', 'Des actions sont-elles associées aux données récoltées et analysées ?', NULL, NULL, NULL, 'RECO', '***', NULL, NULL, '#USE ; #VALUE ; #METH ;', 'Dev ; Designer ; ProjectMngr ; ProductOwner ; Manager ; ITMngr ;', '(X / Y ) * 100', 'Formalisés = 100 ; prévus = 75 ; Identifiés = 50 ; Non = 0', '100', 'High', 'Periodic', 'Analyse d\'impact', NULL, NULL, NULL, NULL, NULL, NULL),
(156, 'UX/UI', 'UX/UI-5.03', 'B', 'B', 'B', '\"Less is More\" : Concentrez vous sur les fonctionnalités essentielles et simplifiez votre interface', 'Une politique / démarche / une routine de réduction et de priorisation progressive des fonctionnalités, interactions et éléments graphiques, est-elle mise en place ?', 'Les suppressions d\'éléments sur les interfaces peuvent ne pas perturber les utilisateurs, et donc générer une empreinte réduite avec moins d\'éléments et de fonctionnalités sans que cela ne détériore la valeur du service tout en limitant son impact environnemental', 'Maintenance', 'Un outil permettant de prioriser et suivre l\'usage des fonctionnalités tout au long du cycle de vie du service est-il utilisé ?', 'La suppression des éléments non ou trop peu usités est-elle gérée ?', NULL, '- Revues de simplification - Tests d\'absence - Le focus fanatique - Poker planning', NULL, 'RECO', '**', NULL, NULL, '#USE ; #VALUE ; #METH ;', 'Dev ; Designer ; ProductOwner ;', '(X / Y ) * 100', 'Formalisés = 100 ; prévus = 75 ; Identifiés = 50 ; Non = 0', '100', 'High', 'Periodic', 'Backlog', NULL, NULL, NULL, NULL, NULL, NULL),
(157, 'UX/UI', 'UX/UI-5.C02', 'C', 'C', 'B', '\"Less is More\" : Concentrez vous sur les fonctionnalités essentielles et simplifiez votre interface', 'Les parties prenantes du service SEO sont-elles intégrées, dés la phase de conception ?', 'Les algorithmes d\'optimisation ont tendance é accumuler des données, dans la majorité des cas les données collectées ne sont pas analysées et ne servent donc pas é rationaliser les usages. Les optimisations doivent s\'inscrire dans une démarche itérative et chaque retour doit permettre un gain d\'efficacité mesuré avant de passer é l\'étape suivante.', 'Administration', 'Les retours SEO sont-ils pris en compte dans la phase de conception pour améliorer les impacts NR ?', 'Lors d\'opérations ciblées, une revue quotidienne de la performance SEO est-elle prévue ?', 'L\'analyse fine et les critéres (ROI) de votre SEO sont-ils mis en place, avec une méthode pour éliminer/prioriser les éléments inefficaces ?', NULL, NULL, 'CONSEIL', 'N/A', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(158, 'UX/UI', 'UX/UI-5.C03', 'A', 'B', 'B', '\"Less is More\" : Concentrez vous sur les fonctionnalités essentielles et simplifiez votre interface', 'La pratique de l\'e-mailing est-elle réfléchie, justifiée et réduite é son stricte nécessaire ?', 'Les diffusions de mails notamment lorsque les mails contiennent des contenus riches générent une charge environnementale importante. Dans le cas de diffusion en masse, ces impacts sont démultipliés. Le cas des newsletters est particuliérement sujet é cette explosion de trafic. Les contenus, volumes et cibles de diffusions doivent étre réduits pour compenser par l\'efficacité et la pertinence ce qui était pris en charge par la diffusion massive.', 'Utilisation', 'L\'accés au désabonnement est-il rapide et facile pour l\'utilisateur (sans demande de confirmation) ?', 'Un suivi des performances de consultation de l\'e-mailing est-il en place ? L\'information partagée de l\'e-mailing, apporte-t-elle une valeur é son lecteur ? L\'archivage de l\'e-mailing est-il géré ?', 'Des campagnes de gestion de la Base de Données sont-elles prévues ? La base de contacts est-elle segmentée pour éviter des envois en masse ?', NULL, NULL, 'CONSEIL', 'N/A', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(159, 'UX/UI', 'UX/UI-5.04', 'A', 'C', 'B', '\"Less is More\" : Concentrez vous sur les fonctionnalités essentielles et simplifiez votre interface', 'Un planning d\'archivage et une version allégée des contenus anciens consultables sont-ils mis en oeuvre ?', 'Il est souvent produit une quantité non négligeable de fichiers, souvent versionnés, afin de répondre aux exigences client. Instaurer une bonne pratique de gestion des fichiers (livrables client comme internes) est essentiel. Trier et supprimer les versions obsolétes et non utilisables aura un impact positif sur le stockage. En effet, toutes ces données stockées ont un coét environnemental car liées é l\'utilisation de serveurs le plus souvent, et représentent donc un coét financier important également. Définir une politique d\'archivage des contenus contribuera é une gestion responsable et efficace de la production du service.', 'Fin de Vie', 'Les critéres d\'archivage et le planning sont-ils définis ?', 'Une automatisation des archives est-elle mise en place ?', 'Quel volume est gagné par le transfert en format brut ou allégé des contenus archivés qui restent consultables ?', NULL, NULL, 'RECO', '**', NULL, NULL, '#PERF ; #VOL ; #USE ; #COM ; #VALUE ; #METH ;', 'Dev ; Designer ; ProductOwner ;', '(X / Y ) * 100', 'Volume supprimé', 'Volume des contenus inactifs', 'Medium', 'Periodic', 'La gestion de configuration prend en compte les contenus avec leurs mécanismes d\'optimisations en fonction de l\'ancienneté', NULL, NULL, 'Y', '#INSTRUMENTATION', '***', 'Tag EnD Of Life dans les US');
INSERT INTO `goodpractices` (`cle`, `Family`, `Id`, `Planet`, `People`, `Prosperity`, `Recommendation`, `Criteria`, `Justification`, `Stage`, `Tests1`, `Tests2`, `Tests3`, `Correspondence`, `Link`, `Type`, `Difficulty`, `Goal`, `Incontournable`, `Tags`, `Actors`, `Indicators_`, `IndicatorsX`, `IndicatorsY`, `Priority`, `Recurrence`, `UseCase`, `Example`, `Limite`, `Automatizable`, `AutomatizationScript`, `AutomatizationLevel`, `AutomatizationInformation`) VALUES
(160, 'UX/UI', 'UX/UI-5.C04', 'B', 'B', 'C', '\"Less is More\" : Concentrez vous sur les fonctionnalités essentielles et simplifiez votre interface', 'Est-ce que les analytics ont été limités au strict nécessaire pour optimiser le service ?', 'Les collectes de données d\'analytics sont installées dans les codes sources et doivent étre positionnées uniquement aux points de passage oé elles apportent une possibilité d\'amélioration de performance car leurs actions ne sont pas neutres; elles collectent, transmettent, stockent des données et alourdissent les services en rajoutant des codes exécutables qui demandent des ressources de traitements sur le périphérique utilisateur, sur les réseaux et sur les serveurs distants. De plus, les données collectées peuvent étre transmises et utilisées par des tiers pour des usages dont l\'utilisateur n\'est pas toujours conscient.', 'Utilisation', 'L\'utilisation des analytics est-elle optimisée au nécessaire ?', 'Les outils mis en place ne présentent-ils pas de redondances ?', 'Est-ce que les services analytics n\'occupent pas plus de 30% de la performance et de la consommation de ressources ?', NULL, NULL, 'CONSEIL', 'N/A', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Y', '#SCRIPT', '*', 'Outils parser code'),
(161, 'UX/UI', 'UX/UI-5.C05', 'B', 'B', 'C', '\"Less is More\" : Concentrez vous sur les fonctionnalités essentielles et simplifiez votre interface', 'Est-ce que les services tiers (fils réseaux sociaux, social wall, carrousels, google maps etc) ne sont pas utilisés par facilité pour pallier le manque de ressources de production de contenus ?', 'Il est courant d\'utiliser des services tiers rattachés é l\'utilisation des réseaux sociaux, géolocalisation, etc. L\'utilisation de ces services tiers doit étre au service de l\'information et non pas de s\'y substituer car  leur usage est trés gourmand en ressources, bande passante et batterie des terminaux notamment. Il est important de limiter leur usage au strict nécessaire afin de limiter leur impact environnemental.', 'Conception', 'Une alternative aux services tiers a-t-elle été envisagée, est-elle possible ?', 'Les liens proposés par les services tiers sont-ils éliminés au prosperity de contenus utiles plus légers ?', 'L\'utilité des services tiers est-elle éprouvée systématiquement ?', NULL, NULL, 'CONSEIL', 'N/A', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(162, 'UX/UI', 'UX/UI-6', NULL, NULL, NULL, 'Permettre aux utilisateurs de réduire leurs impacts', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'ODD#12 ; ODD#13 ; ODD#14 ; ODD#15', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(163, 'UX/UI', 'UX/UI-6.C01', 'A', 'B', 'B', 'Permettre aux utilisateurs de réduire leurs impacts', 'L\'utilisation du service permet-elle d\'envisager des configurations logicielles ou matérielles moins impactantes pour l\'environnement ?', 'Les équipements utilisateurs sont de plus en plus performants et les services numériques exploitent ces capacités techniques. Cela a pour effet d\'accentuer la fracture numérique en excluant une partie des utilisateurs qui ne peuvent se procurer ces terminaux qui ont un coét financier important.  Il est donc important de se questionner sur l\'utilité du recours é ces technologies pour arriver é la finalité du service numérique tout en préservant l\'accés au plus grand nombre.', 'Utilisation', 'Dans la recherche utilisateurs, des contraintes techniques, matérielles ou humaines qui nécessiteraient une version adaptée du service, ont-elles été identifiées ?', 'Dés la premiére utilisation, un mode d\'interface \"dégradé\", Off line ou moins énergivore du service, est-il poussé é l\'utilisateur via une phase d\'installation et/ou onboarding ?  Les volumes de connexions simultanées ont-ils un impact sur les rendus de l\'interface ?', 'L\'utilisateur a-t-il un accés simple et identifié pour gérer la qualité des médias ?', NULL, NULL, 'CONSEIL', 'N/A', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(164, 'UX/UI', 'UX/UI-6.01', 'A', 'B', 'A', 'Permettre aux utilisateurs de réduire leurs impacts', 'L\'expérience utilisateur prend-elle en compte les évolutions possible du service (ajout/suppression de fonctionnalités) ?', 'Un service numérique posséde une durée de vie qu\'il est important de prolonger au maximum. Il existe souvent une version béta, puis suivent d\'autres fonctionnalités viendront se rajouter au fil de temps. Il est important de soulever ces questions dés la conception du service avec les différents partenaires et clients afin de développer un service modulable et adaptable dans le temps. Cela permettra de limiter les coéts financiers é terme et environnementaux car le service sera évolutif, prolongeant ainsi sa durée de vie.', 'Utilisation', 'Les besoins futurs d\'évolution du service ont-il été identifiés et planifiés ?', 'Les mises é jour du service sont -elles appropriées et NR Compliant ?', 'L\'utilisateur peut-il choisir d\'activer ou non des fonctionnalités de l\'interface en fonction de ses besoins et des éventuelles mises é jour ?', NULL, NULL, 'RECO', '***', NULL, NULL, '#VOL ; #SEC ; #USE ; #MAINT ; #DEBT ; #METH ; #FIN', 'Dev ; Designer ; ProjectMngr ; ProductOwner ; ITMngr', '(X / Y ) * 100', 'Formalisés = 100 ; prévus = 75 ; Identifiés = 50 ; Non = 0', '100', 'Medium', 'OnUpdate', 'Le processus d\'amélioration continue se base sur le Backlog, les améliorations avec une orientation NR sont identifés par des Tags / Labels NR', NULL, NULL, NULL, NULL, NULL, NULL),
(165, 'UX/UI', 'UX/UI-6.C02', 'A', 'A', 'B', 'Permettre aux utilisateurs de réduire leurs impacts', 'Tous les éléments constitutifs de l\'interface font-ils l\'objet d\'une attention particuliére en matiére d\'impact NR, tout en respectant les régles d\'accessibilité ?', 'Plus le théme visuel est simple, plus il sera économe en ressources consommées pour l\'afficher. Plus le service implémente de composants (charge sur les serveurs, les réseaux et les périphériques), plus l\'impact du nombre d\'utilisateur simultanés est important. La solution technique est souvent de surdimensionner les environnements de production pour assurer une meilleure fluidité sur service. Proposer aux utilisateurs de choisir une présentation du service moins gourmande contribuera é réduire l\'impact environnemental (encombrement des réseaux et du surdimensionnement des infrastructures). Avec l\'augmentation du nombre d\'utilisateurs, le service s\'allégera, le périmétre global restera constant et/ou maitrisé avec des moyens techniques justement dimensionnés. De plus, cela aura un réle pédagogique : l\'utilisateur pourra devenir acteur de sa propre consommation et de son impact environnemental.', 'Acquisition', 'Un théme visuel adapté (sombre/clair) est-il proposé é l\'utilisateur, de maniére simple, rapide et modifiable (automatiquement ou manuellement) , en fonction des différents terminaux ? Un théme \"Low\", épuré en terme d\'éléments d\'interface (visuels, animations, vidéos, tactiles, etc.) est-il expliqué et proposé é l\'utilisateur dés sa premiére utilisation et peut-il modifier ce paramétre facilement é tout moment ?', 'Tous les éléments constitutifs de l\'interface (visuels, sonores, tactiles) font-ils l\'objet d\'une démarche de réduction d\'impact et sont-ils éprouvés dans leur utilité ?', 'Le design systéme est-il optimisé, applicable et appliqué tout au long de la vie du service ? Les paramétres de contraste, couleurs, luminosité sont ils identifiables facilement par l\'utilisateur ?', 'Website Carbon    SL : Contrast ratio      SL : Plugin Figma : Test contrast', 'https://www.websitecarbon.com/  SL : https://contrast-ratio.com/   https://www.figma.com/community/plugin/748533339900865323/Contrast', 'CONSEIL', 'N/A', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'CHROME : Extension lighthouse / Accessibilité', NULL, NULL, NULL, NULL, NULL, NULL),
(166, 'UX/UI', 'UX/UI-6.C03', 'A', 'B', 'B', 'Permettre aux utilisateurs de réduire leurs impacts', 'Des solutions sans interface \"graphique\" sont-elles prévues pour certaines fonctionnalités, afin de proposer des alternatives au design visuel dans l\'interface ?', 'Une interface ne se résume pas seulement aux éléments visuels qui la constituent, il peut étre intéressant de challenger les autres approches de l\'expérience utilisateur afin de réduire l\'impact du service.', 'Conception', 'Une version vocale ou conversationnelle de l\'interface est-elle faisable, souhaitable pour l\'utilisateur et le NR ?  Si oui est-elle proposée é l\'utilisateur dés sa premiére utilisation du service ?', 'Une version gestuelle (eye tracking, touch, kinétique) est-elle prévue/envisageable et souhaitable ?', NULL, NULL, NULL, 'CONSEIL', 'N/A', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(167, 'UX/UI', 'UX/UI-6.C04', 'A', 'B', 'B', 'Permettre aux utilisateurs de réduire leurs impacts', 'Un espace de paramétrage orienté NR est-il é disposition de l\'utilisateur, pour limiter au maximum le nombre d\'éléments de présentation ?', 'Chaque action compte et l\'utilisateur a pleinement son réle é jouer dans la réduction de son incidence environnementale. Sensibiliser l\'utilisateur et lui permettre d\'en étre acteur. Informer de l\'impact de ses actions et proposer des alternatives simples et rapides permettront d\'avoir un usage plus responsable du service et de développer une conscience collective, é terme, concernant la pollution numérique.', 'Utilisation', 'Existe-il un espace rapidement accessible, oé l\'utilisateur puisse gérer les paramétres dits éécoé de la UI ?', 'Les conseils utilisateurs et/ou des challenges sont-ils proposés pour améliorer l\'impact du service ?', 'Des alertes sont-elles activables par l\'utilisateur pour favoriser les bonnes pratiques (ex: Attention temps d\'écran important, faites une pause) ?', NULL, NULL, 'CONSEIL', 'N/A', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(168, 'UX/UI', 'UX/UI-6.02', 'A', 'B', 'B', 'Permettre aux utilisateurs de réduire leurs impacts', 'La dématérialisation du service est-elle compléte, et dans le cas oé la production de documents papier est indispensable, est-elle conéue pour limiter son impact au minimum ?', 'Les flux de dématérialisation sont parfois rompus, et imposent la production d\'écrits sur papier. L\'utilisateur, dans certains cas, peut aussi étre plus confortable é utiliser des documents imprimés plutét que 100% électroniques. Les impressions ont un impact environnemental important, que ce soit dans la consommation de papier, de consommables tels que les cartouches et encres d\'impression. Les besoins de présentation en électronique ou en impression sont différents, d\'autre part la limitation de l\'empreinte environnementale doit conduire é réduire la densité et le volume d\'information imprimé', 'Utilisation', 'Les écrans concernés par l\'impression sont-ils identifiés et leur utilité est-elle démontrée  ?', 'Une feuille de style pour l\'impression est-elle réalisée ?', 'Est-ce que l\'intégralité du contenu doit étre imprimée ? Les éléments imprimables sont-ils traités (picto réseaux sociaux , background), les éléments non nécessaires é la lecture / compréhension d\'une page sont-ils masqués ?', NULL, NULL, 'RECO', '**', NULL, NULL, '#USE ; #VALUE ;', 'Dev ; Designer ; ProductOwner ;', '(X / Y ) * 100', 'Formalisés = 100 ; prévus = 75 ; Identifiés = 50 ; Non = 0', '100', 'High', 'OnUpdate', 'Des tests d\'impression sont réalisés pour vérifier les consommations d\'encre, papiers et la pertinence des informations imprimées.', NULL, NULL, NULL, NULL, NULL, NULL),
(169, 'UX/UI', 'UX/UI-6.03', 'A', 'A', 'B', 'Permettre aux utilisateurs de réduire leurs impacts', 'L\'utilisateur peut-il controler l\'activation de solutions tierces ?', 'Les connexions externes au service peuvent apporter de la valeur au service, mais dans certains cas lier l\'utilisateur avec des pratiques auxquelles il n\'adhére pas. Le choix de l\'utilisation des services tiers doit étre transparent et dévolu é l\'utilisateur.', 'Utilisation', 'Le service numérique permet-il é l\'utilisateur de décider de l\'activation d\'un service tiers ?', NULL, NULL, NULL, NULL, 'RECO', '**', NULL, NULL, '#USE ; #VALUE ;', 'Dev ; Designer ; ProductOwner ;', '(X / Y ) * 100', 'Formalisés = 100 ; prévus = 75 ; Identifiés = 50 ; Non = 0', '100', 'High', 'OnUpdate', 'Cartographie des services périphériques', NULL, NULL, 'Y', '#SCRIPT', '**', 'Outils parser code'),
(170, 'UX/UI', 'UX/UI-7', NULL, NULL, NULL, '\"Barebone Iteration\": Itérer votre solution jusqu\'au minimalisme \"acceptable\" par vos utilisateurs', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'ODD#12 ; ODD#13', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(171, 'UX/UI', 'UX/UI-7.01', 'B', 'B', 'B', '\"Barebone Iteration\": Itérer votre solution jusqu\'au minimalisme \"acceptable\" par vos utilisateurs', 'Une méthode de démarche systémique est-elle en place, maétrisée par les acteurs et associée é des outils ?', 'Se lancer dans un projet sans prendre en compte l\'écosystéme du projet/contexte peut avoir un impact conséquent sur le ROI. Mettre en place une démarche systémique et itérative permet d\'étre plus efficace et responsable pour répondre aux besoins et réduire alors les délais de ROI.', 'Conception', 'Les outils du Sytem Design ont-ils été utilisés ?', 'Combien de personnes maétrisent cette méthode ?', 'Des outils liés é la méthode sont-ils mis é disposition ?', 'Systemic Design Toolkit', 'https://www.systemicdesigntoolkit.org/', 'RECO', '***', NULL, NULL, '#VOL ;  #USE ; #COM ; #VALUE ; #METH ;', 'Dev ; Designer ; ProjectMngr ; ProductOwner', NULL, NULL, NULL, 'High', 'OnUpdate', 'Framework systémique', NULL, NULL, NULL, NULL, NULL, NULL),
(172, 'UX/UI', 'UX/UI-7.02', 'C', 'A', 'A', '\"Barebone Iteration\": Itérer votre solution jusqu\'au minimalisme \"acceptable\" par vos utilisateurs', 'Est-ce que la solution a été prototypée en amont, avec le parcours utilisateur et les interactions ?', 'Le prototypage interactif permet, é moindre coét, d\'avoir une vision graphique, ergonomique et technique de la future solution, de naviguer mais aussi de la tester. Par itération, le prototype sera de meilleure qualité.', 'Conception', 'Les prototypes sont-ils validés par toutes les parties prenantes et existe-il un systéme de suivi de validation de ces derniers ?', NULL, NULL, NULL, 'Adobe XD, Sketch (mac), Figma, invision, Balsamiq etc.', 'RECO', '**', NULL, NULL, '#VOL ;  #USE ; #COM ; #VALUE ; #METH ;', 'Dev ; Designer ; ProjectMngr ; ProductOwner', NULL, NULL, NULL, 'High', 'OnUpdate', 'Tests utilisateurs', NULL, NULL, NULL, NULL, NULL, NULL),
(173, 'UX/UI', 'UX/UI-7.03', 'C', 'A', 'B', '\"Barebone Iteration\": Itérer votre solution jusqu\'au minimalisme \"acceptable\" par vos utilisateurs', 'Le comportement et le feedback utilisateur ont-ils été étudiés pour répondre au mieux é leurs besoins ?', 'Le projet est réalisé pour des utilisateurs, ne pas prendre en compte leurs comportements, leurs retours d\'utilisation de la solution n\'est pas recommandable. Faire tester sa solution auprés d\'un panel d\'utilisateurs permet d\'avoir un réel feedback terrain et d\'ajuster le projet pour mieux répondre aux besoins.', 'Utilisation', 'Les prototypes statiques et animés sont-ils testés sur le panel  étudié ?', 'Les besoins clients sont-ils différenciés des besoins utilisateurs ?', 'Des Users stories (couverture de tests) sont-elles rédigées ?', NULL, NULL, 'RECO', '**', NULL, NULL, '#VOL ;  #USE ; #COM ; #VALUE ; #METH ;', 'Dev ; Designer ; ProjectMngr ; ProductOwner', NULL, NULL, NULL, 'High', 'OnUpdate', 'Sondage / évaluation user', NULL, NULL, NULL, NULL, NULL, NULL),
(174, 'UX/UI', 'UX/UI-7.04', 'B', 'B', 'A', '\"Barebone Iteration\": Itérer votre solution jusqu\'au minimalisme \"acceptable\" par vos utilisateurs', 'La conception est-elle documentée (fonctionnellement et techniquement), les livrables de conception doivent étre intelligibles par l\'équipe projet et transférables é l\'équipe de développement ?', 'Trop de documents inadaptés sont produits lors des phases de conception. Une synchronisation entre les différents niveaux d\'attentes (tant sur le fond que sur la forme) améliorera la conduite du projet et évitera les frustrations de toutes parts ainsi que l\'impact énergétique et humain.', 'Réalisation', 'Les processus et formats d\'échanges de spécifications ont-ils été discutés et validés entre les concepteurs, les développeurs et plus largement les parties prenantes ?', 'Est-ce qu\'une production de spécifications associée aux schémas de conception est prévue ?', 'Des outils de conception permettant un export pour développement prenant en compte des recommandations métiers, fonctionnelles et techniques, sont-ils utilisés?', NULL, 'Thinking aloud (eye tracking), questionnaire SUS', 'RECO', '*', NULL, NULL, '; #COM ; #VALUE ; #METH', 'Dev ; Designer ; ProjectMngr ;', '(X / Y ) * 100', 'Formalisés = 100 ; prévus = 75 ; Identifiés = 50 ; Non = 0', '100', 'Low', 'OnUpdate', 'Documentation technique', NULL, NULL, NULL, NULL, NULL, NULL),
(175, 'UX/UI', 'UX/UI-7.05', 'C', 'B', 'B', '\"Barebone Iteration\": Itérer votre solution jusqu\'au minimalisme \"acceptable\" par vos utilisateurs', 'Est-ce que les recherches les plus fréquentes ont été étudiées et adressées pour qu\'elles soient ou mises en cache ou affichées é l\'utilisateur sans envoi de requéte ou clics inutiles.', 'L\'utilisation des moteurs de recherches internes au service permettent aux utilisateurs d\'accéder plus rapidement aux informations en limitant la navigation sur le service. Ces moteurs de recherche sont donc un atout d\'un point de vue efficacité environnementale et assistance é l\'utilisateur. En revanche ils peuvent ne pas disposer de toutes les réponses et s\'appuient sur des ressources distantes qu\'il est coéteux de consulter. Il est important de réduire les échanges entre votre service et les serveurs distants pour de ne pas annuler le bénéfice acquis par l\'utilisation du moteur de recherche.', 'Utilisation', 'Une liste des services les plus demandés et les \"fail requests\" est-elle constituée ? les \"power requests\" sont-elles précisées ?', 'Une liste des requétes les plus formulées est-elle constituée ?', 'Existe-t-il des solutions d\'accomppagnement de l\'utilisateur dans la qualification de sa recherche afin de limiter le nombre de requétes envoyées au serveur ?', NULL, NULL, 'RECO', '**', NULL, NULL, '#PERF ; #VOL ; #USE ;  #VALUE', 'Dev ; Designer ; ProductOwner ;', '(X / Y ) * 100', 'Sollicitation des caches', 'Requétes de recherche', 'Medium', 'OnUpdate', 'Dossier technique', NULL, NULL, NULL, NULL, NULL, NULL),
(176, 'UX/UI', 'UX/UI-7.C01', 'B', 'B', 'B', '\"Barebone Iteration\": Itérer votre solution jusqu\'au minimalisme \"acceptable\" par vos utilisateurs', 'Le chemin parcouru pour accéder au service est-il le plus rapide et le plus simple possible ?', 'Il y a plusieurs scénarios d\'accés aux services et/ou information d\'un applicatif. Il faut les examiner et les faire coexister afin d\'améliorer l\'expérience des utilisateurs.', 'Utilisation', 'Le nombre d\'étapes et le temps sont-ils inclus dans les users journey/maps ?', 'Le nombre d\'étapes et le temps sont-ils inclus dans les tests du service par tous les types d\'utilisateurs ?', NULL, NULL, NULL, 'CONSEIL', 'N/A', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(177, 'UX/UI', 'UX/UI-7.06', 'C', 'B', 'A', '\"Barebone Iteration\": Itérer votre solution jusqu\'au minimalisme \"acceptable\" par vos utilisateurs', 'Une démarche d\'amélioration continue a-t-elle été mise en place ?', 'Au fil du temps, l\'organisation va augmenter sa maturité NR. Les services doivent pouvoir bénéficier de ces progrés pour s\'enrichir de plus d\'intelligence NR embarquée', 'Maintenance', 'La conception est-elle pensée de maniére itérative ?', 'Toutes les parties prenantes sont-elles identifiées et consultées aux moments clés pour la prise en compte de leurs retours ?', NULL, NULL, NULL, 'RECO', '**', NULL, NULL, '#MAINT ; #VALUE ; #METH ;', 'Dev ; Designer ; ProjectMngr ;', '(X / Y ) * 100', 'Formalisés = 100 ; prévus = 75 ; Identifiés = 50 ; Non = 0', '100', 'High', 'Periodic', 'Backlog', NULL, NULL, NULL, NULL, NULL, NULL),
(178, 'UX/UI', 'UX/UI-8', NULL, NULL, NULL, 'Ingénierie média : alléger au maximum les médias du service numérique ( images, animations, contenu ... )', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'ODD#12 ; ODD#13', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(179, 'UX/UI', 'UX/UI-8.01', 'A', 'B', 'B', 'Ingénierie média : alléger au maximum les médias du service numérique ( images, animations, contenu ... )', 'Est ce que la fonctionnalité peut étre proposée sans l\'appel de scripts ou de bibliothéques externes (JS, AJAX, ...) ?', 'Les périphériques utilisateurs disposent de ressources de traitement, la mise en oeuvre de ces ressources consomme de l\'énergie et nécessite de transporter vers le périphérique utilisateur les codes exécutables. En réduisant l\'utilisation de scripts qui fonctionnent sur le poste utilisateur les 2 aspects sont atténués. De plus, certains utilisateurs peuvent ne pas pouvoir utiliser des scripts.', 'Conception', 'Combien de fonctions sont gérées via des bibliothéques externes ?', NULL, NULL, NULL, NULL, 'RECO', '**', NULL, NULL, '#PERF ; #VOL ; #USE ; #METH', 'Dev ; Designer ;', '(X / Y ) * 100', 'Fonctionnalités analysées', 'Fonctionnalités candidates', 'High', 'OnUpdate', 'Le code source est analysé pour identifier les appels de bibliothéques externes', NULL, NULL, 'Y', '#SCRIPT', '*', 'Outils parser code'),
(180, 'UX/UI', 'UX/UI-8.C01', 'A', 'B', 'B', 'Ingénierie média : alléger au maximum les médias du service numérique ( images, animations, contenu ... )', 'La fonctionnalité cible nécessite -t-elle l\'appel d\'une iFrame ?', 'L\'accessibilité d\'une iframe étant nulle et l\'ergonomie de la page proposant le service étant restreinte, l\'iFrame est une solution qui dégradera l\'experience utilisateur.', 'Réalisation', 'Une solution API est-elle disponible ?', 'Combien d\'iFrame sont utilisées dans le service ? Combien sont indispensables pour des raisons de sécurité (Modules de paiements, ....) ?', NULL, NULL, NULL, 'CONSEIL', 'N/A', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(181, 'UX/UI', 'UX/UI-8.02', 'A', 'B', 'A', 'Ingénierie média : alléger au maximum les médias du service numérique ( images, animations, contenu ... )', 'Une politique de gestion/utilisation des médias afin de réduire leurs impacts est-elle en place, avec des critéres de compression et de formats des médias ?', 'Le volume de données transférées augmente l\'empreinte environnementale du service. Il est nécessaire de proposer les médias nécessaires é l\'affichage du service dans la compression idoine afin d\'améliorer le service et augmenter sa sobriété.', 'Réalisation', 'Une checklist de qualité environnementale des médias utilisés dans le service est-elle utilisée ?', 'L\'automatisation de compression de tous les médias utilisés est-elle mise en place ?', 'Une alternative é chaque média a-t-elle été proposée (remplacement d\'une vidéo par une image ou du texte par ex.) ?', NULL, NULL, 'RECO', '*', NULL, 'INCONTOURNABLE', '#PERF ; #VOL ; #USE ; #VALUE ; #METH', 'Dev ; Designer ;', '(X / Y ) * 100', 'Media compressés', 'Supports media', 'High', 'OnUpdate', 'Les outils/consoles de développement et les extensions des navigateurs permettent de vérifier les bonnes pratiques (ex : CHROME - Extension greenIT / bonnes pratiques)', NULL, NULL, 'Y', '#SCRIPT', '*', 'Outils parser code'),
(182, 'UX/UI', 'UX/UI-8.03', 'A', 'B', 'B', 'Ingénierie média : alléger au maximum les médias du service numérique ( images, animations, contenu ... )', 'Le nombre de polices de caractéres (fonts) et les variantes de polices appelées (graisse, caractéres utilisés dans le projet) sont-ils limités ?', 'Les polices de caractéres peuvent étre trés volumineuses. En réduisant le nombre de polices uniquement é celles utilisées dans le service, la  volumétrie échangée diminue.', 'Conception', 'Une typo systéme peut-elle étre utilisée ?', 'Lorsqu\'une typo externe est utilisée, son poids a-t-il été vérifié et existe-t-il une alternative plus légére ?', 'Le fichier typographique chargé est-il limité aux stricts caractéres, langues et graisses utiles pour le service, la création d\'une sub-font est-elle envisagée ? Lors de l\'intégration de polices propriétaires, les formats récents optimisés (par ex : WOTF2) sont-ils utilisés ?', NULL, NULL, 'RECO', '*', NULL, 'INCONTOURNABLE', '#PERF ; #VOL ; #USE ; #VALUE ; #METH', 'Dev ; Designer ;', '(X / Y ) * 100', 'Moins de 3 polices = 100 ; 3-5 polices = 50 ; Plus de 5 = 0', '100', 'High', 'OnUpdate', 'Les outils/consoles de développement et les extensions des navigateurs permettent de vérifier les bonnes pratiques (ex : FIREFOX - console / alertes)', NULL, NULL, 'Y', '#SCRIPT', '*', 'Outils parser code'),
(183, 'UX/UI', 'UX/UI-8.04', 'B', 'B', 'B', 'Ingénierie média : alléger au maximum les médias du service numérique ( images, animations, contenu ... )', 'Est-ce que les fonctionnalités et composants natifs du systéme peuvent correspondre au besoin ?', 'Certaines fonctionnalités sont disponibles sous plusieurs formes. Lorsqu\'elles sont natives, aucun code ou composants supplémentaires ne sont nécessaires pour les utiliser ce qui réduit l\'impact du service.', 'Conception', 'Le fait que le systéme ne propose pas nativement le service est-il établi ?', 'é fonctionnalités égales, le systéme natif est-il plus efficace que l\'alternative imaginée ?', NULL, NULL, NULL, 'RECO', '**', NULL, NULL, '#PERF ; #VOL ; #SEC ; #USE ; #VALUE ; #METH', 'Dev ; Designer ; ProjectMngr ;', '(X / Y ) * 100', 'Fonctionnalités analysées', 'Fonctionnalités candidates', 'High', 'OnUpdate', 'Code source', NULL, NULL, NULL, NULL, NULL, NULL),
(184, 'UX/UI', 'UX/UI-8.05', 'A', 'B', 'B', 'Ingénierie média : alléger au maximum les médias du service numérique ( images, animations, contenu ... )', 'Les vidéos et animations sont-elles utilisées uniquement lorsqu\'elles apportent une valeur pour l\'utilisateur ?', 'Les flux vidéo et les animations générent un volume de données important. La limitation des vidéos réduit la charge environnementale du service. Certains publics ne sont pas en mesure d\'utiliser des vidéos, ils seront donc exclus du service si celui-ci s\'appuie sur ce type de média.', 'Conception', 'Les informations de la vidéo ou de l\'animation ne peuvent-elles pas étre retranscrites dans un format alternatif ?', 'Le poids total, le format et la résolution de chaque vidéo ou animation sont-ils correctement calibrés ?', NULL, NULL, NULL, 'RECO', '*', NULL, NULL, '#PERF ; #VOL ; #USE ; #VALUE ; #METH', 'Dev ; Designer ;', '(X / Y ) * 100', 'Vidéos analysées', 'Vidéos', 'High', 'OnUpdate', 'Les parcours utilisateur présentent les objets média et la valeur ajoutée des informations proposées par ces objets', NULL, NULL, NULL, NULL, NULL, NULL),
(185, 'UX/UI', 'UX/UI-8.06', 'A', 'B', 'B', 'Ingénierie média : alléger au maximum les médias du service numérique ( images, animations, contenu ... )', 'Les images sont-elles optimisées par rapport é leurs fonctions ?', 'Le volume de données échangées pour la présentation d\'une image dépend de sa taille et de la qualité de son format. Certaines images ont des valeurs d\'information importantes, d\'autres ne sont que de l\'illustration. Les consommations de ressources liées doivent donc étre en rapport avec l\'importance de l\'image et permettre é des publics pour lesquels les images ne sont pas accessibles de comprendre leur signification.', 'Réalisation', 'Est-ce que les images utilisées sont adaptées é leurs formats/tailles d\'affichage ?', 'Les images vectorielles ont-elles un format qui le supporte (SVG) ?', 'Est-ce que les pictogrammes et illustrations sont exportés au format SVG ?', NULL, 'AF : Pour optimiser JPG/PNG : https://tinypng.com/  format WebP', 'RECO', '*', NULL, NULL, '#PERF ; #VOL ; #USE ; #VALUE ; #METH', 'Dev ; Designer ;', '(X / Y ) * 100', 'Images analysées', 'Images', 'High', 'OnUpdate', 'Les outils/consoles de développement et les extensions des navigateurs permettent de vérifier les bonnes pratiques (ex : CHROME - Extension greenIT / bonnes pratiques)', NULL, NULL, NULL, NULL, NULL, NULL),
(186, 'UX/UI', 'UX/UI-9', NULL, NULL, NULL, 'Valeur : soyez transparent avec vos utilisateurs', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'ODD#10 ; ODD#12', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(187, 'UX/UI', 'UX/UI-9.C01', 'C', 'B', 'B', 'Valeur : soyez transparent avec vos utilisateurs', 'Les informations partagées sont-elles utiles é l\'utilisateur ?', 'L\'efficacité d\'utilisation d\'un service numérique est basée sur la compréhension par l\'utilisateur de la maniére de satisfaire ses attentes. Les informations que le service numérique communique doivent permettre cette efficacité, en revanche des informations parasites peuvent brouiller sa compréhension et engendrer des comportements qui ne sont pas optimum et qui générent donc des actions inutiles pour l\'utilisateur et impactantes pour l\'environnement.', 'Utilisation', 'Les informations utiles sont-elles accessibles tout au long des parcours utilisateurs ?', NULL, NULL, NULL, NULL, 'CONSEIL', 'N/A', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(188, 'UX/UI', 'UX/UI-9.01', 'B', 'A', 'A', 'Valeur : soyez transparent avec vos utilisateurs', 'La notification de toutes les interactions machine / utilisateur ont-elles été prises en compte pour donner systématiquement é l\'utilisateur le résultat de ses actions et gérer les erreurs ?', 'Les actions déclenchées par l\'utilisateur peuvent conduire é des erreurs. Afin d\'éviter que l\'utilisateur ne répéte les mémes erreurs il doit étre informé du fait qu\'une erreur est survenue et indiquer la cause ou le mode opératoire pour la corriger. A l\'inverse lorsqu\'une action se déroule sans anomalie, l\'utilisateur doit aussi le savoir pour ne pas réitérer une méme action.', 'Réalisation', 'Tous les cas d\'erreurs sont-ils testés en interne et par des utilisateurs finaux ?', 'Les actions utilisateurs comportent-elles un feedback avec un intitulé clair et une solution ?', 'Un fichier répertoriant tous les cas d\'erreurs existe-t-il ?sous quel format (backlog, experience map, .. ) ?', NULL, NULL, 'RECO', '**', NULL, NULL, '#PERF ; #VOL ; #USE ; #VALUE ; #METH', 'Dev ; Designer ;  ProductOwner', '(X / Y ) * 100', 'Interactions analysées', 'Interactions utilisateurs', 'Medium', 'OnUpdate', 'Navigation / parcours utilisateur', NULL, NULL, NULL, NULL, NULL, NULL),
(189, 'UX/UI', 'UX/UI-9.C02', 'C', 'B', 'A', 'Valeur : soyez transparent avec vos utilisateurs', 'Les feedbacks utilisateurs d\'évaluation du service sont-ils pris en compte ?', 'La mesure de la satisfaction ou insatisfaction des utilisateurs est un marqueur important de retour d\'expérience qui permet d\'améliorer la qualité de service et traiter les cas erronés qui ne seraient pas directement visibles mais introduiraient des conséquences sur la performance globale du service pour remplir les fonctionnalités attendues.', 'Utilisation', 'Un canal de conversation, de remontée sur la qualité des services et contenus proposés est-il disponible et utilisé dans le cadre de l\'amélioration des services et contenus ?', 'Un planning d\'enquéte est-il mis en place ?', 'Dans le cas d\'une App, une récurrence de consultation des notifications disponibles sur le store est-elle en place?', NULL, NULL, 'CONSEIL', 'N/A', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(190, 'UX/UI', 'UX/UI-9.C03', 'C', 'A', 'B', 'Valeur : soyez transparent avec vos utilisateurs', 'L\'utilisateur a-t-il les moyens de vérifier simplement les informations exposées dans le service ?', 'Lorsque les informations communiquées ne sont pas jugées fiables par les utilisateurs, cela peut engendrer des recherches supplémentaires pour confirmer des assertions et génére une navigation parasite. D\'autre part, la crédibilité du service peut étre remise en cause et écartée par les utilisateurs. Les utilisateurs ont le droit d\'avoir des informations fiables, les éditeurs de service ont le devoir de respecter leurs utilisateurs en transmettant des informations vérifiées.', 'Utilisation', 'Un processus de vérification de la transparence est-il en place ?', 'Les sources des informations données sont-elles accessibles par l\'utilisateur ?', NULL, NULL, NULL, 'CONSEIL', 'N/A', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(191, 'UX/UI', 'UX/UI-9.02', 'A', 'A', 'B', 'Valeur : soyez transparent avec vos utilisateurs', 'Chacune des actions sont-elles initiées par l\'utilisateur ? est-il informé de la portée et des conséquences de ses actions ?', 'Le mode de fonctionnement du service numérique doit étre transparent vis-é-vis de l\'utilisateur, les actions proposées doivent étre clairement décrites et les conséquences exposées.', 'Utilisation', 'Une carte des interactions du service a-t-elle été réalisée afin de garantir une action utilisateur é chaque action ?', 'Est-ce que les confirmations d\'actions sont limitées en exprimant les conséquences ?', NULL, NULL, NULL, 'RECO', '*', NULL, NULL, '#PERF ; #VOL ; #SEC ; #USE ; #VALUE ; #METH', 'Dev ; Designer ;  ProductOwner', '(X / Y ) * 100', 'Interactions analysées', 'Interactions utilisateurs', 'High', 'OnUpdate', 'Les validations de parcours utilisateur couvrent la recherche de comportments du service ou l\'utilisateur n\'a pas l\'initiative de l\'opération ou n\'et pas informé du résultat', NULL, NULL, 'Y', '#SCRIPT', '**', 'Outils parser code'),
(192, 'UX/UI', 'UX/UI-9.C04', 'C', 'A', 'B', 'Valeur : soyez transparent avec vos utilisateurs', 'Les utilisateurs sont-ils informés que des informations confidentielles sont collectées ?', 'Les données que les utilisateurs confient sont sous votre responsabilité et les utilisateurs doivent étre tenus informés du soin que vous mettez en oeuvre pour assurer la confidentialité de leurs données. Le RGPD impose un cadre pour les données personnelles mais ne couvre pas les autres catégories de données qui mériteraient pourtant les mémes attentions et précautions.', 'Utilisation', 'Les utilisateurs sont-ils informés de l\'utilisation qui sera faite de leurs informations confidentielles ? Les informations de l\'utilisation des données collectées sont-elles rendues accessibles et facilement consultables, le DPO a-t-il été consulté ? Les cases é cocher des formulaires ne sont-elles pas préremplies  ?', 'Le droit é l\'oubli des informations confidentielles est-il exposé aux utilisateurs ?', 'L\'accés aux préférences RGPD est-il en permanence accessible et modifiable par l\'utilisateur ?  La cinématique de gestion des cookies acceptés / refusés a-t-elle été réfléchie lors de la conception ?', NULL, 'RGPD - CNIL', 'CONSEIL', 'N/A', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(193, 'UX/UI', 'UX/UI-10', NULL, NULL, NULL, 'Valeur: concevez un Service Numérique éthique, en ligne avec vos valeurs', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'ODD#3 ; ODD#5 ; ODD#10 ; ODD#12', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(194, 'UX/UI', 'UX/UI-10.01', 'A', 'B', 'A', 'Valeur: concevez un Service Numérique éthique, en ligne avec vos valeurs', 'Le service évite-t-il les Dark Patterns ?', 'Certains services sont pensés pour créer une certaine forme de dépendance é celui-ci. L\'utilisateur ne doit pas étre soumis et exposé é certaines mécaniques de sur-consommation numérique.', 'Conception', 'La Force Continuity (perpétuation d\'abonnement) peut-elle étre bannie dans les services proposés ?', 'Le principe de \"Friend spam\" est-il exclu ?', 'L\'autoplay en continu des vidéos est-il désactivé par défaut  (Binge watching) ? Aucune contre-partie n\'est-elle demandée en feedback d\'une action utilisateur ?  La \"technique Privacy Zuckering\" afin de forcer l\'utilisateur é partager publiquement des informations personnelles est-elle exclue ? Est-il possible de la supprimer ou é défaut, d\'expliquer é l\'utilisateur dans quel but elle est en place ?', NULL, NULL, 'RECO', '**', NULL, 'INCONTOURNABLE', '#USE ; #COM ; #VALUE ; #METH', 'Dev ; Designer ;  ProductOwner', '(X / Y ) * 100', 'Formalisés = 100 ; prévus = 75 ; Identifiés = 50 ; Non = 0', '100', 'High', 'OnUpdate', 'Réalisation d\'une étude de captologie', NULL, NULL, 'Y', '#SCRIPT', '***', 'Outils parser code'),
(195, 'UX/UI', 'UX/UI-10.02', 'C', 'A', 'A', 'Valeur: concevez un Service Numérique éthique, en ligne avec vos valeurs', 'Les normes légales d\'affichage et de communication sur le RGAA (service publique et Société privée de plus de 250 M d\'é de CA) sont-elles prises en compte ?', 'Tous support numérique doit étre accessible par tout é chacun. Il est nécessaire de pouvoir tenir informé l\'utilisateur du niveau d\'accessibilité et des efforts en cours pour améliorer le service. Ces informations doivent étre disponibles et explicitement fléchées dans le parcours de l\'utilisateur.', 'Réalisation', 'Les évaluations de conformité RGAA sont-elles disponibles ?', 'Existe-t-il des \"coéts cachés\" pour l\'utilisateur dans un parcours de paiement qui ne seraient pas bien peréus par certains publics ?', 'La démarche NR est-elle exposée aux utilisateurs afin qu\'ils puissent adapter leurs usages ?', 'RGAA   SL : W3C : Web Content Accessibility Guidelines (WCAG) 2.0  SL: Coulour contrast Analyser (recommandé par Orange)   SL : Simulateur de Daltomisme     SL : Modéle de grille déaudit RGAA version 4', 'https://www.numerique.gouv.fr/publications/rgaa-accessibilite/#contenu  SL : https://www.w3.org/TR/WCAG20/  SL : https://developer.paciellogroup.com/resources/contrastanalyser/   SL : https://colororacle.org/SL    SL : https://www.numerique.gouv.fr/uploads/rgaa/rgaa4.0.modele-de-grille-d-audit.ods', 'RECO', '***', NULL, NULL, '#USE ; #METH ; #LEG', 'Dev ; Designer ;  ProductOwner', NULL, NULL, NULL, 'High', 'OnUpdate', 'Déclaration d\'accessibilité', NULL, NULL, 'Y', '#SCRIPT', '*', 'Outils parser code'),
(196, 'UX/UI', 'UX/UI-10.03', 'B', 'A', 'A', 'Valeur: concevez un Service Numérique éthique, en ligne avec vos valeurs', 'Des régles en matiére de respect de l\'utilisateur sont-elles mises en place ?', 'Certains services sont pensés pour créer une certaine forme de dépendance é celui-ci. L\'utilisateur doit pouvoir rester objectivement juge de sa consommation numérique', 'Conception', 'Une charte éthique numérique est-elle définie ?', 'Un diagnostic du design attentionnel a-t-il été réalisé ?  L\'éthique du projet a-t-elle été testée auprés du référent juridique / éthique ?', 'La Misdirection (manipulation, méme visuelle, pour orienter les choix utilisateurs) est-elle pratiquée ? L\'usage des notifications est-il limité au strict nécessaire ?', 'Design attentionnel + Jeu Beurktionary par les Designers ethiques', 'Méthode de diagnostic du design attentionnel  beurk-design-agency  Designers ethiques.org', 'RECO', '***', NULL, 'INCONTOURNABLE', '#USE ; #COM ; #VALUE ;', 'Dev ; Designer ;  ProductOwner', '(X / Y ) * 100', 'Formalisés = 100 ; prévus = 75 ; Identifiés = 50 ; Non = 0', '100', 'Medium', 'OnUpdate', 'Réalisation d\'une étude de captologie', NULL, NULL, NULL, NULL, NULL, NULL),
(197, 'UX/UI', 'UX/UI-10.04', 'B', 'A', 'B', 'Valeur: concevez un Service Numérique éthique, en ligne avec vos valeurs', 'Est-ce que les publicités sont bien identifiées comme telles et ont-elles une vraie valeur économique dans le service ?', 'La publicité ciblée ou non peut étre une source de revenus incontournable pour le service numérique, la valeur apportée doit étre suffisante pour justifier ces encarts promotionnels, et les utilisateurs ne doivent pas pouvoir confondre ces banniéres de publicité et les contenus du service.', 'Conception', 'Les publicités sont-elles désactivables ?', 'Les publicités sont-elles identifiables visuellement simplement par l\'utilisateur ?', 'D\'un point de vue éthique, les publicités sont-elles liées au contenu du site ou au comportement de l\'utilisateur ?  Une politique Numérique Responsable ou é minima des KPI sur l\'intérét de la publicité pour l\'utilisateur sont-ils en place ?', NULL, NULL, 'RECO', '**', NULL, NULL, '#USE ; #COM ; #VALUE ; #LEG', 'Dev ; Designer ;  ProductOwner', '(X / Y ) * 100', 'Formalisés = 100 ; prévus = 75 ; Identifiés = 50 ; Non = 0', '100', 'High', 'OnUpdate', 'Analyse de bénéfices', NULL, NULL, NULL, NULL, NULL, NULL),
(198, 'UX/UI', 'UX/UI-10.05', 'B', 'A', 'C', 'Valeur: concevez un Service Numérique éthique, en ligne avec vos valeurs', 'Dans quelle mesure l\'attention de l\'utilisateur est-elle contrélée par le service ?', 'L\'attention de l\'utilisateur est une ressource qui doit étre considérée comme rare et précieuse. Dans cette optique le respect de l\'utilisateur passe aussi par sa capacité é concentrer son attention sur les éléments de son choix, plutét que ceux que le service souhaiterait mettre en avant.', 'Utilisation', 'Un diagnostic du design attentionnel a-t-il été réalisé ?', 'Lors des séances d\'idéation, le pire scénario pour les utilisateurs a-t-il été envisagé ?', NULL, '- Design attentionnel - Planet centric - Matrice d\'évaluation de la captologie', 'beurk-design-agency  Designers ethiques.org  https://docs.google.com/spreadsheets/d/1dj_KIKzyPlSND1-fSYNb0BgupCmh2XoYTPkFTKPiaq0/htmlview#', 'RECO', '**', NULL, NULL, '#VOL ;  #USE ; #COM ; #VALUE ; #METH ;', 'Dev ; Designer ; ProjectMngr ; ProductOwner', NULL, NULL, NULL, 'Medium', 'OnUpdate', 'Etude de captologie', NULL, NULL, NULL, NULL, NULL, NULL),
(199, 'CONTENUS', 'CTN-1', NULL, NULL, NULL, 'Appliquer une démarche éditoriale pour les contenus', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'ODD#12', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(200, 'CONTENUS', 'CTN-1.01', 'B', 'C', 'B', 'Appliquer une démarche éditoriale pour les contenus', 'Est-ce que les prestataires retenus pour la production des contenus ont un engagement NR, RSE, accessibilité (formations, inclusion, éthique) ?', 'Beaucoup d\'éléments sont produits en externe, il serait dommageable de casser une dynamique NR en choisissant des prestataires dont le NR n\'est pas un moteur', 'Acquisition', 'Les indicateurs NR des fournisseurs entrent-ils dans le processus de sélection du fournisseur ?', NULL, NULL, 'Accessibilité : prestataires, schéma pluri-annuel de mise en accessibilité', NULL, 'RECO', '**', NULL, NULL, '#COM ; #VALUE ; #GOV ; #BUY ;', 'ProjectMngr ;  Manager ; RSE/NR ; ITMngr', '(X / Y ) * 100', 'Engagements NR ou RSE', 'Prestataires', 'High', 'Periodic', 'Projet', NULL, NULL, NULL, NULL, NULL, NULL),
(201, 'CONTENUS', 'CTN-1.C01', 'C', 'A', 'B', 'Appliquer une démarche éditoriale pour les contenus', 'Est-ce que l\'ensemble des sources utilisées a été vérifié ?', 'Les informations transmises peuvent étre sujettes é controverses et vont nécessiter du temps et l\'utilisation de ressources techniques ayant un impact environnemental, pour que les utilisateurs puissent les vérifier ou les utiliser sans étre abusés', 'Acquisition', 'Avez-vous une validation formelle et la preuve de la validité des contenus tiers exposés ?', NULL, NULL, NULL, NULL, 'CONSEIL', 'N/A', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(202, 'CONTENUS', 'CTN-1.C02', 'C', 'B', 'B', 'Appliquer une démarche éditoriale pour les contenus', 'Est-ce que les faits et opinions sont clairement identifiables ?', 'Lorsque les informations communiquées ne sont pas jugées fiables par les utilisateurs, cela peut engendrer des recherches supplémentaires pour confirmer des assertions et une navigation parasite. D\'autre part, la crédibilité du service peut étre remise en cause et écarter des utilisateurs. Les utilisateurs ont le droit d\'avoir des informations fiables. Les éditeurs de service ont le devoir de respecter leurs utilisateurs en transmettant des informations vérifiées.', 'Conception', 'Comment différenciez-vous visuellement les faits et opinions ?', NULL, NULL, NULL, NULL, 'CONSEIL', 'N/A', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(203, 'CONTENUS', 'CTN-1.02', 'C', 'B', 'B', 'Appliquer une démarche éditoriale pour les contenus', 'Quels sont les droits d\'utilisation des images, vidéos, illustrations et textes de mon site ?', 'L\'emploi de ressources et matériels externes est courant dans les services numériques. Il est important de respecter la propriété intellectuelle et rendre aux créateurs la visibilité liée é leurs créations originales pour respecter une éthique numérique.', 'Acquisition', 'Avez-vous une liste exhaustive et é jour des crédits ?', 'Est-ce que les conditions actuelles disponibles pour les images et vidéos tiennent bien compte des contraintes d\'optimisation, afin de ne pas dénaturer l\'oeuvre d\'origine mais limiter les impacts NR ?', NULL, NULL, NULL, 'RECO', '*', NULL, NULL, '#USE ;  #COM ; #LEG', 'Dev ; Designer ;  ProductOwner', '(X / Y ) * 100', 'Ressources analysées', 'Ressources', 'Medium', 'OnUpdate', 'Projet', NULL, NULL, NULL, NULL, NULL, NULL),
(204, 'CONTENUS', 'CTN-1.03', 'B', 'B', 'C', 'Appliquer une démarche éditoriale pour les contenus', 'Les balises de mise en forme de texte répondent-elles au besoin de hiérarchisation des informations, et pas é des mises en valeur de présentation ?', 'Les automates et assistants d\'accessibilité utilisent les balises pour se repérer et structurer la restitution. L\'emploi de balises ne correspondant pas é la structure du contenu, par exemple pour des mises en forme de texte, rendent l\'utilisation de ces assistants plus difficiles pour une bonne compréhension du contenu.', 'Réalisation', 'N\'utilisez vous pas des balises H pour mettre en évidence du texte ?', NULL, NULL, 'Critére RGAA 8.9', NULL, 'RECO', '*', NULL, 'INCONTOURNABLE', '#USE ; #COM ;', 'Dev ; Designer ;', '(X / Y ) * 100', 'Tags analysés', 'Tags Hx', 'Medium', 'OnUpdate', 'L\'analyse des codes sources permet de vérifier l\'utilisation des balises HTML', NULL, NULL, NULL, NULL, NULL, NULL),
(205, 'CONTENUS', 'CTN-2', NULL, NULL, NULL, 'Réduire l\'empreinte des contenus statiques', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'ODD#12 ; ODD#13', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(206, 'CONTENUS', 'CTN-2.01', 'A', 'B', 'B', 'Réduire l\'empreinte des contenus statiques', 'Est-ce  que les images ont été compressées en amont dans un format adapté pour sa visualisation ?', 'La transmission de données compressées est indispensable pour réduire l\'impact des échanges, en revanche les algorithmes de compression sont consommateurs de ressources techniques de traitement, de stockage intermédiaire. Lorsque les compressions sont effectuées é chaque demande d\'échange, le bénéfice de la compression peut disparaétre ou méme augmenter l\'impact.', 'Réalisation', 'Chaque image utilise-t-elle un format  standard du web et propose une alternative pour certains formats optimisés qui ne seraient pas disponibles sur tous les environnements ?', 'Chaque image utilise-t-elle un degré de compression adapté au rendu de l\'image sur le service numérique ?', '...', 'RGAA / norme ou certif (14062?)', NULL, 'RECO', '*', NULL, 'INCONTOURNABLE', '#PERF ; #VOL ;  #USE ; #COM ; #VALUE ; #METH', 'Dev ; Designer ;', '(X / Y ) * 100', 'Images compressées', 'Images', 'High', 'OnUpdate', 'Les outils/consoles de développement et les extensions des navigateurs permettent de vérifier les bonnes pratiques (ex : CHROME - Extension greenIT / bonnes pratiques)', NULL, NULL, 'Y', '#SCRIPT', '*', 'Outils parser code');
INSERT INTO `goodpractices` (`cle`, `Family`, `Id`, `Planet`, `People`, `Prosperity`, `Recommendation`, `Criteria`, `Justification`, `Stage`, `Tests1`, `Tests2`, `Tests3`, `Correspondence`, `Link`, `Type`, `Difficulty`, `Goal`, `Incontournable`, `Tags`, `Actors`, `Indicators_`, `IndicatorsX`, `IndicatorsY`, `Priority`, `Recurrence`, `UseCase`, `Example`, `Limite`, `Automatizable`, `AutomatizationScript`, `AutomatizationLevel`, `AutomatizationInformation`) VALUES
(207, 'CONTENUS', 'CTN-2.02', 'A', 'B', 'B', 'Réduire l\'empreinte des contenus statiques', 'Quelles sont les images réellement porteuses de sens utiles é la compréhension du service (exemples: logo, images liens, images contractuelles...) ?', 'Le poids, et donc son impact, d\'une image est sans commune mesure avec du texte. Chaque fois que le choix d\'utilisation d\'une image est fait, il doit étre justifié. Une image peut étre nécessaire voir contractuelle (image d\'un produit sur un service de vente en ligne) ou ne servir qu\'é un remplissage d\'espace dans le contenu. Réduire le nombre d\'images diminue l\'impact d\'utilisation du service.', 'Conception', 'Avez-vous évalué chaque image par rapport é son apport pour la compréhension du service ?', NULL, NULL, NULL, NULL, 'RECO', '*', NULL, NULL, '#PERF ; #VOL ;  #USE ; #COM ; #VALUE ; #METH', 'Dev ; Designer ;  ProductOwner', '(X / Y ) * 100', 'Images analysées', 'Images', 'High', 'OnUpdate', 'Les parcours utilisateur présentent les images exposées dans le service et la valeur ajoutée des informations proposées par ces objets', NULL, NULL, NULL, NULL, NULL, NULL),
(208, 'CONTENUS', 'CTN-2.03', 'B', 'B', 'B', 'Réduire l\'empreinte des contenus statiques', 'De quelles images peut-on se passer (pas utile é la compréhension du service) ?', 'Le poids et donc son impact d\'une image est sans commune mesure avec du texte. Chaque fois que le choix d\'utilisation d\'une image est fait, il doit étre justifié. Une image peut étre nécessaire voir contractuelle (image d\'un produit sur un service de vente en ligne) ou ne servir qu\'é un remplissage d\'espace dans le contenu. Réduire le nombre d\'images diminue l\'impact d\'utilisation du service.', 'Conception', 'Les images non porteuses de valeur sont-elles supprimées ?', NULL, NULL, NULL, NULL, 'RECO', '*', NULL, NULL, '#PERF ; #VOL ;  #USE ; #COM ; #VALUE ; #METH', 'Dev ; Designer ;  ProductOwner', '(X / Y ) * 100', 'Images analysées', 'Images', 'High', 'OnUpdate', 'Tests navigation', NULL, NULL, NULL, NULL, NULL, NULL),
(209, 'CONTENUS', 'CTN-2.04', 'A', 'B', 'B', 'Réduire l\'empreinte des contenus statiques', 'Est-ce que du texte peut remplacer certains types d\'images (icons, picto...)', 'Le poids et donc son impact d\'une image est sans commune mesure avec du texte. Chaque fois que le choix d\'utilisation d\'une image est fait, il ne doit pas pouvoir étre remplacé par un texte simple. Réduire le nombre d\'images diminue l\'impact d\'utilisation du service.', 'Conception', 'Combien d\'images comportent uniquement du texte ?', NULL, NULL, 'Pidila-336', NULL, 'RECO', '*', NULL, NULL, '#PERF ; #VOL ;  #USE ; #COM ; #VALUE ; #METH', 'Dev ; Designer ;  ProductOwner', '(X / Y ) * 100', 'Images analysées', 'Images', 'High', 'OnUpdate', 'Les parcours utilisateur présentent les images exposées dans le service et la valeur ajoutée des informations proposées par ces objets', NULL, NULL, NULL, NULL, NULL, NULL),
(210, 'CONTENUS', 'CTN-2.05', 'B', 'A', 'B', 'Réduire l\'empreinte des contenus statiques', 'Une alternative aux images est-elle systématiquement disponible ?', 'Les assistants d\'accessibilité doivent pouvoir utiliser une description de l\'information portée par l\'image afin de permettre une bonne compréhension du service sans la vision de l\'image.', 'Conception', 'Chaque image dispose-t-elle d\'un texte alternatif ?', 'Avez-vous testé le service avec un assistant d\'accessibilité (lecteur d\'écran) ?', NULL, 'CF RGAA 4 critéres 1.1 é 1.9', NULL, 'RECO', '*', NULL, NULL, '#PERF ; #VOL ;  #USE ; #COM ; #VALUE ; #METH ; #LEG', 'Dev ; Designer ;  ProductOwner', '(X / Y ) * 100', 'Images analysées', 'Images', 'High', 'OnUpdate', 'Code source / CHROME : extension lighthouse - Accessibilité', NULL, NULL, 'Y', '#SCRIPT', '*', 'Outils parser code'),
(211, 'CONTENUS', 'CTN-2.C01', 'B', 'B', 'C', 'Réduire l\'empreinte des contenus statiques', 'La superposition d\'information est-elle supprimée ?', 'La prolifération d\'informations superposées rend le service plus difficile é comprendre et é utiliser par des publics moins familiers avec les technologies ou ceux pour lesquels la technologie ne peut étre peréu facilement.', 'Réalisation', 'Les pop-up sont-elles utiles et indispensables é la compréhension du service ou d\'un message ?', NULL, NULL, NULL, NULL, 'CONSEIL', 'N/A', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Navigation / code source', NULL, NULL, NULL, NULL, NULL, NULL),
(212, 'CONTENUS', 'CTN-2.06', 'C', 'C', 'B', 'Réduire l\'empreinte des contenus statiques', 'L\'impact de l\'internationalisation (multi-langues ...) est-il évalué ?', 'La gestion multi-langues ajoute une volumétrie conséquente de messages traduits dans les différentes langues. Certaines langues imposent des encodages de caractéres multi-Bytes plus consommateurs d\'espace mémoire.', 'Réalisation', 'Combien de langues utilisez-vous ?', 'Sont-elles toutes utiles vis-é-vis du public attendu/réel ?', 'Combien sont des langues multi bytes ?', NULL, NULL, 'RECO', '*', NULL, NULL, '#PERF ; #VOL ;  #USE ; #COM ; #VALUE ; #METH', 'Dev ; Designer ;  ProductOwner', '(X / Y ) * 100', 'Formalisés = 100 ; prévus = 75 ; Identifiés = 50 ; Non = 0', '100', 'Low', 'OnUpdate', 'Fiche de cadrage projet', NULL, NULL, NULL, NULL, NULL, NULL),
(213, 'CONTENUS', 'CTN-2.07', 'B', 'C', 'B', 'Réduire l\'empreinte des contenus statiques', 'Le nombre de polices différentes est-il limité ?', 'Chaque police est encodée dans un fichier présentant tous les symboles associés ce qui représente un volume de données important échangé et manipulé.', 'Réalisation', 'Combien de polices différentes sont utilisées ?', 'Un subset est-il utilisé pour limiter la volumétrie avec un format optimisé ?', NULL, NULL, NULL, 'RECO', '*', NULL, NULL, '#PERF ; #VOL ;  #USE ; #COM ; #VALUE ; #METH', 'Dev ; Designer ;  ProductOwner', NULL, NULL, NULL, 'High', 'OnUpdate', 'Code source / CHROME', NULL, NULL, 'Y', '#SCRIPT', '*', 'Outils parser code'),
(214, 'CONTENUS', 'CTN-2.08', 'B', 'C', 'B', 'Réduire l\'empreinte des contenus statiques', 'Privilégiez-vous les polices systéme ?', 'Les polices systémes sont embarquées dans les outils de présentation et ne sont pas véhiculées. Les flux sont donc réduits lorsqu\'elles sont utilisées.', 'Réalisation', 'Quelles sont les polices systémes VS les webfonts ?', NULL, NULL, 'Opquast 17 / GreenIt 29', NULL, 'RECO', '*', NULL, NULL, '#PERF ; #VOL ;  #USE ; #COM ; #VALUE ; #METH', 'Dev ; Designer ;  ProductOwner', NULL, NULL, NULL, 'High', 'OnUpdate', 'Code source / CHROME', NULL, NULL, 'Y', '#SCRIPT', '*', 'Outils parser code'),
(215, 'CONTENUS', 'CTN-2.C02', 'B', 'B', 'B', 'Réduire l\'empreinte des contenus statiques', 'Est-ce que la mise en forme du formulaire est minimale (afin de ne pas surcharger le code pour des styles peu utiles) ?', 'Les balises de mise en forme générent un volume supplémentaire de données échangées et manipulées, réduire ces éléments permet d\'optimiser les traitements et les échanges', 'Réalisation', 'Combien de zones de saisie sont disponibles sur les formulaires ?', 'L\'enchaénement des zones de saisie est-il validé avec les touches clavier ?', NULL, NULL, NULL, 'CONSEIL', 'N/A', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(216, 'CONTENUS', 'CTN-2.09', 'B', 'B', 'B', 'Réduire l\'empreinte des contenus statiques', 'Est-ce que le formulaire permet é l\'utilisateur de désactiver les fonctions dynamiques (ex: auto completion)', 'Certaines fonctions lorsqu\'elles sont activées vont utiliser des ressources de calcul et des échanges réseaux, c\'est le cas des systémes d\'auto-complétion qui peuvent étre utiles pour certains publics dont l\'expression écrite n\'est pas trés fluide mais qui sont superflus pour des utilisateurs plus autonomes.', 'Réalisation', 'Les saisies dans le formulaire ne nécessitent-t-elles pas d\'échanges superflus ?', NULL, NULL, NULL, NULL, 'RECO', '**', NULL, NULL, '#PERF ; #VOL ; #SEC ; #USE ; #COM ; #VALUE ; #LEG', 'Dev ; Designer ; ProjectMngr ; ProductOwner', NULL, NULL, NULL, 'Medium', 'OnUpdate', 'Code source', NULL, NULL, NULL, NULL, NULL, NULL),
(217, 'CONTENUS', 'CTN-2.10', 'C', 'B', 'B', 'Réduire l\'empreinte des contenus statiques', 'Est-ce que les formulaires sont utiles (exemple : ne pas créer de compte pour l\'achat d\'un produit/service - e-commerce) ?', 'Les formulaires de saisie, outre la gestion technique et leurs impacts (traitements, échanges réseaux) nécessitent des interactions de la part de l\'utilisateur qui peuvent s\'avérer contraignantes pour certains publics.', 'Conception', 'Avez-vous vérifié que toutes les données collectées sont en rapport avec l\'action utilisateur ?', NULL, NULL, NULL, NULL, 'RECO', '*', NULL, NULL, '#PERF ; #VOL ; #SEC ; #USE ; #COM ; #VALUE ; #LEG', 'Dev ; Designer ; ProjectMngr ; ProductOwner', NULL, NULL, NULL, 'High', 'OnUpdate', 'Parcours utilisateur', NULL, NULL, NULL, NULL, NULL, NULL),
(218, 'CONTENUS', 'CTN-2.C03', 'C', 'B', 'B', 'Réduire l\'empreinte des contenus statiques', 'Est-ce que le blocage des robots et accés non sollicités sont traités par des solutions peu coéteuses (captcha vs \"addition\" , ....) ?', 'La protection vis é vis des automates peut mettre en oeuvre des techniques qui générent une charge d\'échange importante (images multiples par exemple) d\'autres techniques sont plus légéres de ce point de vue pour bloquer les accés \"non humains\"', 'Réalisation', 'Quelle solution de validation utilisez-vous ?', NULL, NULL, 'Critére RGAA 1.5', NULL, 'CONSEIL', 'N/A', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Y', '#SCRIPT', '**', 'Outils parser code'),
(219, 'CONTENUS', 'CTN-2.11', 'C', 'B', 'B', 'Réduire l\'empreinte des contenus statiques', 'Lorsque des listes de sélection sont disponibles, sont-elles limitées aux valeurs pertinentes pour l\'utilisateur du service ?', 'Lors des saisies de données é partir de listes, le volume des listes peut fournir des entrées qui n\'ont aucun intérét et générent un volume totalement inutile, par exemple une liste de pays de l\'Afghanistan au Zimbabwe pour un service franco-franéais pour lequel le pays n\'est pas une donnée critique, par exemple des choix :  France, Europe, Autre suffiraient', 'Réalisation', 'Avez-vous dans les listes (pays par exemple) des éléments inutiles (AUSTRALIE par exemple pour un service franco-franéais) ?', NULL, NULL, NULL, NULL, 'RECO', '**', NULL, NULL, '#PERF ; #VOL ;  #USE ; #COM ; #VALUE ; #METH', 'Dev ; Designer ;  ProductOwner', '(X / Y ) * 100', 'Listes analysées', 'Listes de sélections', 'Low', 'OnUpdate', 'Navigation / code source', NULL, NULL, 'Y', '#SCRIPT', '**', 'Outils parser code'),
(220, 'CONTENUS', 'CTN-3', NULL, NULL, NULL, 'Eviter les contenus actifs, conserver les contenus dynamiques sous contréle de l\'utilisateur', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'ODD#12 ; ODD#13', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(221, 'CONTENUS', 'CTN-3.C01', 'A', 'B', 'B', 'Eviter les contenus actifs, conserver les contenus dynamiques sous contréle de l\'utilisateur', 'Avez-vous limité les animations ?', 'Le traitement des animations consomme des ressources techniques et détourne l\'attention des utilisateurs, de plus les animations sont généralement dépendantes de caractéristiques techniques des terminaux et générent des incompatibilités qui poussent artificiellement  au renouvellement des équipements.', 'Conception', 'Utilisez-vous des carrousels ? sont-ils utiles é la compréhension du service ?', NULL, NULL, NULL, NULL, 'CONSEIL', 'N/A', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(222, 'CONTENUS', 'CTN-3.01', 'A', 'C', 'B', 'Eviter les contenus actifs, conserver les contenus dynamiques sous contréle de l\'utilisateur', 'Est-ce que l\'utilisation de Flash est supprimée, remplacée par un autre effet?', 'Les technologies Flash (en fin de vie) utilisent beaucoup de ressources techniques sur le terminal utilisateur et demandent un suivi et des mises é jour de versions fréquentes, avec des limites de \"rétro-compatibilité\" fortes conduisant é une obsolescence anticipée des périphériques.', 'Réalisation', 'Avez-vous supprimé l\'utilisation de flash dans le service ?', NULL, NULL, 'Opquast 14 / GreenIT 115', NULL, 'RECO', '**', NULL, NULL, '#PERF ; #VOL ; #USE ; #METH', 'Dev ; Designer ;  ProductOwner', '(X / Y ) * 100', 'Appels flash supprimés', 'Appels flash', 'High', 'OnUpdate', 'Le code source est analysé pour rechercher les utilisation de flash et les supprimer', NULL, NULL, 'Y', '#SCRIPT', '*', 'Outils parser code'),
(223, 'CONTENUS', 'CTN-3.02', 'A', 'A', 'B', 'Eviter les contenus actifs, conserver les contenus dynamiques sous contréle de l\'utilisateur', 'Les contenus actifs sont-ils déclenchés uniquement sur action de l\'utilisateur ?', 'Les contenus actifs utilisent des ressources techniques pour fonctionner. L\'activité de ces composants doit étre lancée é la demande de l\'utilisateur pour éviter de consommer de l\'énergie inutilement.', 'Réalisation', 'Si un carrousel est par exemple présent dans le service, est-il activable uniquement sur action de l\'utilisateur (pas d\'autoplay) ?', NULL, NULL, 'RGAA Critére 13.8 Cf opquast 121 Les animations, sons et clignotements peuvent étre mis en pause.', NULL, 'RECO', '**', NULL, NULL, '#PERF ; #VOL ;  #USE ; #COM ; #VALUE ; #METH', 'Dev ; Designer ;  ProductOwner', '(X / Y ) * 100', 'Contenus analysés', 'Contenus actifs', 'High', 'OnUpdate', 'Les validations de parcours utilisateur couvrent la recherche de comportments du service ou l\'utilisateur n\'a pas l\'initiative de l\'opération', NULL, NULL, 'Y', '#SCRIPT', '***', 'Outils parser code'),
(224, 'CONTENUS', 'CTN-3.03', 'B', 'A', 'B', 'Eviter les contenus actifs, conserver les contenus dynamiques sous contréle de l\'utilisateur', 'Est-ce que le contenu animé est sur la partie visible de l\'écran (pas d\'animation déclenchée sous la partie active de l\'écran)', 'La présentation d\'un service est rarement contenue sur la surface de l\'écran et du scrolling ou de la pagination sont nécessaires. Les animations demandées par l\'utilisateur peuvent étre en dehors de la zone visible. Afin de réduire la consommation de ressources inutiles, toutes les animations non directement visibles doivent étre stoppées.', 'Réalisation', 'Comment bloquez-vous les diffusions pour les contenus hors de la zone visible ?', NULL, NULL, 'RGAA Critére 4.10 CF opquast 119 les vidéos sont déclenchées par l\'utilisateur  CF opquast 120 les sons sont déclenchées par l\'utilisateur  CF Opquast 122 Le déroulement des animations ne bloque pas la navigation ou l\'accés aux contenus.', NULL, 'RECO', '**', NULL, NULL, '#PERF ; #VOL ;  #USE ; #COM ; #VALUE ; #METH', 'Dev ; Designer ;  ProductOwner', '(X / Y ) * 100', 'Contenus analysés', 'Contenus actifs', 'High', 'OnUpdate', 'Navigation', NULL, NULL, NULL, NULL, NULL, NULL),
(225, 'CONTENUS', 'CTN-3.C02', 'B', 'B', 'B', 'Eviter les contenus actifs, conserver les contenus dynamiques sous contréle de l\'utilisateur', 'Est-ce que la durée des contenus animés ayant été validés comme nécessaire est optimisée ?', 'La durée totale des animations utilise des ressources. Le fait de réduire la durée et de ne pas les faire jouer en boucle permet, lorsque les animations sont indispensables, de réduire l\'impact de consommation.', 'Conception', 'Quelle durée est associée é chaque contenu animé ?', 'Avez-vous désactivé les replay en boucle ?', NULL, NULL, NULL, 'CONSEIL', 'N/A', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(226, 'CONTENUS', 'CTN-3.C03', 'A', 'B', 'B', 'Eviter les contenus actifs, conserver les contenus dynamiques sous contréle de l\'utilisateur', 'Quelles sont les vidéos réellement porteuses de sens et utiles é la compréhension du service (exemples: logo, images liens, images contractuelles...) ?', 'Les flux vidéo sont dans les usages de service numérique l\'un des éléments qui consomme le plus de ressources de traitement, de volume d\'échange. Les vidéos doivent étre limitées aux cas oé elles apportent une valeur importante. La vidéo n\'est pas accessible pour certains publics et doit donc étre limitée.', 'Conception', 'Combien de vidéos comporte le service ?', 'Quelle durée cumulée représente l\'ensemble des vidéos ?', NULL, NULL, NULL, 'CONSEIL', 'N/A', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(227, 'CONTENUS', 'CTN-3.04', 'A', 'B', 'B', 'Eviter les contenus actifs, conserver les contenus dynamiques sous contréle de l\'utilisateur', 'De quelles vidéos peut-on se passer (pas utile é la compréhension du service) ?', 'Les flux vidéo sont dans les usages de service numérique l\'un des éléments qui consomment le plus de ressources de traitement, de volume d\'échange. Les vidéos doivent étre limitées aux cas oé elles apportent une valeur importante. La vidéo n\'est pas accessible pour certains publics et doit donc étre limitée. En revanche, elle peut aussi permettre une perception plus simple d\'un service.', 'Conception', 'Avez-vous validé que la vidéo apporte une valeur pour le service ?', NULL, NULL, NULL, NULL, 'RECO', '*', NULL, NULL, '#PERF ; #VOL ;  #USE ; #COM ; #VALUE ; #METH', 'Dev ; Designer ;  ProductOwner', '(X / Y ) * 100', 'Vidéos analysées', 'Vidéos', 'High', 'OnUpdate', 'Les parcours utilisateur présentent les objets média et la valeur ajoutée des informations proposées par ces objets', NULL, NULL, NULL, NULL, NULL, NULL),
(228, 'CONTENUS', 'CTN-3.05', 'A', 'B', 'B', 'Eviter les contenus actifs, conserver les contenus dynamiques sous contréle de l\'utilisateur', 'Est-ce que l\'information portée par la vidéo peut étre remplacée par une alternative (infographie...)', 'Les informations apportées par la vidéo sont souvent transposables de maniére é étre proposées sous forme statique, nettement moins consommatrices de ressources.', 'Conception', 'Combien de vidéos avez-vous transformé, par exemple en infographie qui réduit l\'impact NR ?', 'La solution vidéo ou des alternatives ont-elle été évaluées du point de vue NR ?', NULL, NULL, NULL, 'RECO', '*', NULL, 'INCONTOURNABLE', '#PERF ; #VOL ;  #USE ; #COM ; #VALUE ; #METH', 'Dev ; Designer ;  ProductOwner', '(X / Y ) * 100', 'Vidéos analysées', 'Vidéos', 'High', 'OnUpdate', 'Les vidéos portent des méta-datas permettant de valider la pertinence du contenu et de le restituer sous une autre forme', NULL, NULL, NULL, NULL, NULL, NULL),
(229, 'CONTENUS', 'CTN-3.C04', 'B', 'C', 'B', 'Eviter les contenus actifs, conserver les contenus dynamiques sous contréle de l\'utilisateur', 'Est-ce que les alternatives au streaming (vs vidéo locale) ont été évaluées ?', 'Le chemin total parcouru par les flux vidéo génére é chaque étape un impact environnemental. Les solutions pour réduire la longueur de ce chemin sont bénéfiques.', 'Réalisation', 'Les évaluations de charges des vidéos entre streaming et hébergement local sont-elles toutes disponibles et utilisées ?', NULL, NULL, NULL, NULL, 'CONSEIL', 'N/A', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(230, 'CONTENUS', 'CTN-3.06', 'A', 'B', 'B', 'Eviter les contenus actifs, conserver les contenus dynamiques sous contréle de l\'utilisateur', 'Est-ce que la durée des vidéos ayant été validées comme nécessaires est optimisée ?', 'L\'impact des flux vidéo est lié é la durée de la vidéo, é la qualité des images et du son, au nombre de diffusion. Réduire la durée est l\'un des leviers facilement gérable pour réduire l\'impact.', 'Réalisation', 'Avez-vous une phase de post-production sur les vidéos pour réduire la durée avec un montage efficace ?', NULL, NULL, NULL, 'guides : theshiftproject.org et ecoprod.com', 'RECO', '*', NULL, NULL, '#PERF ; #VOL ;  #USE ; #COM ; #VALUE ; #METH', 'Dev ; Designer ;  ProductOwner', '(X / Y ) * 100', 'Vidéos analysées', 'Vidéos', 'High', 'OnUpdate', 'Les vidéos font l\'objet d\'une post-production pour réduire la durée des éléments sans valeur et proposer un \"chapitrage\"', NULL, NULL, NULL, NULL, NULL, NULL),
(231, 'CONTENUS', 'CTN-3.C05', 'A', 'B', 'B', 'Eviter les contenus actifs, conserver les contenus dynamiques sous contréle de l\'utilisateur', 'Est-ce que la diffusion de sessions live est utile ?', 'Le nombre de flux diffusés multiplie par autant l\'impact environnemental de la vidéo.', 'Réalisation', 'Le nombre de participants sur les sessions en direct est-il limité ?', NULL, NULL, 'CF opquast 116 et 117 et 118', NULL, 'CONSEIL', 'N/A', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(232, 'CONTENUS', 'CTN-3.07', 'C', 'A', 'B', 'Eviter les contenus actifs, conserver les contenus dynamiques sous contréle de l\'utilisateur', 'Est-ce que le son de la vidéo peut-étre restitué sous forme texte ?', 'L\'accés au son est difficile ou impossible pour certains publics. La fourniture d\'un texte en support ou remplacement du son est indispensable.', 'Réalisation', 'Avez-vous une piste de sous-titre pour chaque vidéo ?', NULL, NULL, NULL, NULL, 'RECO', '**', NULL, NULL, '#PERF ;  #USE ;  #COM ; #VALUE ; #LEG', 'Dev ; Designer ; ProjectMngr ; ProductOwner', NULL, NULL, NULL, 'High', 'OnUpdate', 'Navigation', NULL, NULL, 'Y', '#PROCESS', '***', 'Outils simulateurs'),
(233, 'CONTENUS', 'CTN-3.C06', 'B', 'B', 'B', 'Eviter les contenus actifs, conserver les contenus dynamiques sous contréle de l\'utilisateur', 'Est-ce que les CODECS des sons sont compressés au maximum ?', 'Le volume de données utilisé par la qualité des sons peut étre réduit avec une précision, une qualité sonore plus basse. Les mécanismes de compression audio (CODEC) vont réduire le volume de données échangées mais demander plus de ressources CPU pour décoder et restituer les sons sur le périphérique utilisateur.', 'Réalisation', 'La qualité du son est-elle réduite et testée pour rester compréhensive ?', NULL, NULL, NULL, NULL, 'CONSEIL', 'N/A', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Y', '#PROCESS', '***', 'Outils simulateurs'),
(234, 'CONTENUS', 'CTN-4', NULL, NULL, NULL, 'Faciliter la perception des contenus par l\'utilisateur', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'ODD#10 ; ODD#12', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(235, 'CONTENUS', 'CTN-4.C01', 'A', 'C', 'B', 'Faciliter la perception des contenus par l\'utilisateur', 'Chaque contenu vidéo est-il associé é une durée de vie ?', 'Les vidéos peuvent étre des faits d\'actualité qui n\'ont que peu d\'intérét en dehors de la période oé elle a été produite. Généralement ces informations sont disponibles lors de la création et doivent étre utilisées pour supprimer la vidéo aprés un délai défini. Cela libére de l\'espace de stockage et évite aux utilisateurs de consulter des documents périmés.', 'Fin de Vie', 'A t-on défini une stratégie de cycle de vie d\'une vidéo ?', NULL, NULL, 'CF RGAA 4 critéres 4.1 é 4.13', 'pour mesurer : carbonclap.ecoprod.com', 'CONSEIL', 'N/A', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(236, 'CONTENUS', 'CTN-4.C02', 'B', 'B', 'B', 'Faciliter la perception des contenus par l\'utilisateur', 'A t-on défini la proportion des contenus de type flux (contenus chauds de type actu...) et stock (contenus froid, longue traéne en SEO) ?', 'Les contenus liés é un événement ou une actualité sont peu intéressants aprés un certain temps. Toutes ces informations doivent étre utilisées pour que les informations périmées soient supprimées ou si nécessaire retravaillées pour réduire leurs tailles, et enfin étre mis é disposition des utilisateurs sur des liens d\'archives pour bien exprimer que ces contenus sont anciens. Conserver les contenus anciens peut étre utile pour conserver les référencement SEO acquis.', 'Fin de Vie', 'Comment les contenus récents sont-ils mis en avant ?', 'Le volume des contenus froids est-il réduit et suivi réguliérement ?', 'Tous les contenus ont-ils une date d\'expiration ?', 'CF Opquast 1 et 6', NULL, 'CONSEIL', 'N/A', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(237, 'CONTENUS', 'CTN-4.01', 'B', 'C', 'B', 'Faciliter la perception des contenus par l\'utilisateur', 'Les contenus sont-ils uniques dans tout le service ?', 'Outre les espaces de stockage supplémentaires et la confusion pour l\'utilisateur que la duplication de contenus améne, d\'un point de vue SEO, ces duplications sont inefficaces.', 'Réalisation', 'Avez-vous un suivi des contenus dupliqués ?', NULL, NULL, NULL, NULL, 'RECO', '*', NULL, NULL, '#VOL ;  #USE ; #COM ; #VALUE ; #METH', 'Dev ; Designer ;  ProductOwner', '(X / Y ) * 100', 'Contenus uniques', 'Contenus', 'Low', 'Periodic', 'File system', NULL, NULL, NULL, NULL, NULL, NULL),
(238, 'CONTENUS', 'CTN-4.02', 'A', 'B', 'B', 'Faciliter la perception des contenus par l\'utilisateur', 'Les notifications utilisateurs sont-elles nécessaires et désactivables par l\'utilisateur ?', 'Les méthodes de notifications peuvent employer des technologies consommatrices de ressources et certaines pratiques visent é les multiplier ce qui augmente l\'empreinte environnementale du service. Réduire les notifications, utiliser des mécanismes légers, et permettre é l\'utilisateur de désactiver ces notifications est utile pour réduire les consommations d\'énergie ainsi que le détournement d\'attention des utilisateurs.', 'Utilisation', 'Quelles sont les méthodes de notification utilisateur ? L\'utilisateur a-t-il le choix des mécanismes de notification, de leur fréquence pour lui éviter de tout recevoir par défaut ?', 'L\'abonnement sur un flux RSS est-il proposé comme alternative é des notifications spontanées de type push ?', NULL, NULL, NULL, 'RECO', '**', NULL, NULL, '#VOL ;  #USE ; #COM ; #VALUE ; #METH', 'Dev ; Designer ;  ProductOwner', '(X / Y ) * 100', 'Notifications analysées', 'Notifications', 'Medium', 'OnUpdate', 'Les validation des parcours utilisateurs identifient les notifications utilisateurs et les possibilité de désactivation de ces notifications', NULL, NULL, NULL, NULL, NULL, NULL),
(239, 'CONTENUS', 'CTN-4.03', 'B', 'A', 'B', 'Faciliter la perception des contenus par l\'utilisateur', 'Est-ce que la validité des données est contrélée avant la soumission du formulaire ?', 'Les saisies de données dans les formulaires sont sources d\'erreurs. Si les données ne sont contrélées qu\'aprés soumission du formulaire, le nombre d\'échanges va augmenter. Le contréle du format des données attendues avant l\'envoi au serveur permet de réduire les transferts de données et les traitements inutiles.', 'Utilisation', 'Avez-vous une plage de valeur, un format associé é chaque zone de saisie ?', 'Les données attendues dans chaque champ sont-elles clairement exprimées et contrélées au plus tét aprés leur saisie par l\'utilisateur ?', NULL, NULL, NULL, 'RECO', '*', NULL, NULL, '#PERF ; #VOL ;  #USE ; #COM ; #VALUE ; #METH', 'Dev ; Designer ;', '(X / Y ) * 100', 'Données validées en local', 'Données \"formelles\"', 'Medium', 'OnUpdate', 'Navigation / CHROME : Network', NULL, NULL, 'Y', '#SCRIPT', '**', 'Outils simulateurs'),
(240, 'CONTENUS', 'CTN-4.C03', 'B', 'A', 'B', 'Faciliter la perception des contenus par l\'utilisateur', 'La présentation des informations est-elle accessible sans générer une cascade de zones de présentations dynamiques ?', 'Le dynamisme des affichages peut étre peréu comme un critére de modernité, mais cela génére des calculs et traitements permanents qui sont une source de consommation, et l\'attention de l\'utilisateur peut étre perturbée par des actions spontanées qu\'il n\'a pas déclenché.', 'Utilisation', 'Chaque information est-elle visible sans superposition ni  recalcul intempestif ?', NULL, NULL, NULL, NULL, 'CONSEIL', 'N/A', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(241, 'CONTENUS', 'CTN-4.04', 'B', 'A', 'B', 'Faciliter la perception des contenus par l\'utilisateur', 'Chaque élément actif dispose-t-il d\'un nom permettant de comprendre l\'action qui sera réalisée par son utilisation ?', 'Le dynamisme des interfaces peut ne pas étre appréhendé par certains publics ou certains assistants d\'accessibilité. Il est indispensable que chaque élément actif puissent étre décrit pour que l\'utilisateur comprenne le mode de fonctionnement.', 'Utilisation', 'Avez-vous validé que chaque élément est bien pris en charge par un lecteur d\'accessibilité (lecteur d\'écran)?', 'Utilisez vous toujours les mémes termes pour les mémes actions, en précisant systématiquement le contexte ?', 'Chaque action est-elle toujours située dans le parcours ?', 'Critéres RGAA 6.1 11.9', NULL, 'RECO', '*', NULL, NULL, '#USE ; #COM ; #VALUE ; #METH ; #LEG', 'Dev ; Designer ;  ProductOwner', '(X / Y ) * 100', 'Actions analysées', 'Actions user', 'High', 'OnUpdate', 'Code source / CHROME : Lighthouse / accessibilité', NULL, NULL, 'Y', '#SCRIPT', '*', 'Outils parser code'),
(242, 'CONTENUS', 'CTN-5', NULL, NULL, NULL, 'Assurer la liaison entre les contenus et usages', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'ODD#10 ; ODD#12', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(243, 'CONTENUS', 'CTN-5.C01', 'B', 'B', 'A', 'Assurer la liaison entre les contenus et usages', 'Les informations de tracking de SEO sont-elles suivies et rationalisées afin de ne pas collecter d\'éléments inutiles ?', 'Le suivi de l\'activité des utilisateurs dans le service peut engendrer la collecte d\'un volume important d\'informations qui seront échangées avec les moteurs SEO. Ces collectes sont utiles lorsqu\'elles permettent de fournir plus efficacement les informations aux utilisateurs et de faciliter leurs parcours, elles sont parasites et gaspillées lorsqu\'elles ne sont pas utilisées.', 'Administration', 'Avez-vous validé que chaque information de tracking SEO est bien utilisée ?', NULL, NULL, NULL, NULL, 'CONSEIL', 'N/A', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(244, 'CONTENUS', 'CTN-5.01', 'B', 'B', 'B', 'Assurer la liaison entre les contenus et usages', 'Est-ce que tous les liens et images-liens sont utiles ?', 'Les contenus utilisent des références externes plutét que dupliquer des informations. Le renvoi vers ces sources externes met en oeuvre des mécanismes techniques et consomment des ressources qui doivent avoir une valeur.', 'Utilisation', 'Comment présentez-vous é l\'utilisateur le fait qu\'il s\'agit d\'un lien interne ou externe ?', NULL, NULL, NULL, NULL, 'RECO', '*', NULL, NULL, '#USE ; #COM ; #VALUE ; #METH', 'Dev ; Designer ;  ProductOwner', '(X / Y ) * 100', 'Liens validés', 'Liens', 'Low', 'Periodic', 'Navigation / parcours utilisateur', NULL, NULL, NULL, NULL, NULL, NULL),
(245, 'CONTENUS', 'CTN-5.02', 'B', 'B', 'B', 'Assurer la liaison entre les contenus et usages', 'A t-on vérifié que les urls de destination sont bien valides ?', 'Les contenus utilisent des références externes plutét que de dupliquer des informations. Chaque lien est susceptible d\'étre activé et générera une requéte réseau, elle doit donc étre valide. Dans le cas contraire, l\'utilisateur risque de ne pas comprendre la source de l\'erreur et générer de multiples tentatives infructueuses avec l\'utilisation de ressources techniques sans aucun résultat.', 'Administration', 'A quelle fréquence contrélez-vous les liens ?', NULL, NULL, NULL, NULL, 'RECO', '*', NULL, NULL, '#USE ; #COM ; #VALUE ; #METH', 'Dev ; Designer ;  ProductOwner', '(X / Y ) * 100', 'Liens validés', 'Liens', 'Low', 'Periodic', 'Navigation / parcours utilisateur', NULL, NULL, 'Y', '#SCRIPT', '*', 'Outils simulateurs'),
(246, 'CONTENUS', 'CTN-5.C02', 'B', 'A', 'A', 'Assurer la liaison entre les contenus et usages', 'Les textes mis é disposition des utilisateurs sont-ils adaptés é des publics cibles éloignés de la lecture de contenus électroniques (illectronisme) ?', 'La compréhension de textes sur un support numérique peut étre difficile pour des publics éloignés de ces technologies ou avec un confort visuel ou littéraire restreint. L\'illectronisme doit étre pris en compte dans les choix éditoriaux des services pour inclure le plus de personnes.', 'Utilisation', 'Combien de mots par paragraphe sont affichés sur chaque page ?', 'Le vocabulaire utilisé est-il réduit ? Combien de mots, quel nombre de lettres par mot ?', NULL, NULL, NULL, 'CONSEIL', 'N/A', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Y', '#SCRIPT', '**', 'Outils contréle de vocabulaire complexité des paragraphes'),
(247, 'CONTENUS', 'CTN-5.C03', 'B', 'B', 'B', 'Assurer la liaison entre les contenus et usages', 'A t-on réduit au minimum le nombre de champs (exemple : les champs non obligatoires sont-ils nécessaires ?)', 'Les formulaires de saisie sont l\'occasion de collecter des données de la part de l\'utilisateur. Lorsque ces données ne sont pas indispensables, elles générent tout de méme des impacts d\'échange, de stockage et de sauvegarde. La réglementation encadre les données é caractére personnel et médicales. Toutes les données fournies par un utilisateur doivent étre traitées avec la méme parcimonie et respect.', 'Réalisation', 'Collectez-vous des données facultatives qui ne font l\'objet d\'aucun traitement ?', NULL, NULL, NULL, NULL, 'CONSEIL', 'N/A', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(248, 'CONTENUS', 'CTN-5.03', 'C', 'A', 'B', 'Assurer la liaison entre les contenus et usages', 'Les données utilisateurs collectées et exposées sont-elles clairement identifiées comme données personnelles et/ou sensibles lorsque c\'est le cas?', 'La notion de données personnelles fait l\'objet d\'une définition légale, qui n\'est pas toujours connue par les utilisateurs et donc les régles associées é la protection des données personnelles peuvent étre ignorées. Pour garantir que l\'utilisateur dispose du bon niveau d\'information, il est important que les données é caractére personnel et plus encore les données sensibles soient clairement identifiées comme telles.', 'Utilisation', 'Combien de données personnelles couvertes par le RGPD collectez-vous ?', 'Combien sont des données sensibles ?', NULL, 'RGPD', NULL, 'RECO', '*', NULL, NULL, '#SEC ; #USE ; #COM ; #VALUE ; #METH ; #LEG', 'Dev ; Designer ;  ProductOwner', '(X / Y ) * 100', 'Informations données personnelles', 'Données personnelles', 'High', 'OnUpdate', 'Liasse RGPD', NULL, NULL, NULL, NULL, NULL, NULL),
(249, 'CONTENUS', 'CTN-5.04', 'B', 'B', 'B', 'Assurer la liaison entre les contenus et usages', 'L\'utilisateur peut-il personnaliser la fréquence et le périmétre des contenus diffusés ?', 'Les services numériques peuvent avoir besoin de garder le contact avec leurs utilisateurs, le plus souvent au travers de communications automatiques (newsletter par exemple). Ces diffusions en masse générent des coéts environnementaux de création de ces contenus, de diffusion, de consultation ou stockage (volontaire ou non) par l\'utilisateur. Le volume de communication doit pouvoir étre réglé par l\'utilisateur, de méme que le périmétre du contenu pour correspondre é ses attentes uniquement. La réglementation impose déjé l\'acceptation explicite (opt-In) pour recevoir ce type de communications', 'Utilisation', 'Quelle est votre fréquence d\'émission de contenus ?', 'Quel est le volume mensuel total de toutes vos diffusions?', 'Le Optin RGPD est-il systématique ?', 'CF Opquast 170 : La désinscription depuis une newsletter ne demande pas de confirmation par courriel.', NULL, 'RECO', '**', NULL, NULL, '#VOL ; #USE ; #COM ; #VALUE ; #METH', 'Dev ; Designer ;  ProductOwner', '(X / Y ) * 100', 'Formalisés = 100 ; prévus = 75 ; Identifiés = 50 ; Non = 0', '100', 'Low', 'OnUpdate', 'Parcours utilisateurs', NULL, NULL, NULL, NULL, NULL, NULL),
(250, 'CONTENUS', 'CTN-5.05', 'A', 'C', 'A', 'Assurer la liaison entre les contenus et usages', 'Les documents é télécharger sont-ils compressés, optimisés et accessibles ?', 'Les documents disponibles pour l\'utilisateur dans le service doivent étre compressés pour réduire les impacts liés aux transferts de ces données.', 'Déploiement', 'Tous vos documents sont-ils compressés ?', 'Les tailles et  formats des fichiers sont-ils connus de l\'utilisateur avant son opération de téléchargement ?', NULL, 'PIDILA-443 / Opquast 108-109 / CF 115 bonnes pratiques d\'écoconception (BP né108 and 109)', NULL, 'RECO', '*', NULL, 'INCONTOURNABLE', '#VOL ; #USE ; #METH', 'Dev ; Designer ;  ProductOwner', '(X / Y ) * 100', 'Documents compressés', 'Documents disponibles', 'High', 'OnUpdate', 'Les tests de navigation / parcours utilisateur couvrent les téléchargements, les outils de développement des navigateurs permettent de suivre les volumes de données (ex:  CHROME - network)', NULL, NULL, 'Y', '#SCRIPT', '**', 'Outils simulateurs'),
(251, 'CONTENUS', 'CTN-5.C04', 'B', 'C', 'B', 'Assurer la liaison entre les contenus et usages', 'Dans le cas d\'un long document de type PDF, est-il possible de le télécharger chapitre par chapitre?', 'Les documents volumineux sont rarement intégralement lus par les utilisateurs ou du moins en une seule fois. Afin de réduire é la fois les volumes de données échangées et le volume de stockage sur le périphérique utilisateur, un document long doit pouvoir étre segmenté pour étre chargé en segments plus petits que l\'utilisateur ira chercher s\'il en ressent la nécessité.', 'Déploiement', 'Au delé de quel volume, proposez-vous un chargement segmenté ?', 'Un fichier é télécharger est-il indispensable et la meilleure solution pour l\'utilisateur, par rapport é une version en ligne ?', 'Une fonction de preview, ou de vue dans le browser est-elle disponible ?', 'Opquast 108-109 / CF 115 bonnes pratiques d\'écoconception (BP né107 and 108)', NULL, 'CONSEIL', 'N/A', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Y', '#SCRIPT', '**', 'Outils simulateurs'),
(252, 'ARCHITECTURE', 'ARCH-1', NULL, NULL, NULL, 'Appliquer une démarche qui garantit les aspects NR', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'ODD#12', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(253, 'ARCHITECTURE', 'ARCH-1.01', 'B', 'B', 'A', 'Appliquer une démarche qui garantit les aspects NR', 'Chaque composant peut-il faire l\'objet de tests de \"Non Régression\" au niveau fonctionnel ?', 'La couverture des tests fonctionnels et la qualité gagnée par la prise en compte de tous les cas de tests permet de fiabiliser les développements, d\'éviter des fonctionnements logiciels inefficaces qui consomment des ressources techniques sans rendre la fonctionnalité et générent de multiples mises é jour. A l\'insatisfaction des utilisateurs s\'ajoute la frustration des équipes de développement qui doivent traiter la non-qualité sans pouvoir progresser dans leurs métiers.', 'Réalisation', 'Existence des tests d\'acceptance ? Est-ce que les tests d\'acceptance dans le cadre du developpement logiciel font l\'objet d\'une approche \"test driven development\" ?', 'Existence des cas non passants ?', 'Le DOD impose que tous les tests soient passés avant une livraison du code', NULL, NULL, 'RECO', '**', NULL, NULL, '#PERF ; #VOL ; #SEC ; #USE ; #MAINT ;  #VALUE ; #DEBT ; #METH ; #FIN', 'Dev ; ProjectMngr ; ProductOwner ; ITMngr', NULL, NULL, NULL, 'Medium', 'OnUpdate', 'Intégration continue', NULL, NULL, NULL, NULL, NULL, NULL),
(254, 'ARCHITECTURE', 'ARCH-1.C01', 'B', 'A', 'A', 'Appliquer une démarche qui garantit les aspects NR', 'Une démarche BDD (Behaviour Driven Development)  est-elle mise en oeuvre pour piloter la réalisation par les usages ?', 'Les usages identifiés et entérinés permettent de limiter les réalisations é des cas concrétement attendus par les utilisateurs. La sélection des usages doit toutefois étre conforme aux usages réels, et ne pas étre extrapolée par rapport aux interprétations des équipes techniques.', 'Réalisation', 'Les scénarios BDD sont-ils disponibles pour toutes les fonctionnalités ?', NULL, NULL, NULL, NULL, 'CONSEIL', 'N/A', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(255, 'ARCHITECTURE', 'ARCH-1.C02', 'B', 'A', 'A', 'Appliquer une démarche qui garantit les aspects NR', 'L\'automatisation des téches de compilation, de test, d\'intégration et de déploiement existent-elles dans le workflow d\'intégration continue ?', 'La fiabilisation du processus de production permet de réduire les impacts liés é des re-livraisons suite é des erreurs et permet de livrer des codes qui remplissent leurs fonctions. La consommation de ressources pour un résultat erroné étant considéré comme un gaspillage inutile de ressources.', 'Déploiement', 'Existence d\'un pipeline DevOps ?', 'Les commits de code sont-ils encadrés dans un pipeline d\'intégration continue de sorte é réduire les impacts du coét associé aux livraisons et régressions applicatives ?', 'La validation des tests unitaires est-elle intégrée dans les chaénes de re-livraison de code ?', NULL, NULL, 'CONSEIL', 'N/A', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Y', '#PROCESS', '**', 'CI / CD'),
(256, 'ARCHITECTURE', 'ARCH-2', NULL, NULL, NULL, 'Anticiper l\'exploitation des services', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'ODD#9', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(257, 'ARCHITECTURE', 'ARCH-2.01', 'B', 'C', 'B', 'Anticiper l\'exploitation des services', 'Des sondes existent-elles pour mesurer en production la consommation réelle du code et permettre un dimensionnement au plus juste ?', 'En l\'absence de métriques fiables, le dimensionnement des composants physiques est basé sur des estimations qui peuvent ne pas étre adaptées aux cas d\'usages réels. Ces métriques doivent ensuite étre exploitées suivant 2 principes : ajuster les dimensionnements, et identifier les surconsommations qui doivent systématiquement faire l\'objet d\'une revue pour les traiter.', 'Administration', 'La consommation disque a-t-elle fait l\'objet d\'un dimensionnement et d\'une estimation lors des phases de DAT ?', 'Dans le cas de progiciels l\'utilisation des matrices de dimensionnement éditeur ont-elles été utilisées ?', NULL, NULL, NULL, 'RECO', '**', NULL, NULL, '#PERF ; #VOL ; #USE ; #MAINT ; #VALUE ; #DEBT ; #METH ; #FIN', 'Dev ; ProjectMngr ; ProductOwner ; ITMngr', NULL, NULL, NULL, 'High', 'Periodic', 'Gestion de configuration', NULL, NULL, 'Y', '#SCRIPT', '***', 'Outils parser code'),
(258, 'ARCHITECTURE', 'ARCH-2.02', 'A', 'C', 'B', 'Anticiper l\'exploitation des services', 'Est-ce que l\'hébergement des applications est densifié?', 'L\'adaptation des ressources techniques et leurs utilisations doivent étre suivies par rapport é la somme des ressources virtuelles allouées et consommées par les applicatifs. Certaines technologies (Virtualisation) permettent d\'allouer plusieurs composants virtuels sur le méme composant physique afin d\'augmenter la densité des solutions d\'hébergement.', 'Déploiement', 'Le taux \"d\'over-provisonning\" des VM est-il suivi ?', 'Si une plateforme de conteneurisation est mise en oeuvre, les couches de stockage par overlay sont-elles utilisées? Une réduction du stockage est-elle nécessaire ?', NULL, NULL, NULL, 'RECO', '**', NULL, NULL, '#PERF ; #VOL ;', 'Dev ; ProjectMngr ; ITMngr ;', '(X / Y ) * 100', 'Formalisés = 100 ; prévus = 75 ; Identifiés = 50 ; Non = 0', '100', 'Medium', 'OnUpdate', 'Le dossier technique identifie les besoins de ressources techniques et les solutions d\'optimisation de ces ressources (virtualisation, container, ...)', NULL, NULL, NULL, NULL, NULL, NULL),
(259, 'ARCHITECTURE', 'ARCH-2.03', 'A', 'B', 'A', 'Anticiper l\'exploitation des services', 'Est-ce que les environnements autres que production (DEV, QA, ...) sont éteints ou décommissionnés en dehors des plages d\'usage (la nuit, en dehors des périodes de tests)?', 'En dehors de la production, qui peut avoir une exigence de disponibilité 24/7, les autres environnements (tests, intégration, recette) sont le plus souvent liés é des opérations humaines soumises é des plages horaires restreintes, voire é des périodes limitées. Lorsque les environnements ne sont pas utilisés, ils ne doivent pas étre actifs afin de réduire les consommations énergétiques de ces configurations. Ils doivent étre décommissionnés lorsqu\'aucune utilisation ultérieure n\'est envisagée sur une longue période.', 'Administration', 'Est-ce que l\'infrastructure / l\'application éteint les environnements non sollicités en permanence ?', 'Est-ce que l\'application met en place de l\'autoscaling pour ajuster é la baisse (ou é la hausse) le nombre d\'instances du service (scalabilité horizontale) ?', NULL, 'Lien d\'une étude qui statue que 30% des serveurs sont dans un état \"comateux\" https://www.anthesisgroup.com/wp-content/uploads/2019/11/Case-Study_DataSupports30PercentComatoseEstimate-FINAL_06032015.pdf (Amaury)', NULL, 'RECO', '*', NULL, 'INCONTOURNABLE', '#PERF ; #VOL ; #SEC ; #USE ;', 'Dev ; ProjectMngr ; ITMngr ;', '(X / Y ) * 100', 'Formalisés = 100 ; prévus = 75 ; Identifiés = 50 ; Non = 0', '100', 'Low', 'Periodic', 'Le dossier technique des équipements prend en compte les périodes d\'utilisation et les régles d\'extinction / décommissionnement.', NULL, NULL, 'Y', '#PROCESS', '***', 'Outils supervision'),
(260, 'ARCHITECTURE', 'ARCH-2.04', 'A', 'B', 'A', 'Anticiper l\'exploitation des services', 'Est-ce que les environnements de production non sollicités en dehors des pics de charge sont éteints ou décommissionnés?', 'Les sécurisations de service mettent en oeuvre des solutions de distribution de charge. Les environnements sont dimensionnés par rapport aux pics d\'utilisation estimés, le plus souvent saisonniers. Ces réplications de serveurs lorsqu\'ils restent actifs prennent des charges alors qu\'un environnement plus réduit serait parfaitement en mesure de traiter l\'intégralité du trafic. Plus le nombre d\'instances serveurs est faible, plus l\'impact environnemental est réduit. La remise en ligne d\'un réplicat étant une opération peu complexe et parfaitement maétrisée, il n\'y a aucun bénéfice é maintenir des images de serveurs 11 mois par an, si le pic de charge n\'est vraiment présent que pendant 1 mois.', 'Administration', 'Les seuils d\'extinction/activation sont-ils définis et suivis ?', 'Au bout de quelle période d\'extinction un environnement est-il décommissionné ?', NULL, NULL, NULL, 'RECO', '**', NULL, NULL, '#PERF ; #VOL ; #SEC ; #USE ;', 'Dev ; ProjectMngr ; ITMngr ;', '(X / Y ) * 100', 'Formalisés = 100 ; prévus = 75 ; Identifiés = 50 ; Non = 0', '100', 'High', 'Periodic', 'La gestion de configuration permet de préciser les régles de provisionnemnent / dé-provisionnement en fonction de seuils', NULL, NULL, 'Y', '#PROCESS', '***', 'Outils supervision'),
(261, 'ARCHITECTURE', 'ARCH-2.05', 'B', 'B', 'A', 'Anticiper l\'exploitation des services', 'Les définitions des SLA et QoS pour chacune des fonctions de l\'architecture applicative sont-elles spécifiées en accord avec les usages métiers ?', 'L\'état de l\'art de l\'IT tend é fiabiliser tous les composants de la méme maniére et de porter é l\'excellence dans les domaines de disponibilité et qualité de service. Les besoins métiers pourraient dans certains cas ou pour certaines fonctionnalités se satisfaire de contraintes plus légéres. Lorsque les métiers et les usages ne sont pas consultés, l\'IT se sécurise en outrepassant les attentes plutét que prendre le risque d\'étre mis en cause.', 'Déploiement', 'Existe-t-il différentes familles de SLA pour les éléments de l\'architecture technique ?', 'Est-ce que l\'application aligne ses besoins de SLA par rapport é ses besoins de performance (Actif/Actif, Actif/passif) ? Est-ce que les SLA sont évalués par rapport é l\'impact environnemental ?', NULL, NULL, NULL, 'RECO', '**', NULL, NULL, '#PERF ;  #SEC ;  #VALUE ; #METH ; #FIN ;', 'Dev ; ProjectMngr ; ITMngr ;', '(X / Y ) * 100', 'Formalisés = 100 ; prévus = 75 ; Identifiés = 50 ; Non = 0', '100', 'High', 'Once', 'Projet', NULL, NULL, NULL, NULL, NULL, NULL),
(262, 'ARCHITECTURE', 'ARCH-2.06', 'A', 'B', 'A', 'Anticiper l\'exploitation des services', 'Le service numérique reste-t-il efficace pendant toute sa durée de vie effective ?', 'Les évolutions techniques et les corrections d\'anomalies sont nombreuses au cours de la durée d\'utilisation d\'un service numérique, celui-ci doit pouvoir rester fonctionnel sans nécessiter de nouvelles installations plus gourmandes en ressources.', 'Déploiement', 'Le service numérique garantit-il la mise é disposition de mises é jours correctives pendant toute la durée de vie prévue des équipements et des logiciels liés au service ?', 'Le service numérique propose-t-il d\'installer des mises é jours correctives indépendamment des mises é jours évolutives ?', NULL, NULL, NULL, 'RECO', '**', NULL, NULL, '#PERF ;  #SEC ;  #VALUE ; #METH ; #FIN ;', 'Dev ; ProjectMngr ; ITMngr ;', '(X / Y ) * 100', 'Formalisés = 100 ; prévus = 75 ; Identifiés = 50 ; Non = 0', '100', 'High', 'Once', 'Projet', NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `goodpractices` (`cle`, `Family`, `Id`, `Planet`, `People`, `Prosperity`, `Recommendation`, `Criteria`, `Justification`, `Stage`, `Tests1`, `Tests2`, `Tests3`, `Correspondence`, `Link`, `Type`, `Difficulty`, `Goal`, `Incontournable`, `Tags`, `Actors`, `Indicators_`, `IndicatorsX`, `IndicatorsY`, `Priority`, `Recurrence`, `UseCase`, `Example`, `Limite`, `Automatizable`, `AutomatizationScript`, `AutomatizationLevel`, `AutomatizationInformation`) VALUES
(263, 'ARCHITECTURE', 'ARCH-3', NULL, NULL, NULL, 'Gérer la vie des données', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'ODD#9 ; ODD#12', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(264, 'ARCHITECTURE', 'ARCH-3.01', 'B', 'B', 'B', 'Gérer la vie des données', 'Est ce que les contraintes légales liées é la persistance des données ont été analysées et les régles et délais d\'oubli spécifiés ? La durée de vie des données a-t-elle fait l\'objet d\'une étude approfondie ? Les durées légales ont-elle été analysées ?', 'La réglementation impose de maétriser le cycle de vie des données pour certaines catégories (données personnelles encadrées par le RGPD), les autres catégories de données ne sont pas prises en compte, ce qui conduit é accumuler les données sans prendre en compte les délais de péremption, ce qui introduit un accroissement permanent de la volumétrie de données, des volumes de sauvegardes, de ressources consommées par les accés aux données. Au fil du temps, le niveau de précision des données perd de son importance ce qui doit conduire é réduire la volumétrie dans la phase précédant le retrait pur et simple.', 'Fin de Vie', 'Existe-t-il des mécanismes de gestion du cycle de vie des données (expiration) ? Des mécanismes d\'historisation ont-ils été mis en place pour les données avec un faible taux de manipulation ? Une fin de vie de la donnée a-t-elle été prévue (mise en place d\'une date de validité de chaque donnée, mise en place d\'une date de validité par type de donnée technique, mise en place d\'une date de validation par type de donnée fonctionnelle) ? Les utilisateurs ont-ils été concertés pour étudier la durée de vie nécessaire des données principales ?', 'Les contraintes de durée de stockage liées au RGPD ont-elles été prises en compte ? Un protocole d\'effacement automatique a-t-il été mis en place ?', NULL, NULL, NULL, 'RECO', '*', NULL, NULL, '#VOL ; #SEC ; #METH ; #LEG', 'Dev ; ProjectMngr ; ITMngr ;', '(X / Y ) * 100', 'Formalisés = 100 ; prévus = 75 ; Identifiés = 50 ; Non = 0', '100', 'High', 'Periodic', 'Analyse de risques', NULL, NULL, NULL, NULL, NULL, NULL),
(265, 'ARCHITECTURE', 'ARCH-3.02', 'A', 'B', 'A', 'Gérer la vie des données', 'Est-ce que la stratégie de stockage minimise les infrastructures ou la duplication des données par rapport é leur criticité ?', 'Le volume occupé par une donnée est impactant pour la consommation énergétique du support de stockage, par l\'espace occupé sur les supports physiques qui sont ajoutés au fur et é mesure de leurs remplissages, par la consommation de ressources de manipulation des données, par les jeux de sauvegardes, par les éléments de sécurisations et duplications. Tous les leviers activés sur l\'un de ces aspects réduisent l\'impact environnemental des données indispensables au service.', 'Déploiement', 'Est-ce que les infrastructures minimisent les volumes disques nécessaires (ex: déduplication, compression, ...) ?', 'Est-ce que le taux de réplication des données est en adéquation avec le besoin de résilience et les risques?', 'Est-ce que les sauvegardes utilisent principalement des modes par différentiel (incrémentaux, ...) ?', NULL, NULL, 'RECO', '**', NULL, NULL, '#PERF ; #VOL ; #SEC ; #VALUE ; #FIN', 'Dev ; ProjectMngr ; ITMngr ;', '(X / Y ) * 100', 'Formalisés = 100 ; prévus = 75 ; Identifiés = 50 ; Non = 0', '100', 'High', 'Once', 'Le dossier technique détermine les sensibilités des données et le mode de redondance é appliquer pour assurer la disponibilité requise', NULL, NULL, NULL, NULL, NULL, NULL),
(266, 'ARCHITECTURE', 'ARCH-3.03', 'C', 'A', 'B', 'Gérer la vie des données', 'Les données hors production sont-elles anonymisées et la volumétrie réduite pour obtenir un échantillon représentatif de la production ?', 'La précision et concision des données du jeu de test permet de réduire les impacts des phases de développement, test  / Q&A avec une volumétrie réduite. Et d\'autre part de valider la définition et la couverture des cas de test ce qui participe é la qualité finale du logiciel. Les données de test sont visibles, manipulées hors des contraintes de sécurité de la production, elles sont donc moins bien protégées et ne doivent en aucun étre une source de fuite d\'information qui pourrait porter préjudice aux utilisateurs réels du service.', 'Déploiement', 'Les données me permettant de tester et de développer chacune de mes fonctionnalités ont-elles été identifiées ?', 'La fréquence de réplication des données est-elle adaptée aux besoins ?', NULL, NULL, NULL, 'RECO', '***', NULL, NULL, '#PERF ; #VOL ; #SEC ; #VALUE ; #FIN', 'Dev ; ProjectMngr ; ITMngr ;', '(X / Y ) * 100', 'Formalisés = 100 ; prévus = 75 ; Identifiés = 50 ; Non = 0', '100', 'Low', 'Periodic', 'Dossier technique', NULL, NULL, NULL, NULL, NULL, NULL),
(267, 'ARCHITECTURE', 'ARCH-3.C01', 'B', 'B', 'B', 'Gérer la vie des données', 'La fréquence d\'actualisation des données est-elle déterminée par rapport aux attentes des utilisateurs ?', 'La mise é jour de données complexes en temps réel met en oeuvre des processus lourds, ces opérations doivent étre adaptées aux besoins des utilisateurs. Si elles peuvent étre déportées é des moments de charge plus réduite, la consommation de ressources techniques sera réduite.', 'Utilisation', 'Quel est le niveau minimum de rafraichissement nécessaire par mon systéme ?', 'Une désactivation est-elle prévue en cas de non utilisation du service ?', NULL, NULL, NULL, 'CONSEIL', 'N/A', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(268, 'ARCHITECTURE', 'ARCH-3.C02', 'B', 'A', 'A', 'Gérer la vie des données', 'Les responsabilités du point de vue RGPD (ou cadre local dans d\'autres pays : privacy Act par exemple) avec les prestataires sont-elles clairement définies ?', 'La réglementation impose une connaissance affirmée de tous les prestataires en contact avec des données personnelles. Les données hors du cadre RGPD ne sont pas soumises é ce traéage. Les données indispensables au fonctionnement d\'un service numérique ont toutes une importance du point de vue humain ou business. La pratique RGPD fait ses preuves pour une catégorie de données, la généralisation du principe é toutes les données permet d\'augmenter la cohérence de traitement de tout le patrimoine d\'une organisation', 'Utilisation', 'La liste des prestataires et les modes d\'interventions et de responsabilités sur les données est-elle formalisée et suivie ?', NULL, NULL, NULL, NULL, 'CONSEIL', 'N/A', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(269, 'ARCHITECTURE', 'ARCH-4', NULL, NULL, NULL, 'Contréler et mesurer les impacts', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'ODD#12', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(270, 'ARCHITECTURE', 'ARCH-4.01', 'A', 'B', 'A', 'Contréler et mesurer les impacts', 'Est ce qu\'un modéle mettant en relation les ressources CPU/RAM/disques et les indicateurs physiques (consommation électrique, eau, ...) est utilisé ?', 'Chaque élément physique génére un impact, qu\'il soit énergétique ou sur la consommation de ressources naturelles. Certains indicateurs sont déjé établis et doivent servir de base pour l\'évaluation d\'impact.', 'Administration', 'Est ce que ces indicateurs sont communiqués et compris (formation) ?', 'Est-ce que les indicateurs sont revus/validés par des instances responsables?', NULL, NULL, NULL, 'RECO', '***', NULL, NULL, '#PERF ; #VOL ; #SEC ;  #COM ; #VALUE ;  #METH ; #GOV', 'ProjectMngr ; ProductOwner ; Manager ; RSE/NR ; HR ; ITMngr ; Staff', '(X / Y ) * 100', 'Formalisés = 100 ; prévus = 75 ; Identifiés = 50 ; Non = 0', '100', 'High', 'Periodic', 'Le suivi de production met en oeuvre des indicateurs d\'utilisation des ressources techniques', NULL, NULL, NULL, NULL, NULL, NULL),
(271, 'ARCHITECTURE', 'ARCH-4.C01', 'A', 'B', 'A', 'Contréler et mesurer les impacts', 'Est ce que l\'ensemble des composants techniques sont évalués selon les critéres eco-responsables?', '?', 'Acquisition', 'Est-ce que chaque offre est evaluée selon des critéres (cf modéle précédent)', 'Le catalogue de service est diffusé et validé/reconnu', NULL, NULL, NULL, 'CONSEIL', 'N/A', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(272, 'ARCHITECTURE', 'ARCH-4.02', 'A', 'B', 'A', 'Contréler et mesurer les impacts', 'Des mesures d\'impact des services numériques ont-elle été mises en place sur le service ?', 'Chaque élément physique ou logiciel génére un impact, qu\'il soit énergétique ou sur la consommation de ressources naturelles. Certains indicateurs sont déjé établis et doivent servir de base pour l\'évaluation d\'impact.', 'Administration', 'Quels sont les indicateurs analysés ?', 'Quelle est la fréquence de mise é jour des indicateurs dans le processus de création et de maintenance du service ?', NULL, NULL, NULL, 'RECO', '***', NULL, NULL, '#PERF ; #VOL ; #SEC ;  #COM ; #VALUE ;  #METH ; #GOV', 'ProjectMngr ; ProductOwner ; Manager ; RSE/NR ; HR ; ITMngr ; Staff', '(X / Y ) * 100', 'Formalisés = 100 ; prévus = 75 ; Identifiés = 50 ; Non = 0', '100', 'Medium', 'Periodic', 'L\'analyse d\'impact détermne les actions de réduction des impacts avec les acteurs concernés', NULL, NULL, NULL, NULL, NULL, NULL),
(273, 'ARCHITECTURE', 'ARCH-4.C02', 'A', 'C', 'B', 'Contréler et mesurer les impacts', 'Quelles données sont consolidées pour établir la performance environnementale ?', 'La fiabilité et la pertinence des indicateurs reposent sur la validité des données d\'entrées. Il est important de s\'assurer de cette validité avant de se lancer dans l\'exploitation de données de performance environnementale.', 'Administration', 'Les sources de données ont-elles été validées ?', NULL, NULL, 'CF les données du GT AIR sur les KPI', NULL, 'CONSEIL', 'N/A', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(274, 'ARCHITECTURE', 'ARCH-5', NULL, NULL, NULL, 'Limiter les volumétries dans les échanges', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'ODD#10', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(275, 'ARCHITECTURE', 'ARCH-5.01', 'B', 'B', 'B', 'Limiter les volumétries dans les échanges', 'Quelle est l\'efficience de mon protocole d\'échange entre le contenu et le contenant ?', 'La distribution des traitements sur des hardwares différents et distants impose des mécanismes de transfert qui peuvent étre é la fois fréquents et volumineux. Les gains environnementaux sur ces points peuvent étre traités en réduisant le volume de données échangées. Les compressions de données requiérent des ressources de calcul qui peuvent rendre inefficaces ou pénalisantes ces opérations.', 'Acquisition', 'Les données sont-elles compressées avant de transiter sur le réseau ?', NULL, NULL, NULL, NULL, 'RECO', '*', NULL, NULL, '#PERF ; #VOL ; #SEC ; #USE ; #MAINT', 'Dev ; ProjectMngr ; ProductOwner ; ITMngr', NULL, NULL, NULL, 'High', 'OnUpdate', 'Audit de performance', NULL, NULL, NULL, NULL, NULL, NULL),
(276, 'ARCHITECTURE', 'ARCH-5.C01', 'B', 'B', 'B', 'Limiter les volumétries dans les échanges', 'Les protocoles de communication mis en oeuvre sont-ils évalués par rapport au ratio entétes/données utiles ?', 'Les gains environnementaux sur les architectures distribuées peuvent étre traités en ayant un rapport efficace entre les données d\'encapsulation du protocole d\'échange et la \"charge utile\" : payload. Les encapsulations des protocoles requiérent des ressources de calcul qui peuvent introduire un surcoét important.', 'Acquisition', 'Existe-t-il un protocole d\'échange moins verbeux ?', NULL, NULL, NULL, NULL, 'CONSEIL', 'N/A', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(277, 'ARCHITECTURE', 'ARCH-5.02', 'C', 'B', 'B', 'Limiter les volumétries dans les échanges', 'Le protocole de communication choisi est-il situé dans la phase ascendante, stable ou descendante de son utilisation par rapport é des contraintes de performance, de sécurité, d\'obligations légales ?', 'Les avancées technologiques prennent de plus en plus en compte les aspects NR. Plus le projet technique fournissant les protocoles de communication est avancé plus il y aura de chance que les aspects environnementaux soient intégrés nativement.', 'Acquisition', 'Le protocole d\'échange est-il propriétaire ou Open Source ?', 'Ce protocole est-il voué é étre remplacé ou devenir obsoléte é court terme ?', NULL, NULL, NULL, 'RECO', '**', NULL, NULL, '#PERF ; #VOL ; #SEC ; #USE ; #MAINT  ; #DEBT ; #FIN', 'Dev ; ProjectMngr ; ProductOwner ; ITMngr', NULL, NULL, NULL, 'Medium', 'OnUpdate', 'Analyse de risque', NULL, NULL, NULL, NULL, NULL, NULL),
(278, 'ARCHITECTURE', 'ARCH-5.C02', 'A', 'C', 'B', 'Limiter les volumétries dans les échanges', 'Les alternatives réseau sont-elles évaluées par rapport é  la consommation d\'énergie ?', 'Le rapport ARCEP (Octobre 2019 - L\'empreinte carbone du numérique) classe les consommations de GES par types de réseaux. Les réseaux filaires ou fibre sont au moins 3 fois plus économes en énergie que les réseaux sans fils. La 4G étant l\'un des consommateur le plus énergivore. Sur les réseaux sans fils, la consommation est trés étroitement liée é la volumétrie des données, ce qui est moins vrai sur les réseaux filaires. D\'un autre point de vue les composants électroniques du \"sans fils\" sont moins robustes et peuvent entraéner plus de besoins de renouvellement. Cces composants étant intégrés le plus souvent sur les cartes méres des périphériques, la panne nécessite le plus souvent le remplacement de la totalité de l\'appareil.', 'Réalisation', 'Le réseau utilisé majoritairement est-il le moins énergivore, parmi les solutions existantes pour ne pas avoir é ajouter de nouvelles infrastructures  ?', NULL, NULL, NULL, NULL, 'CONSEIL', 'N/A', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(279, 'ARCHITECTURE', 'ARCH-5.C03', 'B', 'A', 'A', 'Limiter les volumétries dans les échanges', 'La capacité de mon systéme é fonctionner malgré les difficultés de connexion est-il spécifié et accepté par les métiers?', 'L\'inégalité d\'accés aux réseaux est un facteur important qui écarte une partie de la population de l\'usage des services numériques.', 'Réalisation', 'Un mode hors ligne a-t-il été prévu ?', 'Le confort d\'utilisation est-il fortement dégradé (temps de chargement, informations actualisées...) sans une bonne couverture réseau ?', NULL, NULL, NULL, 'CONSEIL', 'N/A', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(280, 'ARCHITECTURE', 'ARCH-5.C04', 'B', 'B', 'B', 'Limiter les volumétries dans les échanges', 'Un systéme de communication asynchrone existe t-il ?', 'L\'introduction de délais de traitement, notamment dans le cas de couverture réseau de faible qualité, écarte certains utilisateurs.', 'Réalisation', 'Les échanges qui ne nécessitent pas de réponse immédiate sont-ils bien effectués de maniére asynchrone ?', NULL, NULL, NULL, NULL, 'CONSEIL', 'N/A', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(281, 'ARCHITECTURE', 'ARCH-6', NULL, NULL, NULL, 'Définir un modéle de production évolutif', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'ODD#10 ; ODD#12', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(282, 'ARCHITECTURE', 'ARCH-6.01', 'B', 'B', 'A', 'Limiter les volumétries dans les échanges', 'L\'architecture est-elle modulaire ? Permet-elle de recycler des composants déjé développés pour d\'autres projets (authentification, référentiels de données, API, etc.) ?', 'Les efforts d\'adaptation aux contraintes environnementales réalisés et stabilisés doivent pouvoir bénéficier é l\'ensemble des projets de l\'organisation. Capitaliser sur les meilleures pratiques déployées est un gage d\'efficacité environnementale et opérationnelle. La concept modulaire permet aussi de propager rapidement tous les bénéfices environnementaux qui pourraient étre intégrés dans un composant, dans le cadre du suivi de l\'état de l\'art et des performances environnementales.', 'Revalorisation', 'Les fonctionnalités de l\'application sont-elles découpées en briques autonomes avec une standardisation des entrées/sorties permettant leur remplacement individuel ?', 'Les fonctionnalités sont-elles découpées en briques autonomes avec une standardisation des entrées/sorties permettant leur réutilisation dans d\'autres contextes ?', 'Un référentiel des composants logiciels partagés existe-t-il dans l\'entreprise ?', NULL, NULL, 'RECO', '**', NULL, NULL, '#SEC ; #USE ; #MAINT ; #DEBT ; #METH ;', 'Dev ; ProjectMngr ; ITMngr ;', '(X / Y ) * 100', 'Formalisés = 100 ; prévus = 75 ; Identifiés = 50 ; Non = 0', '100', 'High', 'OnUpdate', 'Projet / DEV', NULL, NULL, NULL, NULL, NULL, NULL),
(283, 'ARCHITECTURE', 'ARCH-6.02', 'B', 'B', 'A', 'Limiter les volumétries dans les échanges', 'L\'architecture est-elle granulaire  et les interfaces sont-elles cohérentes pour respecter l\'indépendance de chaque entité ?', 'La capacité de réaliser des assemblages permettant de couvrir l\'ensemble des fonctionnalités en utilisant des éléments qui ont déjé fait l\'objet d\'adaptations pour réduire les impacts du point de vue NR est une solution efficace.', 'Revalorisation', 'Les fonctionnalités de l\'application ont-elles fait l\'objet d\'une classification permettant de définir l\'essentialité de certaines fonctionnalités au regard d\'autres fonctionnalités tout aussi importantes mais non considérées comme vitales au systéme ?', 'Est-ce que 20% des fonctionnalités utilisées par 80% des utilisateurs sont intégrées au é noyau é?', 'Est-ce que les 80% des fonctionnalités restantes utilisées par seulement 20% des utilisateurs sont disponibles sous la forme déextensions ?', NULL, NULL, 'RECO', '**', NULL, NULL, '#SEC ; #USE ; #MAINT ; #DEBT ; #METH ;', 'Dev ; ProjectMngr ; ITMngr ;', '(X / Y ) * 100', 'Formalisés = 100 ; prévus = 75 ; Identifiés = 50 ; Non = 0', '100', 'Medium', 'OnUpdate', 'Projet / DEV', NULL, NULL, NULL, NULL, NULL, NULL),
(284, 'ARCHITECTURE', 'ARCH-6.C01', 'B', 'A', 'A', 'Limiter les volumétries dans les échanges', 'La capacité de mon réseau é s\'adapter dans le temps fait-elle l\'objet d\'une étude spécifique ?', 'Les avancées techniques et les progrés en matiére de consommation énergétique des réseaux doivent pouvoir étre rapidement implémentées sans générer de refonte lourde du design logiciel du service.', 'Administration', 'Mon architecture permet-elle de remplacer la technologie d\'une couche par une autre ?', NULL, NULL, NULL, NULL, 'CONSEIL', 'N/A', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(285, 'ARCHITECTURE', 'ARCH-6.03', 'A', 'B', 'B', 'Limiter les volumétries dans les échanges', 'Un cache est-il mis en place entre les différents composants du SI pour limiter les échanges sur le réseau, lorsque la nature des données le permet ?', 'La réduction des volumes d\'échanges, des opérations de calculs, traitements, et accés aux données est un axe d\'optimisation des consommations de ressources. La mise en place de systémes de cache permet de réduire les coéts des opérations répétitives. Les mécanismes de cache peuvent étre déployés é différents points de passage dans un service. Plus le cache est proche du demandeur qu\'il doit servir, plus le volume de données en transit et la longueur du chemin emprunté par les données sont faibles. Plus le cache est proche de la source de données, plus il peut étre mutualisé par un nombre important d\'utilisateurs mais les données seront tout de méme échangées, seules les opérations de traitement et collecte seront réduites.', 'Utilisation', 'Les données de traduction des libellés sont-elles mises en cache localement ?', NULL, NULL, NULL, NULL, 'RECO', '**', NULL, NULL, '#PERF ; #VOL ; #USE ;', 'Dev ; ProjectMngr ; ITMngr ;', '(X / Y ) * 100', 'Formalisés = 100 ; prévus = 75 ; Identifiés = 50 ; Non = 0', '100', 'High', 'OnUpdate', 'Le dossier technique identifie les caches et la stratégie de cache utilisée pour chaque type de flux. Les outils de gestions des caches (purge / désactivation) sont utilisés pour valider les performances effectives (ex: CHROME : console /network enable - disable cache)', NULL, NULL, 'Y', '#SCRIPT', '***', 'Outils parser code'),
(286, 'ARCHITECTURE', 'ARCH-6.C02', 'A', 'A', 'A', 'Limiter les volumétries dans les échanges', 'Le choix des fournisseurs prend-il en compte les aspects Numérique Responsable et RSE au méme titre que les données de solvabilité ?', 'La qualification des fournisseurs s\'appuie sur des régles de décisions qui permettent de statuer sur la pérennité de la relation. Parmi ces régles doivent figurer des éléments liés au NR. Ces aspects NR doivent avoir un impact important dans l\'évaluation de la solvabilité du fournisseur.', 'Acquisition', 'Quels sont les indicateurs demandés et les seuils d\'acceptabilité ?', NULL, NULL, NULL, NULL, 'CONSEIL', 'N/A', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(287, 'ARCHITECTURE', 'ARCH-7', NULL, NULL, NULL, 'Associer les données, les flux, les applicatifs et la sécurité pour permettre leur identification et leur traéabilité', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'ODD#9 ; ODD#12', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(288, 'ARCHITECTURE', 'ARCH-7.C01', 'A', 'B', 'B', 'Associer les données, les flux, les applicatifs et la sécurité pour permettre leur identification et leur traéabilité', 'La frugalité des données a-t-elle été intégrée ?', 'La réduction de la volumétrie, notamment des données, réduit l\'impact du point de vue NR', 'Conception', 'Quel est le niveau de respect des NF et principalement la 2NF ?', NULL, NULL, NULL, NULL, 'CONSEIL', 'N/A', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(289, 'ARCHITECTURE', 'ARCH-7.01', 'A', 'B', 'B', 'Associer les données, les flux, les applicatifs et la sécurité pour permettre leur identification et leur traéabilité', 'Est-ce que l\'application minimise les données devant étre échangées avec d\'autres applications ou les utilisateurs avec l\'utilisation d\'une matrice de flux ?', 'Dans les usages de services numériques les échanges de données sont é la fois indispensables mais générent une consommation importante de ressources. La réduction des besoins d\'échanges au strict minimum diminue l\'impact de ces échanges. Seule la vue globale des flux avec leurs sources, destinations et objectifs permet de qualifier la nécessité et d\'accepter l\'impact des consommations qu\'ils générent.', 'Conception', 'Est-ce que l\'application nécessite d\'échanger des données avec d\'autres applications, consommateurs, producteurs qu\'elle ne peut pas stocker en local ?', 'Est-ce que l\'application effectue en permanence des requétes réseau de petites tailles ? Est-ce que ces échanges peuvent étre optimisés (éviter les aller-retours) ?', 'Est-ce que l\'application utilise des caches applicatifs ?', NULL, NULL, 'RECO', '**', NULL, NULL, '#PERF ; #VOL ; #SEC ; #USE ;', 'Dev ; ProjectMngr ; ITMngr ;', '(X / Y ) * 100', 'Formalisés = 100 ; prévus = 75 ; Identifiés = 50 ; Non = 0', '100', 'High', 'OnUpdate', 'Une matrice de flux est formalisée', NULL, NULL, NULL, NULL, NULL, NULL),
(290, 'ARCHITECTURE', 'ARCH-7.02', 'B', 'B', 'B', 'Associer les données, les flux, les applicatifs et la sécurité pour permettre leur identification et leur traéabilité', 'Chaque composant déployé est-il qualifié du point de vue de sa durée de vie, et les procédures de déprovisonnement sont-elles systématiquement exprimées ?', 'Toutes les ressources d\'un composant doivent étre gérées et une durée de vie, un processus de retrait doit étre mis en place. Ces éléments doivent aussi prendre en compte les traitements récurrents (batch, CRON, ..), les données et les sauvegardes. Le maintien ou l\'oubli sur des systémes en production de composants inactifs est é la fois un gaspillage de ressources, mais aussi une fragilité dans la sécurité.', 'Fin de Vie', 'Quelle durée de vie est associée é chaque composant ?', 'Quelle est la fréquence de revue des données expirées ?', NULL, NULL, NULL, 'RECO', '*', NULL, 'INCONTOURNABLE', '#PERF ; #VOL ; #SEC ; #USE ; #MAINT ; #DEBT ; #METH ;', 'Dev ; ProjectMngr ; ITMngr ;', '(X / Y ) * 100', 'Formalisés = 100 ; prévus = 75 ; Identifiés = 50 ; Non = 0', '100', 'Medium', 'Periodic', 'Chaque élément référencé dans le Dossier Technique porte une information de fin de vie', NULL, NULL, NULL, NULL, NULL, NULL),
(291, 'ARCHITECTURE', 'ARCH-7.C02', 'B', 'B', 'A', 'Associer les données, les flux, les applicatifs et la sécurité pour permettre leur identification et leur traéabilité', 'Les aspects sécurité, accessibilité, et Numérique Responsable sont-ils traités de la méme maniére pour chaque élément de l\'architecture ?', 'Les organisations sont structurées pour que la phase d\'architecture intégre trés tét dans la démarche les aspects de sécurité. La sécurité doit étre adressée pour tous éléments de l\'architecture. La sécurité n\'étant pas le seul domaine qui s\'applique de maniére transversale, elle doit étre associée avec les autres sujets : accessibilité et NR. Chaque élément de l\'architecture doit étre traité avec la méme excellence pour la sécurité, l\'accessibilité et le NR.', 'Conception', 'Les processus et canevas d\'architecture intégrent-ils systématiquement les aspects sécurité, accessibilité, et Numérique Responsable ?', NULL, NULL, NULL, NULL, 'CONSEIL', 'N/A', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(292, 'ARCHITECTURE', 'ARCH-7.03', 'C', 'A', 'A', 'Associer les données, les flux, les applicatifs et la sécurité pour permettre leur identification et leur traéabilité', 'La traéabilité des exigences de sécurité est-elle garantie ?', 'La sécurité des systémes d\'information impacte l\'activité de l\'organisation, le respect et la confiance des utilisateurs. La sécurité n\'est pas un acquis, elle doit étre remise en cause périodiquement et les procédures de suivi, validation doivent étre tracées.', 'Réalisation', 'Quels sont les indicateurs de suivis de sécurité définis et sont-ils basés sur des données fiables et éprouvées ?', NULL, NULL, NULL, 'HAL Id: hal-00549136', 'RECO', '***', NULL, NULL, '#SEC ;  #MAINT ; #METH', 'Dev ; ProjectMngr ; ITMngr ;', '(X / Y ) * 100', 'Formalisés = 100 ; prévus = 75 ; Identifiés = 50 ; Non = 0', '100', 'Low', 'OnUpdate', 'Backlog', NULL, NULL, NULL, NULL, NULL, NULL),
(293, 'ARCHITECTURE', 'ARCH-8', NULL, NULL, NULL, 'Anticiper les impacts sur la production', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'ODD#10 ; ODD#12 ; ODD#13', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(294, 'ARCHITECTURE', 'ARCH-8.C01', 'B', 'A', 'B', 'Anticiper les impacts sur la production', 'L\'utilisation du service exclut-elle certaines configurations logicielles ou matérielles ?', 'Lorsque la conception d\'un service augmente les exigences techniques des environnements sur lesquels il est apte é fonctionner, que ce soit cété serveur ou périphériques utilisateurs, cela entraéne au mieux un nouveau dimensionnement de ces composants matériels, au pire la nécessité de les remplacer par une gamme plus puissante. Dans tous les cas, cela génére un impact environnemental fort sans parler du fait que cela écarte certains utilisateurs qui ne disposent pas des matériels.', 'Utilisation', 'La nouvelle version du service ou produit nécessite t-elle de nouveaux composants externes ?', 'La nouvelle version du service rend-elle obsoléte certaines configurations matérielles ?', NULL, NULL, NULL, 'CONSEIL', 'N/A', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(295, 'ARCHITECTURE', 'ARCH-8.C02', 'A', 'B', 'B', 'Anticiper les impacts sur la production', 'L\'utilisation du service permet-elle d\'envisager des configurations logicielles ou matérielles moins impactantes pour l\'environnement ?', 'L\'escalade technologique au détriment de l\'environnement n\'est pas soutenable. La phase d\'architecture doit prendre en considération la maniére de remplir les fonctionnalités en limitant les impacts de la technologie.', 'Déploiement', 'Quelles solutions low-tech sont envisagées ?', NULL, NULL, NULL, NULL, 'CONSEIL', 'N/A', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(296, 'ARCHITECTURE', 'ARCH-8.01', 'A', 'B', 'B', 'Anticiper les impacts sur la production', 'La valeur ajoutée du framework justifie-t-elle l\'alourdissement de l\'applicatif ? D\'autres librairies ou framework plus légers et correspondant au besoin existent-ils ?', 'Les alternatives entre des solutions développées en interne et l\'utilisation de frameworks doivent prendre en considération les aspects Numérique Responsable. Les frameworks fournissent plus de fonctionnalités que nécessaire pour le projet et intégrent des composants et des dépendances pour remplir la totalité des fonctionnalités offertes. Le rapport entre les fonctionnalités apportées par le framework et celles réellement recherchées est critique. Le choix du framework doit aussi permettre de restreindre son périmétre fonctionnel aux seuls éléments indispensables pour le service.', 'Acquisition', 'L\'utilisation d\'un framework est-elle indispensable pour implémenter les fonctionnalités demandées ?', 'D\'autres framework et/ou librairies plus légers sont-ils envisagés ?', 'Seuls les modules réellement utilisés du framework sont-ils embarqués dans l\'application, et non le framework au complet ?', NULL, NULL, 'RECO', '***', NULL, NULL, '#PERF ; #VOL ; #SEC ; #USE ; #MAINT ; #DEBT ; #METH ;', 'Dev ; ProjectMngr ; ITMngr ;', '(X / Y ) * 100', 'Formalisés = 100 ; prévus = 75 ; Identifiés = 50 ; Non = 0', '100', 'High', 'OnUpdate', 'Le dossier technique analyse les impacts des choix de framework ou bibliothéques externes, ainsi que les capacités d\'évolution de ces éléments vis é vis de leurs impacts environnementaux', NULL, NULL, NULL, NULL, NULL, NULL),
(297, 'ARCHITECTURE', 'ARCH-8.02', 'B', 'B', 'B', 'Anticiper les impacts sur la production', 'La fréquence d\'utilisation de la fonctionnalité l\'oblige-t-elle é étre disponible en permanence ou peut-elle étre mise é jour en arriére-plan (batch) ?', 'Surpasser les attentes peut conduire é proposer des solutions proches du temps réel, en fournissant au plus vite et le plus souvent possible le résultat d\'une opération. Les utilisateurs ou les clients, au sens technique, n\'ont que trés rarement besoin de cette solution, voire sont submergés par des informations plus rapidement qu\'ils ne peuvent ou souhaitent les assimiler. Réduire le nombre d\'opérations instantanées et gérer les temps entre ces opérations en les traitant en arriére plan réduit les charges du service.', 'Déploiement', 'Les opérations récurrentes et prévisibles sont-elles implémentées sous forme de batch plutét que de service web ?', 'Les opérations de maintenance sont-elles bien disponibles sous forme de batch plutét qu\'exposées en continu ?', NULL, NULL, NULL, 'RECO', '***', NULL, NULL, '#PERF ; #VOL ; #SEC ; #USE ; #VALUE ; #METH ;', 'Dev ; ProjectMngr ; ITMngr ;', '(X / Y ) * 100', 'Formalisés = 100 ; prévus = 75 ; Identifiés = 50 ; Non = 0', '100', 'Low', 'OnUpdate', 'Dossier technique', NULL, NULL, NULL, NULL, NULL, NULL),
(298, 'ARCHITECTURE', 'ARCH-8.C03', 'B', 'A', 'A', 'Anticiper les impacts sur la production', 'L\'application est-elle rapide é se lancer / é s\'arréter ?', 'La performance technique est souvent un indicateur de performance environnementale. Plus un service est léger (volume d\'échange, traitements) plus il se lance rapidement et moins il consomme de ressources.', 'Utilisation', 'Un suivi du cold start time est-il effectué au fur et é mesure des itérations de développement ?', NULL, NULL, NULL, NULL, 'CONSEIL', 'N/A', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Y', '#SCRIPT', '*', 'Outils simulateurs'),
(299, 'ARCHITECTURE', 'ARCH-8.03', 'A', 'A', 'A', 'Anticiper les impacts sur la production', 'Est-ce qu\'une optimisation des algorithmes et des traitements est réalisée dans l\'application ?', 'Certains traitements sont systématiquement déclenchés, méme si unitairement ils ne sont pas trés impactants, leur nombre d\'occurrence va impacter l\'empreinte globale. En travaillant au niveau de l\'algorithme de chaque traitement, cette optimisation est possible.', 'Réalisation', 'Est-ce qu\'un audit de performances est réalisé au sein de l\'application/composant/microservice avec identification des bottleneck, ressources utilisées, ... ?', 'Est ce qu\'une optimisation des algorithmes est faite en fonction de l\'audit?', 'Est ce qu\'une recette des optimisations est faite, calculer si possible le différentiel en temps/ressources consommées ?', NULL, NULL, 'RECO', '***', NULL, NULL, '#PERF ; #METH ;', 'Dev ; ProjectMngr ; ITMngr ;', '(X / Y ) * 100', 'Formalisés = 100 ; prévus = 75 ; Identifiés = 50 ; Non = 0', '100', 'High', 'OnUpdate', 'Un audit de performance est systématiquement mis en oeuvre lors des déploiements en production', NULL, NULL, NULL, NULL, NULL, NULL),
(300, 'ARCHITECTURE', 'ARCH-8.04', 'B', 'B', 'A', 'Anticiper les impacts sur la production', 'La capacité d\'évolution / modification de l\'application est-elle étudiée ?', 'Le Numérique Responsable est une démarche d\'amélioration continue qui doit permettre de faire progresser la réduction des impacts.', 'Maintenance', 'Est-ce que l\'application est en mode microservice?', 'Est-ce que l\'application est livrable de maniére automatisée? CI/CD', 'Est-ce que l\'application bénéficie de tests automatiques, tests fonctionnels mais aussi tests éco-conception, tests de performances, ... ?', NULL, NULL, 'RECO', '*', NULL, NULL, '#PERF ; #VOL ; #SEC ; #USE ; #MAINT ; #DEBT ; #METH ;', 'Dev ; ProjectMngr ; ITMngr ;', '(X / Y ) * 100', 'Formalisés = 100 ; prévus = 75 ; Identifiés = 50 ; Non = 0', '100', 'Low', 'OnUpdate', 'Backlog', NULL, NULL, NULL, NULL, NULL, NULL),
(301, 'ARCHITECTURE', 'ARCH-8.C04', 'B', 'A', 'A', 'Anticiper les impacts sur la production', 'Un langage compilé est-il utilisé pour des aspects performance et/ou sécurité ?', 'Le type de langage utilisé introduit soit une proximité vers les processeurs (CPU), c\'est le cas des langages compilés, soit vers les hommes, c\'est le cas des langages interprétés (PHP, JavaScript, ...). La proximité des ordres machine augmente la performance technique mais rend la production logicielle plus lourde (phases de compilation, édition de liens). La proximité \"humaine\" facilite les développements mais aussi les malversations (piratages) et demande aux systémes un effort supplémentaire pour traduire \"é la demande\" les lignes de programme en code machine, avec l\'aide d\'un interpréteur.', 'Réalisation', 'Le code fait-il l\'objet d\'optimisation avant compilation, ou é défaut interprétation ?', NULL, NULL, NULL, NULL, 'CONSEIL', 'N/A', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Y', '#SCRIPT', '*', 'Outils parser code'),
(302, 'ARCHITECTURE', 'ARCH-8.C05', 'B', 'A', 'B', 'Anticiper les impacts sur la production', 'Les éléments de référence sont-ils connus, tenus é jour, et mis é disposition de l\'ensemble de l\'équipe projet ?', 'L\'utilisation de références stables est indispensable pour conserver une démarche cohérente', 'Maintenance', 'Comment est évalué le degré de conformité ?', NULL, NULL, NULL, NULL, 'CONSEIL', 'N/A', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(303, 'ARCHITECTURE', 'ARCH-8.C06', 'C', 'B', 'B', 'Anticiper les impacts sur la production', 'L\'état des lieux des données d\'entrée est-il mis é disposition afin d\'étre réutilisé dans d\'autres projets?', 'Les principes NR sont stables et les bonnes pratiques déployées sont directement utilisables dans d\'autres contextes projet, d\'autant plus que les projets au sein d\'une organisation ont beaucoup de similarités que ce soit dans les méthodes, les objectifs, les périmétres fonctionnels. Dans la phase de spécification, collecter, préparer et produire ces éléments est un gage d\'efficacité et de fiabilité pour augmenter la maturité de l\'organisation dans ses pratiques NR.', 'Revalorisation', 'Quelles sont les données utilisées en entrée et produite pour les autres projets ?', NULL, NULL, NULL, NULL, 'CONSEIL', 'N/A', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(304, 'ARCHITECTURE', 'ARCH-8.C07', 'C', 'A', 'B', 'Anticiper les impacts sur la production', 'Les fonctionnalités liées aux traitements de données réglementées (santé, bancaire, ...)  sont-elles validées en terme d\'interopérabilité ?', 'La pratique des professionnels des domaines réglementés est guidée par la sécurité des données et par la réduction de l\'impact d\'utilisation du service.', 'Réalisation', 'Chaque fonctionnalité liée é des données réglementées est-elle identifiée ?', NULL, NULL, NULL, NULL, 'CONSEIL', 'N/A', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(305, 'ARCHITECTURE', 'ARCH-8.C08', 'C', 'B', 'B', 'Anticiper les impacts sur la production', 'L\'implémentation de nouveaux services liés é des données réglementées est-elle nécessaire et, le cas échéant, s\'appuie-t-elle sur le socle des services disponibles ?', 'Les services doivent prendre en compte les éléments réglementaires et réutiliser le maximum d\'éléments habituels de ces types de services consolidés sous forme d\'un socle de service.', 'Conception', 'Les fonctionnalités ne sont-elles pas disponibles nativement dans le socle de service ?', NULL, NULL, NULL, NULL, 'CONSEIL', 'N/A', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(306, 'ARCHITECTURE', 'ARCH-8.C09', 'B', 'B', 'A', 'Anticiper les impacts sur la production', 'Est il prévu des réunions pour que le cellule urbanisation évalue les évolutions du SI au regard de la conception, évolution mise en place ?', 'Les enjeux NR ne sont jamais établis définitivement et doivent faire l\'objet d\'un suivi périodique', 'Maintenance', 'Comment sont planifiés et évalués les demandes d\'évolutions ?', NULL, NULL, NULL, NULL, 'CONSEIL', 'N/A', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(307, 'ARCHITECTURE', 'ARCH-9', NULL, NULL, NULL, 'Définir les objectifs NR du projet et leur adéquation dans le contexte opérationnel', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'ODD#12 ; ODD#13 ; ODD#15', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(308, 'ARCHITECTURE', 'ARCH-9.C01', 'B', 'B', 'A', 'Définir les objectifs NR du projet et leur adéquation dans le contexte opérationnel', 'Le schéma directeur de l\'organisation propose-t-il des objectifs et actions Numérique Responsable qui vont influencer le design d\'architecture ou les solutions d\'urbanisation ? En l\'absence de ces éléments, une pré-étude doit étre conduite pour définir les objectifs généraux.', 'L\'intégration du Numérique Responsable dans la stratégie de l\'entreprise est indispensable pour que ce concept soit propagé dans toutes les fonctions de l\'organisation. En l\'absence de cette stratégie globale, seule une pré-étude permet de positionner les objectifs généraux et de déterminer les leviers d\'actions qui seront mis en oeuvre.', 'N/A', 'Le schéma directeur prend-il en compte des objectifs NR ?', NULL, NULL, NULL, NULL, 'CONSEIL', 'N/A', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(309, 'ARCHITECTURE', 'ARCH-9.C02', 'A', 'B', 'A', 'Définir les objectifs NR du projet et leur adéquation dans le contexte opérationnel', 'Les objectifs NR sont-ils qualifiés ? Une grille des objectifs du projet au regard des objectifs ODD existe-t-elle ?', 'L\'adhésion aux ODD et aux principes NR doit étre vérifiée pour situer la distance parcourue et restante par rapport aux objectifs. La qualification des objectifs sous forme d\'indicateurs est une opération préalable indispensable', 'N/A', 'Quels indicateurs (Carbone, DEEE, ...) sont-ils exposés aux utilisateurs ?', NULL, NULL, NULL, NULL, 'CONSEIL', 'N/A', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(310, 'ARCHITECTURE', 'ARCH-9.01', 'B', 'B', 'B', 'Définir les objectifs NR du projet et leur adéquation dans le contexte opérationnel', 'L\'ensemble des équipements techniques utilisés par le service sont-ils identifiés?', 'La conception, le développement, les tests, la production, la gestion de disponibilité sont des étapes qui vont utiliser des ressources techniques. Chacune de ces ressources doit étre identifiée et ses caractéristiques répertoriées.', 'Acquisition', 'Quels sont les équipements qui devront faire l\'objet d\'une acquisition ou d\'un remplacement ?', NULL, NULL, NULL, NULL, 'RECO', '*', NULL, 'INCONTOURNABLE', '#PERF ; #VOL ;  #MAINT ;  #DEBT ; #GOV ; #BUY ; #FIN  ; #OBSO', 'Dev ; ProjectMngr ; ITMngr ;', '(X / Y ) * 100', 'Formalisés = 100 ; prévus = 75 ; Identifiés = 50 ; Non = 0', '100', 'High', 'OnUpdate', 'Une gestion de configuration est mise en place', NULL, NULL, 'Y', '#PROCESS', '**', 'Outils Config Management'),
(311, 'ARCHITECTURE', 'ARCH-9.02', 'B', 'C', 'B', 'Définir les objectifs NR du projet et leur adéquation dans le contexte opérationnel', 'Pour chaque équipement, les caractéristiques sont-elles disponibles ? (Ancienneté, données de performances environnementales, ...)', 'La position de l\'équipement dans son cycle de vie et son impact environnemental doivent étre connus pour utiliser au mieux les équipements disponibles.', 'Acquisition', 'Les seuils sont-ils définis pour envisager le remplacement ?', NULL, NULL, NULL, NULL, 'RECO', '*', NULL, NULL, '#PERF ; #VOL ;  #MAINT ;  #DEBT ; #GOV ; #BUY ; #FIN  ; #OBSO', 'Dev ; ProjectMngr ; ITMngr ;', NULL, NULL, NULL, 'High', 'Periodic', 'Gestion de configuration', NULL, NULL, 'Y', '#PROCESS', '**', 'Outils Config Management'),
(312, 'ARCHITECTURE', 'ARCH-9.03', 'B', 'B', 'B', 'Définir les objectifs NR du projet et leur adéquation dans le contexte opérationnel', 'Chaque fonction du service est-elle appréhendée en regard de son importance dans le service ?', 'Les fonctionnalités les plus importantes sont parfois moins consommatrices de ressources que des fonctions marginales ce qui n\'a pas de sens dans une démarche NR cohérente.', 'Conception', 'Avez-vous appliqué une démarche 80/20 pour chaque fonction en rapport avec ses impacts environnementaux et son importance ?', NULL, NULL, NULL, NULL, 'RECO', '**', NULL, NULL, '#PERF ; #VOL ; #SEC ; #USE ; #MAINT ; #DEBT ; #METH ;', 'Dev ; ProjectMngr ; ITMngr ;', NULL, NULL, NULL, 'High', 'OnUpdate', 'Analyse d\'impact', NULL, NULL, NULL, NULL, NULL, NULL),
(313, 'ARCHITECTURE', 'ARCH-9.C03', 'B', 'C', 'B', 'Définir les objectifs NR du projet et leur adéquation dans le contexte opérationnel', 'Les fonctions secondaires ont-elles aussi un impact moindre sur l\'impact environnemental ?', 'La cohérence NR pousse é s\'assurer que les fonctions secondaires n\'ont pas un impact environnemental plus important que les fonctions principales, les spécifications doivent clairement identifier les fonctions principales et secondaires et leur impact du point de vue NR', 'Conception', 'Les régles de validation des impacts NR des fonctions sont-elles décrites ?', NULL, NULL, NULL, NULL, 'CONSEIL', 'N/A', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(314, 'ARCHITECTURE', 'ARCH-9.C04', 'B', 'B', 'B', 'Définir les objectifs NR du projet et leur adéquation dans le contexte opérationnel', 'Chacune des phases du cycle de vie (Démarrage, maturité, arrét) sont-elles identifiées avec des actions spécifiques ?', 'La démarche ACV est une base de la prise en compte du NR dans la conception des services numériques, elle permet d\'associer des actions é chaque étape du cycle de vie.', 'Maintenance', 'A quelle fréquence ces actions sont-elles revues ?', NULL, NULL, NULL, NULL, 'CONSEIL', 'N/A', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(315, 'ARCHITECTURE', 'ARCH-9.04', 'A', 'B', 'A', 'Définir les objectifs NR du projet et leur adéquation dans le contexte opérationnel', 'Les données ACV sont-elles réutilisées dans le cas d\'une adaptation d\'un service existant ?', 'Les analyses ACV peuvent étre réalisées sur des services existants afin de les rendre plus efficaces dans le cas d\'une refonte ou adaptation d\'un service existant', 'Revalorisation', 'Quelles données ACV sont produites pour permettre leur réutilisation dans d\'autres projets ?', NULL, NULL, NULL, NULL, 'RECO', '**', NULL, NULL, '#USE ; #MAINT ; #COM ; #VALUE ; #DEBT ; #METH', 'Dev ; Designer ; ProjectMngr ; ProductOwner ; Manager ; RSE/NR', '(X / Y ) * 100', 'Oui = 100 ; Partiel = 50 ; Non = 0', '100', 'Low', 'OnUpdate', 'Une ACV minimale (\"screening\") est disponible pour le service existant', NULL, NULL, NULL, NULL, NULL, NULL),
(316, 'ARCHITECTURE', 'ARCH-9.C05', 'A', 'B', 'A', 'Définir les objectifs NR du projet et leur adéquation dans le contexte opérationnel', 'La durée du support du service numérique est-elle suffisante pour minimiser le renouvellement des équipements ?', 'Les besoins d\'adaptation des matériels aux services en exploitation peuvent étre une source d\'impact environnemental important.', 'Fin de Vie', 'Le calcul pour la durée du support du service ou produit a-t-il pris en compte l\'impact sur le renouvellement du matériel ?', NULL, NULL, NULL, NULL, 'CONSEIL', 'N/A', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(317, 'ARCHITECTURE', 'ARCH-9.C06', 'B', 'A', 'A', 'Définir les objectifs NR du projet et leur adéquation dans le contexte opérationnel', 'La production documentaire est-elle recherchée/revue/exploitée en amont de la démarche et produite en aval pour étre ré-utilisée dans les projets suivants ?', 'D\'une part, dans une organisation les projets présentent beaucoup de similitudes, d\'autre part les aspects NR adressés dans les projets sont souvent du méme ordre. Afin de capitaliser sur ces éléments et bénéficier des efforts produits, il est nécessaire de s\'appuyer sur un socle de connaissances stables. Toutefois la maturité d\'une organisation d\'un point de vue NR est amenée é progresser, il faut donc que les étapes documentées puissent aussi suivre ces évolutions et participer activement é cet accroissement de maturité.', 'Revalorisation', 'Quelle est la fréquence de revue collégiale des documents ?', NULL, NULL, NULL, NULL, 'CONSEIL', 'N/A', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(318, 'ARCHITECTURE', 'ARCH-10', NULL, NULL, NULL, 'Intégrer les équipes dans le projet', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'ODD#12', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(319, 'ARCHITECTURE', 'ARCH-10.C01', 'A', 'A', 'A', 'Intégrer les équipes dans le projet', 'Chaque partie-prenante a-t-elle la latitude de prendre des décisions pouvant influer sur l\'impact environnemental ?', 'Les décisions projets qui peuvent avoir un impact important sur l\'environnement doivent pouvoir étre prises rapidement. Pour cela il est indispensable de définir l\'espace de décision de chaque acteur et les mécanismes de validation. En l\'absence de cette clarification, des décisions erratiques, méme pour de bonnes raisons, pourraient intervenir et décrédibiliser la démarche NR, ou entraéner une forme d\'auto-censure limitant les bénéfices NR', 'N/A', 'Le processus de décision est-il exposé é tous ?', NULL, NULL, NULL, NULL, 'CONSEIL', 'N/A', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(320, 'ARCHITECTURE', 'ARCH-10.C02', 'B', 'A', 'A', 'Intégrer les équipes dans le projet', 'Les aspects Numérique Responsable sont-ils propagés et entretenus sur l\'ensemble des acteurs ?', 'Les définitions projet peuvent évoluer au fil du temps. Ces évolutions doivent systématiquement prendre en compte les aspects NR. Les adaptations sont le plus souvent le fait des métiers ou d\'impératifs techniques. Les impacts environnementaux doivent pouvoir aussi avoir cette latitude avec des chemins de validation pré-définis.', 'N/A', 'Combien d\'acteurs du département Architecture sont-ils sensibilisés et formés au Numérique Responsable ?', NULL, NULL, NULL, NULL, 'CONSEIL', 'N/A', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `goodpractices` (`cle`, `Family`, `Id`, `Planet`, `People`, `Prosperity`, `Recommendation`, `Criteria`, `Justification`, `Stage`, `Tests1`, `Tests2`, `Tests3`, `Correspondence`, `Link`, `Type`, `Difficulty`, `Goal`, `Incontournable`, `Tags`, `Actors`, `Indicators_`, `IndicatorsX`, `IndicatorsY`, `Priority`, `Recurrence`, `UseCase`, `Example`, `Limite`, `Automatizable`, `AutomatizationScript`, `AutomatizationLevel`, `AutomatizationInformation`) VALUES
(321, 'ARCHITECTURE', 'ARCH-10.01', 'B', 'A', 'A', 'Intégrer les équipes dans le projet', 'Les objectifs Numérique Responsable sont-ils qualifiés pour chaque type de public dans le design d\'architecture ?', 'Pour exprimer clairement les objectifs NR dans chacune des étapes de conception d\'architecture il est indispensable de situer les profils cibles et de leur associer des objectifs NR précis.', 'N/A', 'Comment sont valorisés les objectifs atteints ?', NULL, NULL, NULL, NULL, 'RECO', '**', NULL, NULL, '#COM ; #VALUE  ; #GOV ;  #HR ; #FIN', 'Manager ; RSE/NR ; HR ; Staff', NULL, NULL, NULL, 'Low', 'Once', 'Plan de formation', NULL, NULL, NULL, NULL, NULL, NULL),
(322, 'ARCHITECTURE', 'ARCH-10.C03', 'B', 'B', 'B', 'Intégrer les équipes dans le projet', 'L\'architecte dispose-t-il d\'un réle dans la continuité du produit ? Est-il associé aux phases de présentation, évangélisation, de build et de run du produit mis en oeuvre ?', 'Chaque étape projet doit s\'attacher é prendre en compte la couverture la plus compléte possible du cycle de vie produit.', 'Déploiement', 'Le périmétre d\'action est-il étendu aux différentes phases projet ?', NULL, NULL, NULL, NULL, 'CONSEIL', 'N/A', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(323, 'ARCHITECTURE', 'ARCH-10.C04', 'B', 'A', 'A', 'Intégrer les équipes dans le projet', 'L\'animation de l\'équipe projet intégre-t-elle une dimension Numérique Responsable ?', 'La prise en compte des aspects NR fait au démarrage de la conception peut étre respectée mais dans un mode itératif il est possible de s\'en écarter graduellement. Les revues du respect NR sont indispensables pour conserver la cohérence du projet.', 'Maintenance', 'Des revues Numérique Responsable sont-elles planifiées tout au long du projet ?', NULL, NULL, NULL, NULL, 'CONSEIL', 'N/A', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(324, 'FRONTEND', 'FRT-1', NULL, NULL, NULL, 'Valider le périmétre et la couverture fonctionnelle du projet', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'ODD#12 ; ODD#13', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(325, 'FRONTEND', 'FRT-1.01', 'A', 'A', 'A', 'Valider le périmétre et la couverture fonctionnelle du projet', 'Est-ce que les fonctions proposées sont bien en rapport avec un usage ?', 'Derriére chaque fonctionnalité il y a du code, des échanges, des données. Si certaines fonctions ne correspondent pas é un vrai besoin, des ressources sont gaspillées en vain.', 'Utilisation', 'Toutes les fonctionnalités sont-elles validées par un usage décrit par le métier ?', NULL, NULL, 'Opquast 1 / GreenIt 1', NULL, 'RECO', '**', NULL, NULL, '#VOL ; #USE ; #VALUE ; #METH ; #FIN', 'Dev ; ProjectMngr ; ProductOwner ; ITMngr ;', '(X / Y ) * 100', 'Fonctionnalités validées', 'Fonctionnalités', 'High', 'OnUpdate', 'Les solutions d\'analytics / sondes sont mises en oeuvre et optimisées pour valider les utilisations des fonctionnalités', NULL, NULL, NULL, NULL, NULL, NULL),
(326, 'FRONTEND', 'FRT-1.C01', 'B', 'B', 'B', 'Valider le périmétre et la couverture fonctionnelle du projet', 'Chaque fonction du service est-elle appréhendée en regard de son importance dans le service ?', 'Les fonctions les plus gourmandes en ressources ne sont pas toujours les fonctions les plus utiles, il est acceptable de consommer des ressources pour des éléments critiques, mais plus dommageable lorsque c\'est pour des choses mineures.', 'Utilisation', 'Avez-vous appliqué une démarche 80/20 pour chaque fonction en rapport avec ses impacts environnementaux et son importance ?', NULL, NULL, NULL, NULL, 'CONSEIL', 'N/A', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(327, 'FRONTEND', 'FRT-1.C02', 'B', 'B', 'B', 'Valider le périmétre et la couverture fonctionnelle du projet', 'Les fonctions secondaires ont-elles aussi un impact moindre sur l\'impact environnemental ?', 'La cohérence NR pousse é s\'assurer que les fonctions secondaires n\'ont pas un impact environnemental plus important que les fonctions principales. Les phases de développement doivent clairement identifier les fonctions principales et secondaires et leur impact du point de vue NR.', 'Utilisation', 'Les impacts environnementaux de chaque fonctionnalité sont-ils évalués par rapport é la nature et l\'importance de la fonctionnalité ?', NULL, NULL, NULL, NULL, 'CONSEIL', 'N/A', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(328, 'FRONTEND', 'FRT-1.02', 'C', 'B', 'A', 'Valider le périmétre et la couverture fonctionnelle du projet', 'Utilisez-vous des standards de développement éprouvés ?', 'Les meilleurs standards de développement couvrent une grande partie des attentes du NR sur les aspects de fiabilité, de réutilisation, validation des codes produits, ainsi que l\'utilisabilité par différentes catégories de publics. L\'adoption et le respect de ces standards donne une base solide pour construire en appliquant les démarches NR qui sont hors du périmétre de ces méthodes et outils.', 'Réalisation', 'Quels sont les standards de référence utilisés (ISO/RFC/IEC) ?', 'Le service respecte -il les recommandations du RGAA, du W3C ? Est-ce que les contraintes RGPD sont toutes traitées ?', NULL, 'Critére RGAA 8.2', NULL, 'RECO', '**', NULL, NULL, '#VALUE ; #METH ; #HR', 'Dev ; ProjectMngr ; ITMngr ;', '(X / Y ) * 100', 'Formalisés = 100 ; prévus = 75 ; Identifiés = 50 ; Non = 0', '100', 'High', 'Periodic', 'Développement', NULL, NULL, NULL, NULL, NULL, NULL),
(329, 'FRONTEND', 'FRT-1.C03', 'C', 'B', 'A', 'Valider le périmétre et la couverture fonctionnelle du projet', 'Est-ce qu\'une méthodologie de qualité est en place ?', 'La qualité du logiciel produit a un impact direct sur les aspects NR. Les défauts entraénent de multiples livraisons de nouvelles versions successives, les anomalies entraénent des productions de traces d\'anomalies importantes, et des fonctionnalités plus consommatrices de ressources techniques, parfois pour un résultat non atteint, donc des ressources ont été gaspillées pour ne produire aucun résultat', 'Réalisation', 'Quels sont les outils d\'analyse, d\'optimisation de codes, de debug mis en place ? Les méthodes de développement suivent-elles une orientation Test Driven Development ?', NULL, NULL, NULL, NULL, 'CONSEIL', 'N/A', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Y', '#PROCESS', '**', 'CI / CD'),
(330, 'FRONTEND', 'FRT-1.03', 'A', 'B', 'B', 'Valider le périmétre et la couverture fonctionnelle du projet', 'Les données ACV sont-elles réutilisées dans le cas d\'une adaptation d\'un service existant ?', 'Les analyses ACV peuvent étre réalisées sur des services existants afin de les rendre plus efficaces dans le cas d\'une refonte ou adaptation d\'un service existant, les spécifications et exigences NR issues des ACV précédentes doivent étre reprises et adaptées si besoin.', 'Revalorisation', 'Avez-vous mis é disposition des équipes projet les ACV précédentes ?', 'L\'ACV de ce projet est-elle rendue disponible pour d\'autres projets ?', NULL, NULL, NULL, 'RECO', '**', NULL, NULL, '#USE ; #MAINT ; #COM ; #VALUE ; #DEBT ; #METH', 'Dev ; Designer ; ProjectMngr ; ProductOwner ; Manager ; RSE/NR', '(X / Y ) * 100', 'Formalisés = 100 ; prévus = 75 ; Identifiés = 50 ; Non = 0', '100', 'Low', 'OnUpdate', 'Une ACV minimale (\"screening\") est disponible pour le service existant', NULL, NULL, NULL, NULL, NULL, NULL),
(331, 'FRONTEND', 'FRT-1.C04', 'B', 'B', 'B', 'Valider le périmétre et la couverture fonctionnelle du projet', 'Est-ce bien la fonctionnalité qui guide les choix plutét que l\'attrait de la technologie é la mode ?', 'Le choix technologique doit étre dicté par les fonctionnalités et le respect des critéres NR, plutét que par l\'attrait technologique de la nouveauté é la mode.', 'Acquisition', 'Quel acteur valide les choix techniques d\'un point de vue NR ?', NULL, NULL, NULL, NULL, 'CONSEIL', 'N/A', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(332, 'FRONTEND', 'FRT-2', NULL, NULL, NULL, 'Fédérer les équipes et le public cible', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'ODD#8', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(333, 'FRONTEND', 'FRT-2.C01', 'B', 'B', 'B', 'Fédérer les équipes et le public cible', 'Quel serait mon réflexe de dev, pour prendre en compte le cycle de vie ?', 'La prise en compte du cycle doit étre intégrée dans les phases de développement. Les développeurs sont les principaux acteurs qui, par leurs choix, leurs pratiques vont conditionner les résultats futurs. La prise en compte du cycle de vie dans le développement est indispensable. Il serait plus coéteux, plus risqué et moins efficace de traiter les aspects de cycle de vie en aval.', 'Réalisation', 'Quelles procédures sont disponibles pour traiter le cycle de vie ?', NULL, NULL, NULL, NULL, 'CONSEIL', 'N/A', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(334, 'FRONTEND', 'FRT-2.C02', 'C', 'B', 'B', 'Fédérer les équipes et le public cible', 'Chaque partie-prenante interne a-t-elle la latitude de prendre des décisions pouvant influer sur l\'impact environnemental ?', 'Les orientations des développements peuvent évoluer au fil du temps. Ces évolutions doivent systématiquement prendre en compte les aspects NR. Les adaptations sont le plus souvent le fait des métiers ou des contraintes techniques qui apparaissent au cours du développement. Les impacts environnementaux doivent pouvoir aussi étre traités par les équipes techniques avec des chemins de validation pré-définis.', 'Réalisation', 'Le circuit et l\'autonomie de décision ayant un impact NR est-il défini pour chaque acteur ?', NULL, NULL, NULL, NULL, 'CONSEIL', 'N/A', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(335, 'FRONTEND', 'FRT-2.C03', 'B', 'A', 'A', 'Fédérer les équipes et le public cible', 'Les aspects Numérique Responsable sont-ils propagés et entretenus sur l\'ensemble des acteurs ?', 'La maturité NR d\'une équipe repose sur la connaissance partagée. C\'est cette connaissance NR qui doit étre présente pour devenir une pratique métier standard en permanente évolution.', 'N/A', 'Quelle est votre fréquence de communication NR pour chaque profil de partie prenante ?', NULL, NULL, NULL, NULL, 'CONSEIL', 'N/A', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(336, 'FRONTEND', 'FRT-2.01', 'B', 'B', 'B', 'Fédérer les équipes et le public cible', 'Le public cible du service est-il déterminé vis-é-vis de ses capacités d\'utilisation du service ?', 'La finalité du développement est dépendante des publics utilisateurs cibles. Les choix d\'implémentation ont une forte conséquence sur la capacité d\'utilisation du service. Les équipes de développement sont souvent éloignées des cas d\'usage réels, il est indispensable de reconstruire cette proximité pour que tous les publics utilisateurs soient inclus.', 'Utilisation', 'Les objectifs NR sont-ils qualifiés pour chaque profil cible?', NULL, NULL, NULL, NULL, 'RECO', '**', NULL, NULL, '#USE ; #COM ; #VALUE', 'Dev ; Designer ; ProjectMngr ; ProductOwner ; RSE/NR ;', NULL, NULL, NULL, 'Medium', 'OnUpdate', 'Fiche de cadrage', NULL, NULL, NULL, NULL, NULL, NULL),
(337, 'FRONTEND', 'FRT-3', NULL, NULL, NULL, 'Utiliser les environnements et outils qui permettent de limiter les impacts', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'ODD#12 ; ODD#13', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(338, 'FRONTEND', 'FRT-3.01', 'A', 'C', 'B', 'Utiliser les environnements et outils qui permettent de limiter les impacts', 'Utilisez vous les mécanismes de cache pour limiter les échanges ?', 'La réduction des volumes d\'échanges, des opérations de calculs, traitements, et accés aux données est un axe d\'optimisation des consommations de ressources. La mise en place de systémes de cache permet de réduire les coéts des opérations répétitives. Les mécanismes de cache peuvent étre déployés é différents points de passage dans un service. Plus le cache est proche du demandeur qu\'il doit servir, plus le volume de données en transit et la longueur du chemin emprunté par les données sont faibles. Plus le cache est proche de la source de données, plus il peut étre mutualisé par un nombre important d\'utilisateurs mais les données seront tout de méme échangées, seules les opérations de traitement et collecte seront réduites.', 'Réalisation', 'Est-ce que les mécanismes de caches sont en place : pas de directive no-cache, cache navigateur, cache API ?', NULL, NULL, NULL, NULL, 'RECO', '**', NULL, NULL, '#PERF ; #VOL ; #USE ; #METH', 'Dev ; ProjectMngr ; ITMngr ;', '(X / Y ) * 100', 'Formalisés = 100 ; prévus = 75 ; Identifiés = 50 ; Non = 0', '100', 'High', 'OnUpdate', 'Le dossier technique identifie les caches et la stratégie de cache utilisée pour chaque type de flux. Les outils de gestions des caches (purge / désactivation) sont utilisés pour valider les performances effectives (ex: CHROME : console /network enable - disable cache)', NULL, NULL, 'Y', '#SCRIPT', '**', 'Outils parser code'),
(339, 'FRONTEND', 'FRT-3.02', 'B', 'C', 'B', 'Utiliser les environnements et outils qui permettent de limiter les impacts', 'Est-ce que les fonctionnalités couvertes par des actions locales (cété client) sont privilégiées plutét que des échanges API ?', 'Les charges introduites par la sollicitation de ressources distantes via des mécanismes API peuvent étre importantes. Certaines fonctionnalités pourraient étre couvertes par des actions locales plutét que distantes ce qui réduit la volumétrie des échanges et le nombre de composants mis en oeuvre, donc réduit l\'impact environnemental. En revanche, il ne faut pas que le traitement en local d\'une fonctionnalité qui peut nécessiter de nouvelles dépendances techniques, alourdisse la charge sur le front end.', 'Réalisation', 'L\'utilisation d\'API externes est-elle limitée (si possible) ?', NULL, NULL, NULL, NULL, 'RECO', '*', NULL, 'INCONTOURNABLE', '#PERF ; #VOL ; #USE ; #METH', 'Dev ; ProjectMngr ; ITMngr ;', '(X / Y ) * 100', 'Fonctionnalités analysées', 'Fonctionnalités elligibles', 'High', 'OnUpdate', 'Les tests de navigation / parcours utilisateur couvrent les accés API, les outils de développement des navigateurs permettent de suivre les volumes de données (ex:  CHROME - network)', NULL, NULL, NULL, NULL, NULL, NULL),
(340, 'FRONTEND', 'FRT-3.03', 'A', 'C', 'B', 'Utiliser les environnements et outils qui permettent de limiter les impacts', 'Est-ce que les données remontées par les API sont bien uniquement celles dont l\'application a besoin au moment oé elles sont demandées ?', 'Les parcours et besoins des utilisateurs sont trés difficilement anticipables. Une tendance consiste é collecter sur le front end un maximum de données pour couvrir le maximum de cas d\'utilisation. Cette pratique est inefficace d\'un point de vue NR car la collecte, le traitement, l\'acheminement, le stockage local des données génére une charge et donc une consommation énergétique importante sans certitude que toutes ces données seront effectivement utilisées. L\'empreinte du service doit étre réduite en ne collectant que les données indispensables au fur et é mesure du déroulement du service via les mécanismes API.', 'Réalisation', 'Les volumes de données transmis sont-ils en rapport avec les capacités de présentation du périphérique utilisateur ?', NULL, NULL, 'IRIWEBs', NULL, 'RECO', '**', NULL, NULL, '#PERF ; #VOL ; #SEC ; #USE ; #VALUE ; #METH', 'Dev ; ProjectMngr ; ITMngr ;', '(X / Y ) * 100', 'Données API analysées', 'Données API collectées', 'High', 'OnUpdate', 'Les outils de développement et consoles des navigateurs sont utilisés pour suivre les échanges API (ex: CHROME : console network)', NULL, NULL, NULL, NULL, NULL, NULL),
(341, 'FRONTEND', 'FRT-3.C01', 'B', 'B', 'A', 'Utiliser les environnements et outils qui permettent de limiter les impacts', 'Est-ce qu\'une alternative openSource est disponible pour les API ?', 'L\'opacité de certains fournisseurs de service ne permet pas de connaétre leurs pratiques sur les différentes dimensions du NR. Les solutions open source, communautaires peuvent lever certaines de ces contraintes, mais l\'open source ne garantit pas que l\'ensemble des critéres NR soient couverts ou visibles.', 'Acquisition', 'La performance NR des API open source est-elle un critére de choix ?', 'Est-ce que ces API OpenSource prennent en compte le NR ?', NULL, NULL, NULL, 'CONSEIL', 'N/A', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(342, 'FRONTEND', 'FRT-3.04', 'A', 'B', 'B', 'Utiliser les environnements et outils qui permettent de limiter les impacts', 'Les bibliothéques utilisées permettent-elles de ne prendre que les composants effectivement utiles ?', 'Bien souvent les composants issus de bibliothéques publiques (open source ou commerciales) apportent un ensemble de fonctionnalités qui ne sont que rarement toutes utilisées et drainent des dépendances vis-é -is d\'autres composants avec les mémes caractéristiques. Au final le projet agrége un volume de bibliothéque important alors qu\'une petite partie est réellement utilisée. Privilégier les composants dont les dépendances et fonctionnalités sont pilotables par rapport aux besoins est efficace du point de vue NR.', 'Acquisition', 'Comment sont tracés les composants utilisés ?', NULL, NULL, NULL, NULL, 'RECO', '***', NULL, NULL, '#PERF ; #VOL ; #SEC ; #USE ; #VALUE ; #METH', 'Dev ; ProjectMngr ; ITMngr ;', '(X / Y ) * 100', 'Formalisés = 100 ; prévus = 75 ; Identifiés = 50 ; Non = 0', '100', 'High', 'OnUpdate', 'Le dossier technique identifie les dépendances de composants et précise les éléments pouvant étre supprimés.', NULL, NULL, NULL, NULL, NULL, NULL),
(343, 'FRONTEND', 'FRT-3.05', 'A', 'C', 'B', 'Utiliser les environnements et outils qui permettent de limiter les impacts', 'Les dépendances non utilisées sont-elles identifiées et retirées ?', 'Les arbres de dépendance donnent la cartographie des composants et leurs relations. Un arbre de dépendance le plus réduit possible est efficace car il réduit les volumes de code, mais cela ne présume pas que chaque branche de cet arbre de dépendance est justifiée par une utilisation effective.', 'Déploiement', 'Quel outil utilisez-vous pour tracer les dépendances ?', 'Concernant leur mise é jour, comment vous assurez-vous de la fiabilité des sources si vous utilisez des ressources extérieures de type CDN ?', NULL, NULL, NULL, 'RECO', '***', NULL, NULL, '#PERF ; #VOL ; #SEC ; #USE ; #VALUE ; #METH', 'Dev ; ProjectMngr ; ITMngr ;', '(X / Y ) * 100', 'Dépendances utilisées', 'Dépendances natives', 'High', 'OnUpdate', 'Les outils de développement et la console des navigateurs permettent de tracer les dépendances chargées mais inutilisées (ex: CHROME : console / coverage)', NULL, NULL, 'Y', '#SCRIPT', '**', 'Outils Analyses de dépendances'),
(344, 'FRONTEND', 'FRT-3.06', 'B', 'B', 'B', 'Utiliser les environnements et outils qui permettent de limiter les impacts', 'Est-ce que la fonctionnalité attendue ne pourrait pas étre mise en place avec les capacités natives du navigateur ?', 'Certaines fonctionnalités sont disponibles sous plusieurs formes. Lorsqu\'elles sont natives, aucun code ou composants supplémentaires ne sont nécessaires pour les utiliser ce qui réduit l\'impact du service.', 'Réalisation', 'Quelles fonctionnalités sont ré-écrites car la fonctionnalité systéme native ne couvre pas l\'intégralité du besoin ?', NULL, NULL, NULL, NULL, 'RECO', '**', NULL, NULL, '#PERF ; #VOL ; #SEC ; #USE ; #VALUE ; #METH', 'Dev ; ProjectMngr ; ITMngr ;', NULL, NULL, NULL, 'High', 'OnUpdate', 'Dossier technique', NULL, NULL, NULL, NULL, NULL, NULL),
(345, 'FRONTEND', 'FRT-3.C02', 'B', 'B', 'A', 'Utiliser les environnements et outils qui permettent de limiter les impacts', 'Une alternative openSource est-elle disponible ?', 'Une solution Open Source n\'est pas un gage d\'efficacité NR et les différentes alternatives doivent étre challengées. Mais l\'open source permet de limiter les ambitions et mainmises de certains acteurs dont les pratiques économiques, sociales sont opaques.', 'Acquisition', 'La performance NR de la solution open source est-elle un critére de choix ?', NULL, NULL, NULL, NULL, 'CONSEIL', 'N/A', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(346, 'FRONTEND', 'FRT-3.07', 'B', 'B', 'B', 'Utiliser les environnements et outils qui permettent de limiter les impacts', 'L\'ensemble des équipements techniques utilisés par le service sont-ils identifiés?', 'La conception, le développement, les tests, la production, la gestion de disponibilité sont des étapes qui vont utiliser des ressources techniques. Chacune de ces ressources doit étre identifiée et ses caractéristiques répertoriées.', 'Utilisation', 'Les certifications de chaque équipement sont-elles disponibles ?', NULL, NULL, NULL, NULL, 'RECO', '*', NULL, NULL, '#PERF ; #VOL ; #SEC ; #USE ; #VALUE ; #METH ; #BUY ; #FIN ; #OBSO', 'Dev ; ProjectMngr ; ITMngr ;', NULL, NULL, NULL, 'Medium', 'OnUpdate', 'Gestion de  configuration', NULL, NULL, NULL, NULL, NULL, NULL),
(347, 'FRONTEND', 'FRT-3.C03', 'B', 'B', 'B', 'Utiliser les environnements et outils qui permettent de limiter les impacts', 'Pour chaque équipement, les caractéristiques sont-elles disponibles ? (Ancienneté, données de perf environnementales, ...)', 'La position de l\'équipement dans son cycle de vie et son impact environnemental doivent étre connus pour utiliser au mieux les équipements disponibles.', 'Administration', 'Les critéres de renouvellement des équipements prennent-ils en compte les impacts NR ?', NULL, NULL, NULL, NULL, 'CONSEIL', 'N/A', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Y', '#PROCESS', '**', 'Outils Config Management'),
(348, 'FRONTEND', 'FRT-3.C04', 'C', 'B', 'B', 'Utiliser les environnements et outils qui permettent de limiter les impacts', 'Les fonctionnalités liées aux traitements de données réglementées (santé, personnelles, bancaires) sont-elles validées en terme d\'interopérabilité ?', 'Le cadre réglementaire de certaines catégories de données est trés strict, le respect de ces données pour protéger les utilisateurs est un critére important. Il est indispensable que dans le processus de développement aucune bréche ne soit possible et que toutes les interopérabilités avec les mécanismes standards de ces domaines soient validés.', 'Utilisation', 'Chaque fonctionnalité liée é des données réglementées (santé, bancaire) est-elle identifiée ?', NULL, NULL, NULL, NULL, 'CONSEIL', 'N/A', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(349, 'FRONTEND', 'FRT-3.C05', 'B', 'C', 'B', 'Utiliser les environnements et outils qui permettent de limiter les impacts', 'Les derniéres avancées techniques, sont-elles utilisées lorsqu\'elles contribuent réellement é réduire l\'impact ?', 'Les avancées technologiques se succédent rapidement, le NR en se diffusant progresse aussi dans les pratiques et l\'efficacité. La conjonction de ces 2 phénoménes nécessite au cours du projet, une veille permanente et une stratégie pour évaluer les bénéfices de l\'adoption de nouveaux éléments techniques vis-é-vis des impacts NR', 'Maintenance', 'A quelle fréquence les améliorations sont-elles évaluées ?', NULL, NULL, NULL, NULL, 'CONSEIL', 'N/A', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(350, 'FRONTEND', 'FRT-4', NULL, NULL, NULL, 'Réduire les effets d\'obsolescence', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'ODD#10 ; ODD#12 ; ODD#13 ; ODD#15', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(351, 'FRONTEND', 'FRT-4.01', 'A', 'B', 'B', 'Réduire les effets d\'obsolescence', 'La plage de rétro-compatibilité est-elle définie ?', 'Les équipements utilisateurs sont de plus en plus performants. Lorsque les services numériques exploitent toutes les capacités des équipements, cela écarte certains publics qui ne disposent pas des méme outils performants. Cela empéche aussi d\'aborder la fourniture des fonctionnalités avec des moyens moins gourmands en ressources mais procurant le méme résultat.', 'Réalisation', 'La liste des équipements est-elle mise é jour ?', NULL, NULL, NULL, NULL, 'RECO', '*', NULL, 'INCONTOURNABLE', '#PERF ; #VOL ; #SEC ; #USE ; #VALUE ; #METH ; #OBSO', 'Dev ; ProjectMngr ; ProductOwner ; ITMngr ;', '(X / Y ) * 100', 'Formalisés = 100 ; prévus = 75 ; Identifiés = 50 ; Non = 0', '100', 'High', 'Periodic', 'Chaque élément référencé dans le Dossier Technique porte une information de degrés de compatibilité', NULL, NULL, NULL, NULL, NULL, NULL),
(352, 'FRONTEND', 'FRT-4.C01', 'B', 'B', 'B', 'Réduire les effets d\'obsolescence', 'Chacune de ces phases du cycle de vie sont-elles identifiées avec des actions spécifiques ?', 'La démarche ACV est une base de la prise en compte du NR dans la conception des services numériques, elle permet d\'associer des actions é chaque étape du cycle de vie.', 'Maintenance', 'A quelle fréquence ces actions sont-elles revues ?', NULL, NULL, NULL, NULL, 'CONSEIL', 'N/A', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(353, 'FRONTEND', 'FRT-4.C02', 'A', 'B', 'B', 'Réduire les effets d\'obsolescence', 'Une version légére du service pour des équipements anciens ou des conditions d\'accés réseau limitées est-elle disponible ?', 'Plus le service implémente de composants qui présentent une charge sur les serveurs, sur les réseaux et sur le périphérique utilisateur, plus l\'impact est important. Une version allégée du service contribue é réduire l\'impact environnemental de l\'encombrement des réseaux, du sur-dimensionnement des infrastructures.', 'Réalisation', 'Cette version est-elle la seule disponible ?', NULL, NULL, NULL, NULL, 'CONSEIL', 'N/A', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(354, 'FRONTEND', 'FRT-4.02', 'A', 'A', 'B', 'Réduire les effets d\'obsolescence', 'Est-ce que les équipements plus anciens resteront compatibles, et dans quelles limites ?', 'Le secteur d\'activité et le profil des utilisateurs permet le plus souvent de qualifier le parc matériel qui sera employé pour le service. Les matériels sont déterminés par leurs capacités techniques (puissance de calcul, mémoire, capacité réseau, capteurs, taille et qualité d\'écran) mais aussi par les systémes d\'exploitation qu\'ils peuvent accueillir. Chaque contrainte que le service impose réduit le nombre de postes qui pourront l\'utiliser et donc exclut des publics. La mise en oeuvre de solutions alternatives permet de conserver un fonctionnement efficace sur des périphériques anciens qui conservent une bonne utilisabilité et de ne pas exclure d\'utilisateurs', 'Utilisation', 'Est-ce qu\'il y a un mode dégradé pour les autres navigateurs ?', NULL, NULL, NULL, NULL, 'RECO', '**', NULL, NULL, '#USE ; #MAINT ; #COM ; #VALUE ; #DEBT  ; #OBSO', 'Dev ; Designer ; ProjectMngr ; ProductOwner', '(X / Y ) * 100', 'Formalisés = 100 ; prévus = 75 ; Identifiés = 50 ; Non = 0', '100', 'Medium', 'OnUpdate', 'L\'analyse d\'impact détermine les enjeux et risques de mise en obsolescence d\'équipements', NULL, NULL, NULL, NULL, NULL, NULL),
(355, 'FRONTEND', 'FRT-5', NULL, NULL, NULL, 'Implémenter et développer les fonctionnalités pour limiter les impacts', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'ODD#12 ; ODD#13', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(356, 'FRONTEND', 'FRT-5.01', 'A', 'A', 'A', 'Implémenter et développer les fonctionnalités pour limiter les impacts', 'Les fonctionnalités du service ne vont-elles pas au-delé des besoins utilisateurs ?', 'Le nombre de fonctionnalités disponibles dans un service numérique impactent directement la consommation de ressources et l\'impact environnemental. En créant des fonctionnalités de peu d\'utilité ce sont des sources de création de dettes environnementales totalement inutiles. Les fonctionnalités augmentent aussi le volume du service, qui introduisent des délais de chargements et des espaces de stockage supplémentaires.', 'Conception', 'Chaque fonctionnalité est-elle retirée si elle n\'est pas en relation avec un usage défini ?', NULL, NULL, NULL, NULL, 'RECO', '*', NULL, 'INCONTOURNABLE', '#PERF ; #VOL ; #SEC ; #USE ; #VALUE ; #METH', 'Dev ; ProjectMngr ; ProductOwner ; ITMngr ;', '(X / Y ) * 100', 'Fonctionnalités utilisées', 'Fonctionnalités déployées', 'High', 'OnUpdate', 'Des données sont identifiées et collectées (analytics / sondes) en production pour identifier les usages et les fonctionnalités superflues.', NULL, NULL, NULL, NULL, NULL, NULL),
(357, 'FRONTEND', 'FRT-5.C01', 'C', 'B', 'B', 'Implémenter et développer les fonctionnalités pour limiter les impacts', 'Est-ce que les logs sont exempts d\'erreurs non traitées ?', 'Les anomalies lors des traitements générent des insatisfactions utilisateurs, des traitements inutiles, des charges produites pour aucun résultat ce qui est un gaspillage de ressources. Les logs d\'erreurs permettent de repérer et de réparer ces anomalies dés leur apparition.', 'Administration', 'A quelle fréquence sont revues les logs applicatives et chaque erreur génére-t-elle une demande de correction ?', NULL, NULL, 'Pidila-360', NULL, 'CONSEIL', 'N/A', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'FIREFOX : Console', NULL, NULL, 'Y', '#SCRIPT', '*', 'Outils Analyses de logs'),
(358, 'FRONTEND', 'FRT-5.C02', 'C', 'C', 'B', 'Implémenter et développer les fonctionnalités pour limiter les impacts', 'Les codes morts sont-ils éliminés ?', 'Dans les différentes évolutions des codes, certaines branches deviennent inutiles, mais restent souvent présentes dans les codes sources. Outre les risques de confusion et donc l\'augmentation des temps de maintenance, ces lignes de codes inactives sont transmises et générent une volumétrie superflue.', 'Réalisation', 'Quel outil d\'analyse utilisez-vous pour identifier les sections de code jamais appelées ?', NULL, NULL, 'Pidila-360', NULL, 'CONSEIL', 'N/A', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Y', '#SCRIPT', '*', 'Outils parser code'),
(359, 'FRONTEND', 'FRT-5.C03', 'A', 'B', 'A', 'Implémenter et développer les fonctionnalités pour limiter les impacts', 'Un langage compilé est-il utilisé pour des aspects performance et/ou sécurité ?', 'Le type de langage utilisé introduit soit une proximité vers les processeurs (CPU), c\'est le cas des langages compilés, soit vers les hommes, c\'est le cas des langages interprétés (PHP, JavaScript, ...). La proximité des ordres machine augmente la performance technique mais rend la production logicielle plus lourde (phases de compilation, édition de liens). La proximité \"humaine\" facilite les développements mais aussi les malversations (piratage) et demande aux systémes un effort supplémentaire pour traduire \"é la demande\" les lignes de programme en code machine, avec l\'aide d\'un interpréteur.', 'Réalisation', 'Le code fait-il l\'objet d\'optimisation avant compilation, ou é défaut avant interprétation ?', NULL, NULL, NULL, NULL, 'CONSEIL', 'N/A', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(360, 'FRONTEND', 'FRT-5.02', 'C', 'B', 'B', 'Implémenter et développer les fonctionnalités pour limiter les impacts', 'Les éléments de références sont-ils connus, tenus é jour, et mis é disposition de l\'ensemble de l\'équipe projet ?', 'Le cadrage des opérations de développement nécessite un socle de référentiels qui soient connus, adoptés et validés par l\'ensemble des équipes techniques. Ces références doivent évoluer en fonction des avancées de l\'état de l\'art pour rester é la pointe des pratiques autant techniques que NR.', 'Réalisation', 'Comment est évalué le degré de conformité ?', NULL, NULL, NULL, NULL, 'RECO', '*', NULL, NULL, '#PERF ; #VOL ; #SEC ; #USE ; #VALUE ; #METH', 'Dev ; ProjectMngr ; ProductOwner ; ITMngr ;', NULL, NULL, NULL, 'Medium', 'OnUpdate', 'Documentation technique', NULL, NULL, NULL, NULL, NULL, NULL),
(361, 'FRONTEND', 'FRT-5.03', 'C', 'B', 'B', 'Implémenter et développer les fonctionnalités pour limiter les impacts', 'L\'état des lieux des données d\'entrée est-il mis é disposition afin d\'étre réutilisé dans d\'autres projets?', 'Les principes NR sont stables et les bonnes pratiques déployées sont directement utilisables dans d\'autres contextes projet, d\'autant plus que les projets au sein d\'une organisation ont beaucoup de similarités que ce soit dans les méthodes, les objectifs ou les périmétres fonctionnels. Dans la phase de développement, collecter, préparer et produire ces éléments est un gage d\'efficacité et de fiabilité pour augmenter la maturité de l\'organisation dans ses pratiques NR.', 'Revalorisation', 'Quelles sont les données utilisées en entrée et produite pour les autres projets ?', NULL, NULL, NULL, NULL, 'RECO', '*', NULL, NULL, '#USE ; #MAINT ; #COM ; #VALUE ; #DEBT ; #METH', 'Dev ; Designer ; ProjectMngr ; ProductOwner', NULL, NULL, NULL, 'Medium', 'OnUpdate', 'Documentation technique', NULL, NULL, NULL, NULL, NULL, NULL),
(362, 'FRONTEND', 'FRT-6', NULL, NULL, NULL, 'S\'assurer que l\'utilisation du service permet le contréle des impacts', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'ODD#12 ; ODD#13', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(363, 'FRONTEND', 'FRT-6.01', 'A', 'B', 'A', 'S\'assurer que l\'utilisation du service permet le contréle des impacts', 'Limitez-vous les flux échangés ?', 'Dans les usages de services numériques les échanges de données sont é la fois indispensables mais générent une consommation importante de ressources. La réduction des besoins d\'échanges au strict minimum diminue l\'impact de ces échanges. Seule la vue globale des flux avec leurs sources, destinations et objectifs permettent de qualifier la nécessité et d\'accepter l\'impact de consommation qu\'ils générent.', 'Réalisation', 'Est-ce qu\'il y a une surveillance des flux échangés? Existe-t-il une matrice des flux ?  Les flux échangés sont-ils conformes é la matrice des flux ?', NULL, NULL, NULL, NULL, 'RECO', '**', NULL, NULL, '#VOL ; #USE ; #VALUE ; #METH ; #FIN', 'Dev ; ProjectMngr ; ITMngr ;', '(X / Y ) * 100', 'Formalisés = 100 ; prévus = 75 ; Identifiés = 50 ; Non = 0', '100', 'High', 'OnUpdate', 'Les outils de développement et la console des navigateurs permettent de tracer les flux échangés, leurs volumes et fréquences (ex: CHROME : console / network)', NULL, NULL, 'Y', '#PROCESS', '**', 'Outils supervision'),
(364, 'FRONTEND', 'FRT-6.C01', 'A', 'B', 'A', 'S\'assurer que l\'utilisation du service permet le contréle des impacts', 'Est-ce qu\'il y a un suivi du nombre de requétes ?', 'Les opérations réalisées sur le front end générent des requétes vers les services distants. Plus le nombre de requétes est important plus les mécanismes de traitements et d\'échanges vont consommer de ressources.', 'Administration', 'Combien de requétes sont utilisées pour un parcours utilisateur ?', NULL, NULL, 'Opquast 9 / GreenIt 9', NULL, 'CONSEIL', 'N/A', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Y', '#PROCESS', '**', 'Outils supervision'),
(365, 'FRONTEND', 'FRT-6.C02', 'A', 'B', 'A', 'S\'assurer que l\'utilisation du service permet le contréle des impacts', 'Les volumétries d\'échanges sont-elles évaluées et réduites avec les traitements techniques les plus pertinents (compression/minification) ?', 'Les échanges indispensables doivent permettre de réduire la volumétrie des transferts. La minification des échanges, la compression des données échangées permettent de réduire ces volumes. Toutefois les opérations de compression et décompression nécessitent des exécutions de traitements qui peuvent annuler le bénéfice de la compression des échanges.', 'Réalisation', 'Est-ce que le poids est mesuré ?', NULL, NULL, NULL, NULL, 'CONSEIL', 'N/A', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Y', '#PROCESS', '**', 'Outils simulateurs'),
(366, 'FRONTEND', 'FRT-6.02', 'B', 'C', 'B', 'S\'assurer que l\'utilisation du service permet le contréle des impacts', 'Est-ce qu\'il y a des fichiers en double ?', 'En plus des besoins de réplication pour gérer la sécurité et la disponibilité des services, certains fichiers sont présents dans plusieurs emplacements sans réelles justifications. La recherche des doublons permet de réduire les volumes de stockage et de sauvegardes sans impact sur le fonctionnement du service.', 'Administration', 'A quelle fréquence sont contrélés les fichiers pour identifier les duplications?', NULL, NULL, NULL, NULL, 'RECO', '**', NULL, NULL, '#VOL ; #VALUE ; #METH', 'Dev ; ProjectMngr ; ITMngr ;', '(X / Y ) * 100', 'Fichiers uniques', 'Fichiers', 'Low', 'Periodic', 'File system', NULL, NULL, 'Y', '#SCRIPT', '**', 'Outils gestion fichiers'),
(367, 'FRONTEND', 'FRT-6.03', 'B', 'B', 'B', 'S\'assurer que l\'utilisation du service permet le contréle des impacts', 'Combien de domaines différents sont utilisés ?', 'Les mécanismes de cache et la mise en oeuvre des accés aux ressources externes est facilité lorsque le nombre de sources sont réduites. L\'utilisation de nombreux domaines est inefficace é la fois d\'un point de vue performance et NR.', 'Déploiement', 'Utilisez-vous plus de 5 domaines différents ?', 'Est-ce vraiment justifié ?', NULL, 'Opquast 55 / GreenIT 18', NULL, 'RECO', '*', NULL, NULL, '#VOL ; #SEC ; #USE ; #VALUE', 'Dev ; ProjectMngr ; ITMngr ;', '(X / Y ) * 100', 'Formalisés = 100 ; prévus = 75 ; Identifiés = 50 ; Non = 0', '100', 'Low', 'Once', 'CHROME : console extension green IT / bonne pratiques', NULL, NULL, 'Y', '#SCRIPT', '*', 'Outils parser code'),
(368, 'FRONTEND', 'FRT-7', NULL, NULL, NULL, 'Limiter les utilisations de composants hardware ou capteurs sur les terminaux utilisateurs', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'ODD#10 ; ODD#12 ; ODD#13 ; ODD#15', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(369, 'FRONTEND', 'FRT-7.01', 'A', 'B', 'B', 'Limiter les utilisations de composants hardware ou capteurs sur les terminaux utilisateurs', 'Les capteurs sont-ils sollicités au besoin réel, plutét qu\'en permanence ?', 'Les opérations techniques réalisées sur le front end peuvent mettre en oeuvre des capteurs physiques du périphérique utilisateur. La sollicitation de ces capteurs, les processus de contréle de ces capteurs peut entraéner une usure prématurée et en cas de panne pousser au renouvellement du périphérique. Certaines techniques logicielles permettent de réduire l\'utilisation des capteurs physiques, ou de s\'assurer qu\'ils sont employés avec le minimum de contraintes.', 'Réalisation', 'Est-ce que l\'on gére de l\'incrément ou de l\'absolu en permanence ?', NULL, NULL, NULL, NULL, 'RECO', '**', NULL, NULL, '#VOL ; #SEC ; #USE ; #VALUE', 'Dev ; ProjectMngr ; ITMngr ;', '(X / Y ) * 100', 'Capteurs mis en oeuvre é la demande', 'Capteurs référencés', 'Medium', 'OnUpdate', 'L\'analyse des codes source couvrent la recherche des modes d\'utilisation des capteurs', NULL, NULL, NULL, NULL, NULL, NULL),
(370, 'FRONTEND', 'FRT-7.02', 'A', 'A', 'B', 'Limiter les utilisations de composants hardware ou capteurs sur les terminaux utilisateurs', 'Les fonctions de mise en pause sont-elles disponibles ?', 'L\'optimisation des utilisations des composants physiques du périphérique utilisateur permet de préserver la durée de vie de ces composants et conserver plus longtemps l\'intégrité du périphérique utilisateur.', 'Réalisation', 'Les mécanismes d\'optimisation sont-ils disponibles (Firmware) pour optimiser la durée de vie du matériel (batteries/capteurs...) ?', NULL, NULL, NULL, NULL, 'RECO', '*', NULL, NULL, '#VOL ; #SEC ; #USE ; #VALUE', 'Dev ; ProjectMngr ; ITMngr ;', '(X / Y ) * 100', 'Capteurs mis en pause hors utilisation', 'Capteurs référencés', 'Low', 'OnUpdate', 'L\'analyse des codes source couvrent la recherche des modes d\'utilisation des capteurs', NULL, NULL, NULL, NULL, NULL, NULL),
(371, 'FRONTEND', 'FRT-7.C01', 'A', 'B', 'A', 'Limiter les utilisations de composants hardware ou capteurs sur les terminaux utilisateurs', 'L\'inaccessibilité d\'un capteur, lié é l\'équipement ou au choix de l\'utilisateur, est-elle traitée pour permettre au service d\'étre néanmoins fonctionnel (si possible) ?', 'L\'inaccessibilité de composants physiques du périphérique utilisateur doit étre adressée pour permettre é des matériels anciens, ou partiellement fonctionnels d\'exécuter le service. Certains cas d\'indisponibilité de capteurs peuvent étre simplement traités par des procédures de réinitialisation ou par des approximations logicielles.', 'Utilisation', 'Les procédures de réinitialisation physique des capteurs sont-elles disponibles ?', NULL, NULL, 'Critéres RGAA 13.10 ; 13.12', NULL, 'CONSEIL', 'N/A', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(372, 'FRONTEND', 'FRT-7.C02', 'A', 'B', 'A', 'Limiter les utilisations de composants hardware ou capteurs sur les terminaux utilisateurs', 'Des algorithmes prédictifs sont-ils mis en place pour éviter l\'utilisation de capteurs physiques ?', 'Les capteurs physiques fournissent des données réputées exactes et précises, la majorité des services n\'ont que trés rarement besoin d\'une précision absolue en permanence. Les algorithmes prédictifs peuvent soulager l\'utilisation des capteurs et rendre le service néanmoins tout é fait efficace.', 'Réalisation', 'Quelle perte de précision est acceptée par les métiers ?', NULL, NULL, NULL, NULL, 'CONSEIL', 'N/A', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(373, 'FRONTEND', 'FRT-7.03', 'B', 'A', 'A', 'Limiter les utilisations de composants hardware ou capteurs sur les terminaux utilisateurs', 'Informez-vous les utilisateurs des contraintes de volume et format de fichiers acceptés ?', 'Les opérations sur le front end peuvent nécessiter des transferts de fichiers entre le poste utilisateur et les serveurs de traitement. Les types et volumes de fichiers acceptés doivent étre exposés é l\'utilisateur pour éviter des charges de transfert, des stockages et des traitements qui seraient voués é l\'échec pour cause de non-respect des formats et volumes attendus. La limitation du volume est indispensable pour réduire l\'impact des transferts.', 'Utilisation', 'Les volumes et formats des fichiers sont-ils limités et exposés aux utilisateurs ?', 'Quelle est la taille maximum autorisée ?', NULL, NULL, NULL, 'RECO', '*', NULL, NULL, '#PERF ; #VOL ; #SEC ; #USE ; #COM ; #VALUE', 'Dev ; ProjectMngr ; ITMngr ;', '(X / Y ) * 100', 'Formalisés = 100 ; prévus = 75 ; Identifiés = 50 ; Non = 0', '100', 'High', 'OnUpdate', 'Parcours utilisateurs', NULL, NULL, NULL, NULL, NULL, NULL),
(374, 'FRONTEND', 'FRT-7.C03', 'A', 'A', 'B', 'Limiter les utilisations de composants hardware ou capteurs sur les terminaux utilisateurs', 'La fréquence de l\'animation est-elle adaptée é la fonctionnalité ?', 'La présentation des opérations sur le front end peut pousser é mettre en place des mécanismes dynamiques. Ces traitements requiérent des ressources de traitements. Il est nécessaire de réduire les impacts de ces éléments dynamiques en les évitant ou lorsqu\'ils sont jugés indispensables en réduisant les fréquences des animations.', 'Réalisation', 'Les animations sont-elles par défaut cadencées au plus bas ?', NULL, NULL, NULL, NULL, 'CONSEIL', 'N/A', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(375, 'FRONTEND', 'FRT-7.04', 'B', 'A', 'A', 'Limiter les utilisations de composants hardware ou capteurs sur les terminaux utilisateurs', 'Est-ce que les uploads permettent uniquement la liste des types de fichiers autorisés ?', 'Les fichiers transférés par l\'utilisateur générent des traitements, des flux d\'échange, des espaces de stockage, une redondance pour sécuriser les données, des sauvegardes. Chaque transfert doit donc étre utile pour le service. Et supprimés lorsqu\'ils n\'ont plus d\'intérét.', 'Réalisation', 'Est-ce que les fichiers uploadés sont exploitables ? afin d\'éviter le stockage de fichiers inutiles.', NULL, NULL, NULL, NULL, 'RECO', '*', NULL, NULL, '#PERF ; #VOL ; #SEC ; #USE ; #COM ; #VALUE ; #FIN', 'Dev ; ProjectMngr ; ITMngr ;', NULL, NULL, NULL, 'Medium', 'Periodic', 'Parcours utilisateurs', NULL, NULL, 'Y', '#SCRIPT', '**', 'Outils parser code'),
(376, 'FRONTEND', 'FRT-7.05', 'A', 'B', 'A', 'Limiter les utilisations de composants hardware ou capteurs sur les terminaux utilisateurs', 'Les images é uploader sont-elles bien redimensionnées et converties dans un format léger cété client avant transfert ?', 'Les transferts d\'images introduisent des particularités liées au format des images, certains générent des tailles de fichiers trés différents. Les tailles et niveaux de qualité des images augmentent trés rapidement les volumes nécessaires pour stocker des images. Les transcodages, et redimensionnements peuvent étre réalisés avant les transferts mais nécessitent des ressources de traitements, des espaces de stockage intermédiaires et créent de nouveaux fichiers qui, en l\'absence de nettoyages automatiques, peuvent augmenter le volume de données.', 'Déploiement', 'Y-a-t-il une fonctionnalité de redimensionnement correspondant é l\'usage prévu de l\'image é uploader intégrée é la fonctionnalité d\'upload ?', 'L\'usage prévu des images uploadées est-il bien défini ?', NULL, NULL, NULL, 'RECO', '*', NULL, NULL, '#PERF ; #VOL ; #SEC ; #USE ; #COM ; #VALUE', 'Dev ; ProjectMngr ; ITMngr ;', '(X / Y ) * 100', 'Images sans redimmentionnement', 'Nombre d\'images', 'Low', 'OnUpdate', 'Les outils de développement et la console des navigateurs permettent de tracer les manipulations d\'images et redimensionnements (ex: CHROME : console / network / extension Green IT', NULL, NULL, 'Y', '#PROCESS', '**', 'Outils simulateurs'),
(377, 'FRONTEND', 'FRT-7.06', 'A', 'B', 'A', 'Limiter les utilisations de composants hardware ou capteurs sur les terminaux utilisateurs', 'Les fichiers échangés sont-ils compressés ?', 'Le volume de données transféré augmente l\'empreinte environnementale du service, car les données sont aussi stockées, sécurisées avec de la duplication, sauvegardées. Certains formats de données peuvent étre compressés pour réduire leur volume, l\'opération de compression nécessitant des ressources de calcul et de stockage intermédiaire il est plus efficace que les données soient disponibles sous forme compressée.', 'Déploiement', 'La compression est-elle efficace ?', NULL, NULL, 'Opquast 108/ pidila-437 / greenIT 107', NULL, 'RECO', '*', NULL, NULL, '#PERF ; #VOL ; #SEC ; #USE ;', 'Dev ; ProjectMngr ; ITMngr ;', '(X / Y ) * 100', 'Fichiers compressés', 'Fichiers échangés', 'High', 'OnUpdate', 'Les outils de développement et la console des navigateurs permettent de tracer les flux échangés, leurs volumes et fréquences (ex: CHROME : console / network)', NULL, NULL, 'Y', '#PROCESS', '**', 'Outils simulateurs'),
(378, 'FRONTEND', 'FRT-8', NULL, NULL, NULL, 'Faciliter l\'utilisation du service dans de bonnes conditions pour tous les utilisateurs', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'ODD#10 ; ODD#12 ;', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(379, 'FRONTEND', 'FRT-8.01', 'B', 'A', 'B', 'Faciliter l\'utilisation du service dans de bonnes conditions pour tous les utilisateurs', 'La navigation rapide est-elle possible ?', 'La facilité d\'utilisation et repérage dans le service rend un meilleur service é l\'utilisateur. Il permet aussi é un utilisateur de trouver plus facilement les informations qu\'il cherche, ce qui réduit les navigations parasites et diminue la consommation de ressources et d\'énergie pour des résultats ne correspondant pas au besoin de l\'utilisateur.', 'Utilisation', 'Un fil d\'ariane est-il disponible en permanence ?', NULL, NULL, 'Opquast 5 / GreenIT 7', NULL, 'RECO', '*', NULL, NULL, '#USE ; #COM ; #VALUE ;', 'Dev ; ProjectMngr ; ITMngr ;', '(X / Y ) * 100', 'Formalisés = 100 ; prévus = 75 ; Identifiés = 50 ; Non = 0', '100', 'Low', 'OnUpdate', 'Parcours utilisateurs', NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `goodpractices` (`cle`, `Family`, `Id`, `Planet`, `People`, `Prosperity`, `Recommendation`, `Criteria`, `Justification`, `Stage`, `Tests1`, `Tests2`, `Tests3`, `Correspondence`, `Link`, `Type`, `Difficulty`, `Goal`, `Incontournable`, `Tags`, `Actors`, `Indicators_`, `IndicatorsX`, `IndicatorsY`, `Priority`, `Recurrence`, `UseCase`, `Example`, `Limite`, `Automatizable`, `AutomatizationScript`, `AutomatizationLevel`, `AutomatizationInformation`) VALUES
(380, 'FRONTEND', 'FRT-8.C01', 'C', 'A', 'B', 'Faciliter l\'utilisation du service dans de bonnes conditions pour tous les utilisateurs', 'Est-ce que l\'attention utilisateur est capturée avec son consentement et pour les usages qu\'il attend ?', 'L\'attention de l\'utilisateur est une ressource qui doit étre considérée comme rare et précieuse. Dans cette optique le respect de l\'utilisateur passe aussi par sa capacité é concentrer son attention sur les éléments de son choix, plutét que ceux que le service souhaiterait mettre en avant.', 'Utilisation', 'La matrice de captologie est-elle utilisée ?', NULL, NULL, NULL, NULL, 'CONSEIL', 'N/A', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(381, 'FRONTEND', 'FRT-8.C02', 'C', 'B', 'B', 'Faciliter l\'utilisation du service dans de bonnes conditions pour tous les utilisateurs', 'Les données réglementées (personnelles, santé, financiéres) sont-elles exposées aux utilisateurs ?', 'Les données que l\'utilisateur confie lorsqu\'elles entrent dans un cadre réglementaire doivent étre exposées aux utilisateurs pour qu\'ils puissent les transmettre avec un consentement éclairé.', 'Utilisation', 'Chaque donnée réglementée est-elle identifiée ?', NULL, NULL, NULL, NULL, 'CONSEIL', 'N/A', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(382, 'FRONTEND', 'FRT-9', NULL, NULL, NULL, 'Implémenter des solutions techniques dont l\'impact est le plus faible', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'ODD#12 ; ODD#13', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(383, 'FRONTEND', 'FRT-9.01', 'C', 'A', 'B', 'Implémenter des solutions techniques dont l\'impact est le plus faible', 'Le service est-il adapté aux différents formats de support d\'accessibilité ?', 'Certains outils et mécanismes d\'accessibilité introduisent des contraintes sur les dispositions des écrans (portrait, paysage, loupe). Le service ne doit pas bloquer les possibilité sous peine d\'exclure une parties des utilisateurs.', 'Utilisation', 'Le changement d\'orientation d\'écran est-il permis et géré dans tout le service ?', NULL, NULL, 'Critéres RGAA 13.9', NULL, 'RECO', '**', NULL, NULL, '#USE ; #VALUE ; #LEG', 'Dev ; Designer ; ProductOwner ;', NULL, NULL, NULL, 'Medium', 'OnUpdate', 'RGAA', NULL, NULL, 'Y', '#PROCESS', '**', 'Outils simulateurs'),
(384, 'FRONTEND', 'FRT-9.02', 'A', 'B', 'A', 'Implémenter des solutions techniques dont l\'impact est le plus faible', 'Les différents formats d\'images disponibles ont-ils été évalués pour ne retenir que le plus efficace ?', 'Les services numériques utilisent des images é des fins d\'illustration ou d\'information. Les tailles, degrés de définition, formats d\'encodage ont un impact important sur le volume des fichiers de ces images. La réduction des volumes permet de limiter les impacts environnementaux.', 'Réalisation', 'Quel est le volume total des images du service ?', NULL, NULL, 'Opquast 22 - 76 / GreenIT 36 - 80', NULL, 'RECO', '**', NULL, NULL, '#VOL ; #USE ;  #METH ; #FIN', 'Dev ; ProjectMngr ; ITMngr ;', '(X / Y ) * 100', 'Images analysées', 'Images', 'High', 'OnUpdate', 'Les outils de développement et la console des navigateurs permettent de tracer les flux échangés, leurs volumes et fréquences (ex: CHROME : console / network)', NULL, NULL, 'Y', '#SCRIPT', '*', 'Outils parser code'),
(385, 'FRONTEND', 'FRT-9.03', 'A', 'C', 'B', 'Implémenter des solutions techniques dont l\'impact est le plus faible', 'Les redimensionnements d\'images sont-ils traités en amont et en statique (cété serveur) ?', 'Les services utilisent plusieurs tailles d\'une méme image pour des besoins différents. Le redimensionnement des images, lorsqu\'il est effectué é la demande génére des charges de traitements, des productions de fichiers intermédiaires qui introduisent des déperditions d\'énergie é chaque demande. Il est plus efficace que les images soient disponibles dans les différentes tailles utilisées par le service.', 'Déploiement', 'Chaque image est-elle disponible dans son format utile ?', NULL, NULL, NULL, NULL, 'RECO', '*', NULL, NULL, '#VOL ; #USE ;  #METH ;', 'Dev ; ProjectMngr ; ITMngr ;', '(X / Y ) * 100', 'Images analysées', 'Images', 'High', 'OnUpdate', 'Les outils de développement et la console des navigateurs permettent de tracer les manipulations d\'images et redimensionnements (ex: CHROME : console / network / extension Green IT', NULL, NULL, 'Y', '#PROCESS', '**', 'Outils simulateurs'),
(386, 'FRONTEND', 'FRT-9.C01', 'A', 'B', 'B', 'Implémenter des solutions techniques dont l\'impact est le plus faible', 'Est-ce que la définition de l\'image est réduite et adaptée é son objectif (illustration, contractuel, ...) ?', 'Les images d\'illustration et les images qui portent une information plus contractuelle comme l\'image d\'un produit n\'ont pas les mémes besoins de précisions de qualité. L\'adaptation du niveau de qualité é l\'importance de l\'image permet de réduire l\'impact environnemental des besoins les moins critiques pour le service.', 'Réalisation', 'Quel niveau de qualité est attendu pour chaque image ?', NULL, NULL, NULL, NULL, 'CONSEIL', 'N/A', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(387, 'FRONTEND', 'FRT-9.04', 'B', 'A', 'B', 'Implémenter des solutions techniques dont l\'impact est le plus faible', 'Toutes les images ont-elles une alternative texte ?', 'Les assistants d\'accessibilité doivent pouvoir utiliser une description de l\'information portée par l\'image afin de permettre une bonne compréhension du service sans la vision de l\'image.', 'Réalisation', 'Avez-vous testé le service avec un lecteur d\'accessibilité ?', NULL, NULL, 'critéres RGAA 1.1 é 1.9', NULL, 'RECO', '*', NULL, NULL, '#USE ;  #METH ; #LEG', 'Dev ; ProjectMngr ; ITMngr ;', '(X / Y ) * 100', 'Images analysées', 'Images', 'High', 'OnUpdate', 'Code source / CHROME : Lighthouse / accessibilité', NULL, NULL, 'Y', '#SCRIPT', '*', 'Outils parser code'),
(388, 'FRONTEND', 'FRT-9.C02', 'B', 'C', 'B', 'Implémenter des solutions techniques dont l\'impact est le plus faible', 'Les alternatives de formats d\'images sont-elles mises é disposition via srcset ?', 'La sélection des différentes tailles d\'images disponibles peut étre laissée au navigateur avec une encapsulation des images dans srcset. Le browser va sélectionner le format le plus adapté é son utilisation. Cette technique n\'est pas toujours la plus efficace du point de vue NR et doit étre validée par rapport é des alternatives plus statiques.', 'Réalisation', 'Les gains ont-ils été mesurés avec l\'utilisation de srcset ?', NULL, NULL, NULL, NULL, 'CONSEIL', 'N/A', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Y', '#SCRIPT', '*', 'Outils parser code'),
(389, 'FRONTEND', 'FRT-9.C03', 'B', 'C', 'B', 'Implémenter des solutions techniques dont l\'impact est le plus faible', 'Les sprites CSS sont-ils mis en place pour limiter les flux de récupération d\'images ?', 'Les techniques sprite css, permettent de réduire le nombre de fichiers d\'images en combinant plusieurs images par fichiers; Cette technique ne réduit pas la volumétrie des échanges, mais réduit le nombre de requétes de récupération d\'images. Cela procure un gain d\'efficacité NR si l\'ensemble des images du sprite sont réellement utilisées, dans le cas contraire des volumes de données ont été transférées pour rien.', 'Réalisation', 'Les gains ont-ils été mesurés avec l\'utilisation de sprite CSS ?', NULL, NULL, 'Opquast 22 / GreenIT 20', NULL, 'CONSEIL', 'N/A', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Y', '#SCRIPT', '**', 'Outils parser code'),
(390, 'FRONTEND', 'FRT-9.05', 'B', 'C', 'B', 'Implémenter des solutions techniques dont l\'impact est le plus faible', 'La mise en forme en css est-elle privilégiée, pour limiter l\'usage des images ?', 'Parmi les éléments de présentation utilisés dans les services, les images représentent un volume important, ce qui génére un impact environnemental. Dans certains cas des opérations de mise en forme avec du CSS permettent de se dispenser d\'une image, cette solution plus légére est é privilégier.', 'Réalisation', 'Quelles images peuvent étre remplacées par du CSS ?', NULL, NULL, 'Critére RGAA 10.1', NULL, 'RECO', '**', NULL, NULL, '#VOL ; #USE ;  #METH ;', 'Dev ; ProjectMngr ; ITMngr ;', '(X / Y ) * 100', 'Images analysées', 'Images', 'High', 'OnUpdate', 'Navigation / CHROME network', NULL, NULL, NULL, NULL, NULL, NULL),
(391, 'FRONTEND', 'FRT-9.C04', 'A', 'B', 'B', 'Implémenter des solutions techniques dont l\'impact est le plus faible', 'Est-ce que le lazy load apporte un gain (poids de la page, ressource CPU) ?', 'Lors du chargement du service sur le périphérique utilisateur, toutes les informations n\'ont pas la méme importance. Le chargement différé de certains éléments est souvent tout é fait acceptable, sans vraiment réduire le volume de chargement cela sollicite moins de ressources techniques en méme temps sur les serveurs ce qui réduit le besoin de dimensionnement de ces serveurs.', 'Réalisation', 'Les gains ont-ils été mesurés avec l\'utilisation de lazy load ?', NULL, NULL, 'GreenIT 37', NULL, 'CONSEIL', 'N/A', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Y', '#SCRIPT', '*', 'Outils parser code'),
(392, 'FRONTEND', 'FRT-9.C05', 'B', 'B', 'B', 'Implémenter des solutions techniques dont l\'impact est le plus faible', 'Est-ce que la définition du son est adaptée ?', 'Les sons diffusés par le service, sont caractérisés par leurs durées, le nombre de répétitions, la qualité du son et enfin le type d\'encodage (Codec) utilisé. Les actions sur chacun de ces points vont réduire l\'impact des fichiers sons autant dans le transfert, que le volume des fichiers. Certains Codec trés compressés réduisent de maniére significative le volume de données pour une méme qualité, mais le codage / décodage de ces formats peut nécessiter beaucoup de ressources de traitements, ce qui atténue fortement le bénéfice global du point de vue NR.', 'Réalisation', 'La qualité de son attendue est-elle spécifiée ?', NULL, NULL, NULL, NULL, 'CONSEIL', 'N/A', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(393, 'FRONTEND', 'FRT-9.06', 'A', 'A', 'B', 'Implémenter des solutions techniques dont l\'impact est le plus faible', 'Une version épurée est-elle disponible pour les impressions ?', 'Les flux de dématérialisation sont parfois rompus, et imposent la production d\'écrits sur papiers. L\'utilisateur dans certains cas peut aussi étre plus confortable é utiliser des documents imprimés plutét que 100% électroniques. Les impressions ont un impact environnemental important, que ce soit dans la consommation de papier, de consommables tels que les cartouches et encres d\'impression. Les besoins de présentation en électronique ou en impression sont différents, d\'autre part la limitation de l\'empreinte environnementale doit conduire é réduire la densité et le volume d\'informations imprimées.', 'Utilisation', 'Le format d\'impression est-il spécifié pour réduire l\'espace et l\'encre utilisé ?', NULL, NULL, 'Opquast 30/GreenIT 27 / Pidila 346 - 347', NULL, 'RECO', '*', NULL, 'INCONTOURNABLE', '#VOL ; #USE ;  #METH ;', 'Dev ; ProjectMngr ; ITMngr ;', '(X / Y ) * 100', 'Formalisés = 100 ; prévus = 75 ; Identifiés = 50 ; Non = 0', '100', 'High', 'OnUpdate', 'Les tests de navigation / parcours utilisateur couvrent les impressions et l\'analyse des documents imprimés', NULL, NULL, 'Y', '#SCRIPT', '**', 'Outils parser code'),
(394, 'FRONTEND', 'FRT-9.07', 'B', 'A', 'B', 'Implémenter des solutions techniques dont l\'impact est le plus faible', 'Est-ce bien une action utilisateur qui déclenche le \"play\" ?', 'Les contenus actifs utilisent des ressources techniques pour fonctionner. L\'activité de ces composants doit étre lancée é la demande de l\'utilisateur pour éviter de consommer de l\'énergie inutilement.', 'Utilisation', 'Est-ce que toutes les animations sont en auto start OFF ?', 'Evite-t-on de pré-charger les animations / vidéo?', NULL, NULL, NULL, 'RECO', '*', NULL, NULL, '#PERF ; #VOL ; #USE ;  #METH ;', 'Dev ; ProjectMngr ; ITMngr ;', '(X / Y ) * 100', 'Actions sans auto start', 'Actions user', 'High', 'OnUpdate', 'Navigation', NULL, NULL, 'Y', '#SCRIPT', '**', 'Outils parser code'),
(395, 'FRONTEND', 'FRT-9.08', 'A', 'B', 'B', 'Implémenter des solutions techniques dont l\'impact est le plus faible', 'Les vidéos ou animations hors de la zone active sont-elles automatiquement mises en pause / stoppées ?', 'Les contenus actifs, animations, vidéos, sons lancés par l\'utilisateur peuvent au cours de la navigation ne plus étre visibles sur la zone écran présentée é l\'utilisateur. La poursuite de la diffusion de ces contenus devient inutiles et consomment inutilement des ressources, ils doivent donc étre arrétés.', 'Utilisation', 'Les vidéos et animation en cours sont-elles stoppées lorsqu\'elles sortent de l\'espace vu par l\'utilisateur ?', NULL, NULL, NULL, NULL, 'RECO', '**', NULL, NULL, '#PERF ; #VOL ; #USE ;  #METH ;', 'Dev ; ProjectMngr ; ITMngr ;', '(X / Y ) * 100', 'Vidéos analysées', 'Vidéos', 'High', 'OnUpdate', 'Les tests de navigation / parcours utilisateur couvrent les comportements des objets dynamiques (vidéo, animations, ...) hors de la zone de visualisation', NULL, NULL, NULL, NULL, NULL, NULL),
(396, 'FRONTEND', 'FRT-9.C06', 'A', 'B', 'A', 'Implémenter des solutions techniques dont l\'impact est le plus faible', 'Une vidéo est-elle la seule solution pour l\'illustration attendue ?', 'Les flux vidéo sont dans les usages de service numérique l\'un des éléments qui consomment le plus de ressources de traitement, de volume d\'échange. Les vidéos doivent étre limitées aux cas oé elles apportent une valeur importante. La vidéo n\'est pas accessible pour certains publics et doit donc étre limitée.', 'Conception', 'Quel est le volume total des vidéos du service ?', NULL, NULL, NULL, NULL, 'CONSEIL', 'N/A', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(397, 'FRONTEND', 'FRT-9.C07', 'B', 'C', 'B', 'Implémenter des solutions techniques dont l\'impact est le plus faible', 'Réduisez-vous le nombre de picto utilisés dans le service ?', 'Tous les éléments de présentation ont un poids supérieur au caractéres de l\'alphabet. Chaque fois qu\'un pictogramme est utilisé alors que l\'information qu\'il véhicule pourrait étre présentée différemment le service augmente son volume. De plus, les assistants d\'accessibilité ne permettent pas toujours de restituer ces pictogrammes dans de bonnes conditions.', 'Réalisation', 'Est-ce que les picto attendus sont disponibles dans une font ?', NULL, NULL, NULL, NULL, 'CONSEIL', 'N/A', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Y', '#SCRIPT', '**', 'Outils parser code'),
(398, 'FRONTEND', 'FRT-9.09', 'B', 'C', 'B', 'Implémenter des solutions techniques dont l\'impact est le plus faible', 'Limitez-vous le nombre de fonts chargées pour le service ? Privilégiez-vous les fonts web-safe ?', 'Chaque police est encodée dans un fichier présentant tous les symboles associés ce qui représente un volume de données important échangé et manipulé.  Les polices systémes sont embarquées dans les outils de présentation et ne sont pas véhiculées, les flux sont donc réduits lorsqu\'elles sont utilisées.', 'Réalisation', 'Combien de Font sont utilisées ? L\'ensemble de ces Font sont-elles nécessaires ? sont-elles web-safe ?', NULL, NULL, NULL, NULL, 'RECO', '*', NULL, NULL, '#PERF ; #VOL ; #USE ;  #METH ;', 'Dev ; ProjectMngr ; ITMngr ;', NULL, NULL, NULL, 'High', 'OnUpdate', 'Code source / CHROME console', NULL, NULL, 'Y', '#SCRIPT', '*', 'Outils parser code'),
(399, 'FRONTEND', 'FRT-9.10', 'A', 'B', 'B', 'Implémenter des solutions techniques dont l\'impact est le plus faible', 'En cas d\'impression, une police permettant l\'économie d\'encre est-elle mise en place ?', 'Les flux de dématérialisation sont parfois rompus, et imposent la production d\'écrits sur papier. L\'utilisateur dans certains cas peut aussi étre plus confortable é utiliser des documents imprimés plutét que 100% électroniques. Les impressions ont un impact environnemental important, que ce soit dans la consommation de papier, de consommables tels que les cartouches et encres d\'impression. Les besoins de présentation en électronique ou en impression sont différents. D\'autre part la limitation de l\'empreinte environnementale doit conduire é réduire la densité et le volume d\'information imprimé.', 'Réalisation', 'Quelle police est prise en charge pour l\'impression ?', NULL, NULL, 'Pidila 346', NULL, 'RECO', '**', NULL, NULL, '#PERF ; #VOL ; #USE ;  #METH ;', 'Dev ; ProjectMngr ; ITMngr ;', '(X / Y ) * 100', 'Formalisés = 100 ; prévus = 75 ; Identifiés = 50 ; Non = 0', '100', 'High', 'OnUpdate', 'Des tests d\'impression sont réalisés pour vérifier les consommations d\'encre, papiers et la pertinence des informations imprimées.', NULL, NULL, 'Y', '#SCRIPT', '*', 'Outils parser code'),
(400, 'FRONTEND', 'FRT-9.11', 'B', 'C', 'B', 'Implémenter des solutions techniques dont l\'impact est le plus faible', 'Est-ce que les polices utilisées ne sont pas chargées uniquement pour un nombre réduit d\'objets ?', 'Chaque police est encodée dans un fichier présentant tous les symboles associés ce qui représente un volume de données important échangé et manipulé. Lorsque seuls quelques éléments de la police sont utilisés, le rapport entre le volume total et le volume réellement utile devient mauvais et un impact environnemental est généré pour aucun bénéfice.', 'Réalisation', 'Avez-vous créé des fonts personnalisées pour ne charger que les objets utilisés ?', NULL, NULL, NULL, NULL, 'RECO', '**', NULL, NULL, '#PERF ; #VOL ; #USE ;  #METH ;', 'Dev ; ProjectMngr ; ITMngr ;', '(X / Y ) * 100', 'Polices analysées', 'Polices', 'Low', 'OnUpdate', 'Code source / CHROME console', NULL, NULL, 'Y', '#SCRIPT', '**', 'Outils parser code'),
(401, 'FRONTEND', 'FRT-9.12', 'B', 'A', 'B', 'Implémenter des solutions techniques dont l\'impact est le plus faible', 'Le service est-il développé en accord avec les recommandations RGAA ?', 'Au-delé des aspects purement réglementaires, l\'anticipation de la prise en compte de l\'accessibilité est nécessaire pour assurer une couverture de ce sujet durant toute la durée de vie du service. Anticiper les futurs points de réglements et normes permet de pérenniser le service en limitant les besoins de mises é jour dictées par l\'environnement légal.', 'Conception', 'Quel est le niveau de conformité RGAA ?', 'Les valeurs de contraste sont-elles en accord avec les recommandations RGAA ?', NULL, 'Critéres RGAA 3.2 & 3.3', NULL, 'RECO', '*', NULL, NULL, '#USE ; #COM ; #VALUE ; #LEG', 'Dev ; ProjectMngr ; ProductOwner ;', NULL, NULL, NULL, 'High', 'OnUpdate', 'Déclaration d\'accessibilité', NULL, NULL, NULL, NULL, NULL, NULL),
(402, 'FRONTEND', 'FRT-9.13', 'A', 'B', 'B', 'Implémenter des solutions techniques dont l\'impact est le plus faible', 'Les animations sont-elles nécessaires, souhaitées par l\'utilisateur ?', 'Le traitement des animations consomme des ressources techniques et détourne l\'attention des utilisateurs, de plus les animations sont généralement dépendantes de caractéristiques techniques des terminaux et générent des incompatibilités qui poussent artificiellement au renouvellement des équipements.', 'Conception', 'Quelles animations pourraient étre remplacées par une infographie ?', NULL, NULL, 'Opquast 47 / GreenIT 55', NULL, 'RECO', '*', NULL, NULL, '#PERF ; #VOL ; #USE ; #VALUE; #METH ;', 'Dev ; ProjectMngr ; ITMngr ;', '(X / Y ) * 100', 'Animations analysées', 'Animations', 'High', 'OnUpdate', 'Les parcours utilisateur présentent les objets média et la valeur ajoutée des informations proposées par ces objets', NULL, NULL, NULL, NULL, NULL, NULL),
(403, 'FRONTEND', 'FRT-9.14', 'B', 'B', 'B', 'Implémenter des solutions techniques dont l\'impact est le plus faible', 'Une alternative CSS est-elle disponible pour une animation ?', 'L\'information transmise é l\'utilisateur par les animations peut aussi étre gérée par des mécanismes CSS qui sont moins lourds donc plus économes de ressources.', 'Réalisation', 'Quelles animations pourraient étre remplacées par une alternative CSS ?', NULL, NULL, NULL, NULL, 'RECO', '**', NULL, NULL, '#PERF ; #VOL ; #USE ;  #METH ;', 'Dev ; ProjectMngr ; ITMngr ;', '(X / Y ) * 100', 'Animations analysées', 'Animations', 'High', 'OnUpdate', 'Code source / CHROME console', NULL, NULL, NULL, NULL, NULL, NULL),
(404, 'FRONTEND', 'FRT-10', NULL, NULL, NULL, 'Rechercher la mise en oeuvre de comportements sobres', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'ODD#12 ; ODD#13', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(405, 'FRONTEND', 'FRT-10.01', 'A', 'B', 'A', 'Rechercher la mise en oeuvre de comportements sobres', 'Les objets de cartographie, animations, vidéos sont-ils présentés dans un mode statique ?', 'Les objets de cartographie n\'ont pas toujours une pertinence é étre présentés sous forme d\'objets dynamiques et interactifs. La version statique sous forme d\'image des plans peut apporter é l\'utilisateur suffisamment d\'information sans qu\'il n\'ait besoin d\'interagir avec l\'objet. Les objets de cartographie sont complexes et nécessitent des ressources de traitements et l\'utilisation de capteurs, ce que les versions images des plans évite. L\'utilisateur conserve la possibilité d\'interaction en activant la version dynamique mais uniquement é sa demande.', 'Réalisation', 'Une image statique est-elle affichée plutét qu\'un objet de cartographie, animation, vidéo ?', 'Les éléments de cartographie, animation, vidéo sont-ils proposés sous forme d\'image et ouverts uniquement sur demande de l\'utilisateur ?', NULL, NULL, NULL, 'RECO', '*', NULL, 'INCONTOURNABLE', '#PERF ; #VOL ; #USE ; #VALUE; #METH ;', 'Dev ; ProjectMngr ; ITMngr ;', '(X / Y ) * 100', 'Objets analysés', 'Objets dynamiques', 'High', 'OnUpdate', 'L\'analyse des codes sources et les tests de navigation couvrent la recherche des éléments dynamiques.', NULL, NULL, NULL, NULL, NULL, NULL),
(406, 'FRONTEND', 'FRT-10.C01', 'B', 'B', 'B', 'Rechercher la mise en oeuvre de comportements sobres', 'Le suivi de positionnement peut-il se satisfaire d\'une fréquence plus réduite ?', 'Les opérations techniques réalisées sur le front end peuvent mettre en oeuvre des capteurs physiques du périphérique utilisateur. La sollicitation de ces capteurs, et leurs les processus de contréle peut entraéner une usure prématurée et en cas de panne pousser au renouvellement du périphérique. Certaines techniques logicielle permettent de réduire l\'utilisation des capteurs physiques, ou de s\'assurer qu\'ils sont employés avec le minimum de contraintes.', 'Utilisation', 'La précision de localisation est-elle spécifiée avec une tolérance d\'incertitude ?', NULL, NULL, NULL, NULL, 'CONSEIL', 'N/A', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(407, 'FRONTEND', 'FRT-10.C02', 'B', 'B', 'B', 'Rechercher la mise en oeuvre de comportements sobres', 'Les recherches inutiles sont-elles limitées par les moteurs de recherche locaux ?', 'L\'utilisation de moteurs de recherches locaux dans le service permettent aux utilisateurs d\'accéder plus rapidement aux informations dont ils ont besoin sans parcourir l\'intégralité du service. Ces moteurs de recherche sont donc efficaces d\'un point de vue efficacité environnementale et assistance é l\'utilisateur. En revanche les moteurs de recherche locaux ne disposent pas de toutes les informations pour proposer des réponses adaptées aux utilisateurs et s\'appuient sur des ressources distantes. Il est important de réduire les échanges avec ces ressources distantes afin de ne pas annuler le bénéfice acquis par l\'utilisation du moteur de recherche.', 'Utilisation', 'A partir de combien de lettres les données sont-elles transmises aux moteurs de recherche ou d\'auto-complétion ?', 'Les recherches les plus fréquentes sont-elles mises en cache ?', NULL, 'Opquast 4 / GreenIT 4', NULL, 'CONSEIL', 'N/A', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(408, 'BACKEND', 'BCK-1', NULL, NULL, NULL, 'Réduire l\'impact des données de leur stockage et accés', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'ODD#12 ; ODD#13', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(409, 'BACKEND', 'BCK-1.01', 'A', 'C', 'A', 'Réduire l\'impact des données de leur stockage et accés', 'Est-ce que le nombre de requétes est minimisé (proscrire l\'usage de boucle) ?', 'Chaque échange va consommer des ressources sur l\'émetteur et le destinataire, consommer des flux d\'échange et des données. Il est nécessaire de réduire ces échanges', 'Réalisation', 'Combien de requétes sont utilisées pour un parcours utilisateur ?', NULL, NULL, 'Opquast 71/GreenIT 72', NULL, 'RECO', '*', NULL, 'INCONTOURNABLE', '#PERF ; #VOL ; #SEC ; #USE ; #METH ;', 'Dev ; ProjectMngr ; ITMngr ;', '(X / Y ) * 100', 'Formalisés = 100 ; prévus = 75 ; Identifiés = 50 ; Non = 0', '100', 'High', 'OnUpdate', 'L\'analyse des codes sources couvrent les éléments de performance.', NULL, NULL, 'Y', '#SCRIPT', '**', 'Outils parser code'),
(410, 'BACKEND', 'BCK-1.C01', 'B', 'B', 'B', 'Réduire l\'impact des données de leur stockage et accés', 'Est-ce qu\'une alternative aux requétes SQL est utilisée quand c\'est possible (local storage ou assimilé)?', 'Les moteurs de gestion de bases de données procurent des solutions techniques performantes mais au détriment de mécanismes techniques complexes et donc consommateurs de ressources. Parfois des solutions plus simples apportent un résultat similaire avec une déperdition moindre.', 'Réalisation', 'Quelles données sont candidates é un stockage local ?', NULL, NULL, NULL, NULL, 'CONSEIL', 'N/A', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(411, 'BACKEND', 'BCK-1.02', 'B', 'C', 'B', 'Réduire l\'impact des données de leur stockage et accés', 'Est-ce que les requétes implémentées utilisent les jointures plutét que de multiplier les requétes ?', 'Les mécanismes d\'accés aux données peuvent engendrer des temps de traitement importants et donc une utilisation importante de ressources. Le plus souvent ces charges sont associées é une session utilisateur et donc se multiplient avec l\'augmentation du trafic sur le service. Les moteurs de SGBD proposent plusieurs mécanismes d\'analyse efficaces vis-é-vis de l\'organisation des données et du besoin. Réduire la charge d\'accés aux données pour une session utilisateur est utile d\'un point de vue environnemental et efficace pour les performances techniques du service.', 'Réalisation', 'La performance des jointures est-elle optimisée ?', NULL, NULL, NULL, NULL, 'RECO', '**', NULL, NULL, '#PERF ; #VOL ; #USE ; #MAINT ; #METH', 'Dev ; ProjectMngr ;  ITMngr', NULL, NULL, NULL, 'High', 'OnUpdate', 'Code source - SGBD', NULL, NULL, NULL, NULL, NULL, NULL),
(412, 'BACKEND', 'BCK-1.C02', 'A', 'C', 'B', 'Réduire l\'impact des données de leur stockage et accés', 'Est-ce que les données peuvent étre sauvegardées de maniére incrémentale ?', 'Une donnée est présente dans plusieurs environnements différents, notamment sur chaque sauvegarde. Cela va consommer de l\'espace, des flux de transfert si les sauvegardes sont externalisées. La présence d\'une donnée non modifiée sur tous les jeux de sauvegardes n\'apporte que trés peu de valeur par rapport au coét que cela représente. Les solutions incrémentales de sauvegardes doivent étre mises en oeuvre pour réduire l\'impact de cette sécurisation indispensable.', 'Déploiement', 'Quel est le volume cumulé sur un mois des sauvegardes ?', NULL, NULL, NULL, NULL, 'CONSEIL', 'N/A', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Y', '#PROCESS', '**', 'Outils supervision'),
(413, 'BACKEND', 'BCK-1.03', 'A', 'C', 'A', 'Réduire l\'impact des données de leur stockage et accés', 'Est-ce que la suppression des données obsolétes est gérée ?', 'La réglementation impose de maétriser le cycle de vie des données pour certaines catégories (données personnelles encadrées par le RGPD). Les autres catégories de données ne sont pas prises en compte, ce qui conduit é accumuler les données sans prendre en compte les délais de péremptions, ce qui introduit un accroissement permanent de la volumétrie de données, des volumes de sauvegardes, de ressources consommées par les accés aux données. Au fil du temps, le niveau de précision des données perd de son importance ce qui doit conduire é réduire la volumétrie dans la phase précédant le retrait pur et simple.', 'Fin de Vie', 'Les durées de vie des données sont-elles gérées ?', NULL, NULL, NULL, NULL, 'RECO', '**', NULL, NULL, '#PERF ; #VOL ; #SEC ; #USE ; #MAINT; #METH ;', 'Dev ; ProjectMngr ; ITMngr ;', '(X / Y ) * 100', 'Formalisés = 100 ; prévus = 75 ; Identifiés = 50 ; Non = 0', '100', 'High', 'Periodic', 'L\'analyse d\'impact détermne les actions de réduction des impacts avec les acteurs concernés', NULL, NULL, 'Y', '#PROCESS', '**', 'Outils Configuration Management'),
(414, 'BACKEND', 'BCK-1.04', 'A', 'C', 'A', 'Réduire l\'impact des données de leur stockage et accés', 'Est-ce que les index des bases de données sont cohérents par rapport aux opérations ?', 'L\'organisation des données a un impact fort sur la performance de recherche dans un SGBD. Les régles de structuration des collections de données permettent d\'optimiser ces points. Les moteurs de SGBD disposent aussi d\'indicateurs de performance qui facilitent les démarches d\'optimisation. Moins de temps et de traitement d\'accés aux données engendre moins de consommations d\'énergies et un besoin plus faible de surdimensionnement des serveurs qui hébergent ces moteurs.', 'Administration', 'Les performances des index sont-elles revues périodiquement ?', NULL, NULL, NULL, NULL, 'RECO', '**', NULL, NULL, '#PERF ; #VOL ; #SEC ; #USE ; #MAINT; #METH ;', 'Dev ; ProjectMngr ; ITMngr ;', '(X / Y ) * 100', 'Formalisés = 100 ; prévus = 75 ; Identifiés = 50 ; Non = 0', '100', 'High', 'Periodic', 'L\'administration des SGBD permet de suivre les performances des index et les besoins de restructuration des bases de données', NULL, NULL, 'Y', '#PROCESS', '**', 'Outils supervision'),
(415, 'BACKEND', 'BCK-1.C03', 'B', 'C', 'B', 'Réduire l\'impact des données de leur stockage et accés', 'Une alternative au modéle relationnel est-elle envisagée ?', 'Les modéles relationnels des SGBD offrent des solutions efficaces pour gérer des structures de données trés segmentées, mais en contrepartie ces moteurs de SGBD sont plus consommateurs de ressources techniques. Des modéles alternatifs peuvent étre plus efficaces (NoSQL) pour certaines catégories d\'usage.', 'Déploiement', 'Est-ce qu\'une solution NoSql est mise en place ?', NULL, NULL, NULL, NULL, 'CONSEIL', 'N/A', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(416, 'BACKEND', 'BCK-1.C04', 'B', 'C', 'B', 'Réduire l\'impact des données de leur stockage et accés', 'Est-ce qu\'une solution NoSql est plus efficiente que son équivalent relationnel ?', 'La prédiction de gain de performance entre un modéle relationnel et NoSql est difficile et trés dépendante de l\'organisation et de l\'usage des collections de données. Seule une étude comparative sur un échantillon pourra permettre d\'établir les gains et bénéfices de chaque alternative.', 'Déploiement', 'Quel serait le gain d\'une solution NoSql ?', NULL, NULL, NULL, NULL, 'CONSEIL', 'N/A', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(417, 'BACKEND', 'BCK-1.C05', 'B', 'C', 'B', 'Réduire l\'impact des données de leur stockage et accés', 'Est-ce que les différentes solutions d\'accés aux données (requétes, triggers, procédures stockées) ont été testées ?', 'Les différentes méthodes d\'accés aux données fournies par les moteurs SGBD peuvent produire des consommations de ressources différentes pour un résultat similaire. Parmi ces solutions, les requétes, les procédures stockées, les triggers doivent étre évalués pour déterminer pour chaque usage le meilleur candidat, qui réduira la consommation de ressources.', 'Réalisation', 'Les performances des différentes solutions triggers, procédures stockées, requétes sont-elles disponibles ?', NULL, NULL, NULL, NULL, 'CONSEIL', 'N/A', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(418, 'BACKEND', 'BCK-1.C06', 'A', 'C', 'B', 'Réduire l\'impact des données de leur stockage et accés', 'Est-ce que les clauses EXPLAIN sont utilisées sur les requétes issues des \"Slow query\" pour optimiser les index ?', 'La plupart des moteurs de SGBD disposent en standard d\'un mode de supervision permettant d\'identifier les actions les plus consommatrices de ressources. Les méthodes d\'investigations des traitements identifiés comme \"lourds\" sont aussi fournies par les moteurs afin de donner les axes d\'amélioration possibles pour réduire les délais et charges des opérations.', 'Réalisation', 'Est-ce que les critéres permettant d\'identifier une slow query sont clairement définis?', NULL, NULL, NULL, NULL, 'CONSEIL', 'N/A', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(419, 'BACKEND', 'BCK-1.C07', 'B', 'C', 'B', 'Réduire l\'impact des données de leur stockage et accés', 'Les seuils de détection de slow query sont-ils définis de maniére efficace ?', 'Méme si la structure des données n\'évolue pas, les volumétries vont changer entre la conception et l\'usage du service. Cette volumétrie peut entraéner des phénoménes de charge ou de contentions de ressources qui n\'étaient pas apparents auparavant. Le suivi de l\'évolution des traitements les plus consommateurs est indispensable durant toute la vie du service.', 'Administration', 'Les seuils des slow query sont-ils revus périodiquement ?', NULL, NULL, NULL, NULL, 'CONSEIL', 'N/A', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Y', '#PROCESS', '**', 'Outils supervision'),
(420, 'BACKEND', 'BCK-1.05', 'B', 'C', 'B', 'Réduire l\'impact des données de leur stockage et accés', 'Est-ce que les données \"vivantes\" et \"mortes\" sont gérées de maniére différentes (ex: Stockage lent pour les données \"mortes\") ?', 'Les données d\'historiques doivent étre conservées pour des raisons légales ou de fonctionnalités du service. Les taux d\'utilisation des données inactives ne justifient pas l\'usage de mécanismes de haute performance pour leur stockage. Les mécanismes de stockage les plus performants sont aussi souvent ceux qui sont les plus consommateurs d\'énergie ou qui ont un cycle de renouvellement physique plus court.', 'Fin de Vie', 'Quelle volumétrie de données inactives est conservée ?', NULL, NULL, NULL, NULL, 'RECO', '***', NULL, NULL, '#PERF ; #VOL ; #SEC ; #USE ; #MAINT; #METH ;', 'Dev ; ProjectMngr ; ITMngr ;', '(X / Y ) * 100', 'Formalisés = 100 ; prévus = 75 ; Identifiés = 50 ; Non = 0', '100', 'Low', 'Periodic', 'Analyse d\'impact', NULL, NULL, NULL, NULL, NULL, NULL),
(421, 'BACKEND', 'BCK-1.06', 'B', 'C', 'B', 'Réduire l\'impact des données de leur stockage et accés', 'Est-ce que les données souvent accédées sont disponibles en RAM ?', 'La consommation de ressources de traitement est plus importante lorsque les informations doivent étre collectées sur des supports physiques (disques, réseaux) plutét qu\'en mémoire RAM. Les données les plus fréquemment accédées doivent bénéficier de cette solution pour réduire l\'énergie d\'accés, la performance sera aussi plus élevée.', 'Administration', 'Quel taux d\'accés physique aux données est mesuré ?', NULL, NULL, 'Pidila 436/GreenIT 64/ Opquast 62', NULL, 'RECO', '**', NULL, NULL, '#PERF ; #VOL ; #SEC ; #USE ; #MAINT; #METH ;', 'Dev ; ProjectMngr ; ITMngr ;', NULL, NULL, NULL, 'Medium', 'Periodic', 'Dossier technique', NULL, NULL, NULL, NULL, NULL, NULL),
(422, 'BACKEND', 'BCK-1.07', 'A', 'C', 'B', 'Réduire l\'impact des données de leur stockage et accés', 'Est-ce que les réplications de données entre plusieurs instances de moteur de base de données (Cluster) sont adaptées é la sensibilité et au besoin de disponibilité ?', 'Une méme donnée peut étre dupliquée pour assurer les besoins de disponibilité et de sécurisation. La redondance est efficace pour sécuriser les données, mais toutes les données ne justifient pas cette précaution qui augmente les volumes de stockage et générent des traitements de synchronisation. Seules les données sensibles doivent étre sécurisées avec des mécanismes de redondance les plus efficaces. La mise en cluster de moteurs de SGBD permet de gérer la défaillance d\'un moteur. Si en plus chaque moteur utilisent des stockages redondants (Mirroring, RAID 5, ...) la méme donnée é une empreinte qui peut étre multipliée par 6 (le plus souvent un cluster contient 3 instances pour gérer les quorums en cas de défaillance d\'un noeud).', 'Déploiement', 'Les données dupliquées sont-elles tracées et en rapport avec leur sensibilité ?', NULL, NULL, NULL, NULL, 'RECO', '***', NULL, NULL, '#PERF ; #VOL ; #SEC ; #USE ; #MAINT; #METH ; #FIN', 'Dev ; ProjectMngr ; ITMngr ;', '(X / Y ) * 100', 'Formalisés = 100 ; prévus = 75 ; Identifiés = 50 ; Non = 0', '100', 'High', 'OnUpdate', 'Le dossier technique détermine les sensibilités des données et le mode de redondance é appliquer pour assurer la disponibilité requise', NULL, NULL, NULL, NULL, NULL, NULL),
(423, 'BACKEND', 'BCK-1.08', 'B', 'C', 'B', 'Réduire l\'impact des données de leur stockage et accés', 'Les données ont-elles une date d\'expiration é laquelle elles sont supprimées ?', 'Les spécifications font apparaétre des collections de données des procédures asynchrones ou des téches périodiques indispensables au bon fonctionnement du service. Ces éléments sont le plus souvent invisibles et en l\'absence de spécifications explicites des processus de retrait ils sont rarement pris en compte dans les fins d\'exploitation d\'un service. Chacun de ces éléments doit porter sa propre information de fin de vie.', 'Fin de Vie', 'A quelle fréquence sont vérifiées les dates d\'expiration ?', NULL, NULL, NULL, NULL, 'RECO', '**', NULL, NULL, '#PERF ; #VOL ; #SEC ; #USE ; #MAINT; #METH ;', 'Dev ; ProjectMngr ; ITMngr ;', '(X / Y ) * 100', 'Formalisés = 100 ; prévus = 75 ; Identifiés = 50 ; Non = 0', '100', 'High', 'Periodic', 'Dossier technique', NULL, NULL, 'Y', '#PROCESS', '***', 'Outils supervision'),
(424, 'BACKEND', 'BCK-1.09', 'B', 'A', 'B', 'Réduire l\'impact des données de leur stockage et accés', 'Est-ce que des données sensibles sont collectées ? Sont-elles indispensables ?', 'Les données que les utilisateurs confient sont sous votre responsabilité et les utilisateurs doivent étre tenus informés du soin que vous mettez en oeuvre pour assurer la confidentialité de ces données. Le RGPD impose un cadre pour les données personnelles mais ne couvre pas les autres catégories qui mériteraient les mémes précautions. Le registre des traitements, le PIA du RGPD doivent étre réalisés, mis é jour et validés par le DPO (Délégué é la Protection de Données). Les données échangées avec d\'autres prestataires doivent faire l\'objet d\'une analyse de responsabilité. Limiter la collecte de données sensibles, au-delé du respect de l\'utilisateur, réduit aussi la charge et la responsabilité d\'administration de ces données personnelles', 'Conception', 'Combien de données sont identifiées comme sensibles aux sens RGPD ?', NULL, NULL, NULL, NULL, 'RECO', '*', NULL, NULL, '#SEC ; #USE ; #COM ; #VALUE ; #METH ; #LEG', 'Dev ; ProjectMngr ; ProductOwner ;', '(X / Y ) * 100', 'Données sensibles analysées', 'Données sensibles', 'High', 'OnUpdate', 'Liasse RGPD', NULL, NULL, NULL, NULL, NULL, NULL),
(425, 'BACKEND', 'BCK-1.10', 'B', 'B', 'B', 'Réduire l\'impact des données de leur stockage et accés', 'Est-ce que les données collectées sont réellement utiles ?', 'Les données que les utilisateurs confient sont sous votre responsabilité et les utilisateurs doivent étre tenus informés du soin que vous mettez en oeuvre pour assurer la confidentialité de leurs données. Le RGPD impose un cadre pour les données personnelles mais ne couvre pas les autres catégories qui mériteraient les mémes précautions. L\'empreinte des données du service se répercute tout au long du cycle de vie du service, la réduction du volume de données collectées est indispensable pour limiter ces impacts', 'Utilisation', 'Chaque donnée est-elle associée é un traitement ?', NULL, NULL, NULL, NULL, 'RECO', '*', NULL, NULL, '#VOL ; #USE ; #VALUE ; #METH ;', 'Dev ; ProjectMngr ; ITMngr ;', '(X / Y ) * 100', 'Données analysées', 'Données collectées', 'High', 'OnUpdate', 'Navigation / CHROME : network', NULL, NULL, NULL, NULL, NULL, NULL),
(426, 'BACKEND', 'BCK-1.11', 'A', 'B', 'B', 'Réduire l\'impact des données de leur stockage et accés', 'Est-ce que l\'API fournit des limites, des filtres et la liste des champs é retourner ?', 'Les parcours et besoins des utilisateurs sont trés difficilement anticipables. Une tendance consiste é fournir au front end un maximum de données pour couvrir le maximum de cas d\'utilisation. Cette pratique est inefficace d\'un point de vue NR car la collecte, le traitement et l\'acheminement des données générent une charge et donc une consommation énergétique importante sans certitude que toutes ces données seront effectivement utilisées. L\'empreinte du service doit étre réduite en ne collectant que les données indispensables au fur et é mesure du déroulement du service via les mécanismes API.', 'Réalisation', 'Quel volume maximum est retourné par les API ?', 'Est-ce que la pagination est gérée dans les appels API ?', 'La volumétrie des échanges API est-il borné ?', 'Opquast 73 74 /GreenIT 74 75', NULL, 'RECO', '**', NULL, NULL, '#VOL ; #USE ; #VALUE ; #METH ;', 'Dev ; ProjectMngr ; ITMngr ;', '(X / Y ) * 100', 'Données API analysées', 'Données API collectées', 'High', 'OnUpdate', 'L\'analyse des codes sources, recherche les paramétrages des appels API sans définitions de limites. Les outils de développement et la console des navigateurs permettent de tracer les flux échangés, leurs volumes et fréquences (ex: CHROME : console / network)', NULL, NULL, 'Y', '#SCRIPT', '**', 'Outils parser code'),
(427, 'BACKEND', 'BCK-1.12', 'B', 'A', 'A', 'Réduire l\'impact des données de leur stockage et accés', 'Est-ce que les données sensibles des utilisateurs sont sécurisées ?', 'Le RGPD impose l\'établissement d\'un PIA, celui-ci doit étre réellement suivi et intégré dans les processus qualité. En préalable, toutes les solutions de sécurisation des données sensibles doivent étre déployées et validées.', 'Déploiement', 'Quelles procédures de sécurisation sont associées aux données sensibles ?', NULL, NULL, NULL, NULL, 'RECO', '**', NULL, NULL, '#SEC ; #USE ; #COM ; #VALUE ; #METH ; #LEG', 'Dev ; ProjectMngr ; ITMngr ;', '(X / Y ) * 100', 'Données sensibles analysées', 'Données sensibles', 'High', 'OnUpdate', 'Analyse de risques', NULL, NULL, NULL, NULL, NULL, NULL),
(428, 'BACKEND', 'BCK-1.C08', 'C', 'B', 'B', 'Réduire l\'impact des données de leur stockage et accés', 'Les données réglementées (personnelles, santé, financiéres) sont-elles conformes aux recommandations de structuration de ces catégories de données ?', 'Le cadre réglementaire de certaines catégories de données est trés strict, le respect de ces données pour protéger les utilisateurs est un critére important. Il est indispensable que dans le processus de développement aucune bréche ne soit possible et que toutes les interopérabilités avec les mécanismes standards de ces domaines soient validées.', 'Déploiement', 'Combien de données réglementées sont utilisées ?', NULL, NULL, NULL, NULL, 'CONSEIL', 'N/A', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(429, 'BACKEND', 'BCK-2', NULL, NULL, NULL, 'Utiliser les composants techniques qui améliorent les aspects NR, sécurité et performance', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'ODD#9 ; ODD#12 ; ODD#13', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(430, 'BACKEND', 'BCK-2.C01', 'B', 'B', 'B', 'Utiliser les composants techniques qui améliorent les aspects NR, sécurité et performance', 'Est-ce que le serveur web utilisé est asynchrone et multi-thread ?', 'Les architectures physiques des serveurs disposent de mécanismes permettant d\'optimiser les gestions de threads. Les logiciels doivent étre en mesures d\'exploiter ces fonctionnalités pour optimiser l\'utilisation des ressources physiques pour limiter les besoins de sur-dimensionnement des équipements pour traiter la charge opérationnelle des services.', 'Déploiement', 'Quel serveur http est utilisé ?', NULL, NULL, 'GreenIT 97 99', NULL, 'CONSEIL', 'N/A', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Y', '#SCRIPT', '*', 'Outils supervision'),
(431, 'BACKEND', 'BCK-2.C02', 'B', 'B', 'B', 'Utiliser les composants techniques qui améliorent les aspects NR, sécurité et performance', 'Est-ce que l\'on a évalué l\'arbre de dépendance des composants intégrés ?', 'Bien souvent les composants issus de bibliothéques publiques (open source ou commerciales) apportent un ensemble de fonctionnalités qui ne sont que rarement toutes utilisées et drainent des dépendances vis-é-vis d\'autres composants avec les mémes caractéristiques. Au final le projet agrége un volume de bibliothéques important alors qu\'une petite partie est réellement utilisée. Privilégier les composants dont les dépendances et fonctionnalités sont pilotables par rapport aux besoins est efficace du point de vue NR.', 'Déploiement', 'Le graphe de dépendance est-il généré ?', 'La possibilité de limiter les dépendances fait-elle partie des critéres de sélection des solutions techniques ?', NULL, NULL, NULL, 'CONSEIL', 'N/A', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Y', '#SCRIPT', '*', 'Outils contréle de dépendances'),
(432, 'BACKEND', 'BCK-2.C03', 'B', 'B', 'B', 'Utiliser les composants techniques qui améliorent les aspects NR, sécurité et performance', 'Est-ce que l\'on peut réduire les dépendances avec un composant alternatif ?', 'Les arbres de dépendance donnent la cartographie des composants et leurs relations. Un arbre de dépendance le plus réduit possible est efficace car il réduit les volumes de code, mais cela ne présume pas que chaque branche de cet arbre de dépendance est justifiée par une utilisation effective.', 'Acquisition', 'Le graphe de dépendance est-il disponible pour chaque composant ?', NULL, NULL, NULL, NULL, 'CONSEIL', 'N/A', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(433, 'BACKEND', 'BCK-2.C04', 'B', 'B', 'B', 'Utiliser les composants techniques qui améliorent les aspects NR, sécurité et performance', 'Est-ce que l\'utilisateur est informé d\'un traitement en cours en arriére-plan ?', 'Certains traitements sont invisibles du point de vue utilisateur, mais peuvent produire des délais de réponse incompris et inciter l\'utilisateur é renouveler ses opérations. La multiplication des demandes de traitements inutiles dégradent les performances NR.', 'Utilisation', 'La liste des traitements qui ne nécessitent pas un résultat immédiat est-elle établie ?', NULL, NULL, NULL, NULL, 'CONSEIL', 'N/A', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(434, 'BACKEND', 'BCK-2.C05', 'B', 'B', 'B', 'Utiliser les composants techniques qui améliorent les aspects NR, sécurité et performance', 'Est-ce que l\'intégration d\'un traitement asynchrone a été évaluée ?', 'Le traitement différé d\'opérations permet de diluer sur une période de temps plus longue les demandes de charges sur les systémes.', 'Réalisation', 'L\'impact de délai est-il évalué ?', NULL, NULL, NULL, NULL, 'CONSEIL', 'N/A', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `goodpractices` (`cle`, `Family`, `Id`, `Planet`, `People`, `Prosperity`, `Recommendation`, `Criteria`, `Justification`, `Stage`, `Tests1`, `Tests2`, `Tests3`, `Correspondence`, `Link`, `Type`, `Difficulty`, `Goal`, `Incontournable`, `Tags`, `Actors`, `Indicators_`, `IndicatorsX`, `IndicatorsY`, `Priority`, `Recurrence`, `UseCase`, `Example`, `Limite`, `Automatizable`, `AutomatizationScript`, `AutomatizationLevel`, `AutomatizationInformation`) VALUES
(435, 'BACKEND', 'BCK-2.01', 'A', 'B', 'A', 'Utiliser les composants techniques qui améliorent les aspects NR, sécurité et performance', 'Est-ce que les ressources inutilisées sont bien libérées au plus vite ?', 'Les techniques de développement affranchissent les développeurs de l\'allocation et la libération explicite des ressources utilisées. Néanmoins le volume des ressources est conditionné par la portée des informations et l\'utilisation généralisée de données é portée globale va entraéner une surcharge de consommation qui peut conduire é un sur-dimensionnement des équipements alors qu\'une utilisation plus rationnelle permettrait de s\'en dispenser.', 'Fin de Vie', 'Les données locales sont-elles libérées dés qu\'elles ne sont plus utiles ?', NULL, NULL, NULL, NULL, 'RECO', '***', NULL, NULL, '#PERF ; #SEC ; #VOL ; #USE ; #METH ;', 'Dev ; ProjectMngr ; ITMngr ;', '(X / Y ) * 100', 'Formalisés = 100 ; prévus = 75 ; Identifiés = 50 ; Non = 0', '100', 'Low', 'OnUpdate', 'L\'analyse des codes source valide la libération des ressources', NULL, NULL, NULL, NULL, NULL, NULL),
(436, 'BACKEND', 'BCK-2.C06', 'B', 'C', 'B', 'Utiliser les composants techniques qui améliorent les aspects NR, sécurité et performance', 'Est-ce qu\'une VM est indispensable par rapport é une solution container ?', 'Les techniques de virtualisation par Machines Virtuelles dupliquent l\'intégralité des systémes d\'exploitation et cloisonnent les accés aux composants physiques. D\'autres techniques é base de containers sont plus économiques en terme de duplication et leur proximité avec les composants physiques permettent une meilleure utilisation et supervision de ces ressources.', 'Déploiement', 'L\'impact NR est-il évalué pour chaque solution ?', NULL, NULL, NULL, NULL, 'CONSEIL', 'N/A', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(437, 'BACKEND', 'BCK-2.02', 'B', 'C', 'B', 'Utiliser les composants techniques qui améliorent les aspects NR, sécurité et performance', 'La disponibilité du service nécessite-t-elle une redondance ?', 'La redondance permet de garantir un niveau de service élevé. Cette solution nécessite beaucoup de duplications des équipements physiques, des réseaux, des stockages. Cette tolérance aux pannes est parfois justifiée par les fonctionnalités du service, et parfois va au-delé des exigences. L\'adaptation du niveau de service et du besoin du service permet de réduire l\'impact environnemental.', 'Déploiement', 'Le SLA est-il défini ?', NULL, NULL, NULL, NULL, 'RECO', '**', NULL, NULL, '#PERF ; #SEC ; #VOL ; #USE ; #FIN', 'Dev ; ProjectMngr ; ITMngr ;', '(X / Y ) * 100', 'Formalisés = 100 ; prévus = 75 ; Identifiés = 50 ; Non = 0', '100', 'High', 'OnUpdate', 'Analyse de risques', NULL, NULL, NULL, NULL, NULL, NULL),
(438, 'BACKEND', 'BCK-2.03', 'A', 'B', 'B', 'Utiliser les composants techniques qui améliorent les aspects NR, sécurité et performance', 'Les interactions entre les composants bénéficient-ils de systémes de cache ?', 'La réduction des volumes d\'échanges, des opérations de calculs, traitements, et accés aux données est un axe d\'optimisation des consommations de ressources. La mise en place de systémes de cache permet de réduire les coéts des opérations répétitives. Les mécanismes de cache peuvent étre déployés é différents points de passage dans un service. Plus le cache est proche du demandeur qu\'il doit servir, plus le volume de données en transit et la longueur du chemin emprunté par les données sont faibles. Plus le cache est proche de la source de données, plus il peut étre mutualisé par un nombre important d\'utilisateurs mais les données seront tout de méme échangées, seules les opérations de traitements et collectes seront réduites.', 'Déploiement', 'Quel systéme de cache est associé aux interactions entre composants ?', NULL, NULL, NULL, NULL, 'RECO', '**', NULL, NULL, '#PERF ; #VOL ; #SEC ; #USE ;', 'Dev ; ProjectMngr ; ITMngr ;', '(X / Y ) * 100', 'Formalisés = 100 ; prévus = 75 ; Identifiés = 50 ; Non = 0', '100', 'High', 'OnUpdate', 'Le dossier technique identifie les caches et la stratégie de cache utilisée pour chaque type de flux. Les outils de gestions des caches (purge / désactivation) sont utilisés pour valider les performances effectives (ex: CHROME : console /network enable - disable cache)', NULL, NULL, 'Y', '#PROCESS', '**', 'Outils supervision'),
(439, 'BACKEND', 'BCK-2.04', 'B', 'C', 'B', 'Utiliser les composants techniques qui améliorent les aspects NR, sécurité et performance', 'Est-ce que le framework ou la technologie utilisée ne bloque pas les caches navigateur ?', 'L\'utilisation de certains framework notamment dans les cas d\'application single page rendent difficiles la gestion du cache sur le périphérique utilisateur. Les technologies employées et les principes de développement doivent limiter ces effets parasites qui vont surcharger les besoins d\'échanges alors qu\'il n\'y a pas lieu.', 'Acquisition', 'Le cache navigateur est-il sollicité ?', NULL, NULL, NULL, NULL, 'RECO', '**', NULL, NULL, '#PERF ; #VOL ;  #USE ;', 'Dev ; ProjectMngr ; ITMngr ;', NULL, NULL, NULL, 'High', 'OnUpdate', 'Dossier technique', NULL, NULL, NULL, NULL, NULL, NULL),
(440, 'BACKEND', 'BCK-2.C07', 'B', 'B', 'B', 'Utiliser les composants techniques qui améliorent les aspects NR, sécurité et performance', 'Existe-t-il un framework/library plus léger répondant au besoin fonctionnel ?', 'A périmétre fonctionnel égal, plusieurs solutions techniques sont envisageables. Plus le framework est réduit, plus son impact environnemental sera réduit car les flux seront minimisés et les consommations de ressources de traitements réduites. Les performances techniques vont aussi dans la méme direction.', 'Acquisition', 'L\'étude des frameworks envisagés est-elle disponible et revue ?', NULL, NULL, NULL, NULL, 'CONSEIL', 'N/A', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(441, 'BACKEND', 'BCK-2.C08', 'B', 'B', 'A', 'Utiliser les composants techniques qui améliorent les aspects NR, sécurité et performance', 'Est-ce qu\'une solution Open Source est disponible pour le besoin fonctionnel ?', 'Une solution Open Source n\'est pas un gage d\'efficacité NR et les différentes alternatives doivent étre challengées. Mais l\'Open Source permet de limiter les ambitions et mainmises de certains acteurs dont les pratiques économiques, sociales sont opaques.', 'Acquisition', 'La performance NR de la solution open source est-elle un critére de choix ?', NULL, NULL, NULL, NULL, 'CONSEIL', 'N/A', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(442, 'BACKEND', 'BCK-2.C09', 'B', 'B', 'B', 'Utiliser les composants techniques qui améliorent les aspects NR, sécurité et performance', 'Est-ce que les versions des composants utilisés sont suivies et appliquées ?', 'Capitaliser sur les meilleures pratiques déployées est un gage d\'efficacité environnementale et opérationnelle. Les avancées techniques étant rapides il est important de propager rapidement tous les bénéfices environnementaux qui pourraient étre intégrés dans un composant, dans le cadre du suivi de l\'état de l\'art et des performances environnementales.', 'Maintenance', 'Quelle est la fréquence de mise é jour des composants ?', NULL, NULL, NULL, NULL, 'CONSEIL', 'N/A', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(443, 'BACKEND', 'BCK-2.05', 'B', 'B', 'B', 'Utiliser les composants techniques qui améliorent les aspects NR, sécurité et performance', 'Est-ce que la sécurisation implémentée est justifiée au regard des opérations réalisées ?', 'L\'implémentation de sécurisation génére une empreinte environnementale supplémentaire qui doit étre en rapport avec l\'aspect critique de l\'opération concernée, plutét que dans un mode de généralisation du niveau de sécurisation de tout le service. Cette segmentation de niveau de sécurisation doit avoir un bénéfice global, et ne pas engendrer de surcoét d\'impact.', 'Déploiement', 'Chaque opération est-elle associée é une exigence de sécurisation ?', NULL, NULL, NULL, NULL, 'RECO', '**', NULL, NULL, '#PERF ; #VOL ; #SEC ; #USE ; #FIN ; #LEG', 'Dev ; ProjectMngr ; ITMngr ;', '(X / Y ) * 100', 'Formalisés = 100 ; prévus = 75 ; Identifiés = 50 ; Non = 0', '100', 'High', 'OnUpdate', 'Analyse de risques', NULL, NULL, NULL, NULL, NULL, NULL),
(444, 'BACKEND', 'BCK-2.C10', 'B', 'C', 'B', 'Utiliser les composants techniques qui améliorent les aspects NR, sécurité et performance', 'Le niveau des logs est-il adapté é l\'environnement ?', 'La mise en oeuvre de services est associée é la production d\'informations permettant son contréle, tels que les événements d\'anomalies, les traces d\'exécution de debug. Ces logs générées automatiquement sont utiles si elles sont réellement exploitées pour fiabiliser le service, mais générent un gaspillage de ressources lorsqu\'elles sont collectées pour étre ignorées.', 'Administration', 'Les niveaux de log de trace et debug sont-ils désactivés en production ?', NULL, NULL, NULL, NULL, 'CONSEIL', 'N/A', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Y', '#SCRIPT', '*', 'Outils contréle des logs'),
(445, 'BACKEND', 'BCK-2.06', 'B', 'C', 'B', 'Utiliser les composants techniques qui améliorent les aspects NR, sécurité et performance', 'Est-ce que les fichiers temporaires sont inclus dans les \"logrotate\" ?', 'Dans les processus d\'exécution des fichiers temporaires sont produits. Ces fichiers inutiles, aprés leur utilisation trés ponctuelle, doivent étre supprimés des espaces de stockage. Les mécanismes de nettoyages de ces fichiers temporaires sont disponibles sur tous les systémes d\'exploitation et doivent étre mis en oeuvre.', 'Déploiement', 'Les suppressions automatiques des fichiers temporaires sont-elles gérées ?', NULL, NULL, NULL, NULL, 'RECO', '*', NULL, NULL, '#VOL ; #METH ; #FIN', 'Dev ; ProjectMngr ; ITMngr ;', '(X / Y ) * 100', 'Fichiers temporaires surveillés', 'fichiers temporaires', 'Low', 'Periodic', 'File system', NULL, NULL, 'Y', '#SCRIPT', '*', 'Outils fichiers temporaires'),
(446, 'BACKEND', 'BCK-2.07', 'B', 'B', 'B', 'Utiliser les composants techniques qui améliorent les aspects NR, sécurité et performance', 'Les fonctionnalités sont-elles documentées pour permettre leur réutilisation ?', 'D\'une part dans une organisation les projets présentent beaucoup de similitudes, d\'autre part les aspects NR adressés dans les projets sont souvent du méme ordre. Afin de capitaliser sur ces éléments et bénéficier des efforts produits, il est nécessaire de s\'appuyer sur un socle de connaissances stables. Toutefois la maturité d\'une organisation d\'un point de vue NR est amenée é progresser, il faut donc que les étapes documentées puissent aussi suivre ces évolutions et participer activement é cet accroissement de maturité.', 'Revalorisation', 'Quelles informations de conception sont consignées pour étre réutilisées dans les autres projets ?', NULL, NULL, NULL, NULL, 'RECO', '*', NULL, 'INCONTOURNABLE', '#MAINT ; #COM ; #VALUE ; #DEBT ; #METH ; #FIN ;', 'Dev ; ProjectMngr ; ITMngr ;', '(X / Y ) * 100', 'Formalisés = 100 ; prévus = 75 ; Identifiés = 50 ; Non = 0', '100', 'Low', 'OnUpdate', 'La documentation technique intégre des éléments NR', NULL, NULL, NULL, NULL, NULL, NULL),
(447, 'BACKEND', 'BCK-3', NULL, NULL, NULL, 'Limiter la volumétrie des échanges', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'ODD#12 ; ODD#13', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(448, 'BACKEND', 'BCK-3.01', 'A', 'B', 'A', 'Limiter la volumétrie des échanges', 'Est-ce que les données échangées sont compressées / minifiées avant transmission ?', 'Les échanges indispensables doivent permettre de réduire la volumétrie des transferts. La minification des échanges, la compression des données échangées permettent de réduire ces volumes. Toutefois les opérations de compression et décompression nécessitent des exécutions de traitements qui peuvent annuler le bénéfice de la compression des échanges.', 'Déploiement', 'Quel gain est obtenu par compression ?', 'Les données de production sont-elles systématiquement minifiées ?', NULL, 'Pidila 437', NULL, 'RECO', '*', NULL, 'INCONTOURNABLE', '#PERF ; #VOL ;  #USE ;  #METH ;', 'Dev ; ProjectMngr ; ITMngr ;', '(X / Y ) * 100', 'Volume compressé', 'Volume de transfert', 'High', 'OnUpdate', 'Les outils/consoles de développement et les extensions des navigateurs permettent de vérifier les bonnes pratiques (ex : CHROME - Extension greenIT / bonnes pratiques)', NULL, NULL, 'Y', '#PROCESS', '**', 'Outils supervision'),
(449, 'BACKEND', 'BCK-3.02', 'A', 'B', 'A', 'Limiter la volumétrie des échanges', 'Est-ce que la pagination est utilisée sur les échanges de données ?', 'Les capacité de restitution d\'information des périphériques utilisateur sont limitées. D\'autre part la capacité d\'assimilation de données par l\'utilisateur est aussi contrainte. Il serait inefficace que le volume de données transmis soit au-delé de ces limites, cela conduirait é un gaspillage de l\'utilisation de ressources de traitement.', 'Réalisation', 'Les volumes de données transmis sont-ils en rapport avec les capacités de présentation du périphérique utilisateur ?', NULL, NULL, NULL, NULL, 'RECO', '**', NULL, NULL, '#PERF ; #VOL ;  #USE ;  #METH ;', 'Dev ; ProjectMngr ; ITMngr ;', '(X / Y ) * 100', 'Données API analysées', 'Données API échangées', 'High', 'OnUpdate', 'L\'analyse des codes sources, recherche les paramétrages des appels API sans définitions de limites. Les outils de développement et la console des navigateurs permettent de tracer les flux échangés, leurs volumes et fréquences (ex: CHROME : console / network)', NULL, NULL, 'Y', '#SCRIPT', '**', 'Outils parser code'),
(450, 'BACKEND', 'BCK-3.03', 'A', 'B', 'A', 'Limiter la volumétrie des échanges', 'Est-ce que le nombre de requétes entre le client et le serveur est minimisé ?', 'Les opérations réalisées sur le front end générent des requétes vers les services distants. Plus le nombre de requétes est important plus les mécanismes de traitements et d\'échanges vont consommer de ressources. Le périmétre et la conception des requétes du back end doivent permettre de limiter le nombre de requétes.', 'Réalisation', 'Combien de requétes sont nécessaires pour un parcours utilisateur ?', NULL, NULL, 'Opquast 9 / GreenIT 9', NULL, 'RECO', '**', NULL, NULL, '#PERF ; #VOL ; #SEC ; #USE ; #METH ;', 'Dev ; ProjectMngr ; ITMngr ;', '(X / Y ) * 100', 'Requétes analysées', 'Requétes BO', 'High', 'OnUpdate', 'L\'analyse des codes sources, recherche des appels API. Les outils de développement et la console des navigateurs permettent de tracer les flux échangés, leurs volumes et fréquences (ex: CHROME : console / network)', NULL, NULL, 'Y', '#PROCESS', '**', 'Outils supervision'),
(451, 'BACKEND', 'BCK-3.04', 'B', 'B', 'B', 'Limiter la volumétrie des échanges', 'Est-ce que les données échangées correspondent uniquement au besoin immédiat ?', 'Le périmétre et la conception des requétes du back end doivent permettre de limiter le volume de données échangées pour satisfaire au fur et é mesure de son parcours, les besoins utilisateurs sans anticiper sur des prédictions d\'usage hypothétiques.', 'Réalisation', 'Les requétes ont-elles une portée en rapport avec l\'usage ?', NULL, NULL, NULL, NULL, 'RECO', '**', NULL, NULL, '#PERF ; #VOL ; #SEC ; #USE ; #VALUE ; #METH ;', 'Dev ; ProjectMngr ; ITMngr ;', '(X / Y ) * 100', 'Données API analysées', 'Données API échangées', 'High', 'OnUpdate', 'Navigation / CHROME : network', NULL, NULL, NULL, NULL, NULL, NULL),
(452, 'BACKEND', 'BCK-4', NULL, NULL, NULL, 'Utiliser des principes de développement qui réduisent les impacts', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'ODD#12 ; ODD#13', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(453, 'BACKEND', 'BCK-4.01', 'B', 'B', 'B', 'Utiliser des principes de développement qui réduisent les impacts', 'Les notifications utilisateurs sont-elles nécessaires ?', 'Les méthodes de notifications peuvent employer des technologies consommatrices de ressources et certaines pratiques visent é les multiplier ce qui augmente l\'empreinte environnementale du service. Réduire les notifications, utiliser des mécanismes légers, et permettre é l\'utilisateur de désactiver ces notifications est utile pour réduire les consommations d\'énergie ainsi que le détournement d\'attention des utilisateurs.', 'Utilisation', 'Quelles solutions de notifications sont utilisées ?', NULL, NULL, NULL, NULL, 'RECO', '**', NULL, NULL, '#VOL ; #USE ;  #COM ; #VALUE ;', 'Dev ; ProjectMngr ; ProductMngr ;', '(X / Y ) * 100', 'Notifications analysées', 'Notifications', 'Medium', 'OnUpdate', 'Parcours utilisateurs', NULL, NULL, NULL, NULL, NULL, NULL),
(454, 'BACKEND', 'BCK-4.C01', 'B', 'A', 'B', 'Utiliser des principes de développement qui réduisent les impacts', 'Est-ce que le SSO est implémenté lorsqu\'il est possible ?', 'La réduction des opérations d\'identification utilisateur permet d\'alléger la charge de traitement et de sécurisation.', 'Utilisation', 'Les phases d\'identification sont-elles simplifiées ?', NULL, NULL, NULL, NULL, 'CONSEIL', 'N/A', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(455, 'BACKEND', 'BCK-4.C02', 'B', 'B', 'B', 'Utiliser des principes de développement qui réduisent les impacts', 'Quel serait mon réflexe de dev, pour prendre en compte le cycle de vie ?', 'La prise en compte du cycle doit étre intégrée dans les phases de développement. Les développeurs sont les principaux acteurs qui, par leurs choix, leurs pratiques vont conditionner les résultats futurs. La prise en compte du cycle de vie dans le développement est indispensable. Il serait plus coéteux, plus risqué et moins efficace de traiter les aspects de cycle de vie en aval.', 'Réalisation', 'Quelles procédures sont disponibles pour traiter le cycle de vie ?', NULL, NULL, NULL, NULL, 'CONSEIL', 'N/A', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(456, 'BACKEND', 'BCK-4.C03', 'B', 'A', 'B', 'Utiliser des principes de développement qui réduisent les impacts', 'Chaque partie-prenante a-t-elle la latitude de prendre des décisions pouvant influer sur l\'impact environnemental ?', 'Les orientations des développement peuvent évoluer au fil du temps. Ces évolutions doivent systématiquement prendre en compte les aspects NR. Les adaptations sont le plus souvent le fait des métiers. Des contraintes techniques qui apparaissent au cours du développement et engendrant des impacts environnementaux doivent pouvoir aussi étre traités par les équipes techniques avec des chemins de validation pré-définis.', 'N/A', 'Le circuit et l\'autonomie de décision ayant un impact NR est-il défini pour chaque acteur ?', NULL, NULL, NULL, NULL, 'CONSEIL', 'N/A', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(457, 'BACKEND', 'BCK-4.02', 'C', 'B', 'A', 'Utiliser des principes de développement qui réduisent les impacts', 'L\'analyse de code est-elle utilisée dans le processus d\'intégration continue?', 'La standardisation et la vérification de conformité des codes produits permet d\'identifier le respect des recommandations NR et d\'affiner le suivi de ces pratiques.', 'Déploiement', 'Tous les codes produits sont-ils analysés ?', 'Est-ce qu\'un outil d\'analyse de qualité du code a été intégré dans le processus d\'intégration continue ?', 'L\'étape de validation est-elle incontournable dans le processus de livraison ?', NULL, NULL, 'RECO', '***', NULL, NULL, '#PERF ; #VOL ; #SEC ; #MAINT ; #VALUE ; #DEBT ; #METH ;', 'Dev ; ProjectMngr ; ITMngr ;', '(X / Y ) * 100', 'Lignes analysées', 'Lignes de code', 'High', 'OnUpdate', 'Intégration continue', NULL, NULL, NULL, NULL, NULL, NULL),
(458, 'BACKEND', 'BCK-4.03', 'C', 'B', 'A', 'Utiliser des principes de développement qui réduisent les impacts', 'Est-ce que les métriques de qualité du code sont suivies ?', 'La qualité du logiciel produit é un impact direct sur les aspects NR. Les défauts entraénent de multiples livraisons de nouvelles versions successives, les anomalies entraénent des productions de traces d\'anomalies importantes, et des fonctionnalités plus consommatrices de ressources techniques, parfois pour un résultat non atteint, donc des ressources ont été gaspillées pour ne produire aucun résultat.', 'Déploiement', 'Les défauts sont-ils tracés et revus avec leurs impacts sur le NR ?', NULL, NULL, NULL, NULL, 'RECO', '***', NULL, NULL, '#PERF ; #VOL ; #SEC ; #MAINT ; #VALUE ; #DEBT ; #METH ;', 'Dev ; ProjectMngr ; ITMngr ;', '(X / Y ) * 100', 'Formalisés = 100 ; prévus = 75 ; Identifiés = 50 ; Non = 0', '100', 'High', 'Periodic', 'Intégration continue', NULL, NULL, NULL, NULL, NULL, NULL),
(459, 'BACKEND', 'BCK-4.C04', 'B', 'B', 'B', 'Utiliser des principes de développement qui réduisent les impacts', 'Les rapports de tests des fonctionnalités sont-ils disponibles et suivis ?', 'La couverture des tests fonctionnels et la qualité gagnée par la prise en compte de tous les cas de tests permet de fiabiliser les développements, et d\'éviter des fonctionnements logiciels inefficaces qui consomment des ressources techniques sans rendre la fonctionnalité et génére de multiples mises é jour. A l\'insatisfaction des utilisateurs s\'ajoute la frustration des équipes de développement qui doivent traiter la non-qualité sans pouvoir progresser dans leurs métiers.', 'Déploiement', 'Les améliorations NR identifiées sont-elles systématiquement prises en compte ?', NULL, NULL, NULL, NULL, 'CONSEIL', 'N/A', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Y', '#PROCESS', '**', 'Outils CI / CD'),
(460, 'BACKEND', 'BCK-4.C05', 'A', 'B', 'A', 'Utiliser des principes de développement qui réduisent les impacts', 'Est-ce que les outils de suivi des performances NR ont été identifiés et mis é disposition des développeurs ?', 'Les avancées techniques du NR vont permettre de fournir des outils facilitant l\'implémentation et le suivi des performances NR. Les  outils référencés pour assister les développements vont se multiplier avec l\'augmentation de maturité NR de l\'ensemble des organisations. Les premiers outils disponibles étaient orientés sur les usages du point de vue utilisateur, les seuls outils pour les traitements cété serveurs sont ceux utilisés pour le suivi des performances techniques. Les performances techniques et NR vont en majorité dans le méme sens.', 'Déploiement', 'La liste des outils est-elle publiée ?', NULL, NULL, NULL, NULL, 'CONSEIL', 'N/A', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(461, 'BACKEND', 'BCK-4.C06', 'A', 'B', 'A', 'Utiliser des principes de développement qui réduisent les impacts', 'Une analyse du profil de consommation de l\'application a-t-elle été effectuée ?', 'La consommation de ressources des codes peut étre analysée avec des outils qui tracent l\'ensemble des appels et les coéts unitaires et globaux de chaque fonctions ou méthodes. Ce profil de consommation est un bon point de départ pour réduire la consommation du service. En fonction des usages, le profil peut étre amené é changer est doit faire l\'objet de suivis réguliers.', 'Administration', 'A quelle fréquence est revue cette analyse ?', NULL, NULL, NULL, NULL, 'CONSEIL', 'N/A', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Y', '#PROCESS', '**', 'Outils analyse de performance'),
(462, 'BACKEND', 'BCK-4.C07', 'B', 'B', 'B', 'Utiliser des principes de développement qui réduisent les impacts', 'Est-ce que chaque version déployée suit le méme processus de qualification des performances NR ?', 'Chaque mise é jour peut améliorer l\'impact environnemental du service ou l\'annuler si ces principes NR ne sont pas systématiquement appliqués é chaque opération de maintenance applicative. Pour garder la cohérence et capitaliser sur les efforts et bénéfices réalisés, les phases de maintenance doivent suivre les mémes démarches', 'Maintenance', 'Combien de mises é jour sont déployées par année ?', 'Les processus de développement sont-ils identiques aux processus de mise é jour ?', NULL, NULL, NULL, 'CONSEIL', 'N/A', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(463, 'BACKEND', 'BCK-5', NULL, NULL, NULL, 'Contréler les aspects réglementaires', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'ODD#10 ; ODD#12', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(464, 'BACKEND', 'BCK-5.01', 'C', 'B', 'A', 'Contréler les aspects réglementaires', 'Est-ce que la cartographie des traitements (RGPD) est disponible ?', 'Les données que les utilisateurs confient sont sous votre responsabilité et les utilisateurs doivent étre tenus informés du soin que vous mettez en oeuvre pour assurer la confidentialité de ces données. Le registre des traitements, le PIA du RGPD, doit étre réalisé, mis é jour et validé par le DPO (Délégué é la Protection des Données). Les données échangées avec d\'autres prestataires doivent faire l\'objet d\'une analyse de responsabilité. Limiter la collecte de données sensibles, au-delé du respect de l\'utilisateur, réduit aussi la charge et la responsabilité d\'administration de ces données personnelles.', 'Conception', 'Les traitements sont-ils validés par le DPO ?', NULL, NULL, NULL, NULL, 'RECO', '**', NULL, NULL, '#SEC ; #METH ; #LEG', 'ProjectMngr ;  Manager ;  ITMngr ;', NULL, NULL, NULL, 'High', 'OnUpdate', 'Liasse RGPD', NULL, NULL, NULL, NULL, NULL, NULL),
(465, 'BACKEND', 'BCK-5.02', 'C', 'B', 'A', 'Contréler les aspects réglementaires', 'Est-ce que l\'analyse de risques (RGPD) est réalisée ?', 'Les données que les utilisateurs confient sont sous votre responsabilité et les utilisateurs doivent étre tenus informés du soin que vous mettez en oeuvre pour assurer la confidentialité de leurs données. Le registre des traitements, le PIA du RGPD doit étre réalisé, mis é jour et validé par le DPO (Délégué é la Protection des Données). Les données échangées avec d\'autres prestataires doivent faire l\'objet d\'une analyse de responsabilité. Limiter la collecte de données sensibles, au-delé du respect de l\'utilisateur, réduit aussi la charge et la responsabilité d\'administration de ces données personnelles', 'Administration', 'Le PIA est-il validé par le DPO ?', NULL, NULL, NULL, NULL, 'RECO', '***', NULL, NULL, '#SEC ; #METH ; #LEG', 'ProjectMngr ;  Manager ;  ITMngr ;', NULL, NULL, NULL, 'High', 'OnUpdate', 'Liasse RGPD', NULL, NULL, NULL, NULL, NULL, NULL),
(466, 'BACKEND', 'BCK-5.03', 'C', 'A', 'A', 'Contréler les aspects réglementaires', 'Est-ce que le RGAA est pris en compte ?', 'Le non-respect des réglementations est tout d\'abord illégal et peut entraéner des pénalités importantes. Il coupe aussi des utilisateurs méme hors du cadre du handicap de l\'accés au service.', 'Conception', 'Les données personnelles sont-elles identifiées ?', NULL, NULL, 'Loi 2005', NULL, 'RECO', '**', NULL, NULL, '#USE ; #METH ; #LEG', 'ProductOwner ; ProjectMngr ;  Manager ;  ITMngr ;', NULL, NULL, NULL, 'High', 'OnUpdate', 'Audit accessibilité', NULL, NULL, NULL, NULL, NULL, NULL),
(467, 'BACKEND', 'BCK-5.04', 'C', 'B', 'A', 'Contréler les aspects réglementaires', 'Est-ce que les données réglementées (bancaires, santé, ...)  sont sécurisées ?', 'Le cadre réglementaire de certaines catégories de données est trés strict. Le respect de ces données pour protéger les utilisateurs est un critére important. Il est indispensable que dans le processus de développement aucune bréche ne soit possible et que toutes les interopérabilités avec les mécanismes standards de ces domaines soient validées.', 'Déploiement', 'Est-ce que le systéme de paiement est conforme aux normes de sécurité en vigueur ?', NULL, NULL, NULL, NULL, 'RECO', '**', NULL, NULL, '#SEC ; #METH ; #LEG', 'ProductOwner ; ProjectMngr ;  Manager ;  ITMngr ;', NULL, NULL, NULL, 'High', 'OnUpdate', 'Analyse de risques', NULL, NULL, NULL, NULL, NULL, NULL),
(468, 'BACKEND', 'BCK-5.C01', 'C', 'A', 'A', 'Contréler les aspects réglementaires', 'Est-ce que des données réglementées sont utilisées ?', 'Le cadre réglementaire de certaines catégories de données est trés strict. Le respect de ces données pour protéger les utilisateurs est un critére important. Il est indispensable que dans le processus de développement aucune bréche ne soit possible et que toutes les interopérabilités avec les mécanismes standards de ces domaines soient validées.', 'Conception', 'Combien de données réglementées sont utilisées ?', NULL, NULL, NULL, NULL, 'CONSEIL', 'N/A', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(469, 'BACKEND', 'BCK-5.05', 'B', 'B', 'B', 'Contréler les aspects réglementaires', 'Le service en cours de conception est-il conforme aux évolutions de la société et de la législation ?', 'Au-delé des aspects purement réglementaires, l\'anticipation de la prise en compte de l\'évolution des attentes sociétales est nécessaire pour assurer une couverture de ce sujet durant toute la durée de vie du service. Anticiper les futurs points de réglement et normes permet de pérenniser le service en limitant les besoins de mises é jour dictées par l\'environnement légal.', 'Conception', 'Le service juridique est-il consulté ?', NULL, NULL, NULL, NULL, 'RECO', '***', NULL, NULL, '#COM ; #VALUE ; #GOV ; #LEG', 'Manager ; RSE/NR', NULL, NULL, NULL, 'Low', 'OnUpdate', 'Analyse de risques', NULL, NULL, NULL, NULL, NULL, NULL),
(470, 'BACKEND', 'BCK-5.06', 'A', 'B', 'A', 'Contréler les aspects réglementaires', 'Le fournisseur d\'infrastructures est-il en mesure d\'exposer ses métriques environnementales ?', 'Dans les dimensions NR, les aspects humains, environnementaux et économiques occupent une place importante. S\'assurer que tous les intervenants y compris chez les prestataires (et ses propres sous-traitants et fournisseurs) utilisés pour le projet disposent des mémes attentions NR est nécessaire pour que la cohérence des efforts NR ne soit pas anéantie par un choix qui aurait pu étre évité.', 'Acquisition', 'Quelles métriques sont collectées ?', NULL, NULL, NULL, NULL, 'RECO', '*', NULL, NULL, '#USE ; #COM ; #VALUE', 'ProjectMngr ; ProductOwner ; Manager ; RSE/NR ;ITMngr', '(X / Y ) * 100', 'Formalisés = 100 ; prévus = 75 ; Identifiés = 50 ; Non = 0', '100', 'High', 'Periodic', 'Les procédures d\'achat référencent les stratégies NR que doivent prendre en compte les fournisseurs.', NULL, NULL, NULL, NULL, NULL, NULL),
(471, 'BACKEND', 'BCK-6', NULL, NULL, NULL, 'Mettre en place une méthodologie qui favorise la prise en compte des aspects NR', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'ODD#10 ; ODD#12 ; ODD#13', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(472, 'BACKEND', 'BCK-6.C01', 'A', 'A', 'A', 'Mettre en place une méthodologie qui favorise la prise en compte des aspects NR', 'Les aspects NR sont-ils propagés et entretenus sur l\'ensemble des acteurs ?', 'Lorsque chacun des acteurs a un accés simple é des marqueurs permettant d\'évaluer l\'impact environnemental de ses actions et choix, il peut adapter ses pratiques pour rendre le service plus efficace et responsable.', 'N/A', 'Combien d\'acteurs du département sont sensibilisés et formés au NR ?', NULL, NULL, NULL, NULL, 'CONSEIL', 'N/A', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(473, 'BACKEND', 'BCK-6.C02', 'A', 'B', 'A', 'Mettre en place une méthodologie qui favorise la prise en compte des aspects NR', 'Un benchmark sur les critéres environnementaux a-t-il été effectué pour le choix du fournisseur d\'infrastructure ?', 'Les fournisseurs en mesure de communiquer leurs critéres environnementaux affichent un niveau de maturité et une orientation importante. Cette capacité doit intervenir dans le choix des fournisseurs.', 'Acquisition', 'Quels seuils d\'acceptabilité sont définis ?', NULL, NULL, NULL, NULL, 'CONSEIL', 'N/A', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(474, 'BACKEND', 'BCK-6.C03', 'A', 'B', 'A', 'Mettre en place une méthodologie qui favorise la prise en compte des aspects NR', 'Est-ce que le processus de conception / les outils intégrent le traitement des exigences NR ?', 'Chaque fonctionnalité mise en oeuvre doit disposer d\'une prise en compte du NR pour que l\'ensemble du service soit conéu avec le respect le plus complet des principes NR, un aspect non couvert pouvant annuler tout ou partie des bénéfices acquis par ailleurs. Il est plus efficace d\'avoir un traitement équilibré sur l\'ensemble, plutét que viser l\'excellence sur une partie et laisser des trous conséquents sur d\'autres.', 'Conception', 'Combien d\'exigences NR sont associées au projet ?', NULL, NULL, NULL, NULL, 'CONSEIL', 'N/A', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(475, 'BACKEND', 'BCK-6.01', 'A', 'B', 'A', 'Mettre en place une méthodologie qui favorise la prise en compte des aspects NR', 'Est-ce que la fonctionnalité envisagée est utile ?', 'La sélection des fonctionnalités jugées indispensables peuvent étre remises en cause dans leur forme initiale par rapport é leur impact NR. Dans ce cas, les coéts / bénéfices devront prendre en compte ces oppositions et faire l\'objet d\'adaptation pour faire converger les 2 approches : nécessité fonctionnelle et impact NR.', 'Conception', 'Chaque fonctionnalité est-elle associée é un usage identifié ?', NULL, NULL, 'Opquast 1 / GreenIT 1', NULL, 'RECO', '*', NULL, 'INCONTOURNABLE', '#PERF ; #VOL ; #SEC ; #USE ; #VALUE ; #METH', 'Dev ; ProjectMngr ; ProductOwner ;', '(X / Y ) * 100', 'Fonctionnalités utilisées', 'Fonctionnalités déployées', 'High', 'Periodic', 'Des données sont identifiées et collectées (analytics / sondes) en production pour identifier les usages et les fonctionnalités superflues.', NULL, NULL, NULL, NULL, NULL, NULL),
(476, 'BACKEND', 'BCK-6.02', 'B', 'B', 'A', 'Mettre en place une méthodologie qui favorise la prise en compte des aspects NR', 'Le dimensionnement des ressources d\'infrastructures suit-il la vie de l\'application ?', 'Le taux d\'utilisation des ressources physiques de l\'infrastructure doit étre suivi pour permettre une meilleure utilisation des matériels disponibles et éviter de déployer des éléments supplémentaires lorsque le taux d\'utilisation n\'est pas complet.', 'Administration', 'La métrologie est-elle définie et mise en place ?', NULL, NULL, NULL, NULL, 'RECO', '**', NULL, NULL, '#PERF ; #VOL ; #SEC ; #USE ; #VALUE ; #FIN ;', 'Dev ; ProjectMngr ; ITMngr ;', '(X / Y ) * 100', 'Formalisés = 100 ; prévus = 75 ; Identifiés = 50 ; Non = 0', '100', 'Medium', 'Periodic', 'Suivi de configuration', NULL, NULL, NULL, NULL, NULL, NULL),
(477, 'BACKEND', 'BCK-6.03', 'A', 'C', 'A', 'Mettre en place une méthodologie qui favorise la prise en compte des aspects NR', 'Est-ce que l\'architecture technique est en permanence en rapport avec l\'activité du service ?', 'Le suivi des taux utilisations doit aussi permettre de réduire la taille de l\'infrastructure lorsqu\'elle est sur-dimensionnée par rapport au besoin.', 'Administration', 'Le suivi des indicateurs d\'activité sont-ils en relation avec des actions d\'optimisation ?', NULL, NULL, NULL, NULL, 'RECO', '**', NULL, NULL, '#PERF ; #VOL ; #SEC ; #USE ; #VALUE ; #FIN ;', 'Dev ; ProjectMngr ; ITMngr ;', '(X / Y ) * 100', 'Formalisés = 100 ; prévus = 75 ; Identifiés = 50 ; Non = 0', '100', 'High', 'Periodic', 'Le suivi de production met en oeuvre des indicateurs d\'utilisation des ressources techniques', NULL, NULL, NULL, NULL, NULL, NULL),
(478, 'BACKEND', 'BCK-6.C04', 'B', 'B', 'B', 'Mettre en place une méthodologie qui favorise la prise en compte des aspects NR', 'Est-ce qu\'un profil d\'utilisation des fonctionnalités est mis en place ?', 'Les User Story / Use Case doivent étre spécifiés par rapport é des usages afin de ne pas aller vers une inflation de fonctionnalités qui serait superflue par rapport aux besoins réels des utilisateurs. Proposer des fonctionnalités marginales va engendrer une empreinte environnementale, un coét et une incompréhension du service supplémentaire non justifiés.', 'Administration', 'Les fonctionnalités marginales sont-elles identifiées et traitées dans un mode dégradé ?', NULL, NULL, NULL, NULL, 'CONSEIL', 'N/A', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(479, 'BACKEND', 'BCK-6.04', 'A', 'C', 'A', 'Mettre en place une méthodologie qui favorise la prise en compte des aspects NR', 'Est-ce que les opérations de dé-provisionnement sont exprimées lors de la conception ?', 'Lors de la conception, chaque partie composant le projet peut étre amenée é avoir une durée de vie différente. Il est important que l\'ensemble des procédures de fin de vie soient exprimées pour que l\'empreinte des composants inutilisés et arrétés soient nulle.', 'Fin de Vie', 'Les processus de dé-provisionnement sont-ils décrits ?', NULL, NULL, NULL, NULL, 'RECO', '**', NULL, NULL, '#PERF ; #VOL ; #SEC ; #USE ; #VALUE ; #METH ;', 'Dev ; ProjectMngr ; ITMngr ;', '(X / Y ) * 100', 'Formalisés = 100 ; prévus = 75 ; Identifiés = 50 ; Non = 0', '100', 'Medium', 'OnUpdate', 'La gestion de configuration permet de préciser les régles de provisionnemnent / dé-provisionnement en fonction de seuils', NULL, NULL, NULL, NULL, NULL, NULL),
(480, 'BACKEND', 'BCK-6.05', 'A', 'C', 'A', 'Mettre en place une méthodologie qui favorise la prise en compte des aspects NR', 'L\'ensemble des équipements techniques utilisés par le service sont-ils identifiés?', 'La conception, le développement, les tests, la production, la gestion de disponibilité sont des étapes qui vont utiliser des ressources techniques. Chacune de ces ressources doit étre identifiée et ses caractéristiques répertoriées.', 'Utilisation', 'Les certifications de chaque équipement sont-elles disponibles ?', NULL, NULL, NULL, NULL, 'RECO', '*', NULL, NULL, '#USE ; #VALUE ; #METH ; #BUY ; #FIN ; #OBSO', 'Dev ; ProjectMngr ; ITMngr ;', '(X / Y ) * 100', 'Formalisés = 100 ; prévus = 75 ; Identifiés = 50 ; Non = 0', '100', 'High', 'Periodic', 'La gestion de configuration permet de référencer tous les équipements liés au service', NULL, NULL, 'Y', '#PROCESS', '**', 'Outils Configuration Management'),
(481, 'BACKEND', 'BCK-6.06', 'A', 'B', 'B', 'Mettre en place une méthodologie qui favorise la prise en compte des aspects NR', 'Pour chaque équipement, les caractéristiques sont-elles disponibles ? (Ancienneté, données de perf environnementales, ...)', 'La position de l\'équipement dans son cycle de vie et son impact environnemental doivent étre connus pour utiliser au mieux les équipements disponibles.', 'Administration', 'Les critéres de renouvellement des équipements prennent-ils en compte les impacts NR ?', NULL, NULL, NULL, NULL, 'RECO', '*', NULL, NULL, '#USE ; #VALUE ; #METH ; #BUY ; #FIN ; #OBSO', 'Dev ; ProjectMngr ; ITMngr ;', '(X / Y ) * 100', 'Formalisés = 100 ; prévus = 75 ; Identifiés = 50 ; Non = 0', '100', 'Low', 'Periodic', 'La gestion de configuration permet de référencer tous les équipements liés au service', NULL, NULL, 'Y', '#PROCESS', '**', 'Outils Configuration Management'),
(482, 'BACKEND', 'BCK-6.C05', 'B', 'B', 'B', 'Mettre en place une méthodologie qui favorise la prise en compte des aspects NR', 'Chaque fonction du service est-elle appréhendée en regard de son importance dans le service ?', 'Les fonctionnalités les plus importantes sont parfois moins consommatrices de ressources que des fonctions marginales ce qui n\'a pas de sens dans une démarche NR cohérente.', 'Conception', 'Avez-vous appliqué une démarche 80/20 pour chaque fonction en rapport avec ses impacts environnementaux et son importance ?', NULL, NULL, NULL, NULL, 'CONSEIL', 'N/A', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(483, 'BACKEND', 'BCK-6.C06', 'B', 'B', 'B', 'Mettre en place une méthodologie qui favorise la prise en compte des aspects NR', 'Les fonctions secondaires ont-elles aussi un impact moindre sur l\'impact environnemental ?', 'La cohérence NR pousse é s\'assurer que les fonctions secondaires n\'ont pas un impact environnemental plus important que les fonctions principales. Les phases de développement doivent clairement identifier les fonctions principales et secondaires et leur impact du point de vue NR', 'Conception', 'Les impacts environnementaux de chaque fonctionnalité sont-ils évalués par rapport é la nature et l\'importance de la fonctionnalité ?', NULL, NULL, NULL, NULL, 'CONSEIL', 'N/A', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(484, 'BACKEND', 'BCK-6.07', 'B', 'B', 'B', 'Mettre en place une méthodologie qui favorise la prise en compte des aspects NR', 'Chacune des phases du cycle de vie sont-elles identifiées avec des actions spécifiques ?', 'La démarche ACV est une base de la prise en compte du NR dans la conception des services numériques, elle permet d\'associer des actions é chaque étape du cycle de vie.', 'Administration', 'A quelle fréquence ces actions sont-elles revues ?', NULL, NULL, NULL, NULL, 'RECO', '**', NULL, NULL, '#USE ; #VALUE ; #METH ; #BUY ; #FIN', 'Dev ; ProjectMngr ; ITMngr ;', NULL, NULL, NULL, 'Low', 'Periodic', 'Analyse d\'impact', NULL, NULL, NULL, NULL, NULL, NULL),
(485, 'BACKEND', 'BCK-6.08', 'C', 'B', 'B', 'Mettre en place une méthodologie qui favorise la prise en compte des aspects NR', 'Les données ACV sont-elles réutilisées dans le cas d\'une adaptation d\'un service existant ?', 'Les analyses ACV peuvent étre réalisées sur des services existants afin de les rendre plus efficaces dans le cas d\'une refonte ou adaptation d\'un service existant.', 'Revalorisation', 'Avez-vous mis é disposition des équipes projet les ACV précédentes ?', 'L\'ACV de ce projet est-elle rendue disponible pour d\'autres projets ?', NULL, NULL, NULL, 'RECO', '**', NULL, NULL, '#USE ;  #COM ; #VALUE ; #METH', 'Dev ; Designer ; ProjectMngr ; ProductOwner ;RSE/NR', NULL, NULL, NULL, 'Low', 'OnUpdate', 'ACV', NULL, NULL, NULL, NULL, NULL, NULL),
(486, 'HEBERGEMENT', 'HEB-1', NULL, NULL, NULL, 'Réduire l\'impact de l\'hébergement', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'ODD#6 ; ODD#7 ; ODD#12 ; ODD#13 ; ODD#15', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(487, 'HEBERGEMENT', 'HEB-1.C01', 'A', 'C', 'C', 'Réduire l\'impact de l\'hébergement', 'Le refroidissement affecte-t-il  les nappes phréatiques ?', 'L\'utilisation de l\'eau est associée aux hébergements informatiques. Le plus souvent l\'eau circule mais la dégradation peut provenir é la fois de la température et de la pureté.', 'Utilisation', 'Quel delta de température est tolérable pour l\'eau entre l\'entrée et la sortie ?', 'Quel est le volume d\'eau traité et le taux d\'épuration ?', NULL, NULL, NULL, 'CONSEIL', 'N/A', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(488, 'HEBERGEMENT', 'HEB-1.C02', 'A', 'C', 'C', 'Réduire l\'impact de l\'hébergement', 'L\'emplacement du datacenter affecte-t-il l\'écosystéme é proximité ?', 'Les datacenters sont rarement installés au coeur des zones urbaines, ils bénéficient d\'environnement naturels plus favorables qu\'ils ne doivent pas contribuer é appauvrir.', 'Acquisition', 'Oé se trouve le datacenter, peut-il dégrader une zone naturelle, ou affecter des humains ?', 'Une étude d\'impact a-t-elle été réalisée ?', NULL, NULL, NULL, 'CONSEIL', 'N/A', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(489, 'HEBERGEMENT', 'HEB-1.C03', 'A', 'C', 'C', 'Réduire l\'impact de l\'hébergement', 'La qualité de l\'eau est-elle dégradée par son utilisation dans le datacenter ?', 'Le datacenter doit étre en mesure de réutiliser l\'eau qu\'il rejette.', 'Utilisation', 'L\'implémentation des captages et rejets d\'eau ont-ils été vérifiés ?', 'Le captage d\'eau est-il en aval des rejets ?', NULL, NULL, NULL, 'CONSEIL', 'N/A', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(490, 'HEBERGEMENT', 'HEB-1.01', 'A', 'C', 'C', 'Réduire l\'impact de l\'hébergement', 'L\'eau utilisée ne provient-elle pas du réseau d\'eau potable ?', 'Il n\'y a pas de traitement superflu de l\'eau nécessaire au refroidissement du datacenter. En conséquence l\'utilisation de l\'eau potable n\'est pas envisageable.', 'Utilisation', 'La source d\'approvisionnement d\'eau est-elle vérifiée ?', NULL, NULL, NULL, NULL, 'RECO', '**', NULL, NULL, '#USE ; #COM ; #VALUE ; #BUY', 'ProjectMngr ; ITMngr ;', '(X / Y ) * 100', 'Formalisés = 100 ; prévus = 75 ; Identifiés = 50 ; Non = 0', '100', 'Low', 'Once', 'Le dossier technique de l\'hébergement trace les sources d\'approvisionnement en eau', NULL, NULL, NULL, NULL, NULL, NULL),
(491, 'HEBERGEMENT', 'HEB-1.02', 'A', 'C', 'C', 'Réduire l\'impact de l\'hébergement', 'Les déchets issus du datacenter sont-ils valorisés ou recyclés ?', 'Le traitement des déchets générés par un datacenter est complexe, il est donc nécessaire de l\'adresser et de se préoccuper de sa bonne gestion en termes de recyclage et de respect de l\'environnement.', 'Utilisation', 'Avez-vous les moyens de vérifier la filiére de traitement des déchets ?', NULL, NULL, NULL, NULL, 'RECO', '*', NULL, 'INCONTOURNABLE', '#USE ; #COM ; #VALUE ; #BUY', 'ProjectMngr ; ITMngr ;', '(X / Y ) * 100', 'Formalisés = 100 ; prévus = 75 ; Identifiés = 50 ; Non = 0', '100', 'Medium', 'Periodic', 'Le dossier technique de l\'hébergement trace la revalorisation des déchets', NULL, NULL, NULL, NULL, NULL, NULL),
(492, 'HEBERGEMENT', 'HEB-1.C04', 'B', 'C', 'C', 'Réduire l\'impact de l\'hébergement', 'Le datacenter produit-il une nuisance sonore ?', 'Conformément é l\'article L1382 du Code Civil, tout datacenter doit veiller é limiter sa nuisance sonore (= pollution sonore).', 'Utilisation', 'Les mesures en dB sont-elles vérifiées par rapport aux nuisances sur l\'environnement ?', NULL, NULL, '6 - L\'organisme recourt é des sources d\'énergies attestées renouvelables', NULL, 'CONSEIL', 'N/A', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(493, 'HEBERGEMENT', 'HEB-1.C05', 'A', 'C', 'C', 'Réduire l\'impact de l\'hébergement', 'Est-ce que de l\'énergie indirecte produite (chaleur) est réutilisée ?', 'La transformation d\'énergie produit de la chaleur. Afin de ne pas rejeter de source de chaleur dans l\'environnement et éviter la consommation d\'énergie pour produire de la chaleur dans d\'autres activités, la chaleur des datacenters doit étre réutilisée (chauffage de locaux, d\'eau, de serres, ...)', 'Utilisation', 'Quel est le pourcentage de réutilisation ?', NULL, NULL, '7 - La chaleur dégagée par les salles informatiques des centres d\'hébergement est récupérée', NULL, 'CONSEIL', 'N/A', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(494, 'HEBERGEMENT', 'HEB-1.03', 'A', 'C', 'C', 'Réduire l\'impact de l\'hébergement', 'Est-ce que le bétiment est certifié LEED / BDM ou équivalent ?', 'La structure du bétiment peut étre plus ou moins efficace dans sa conception, les normes environnementales les plus performantes de construction doivent étre appliquées.', 'Acquisition', 'Quelle est la certification de la construction ?', NULL, NULL, NULL, NULL, 'RECO', '*', NULL, NULL, '#USE ; #COM ; #VALUE ; #BUY', 'ProjectMngr ; ITMngr ;', '(X / Y ) * 100', 'Formalisés = 100 ; prévus = 75 ; Identifiés = 50 ; Non = 0', '100', 'Low', 'Once', 'Le dossier technique de l\'hébergement trace la certification de la construction', NULL, NULL, NULL, NULL, NULL, NULL),
(495, 'HEBERGEMENT', 'HEB-1.04', 'A', 'C', 'C', 'Réduire l\'impact de l\'hébergement', 'Est-ce que le refroidissement est optimisé en fonction d\'une analyse de température précise ?', 'Chaque degré supplémentaire de refroidissement consomme de l\'énergie, l\'adaptation de la production de froid au besoin instantané permet de réduire cette consommation.', 'Utilisation', 'Le refroidissement est-il optimisé en fonction d\'une analyse de température en temps réel ?', NULL, NULL, NULL, NULL, 'RECO', '*', NULL, NULL, '#USE ; #COM ; #VALUE ; #BUY', 'ProjectMngr ; ITMngr ;', '(X / Y ) * 100', 'Formalisés = 100 ; prévus = 75 ; Identifiés = 50 ; Non = 0', '100', 'Medium', 'Periodic', 'Le dossier technique de l\'hébergement trace la stratégie de refroidissement', NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `goodpractices` (`cle`, `Family`, `Id`, `Planet`, `People`, `Prosperity`, `Recommendation`, `Criteria`, `Justification`, `Stage`, `Tests1`, `Tests2`, `Tests3`, `Correspondence`, `Link`, `Type`, `Difficulty`, `Goal`, `Incontournable`, `Tags`, `Actors`, `Indicators_`, `IndicatorsX`, `IndicatorsY`, `Priority`, `Recurrence`, `UseCase`, `Example`, `Limite`, `Automatizable`, `AutomatizationScript`, `AutomatizationLevel`, `AutomatizationInformation`) VALUES
(496, 'HEBERGEMENT', 'HEB-1.C06', 'A', 'C', 'C', 'Réduire l\'impact de l\'hébergement', 'Comment et sur quels critéres est optimisé le refroidissement ?', 'Les températures de fonctionnement et le niveau d\'hygrométrie des tolérances des serveurs sont étendues, le refroidissement doit étre optimisé pour étre dans les valeurs hautes de ces plages.', 'Administration', 'Le pilotage du refroidissement est-il réalisé automatiquement en fonction de données fiables ?', NULL, NULL, NULL, NULL, 'CONSEIL', 'N/A', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(497, 'HEBERGEMENT', 'HEB-1.C07', 'B', 'C', 'C', 'Réduire l\'impact de l\'hébergement', 'L\'infrastructure du datacenter prend-t-elle en compte les contraintes de refroidissement ?', 'L\'organisation du datacenter peut faciliter la gestion des flux de refroidissements avec des structures permettant de canaliser et réduire le volume d\'air traité par le refroidissement.', 'Utilisation', 'Le datacenter est-il conéu en cold corridor ou équivalent ?', 'Les tolérances de température et hygrométrie des équipements (Serveurs, routeurs, stockage) sont-elles les plus larges possibles ?', NULL, '70. Le mode de climatisation des data centers fait l\'objet d\'une démarche d\'optimisation de la consommation d\'énergie.', NULL, 'CONSEIL', 'N/A', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(498, 'HEBERGEMENT', 'HEB-1.05', 'A', 'C', 'C', 'Réduire l\'impact de l\'hébergement', 'Les alimentations des serveurs sont-elles efficaces ?', 'La consommation énergétique des serveurs est l\'un des facteurs permettant de réduire l\'empreinte de l\'hébergement. Les labels et certifications en constantes évolutions facilitent le choix des gammes et modéles de serveurs. Certains équipements disposent de plusieurs sources d\'alimentations. Les gestions d\'alimentations redondantes doivent aussi entrer dans les critéres de choix', 'Administration', 'Les alimentations des serveurs sont-elles certifiées 80 Plus ?', NULL, NULL, NULL, NULL, 'RECO', '*', NULL, NULL, '#USE ; #COM ; #VALUE ; #BUY', 'ProjectMngr ; ITMngr ;', '(X / Y ) * 100', 'Formalisés = 100 ; prévus = 75 ; Identifiés = 50 ; Non = 0', '100', 'High', 'OnUpdate', 'Le dossier technique de l\'hébergement trace les consommations électriques et la certification des serveurs', NULL, NULL, NULL, NULL, NULL, NULL),
(499, 'HEBERGEMENT', 'HEB-1.06', 'A', 'C', 'C', 'Réduire l\'impact de l\'hébergement', 'Est-ce que le PUE (Power Usage Effectivness) est disponible ?', 'La puissance totale consommée par le datacenter en comparaison de la puissance consommée par les serveurs donnent l\'efficacité globale de toute l\'infrastructure.', 'Administration', 'Le PUE est-il inférieur ou égal é 1,2 ?', 'Le datacenter suit-il le European code of conduct for data centres ?', NULL, NULL, NULL, 'RECO', '*', NULL, 'INCONTOURNABLE', '#USE ; #COM ; #VALUE ; #BUY', 'ProjectMngr ; ITMngr ;', '(X / Y ) * 100', 'PUE <= 1,2 = 100 ; PUE 1,2-1,4 = 75; PUE 14-1,6 = 50 ; PUE 1,6-1,8 = 25 ; PUE > 1,8 = 0', '100', 'High', 'Periodic', 'Le dossier technique de l\'hébergement trace l\'éfficacité énergétique et les PUE des sites.', NULL, NULL, NULL, NULL, NULL, NULL),
(500, 'HEBERGEMENT', 'HEB-1.07', 'B', 'C', 'C', 'Réduire l\'impact de l\'hébergement', 'Le taux d\'occupation des salles du datacenter est-il optimisé pour améliorer le bilan énergétique ?', 'La densité d\'occupation des racks dans les salles d\'hébergement doit permettre de privilégier l\'efficacité des équipements mutualisés.', 'Déploiement', 'Un taux d\'occupation minimum de 80% est-il pris en compte avant l\'ouverture d\'une autre salle ?', 'Les espaces vides sont-ils comblés pour optimiser la circulation d\'air ?', NULL, NULL, NULL, 'RECO', '*', NULL, NULL, '#USE ; #COM ; #VALUE ; #BUY', 'ProjectMngr ; ITMngr ;', NULL, NULL, NULL, 'Medium', 'Periodic', 'Dossier technique', NULL, NULL, NULL, NULL, NULL, NULL),
(501, 'HEBERGEMENT', 'HEB-1.08', 'A', 'A', 'B', 'Réduire l\'impact de l\'hébergement', 'Est-ce que le WUE (Water Usage Effectivness) est disponible ?', 'La quantité d\'eau consommée par le datacenter en comparaison de la puissance consommée par les serveurs donnent l\'efficacité globale de toute l\'infrastructure.', 'Déploiement', 'Le WUE (Water Usage Effectiveness) inférieur é 1 L/kWh ?', NULL, NULL, NULL, NULL, 'RECO', '*', NULL, NULL, '#USE ; #COM ; #VALUE ; #BUY', 'ProjectMngr ; ITMngr ;', '(X / Y ) * 100', 'WUE <= 1L/kwh = 100 ; WUE > 1L/kwh = 0', '100', 'High', 'Periodic', 'Le dossier technique de l\'hébergement trace l\'éfficacité énergétique et les WUE des sites.', NULL, NULL, NULL, NULL, NULL, NULL),
(502, 'HEBERGEMENT', 'HEB-1.09', 'A', 'A', 'B', 'Réduire l\'impact de l\'hébergement', 'L\'hébergeur adhére-t-il aux meilleurs prattiques de son secteur d\'activité ?', 'Le domaine de l\'hébergement se struture pour afficher des pratiques métiers vertueuses en développant des codes de conduite.', 'Déploiement', 'Le datacenter suit-il le European code of conduct for data centres ?', NULL, NULL, NULL, NULL, 'RECO', '*', NULL, NULL, '#USE ; #COM ; #VALUE ; #BUY', 'ProjectMngr ; ITMngr ;', '(X / Y ) * 100', 'Oui = 100 ; En cours = 50 : Prévu = 25 : Non = 0', '100', 'High', 'Periodic', 'Le dossier technique de l\'hébergement trace les affiliations des sites.', NULL, NULL, 'Y', '#PROCESS', '**', 'Outils Site /  base  d\'hébergeurs \"verts\"'),
(503, 'HEBERGEMENT', 'HEB-2', NULL, NULL, NULL, 'Associer les aspects humains et techniques', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'ODD#7 ; ODD#9 ; ODD#10  ODD#12', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(504, 'HEBERGEMENT', 'HEB-2.C01', 'C', 'A', 'B', 'Associer les aspects humains et techniques', 'Est-ce que tous les collaborateurs du prestataire disposent de conditions sociales et salariales équivalentes é celles du client du datacenter ?', 'Les besoins de supervision 24/7 des datacenters peuvent pousser les opérateurs é distribuer leurs ressources techniques et humaines de supervision dans un mode \"Follow the Sun\". Les conditions d\'exercice et de travail dans des pays différents peuvent introduire de grandes inégalités de traitement, de couverture sociale. La transparence et la cohérence de traitement de tous les collaborateurs du prestataire permet de garantir la dimension humaine et économique du NR.', 'Acquisition', 'Le prestataire utilise-t-il des sous-traitants hors du territoire ?', 'Est-ce que les normes environnementales et sociales du prestataire sont en conformité avec celles du \"client\" du datacenter ? Le fournisseur externalise-t-il certaines fonctions dans des pays avec un encadrement social différent ?', NULL, NULL, NULL, 'CONSEIL', 'N/A', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(505, 'HEBERGEMENT', 'HEB-2.C02', 'B', 'B', 'B', 'Associer les aspects humains et techniques', 'Le datacenter est-il situé é proximité de l\'activité principale ?', 'Les déplacements physiques sur les datacenters sont par nature peu fréquents mais des synchronisations avec les équipes techniques sur site est parfois indispensable. La distance doit étre minimisée pour réduire les impacts de ces déplacements', 'Acquisition', 'Les besoins, coéts et impacts de déplacements sur le datacenter ont-ils été évalués ?', NULL, NULL, NULL, NULL, 'CONSEIL', 'N/A', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(506, 'HEBERGEMENT', 'HEB-2.C03', 'B', 'B', 'B', 'Associer les aspects humains et techniques', 'Les trajets pour interventions au datacenter sont-ils possibles en transport en commun ou mobilité douce?', 'La localisation du datacenter dans un périmétre accessible en mobilité faible émission est plus intéressante pour les quelques déplacements qui seraient nécessaires.', 'Acquisition', 'Les différents moyens de transport pour rejoindre le datacenter sont-ils vérifiés ?', NULL, NULL, NULL, NULL, 'CONSEIL', 'N/A', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(507, 'HEBERGEMENT', 'HEB-2.C04', 'C', 'B', 'B', 'Associer les aspects humains et techniques', 'Le datacenter favorise-t-il l\'écosystéme économique local ?', 'La localisation du datacenter, notamment le pays, peut introduire des conséquences sur la territorialité des données. La région de son implémentation dans des zones favorisées ou défavorisées peut permettre de réduire les impacts des zones oé le numérique est peut représenté.', 'Utilisation', 'L\'engagement local du fournisseur est-il validé ?', NULL, NULL, '16. Les matériels électroniques font l\'objet d\'une maintenance réguliére', NULL, 'CONSEIL', 'N/A', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(508, 'HEBERGEMENT', 'HEB-2.01', 'B', 'B', 'B', 'Associer les aspects humains et techniques', 'Le matériel provient-il d\'entreprises réputées comme respectant le code du travail ?', 'Les fournisseurs d\'approvisionnements matériels du datacenter doivent pouvoir étre tracés pour permettre une analyse du cycle de vie. Le strict respect des normes internationales du travail doivent pouvoir étre validées.', 'Acquisition', 'La traéabilité d\'approvisionnement est-elle garantie ?', NULL, NULL, NULL, NULL, 'RECO', '**', NULL, NULL, '#USE ; #COM ; #VALUE ; #BUY', 'ProjectMngr ; ITMngr ;', NULL, NULL, NULL, 'Medium', 'Periodic', 'Procédures d\'achats', NULL, NULL, NULL, NULL, NULL, NULL),
(509, 'HEBERGEMENT', 'HEB-2.02', 'B', 'B', 'B', 'Associer les aspects humains et techniques', 'Est-ce que les équipements sont fabriqués par des entreprises respectueuses de l\'environnement ?', 'Le positionnement NR des fournisseurs de matériels du datacenter doit pouvoir étre exposé et validé, pour assurer la continuité de la vision que demande l\'analyse du cycle de vie.', 'Acquisition', 'Les indicateurs NR des fournisseurs sont-ils disponibles ?', NULL, NULL, '19 - Les disques durs sont choisis en fonction de leur faible consommation énergétique', NULL, 'RECO', '**', NULL, NULL, '#USE ; #COM ; #VALUE ; #BUY', 'ProjectMngr ; ITMngr ;', NULL, NULL, NULL, 'High', 'Periodic', 'Dossier technique', NULL, NULL, NULL, NULL, NULL, NULL),
(510, 'HEBERGEMENT', 'HEB-3', NULL, NULL, NULL, 'Adapter l\'utilisation des équipements techniques aux besoins', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'ODD#7 ; ODD#8 ; ODD#12', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(511, 'HEBERGEMENT', 'HEB-3.01', 'A', 'C', 'C', 'Adapter l\'utilisation des équipements techniques aux besoins', 'Les serveurs /routeurs sont-ils éteints quand ils sont inutilisés ?', 'L\'infrastructure réseau est au coeur de l\'architecture des datacenters, les mécanismes de redondances, de sur-dimensionnement sont largement déployés mais utilisés de maniére ponctuelle. En l\'absence de besoin et lorsque cela n\'impacte pas la sécurité du site, les équipements en sur-nombre devraient étre hors tension pour diminuer les consommations d\'énergie et les besoins de refroidissement.', 'Déploiement', 'Les procédures d\'arrét des serveurs non-utilisés sont-elles mises en place ?', NULL, NULL, '1 - Les matériels qui ne demandent pas un fonctionnement permanent sont systématiquement éteints et non mis en veille.', NULL, 'RECO', '**', NULL, NULL, '#VOL ; #SEC ; #USE ; #MAINT ; #COM ; #VALUE ; #DEBT', 'ProjectMngr ; ITMngr ;', '(X / Y ) * 100', 'Formalisés = 100 ; prévus = 75 ; Identifiés = 50 ; Non = 0', '100', 'High', 'Periodic', 'Le dossier technique de l\'hébergement tracela stratégie d\'activation désactivations des composants en fonction de la charge', NULL, NULL, NULL, NULL, NULL, NULL),
(512, 'HEBERGEMENT', 'HEB-3.02', 'A', 'C', 'B', 'Adapter l\'utilisation des équipements techniques aux besoins', 'La taille du parc physique est-il en adéquation avec le volume de machines virtuelles é exécuter ?', 'Les techniques de virtualisation permettent de mutualiser les ressources physiques d\'un serveur et d\'un espace de stockage. Cette abstraction peut optimiser l\'emploi du matériel, mais peut aussi conduire é sous-utiliser les équipements physiques et déployer de nouveaux équipements avant que le besoin ne soit réellement présent. L\'impact environnemental total d\'un serveur et de son utilisation est important.', 'Déploiement', 'L\'allocation des ressources physiques par rapport aux machines virtuelles est-elle suivie ?', 'Les ratios vCPU:pCPU sont-ils > 1:1 ?', NULL, NULL, NULL, 'RECO', '*', NULL, 'INCONTOURNABLE', '#PERF ; #VOL ;  #USE ; #VALUE ; #FIN', 'ProjectMngr ; ITMngr ;', '(X / Y ) * 100', 'Taux utisation VM > 60 = 100 ; 60-40 = 50 ; < 40 = 0', '100', 'High', 'Periodic', 'Le dossier technique d\'hébergement permet de suivre la gestion de configuration et l\'adaptation aux besoins', NULL, NULL, NULL, NULL, NULL, NULL),
(513, 'HEBERGEMENT', 'HEB-3.03', 'B', 'B', 'B', 'Adapter l\'utilisation des équipements techniques aux besoins', 'La mémoire attribuée est-elle fonction de l\'usage réel ?', 'L\'adaptation et le suivi des consommations de ressources physiques doivent faire intervenir des collectes sur les équipements physiques et sur tous les équipements virtuels qu\'ils accueillent. Les techniques de virtualisation récentes autorisent le changement de paramétres sans interruption pour, en fonction du besoin, augmenter ou diminuer les ressources (CPU/RAM) allouées aux machines virtuelles, voire migrer des machines virtuelles sur des serveurs physiques moins chargés.', 'Administration', 'Quels mécanismes de supervision sont déployés ?', 'Le taux de disponibilité des ressources physiques est-il < 25% avant d\'envisager de provisionner de nouveaux équipements ?', NULL, NULL, NULL, 'RECO', '***', NULL, NULL, '#PERF ; #VOL ;  #USE ; #VALUE ; #FIN', 'ProjectMngr ; ITMngr ;', '(X / Y ) * 100', 'Formalisés = 100 ; prévus = 75 ; Identifiés = 50 ; Non = 0', '100', 'Medium', 'Periodic', 'Dossier technique', NULL, NULL, NULL, NULL, NULL, NULL),
(514, 'HEBERGEMENT', 'HEB-3.04', 'B', 'B', 'B', 'Adapter l\'utilisation des équipements techniques aux besoins', 'Le nombre de coeurs attribués est-il fonction de l\'usage réel ?', 'Les ressources CPU accessibles par les machines virtuelles sont fonction du nombre de processeurs, du nombre de coeurs de chaque processeur et des capacités d\'hyperThreading. Ce nombre de composants CPU constitue la base des ressources virtuelles que les machines virtuelles pourront allouer.', 'Administration', 'Le dimensionnement est-il possible é chaud ?', 'L\'hyperThreading est-il activé pour augmenter le nombre de physical CPU disponibles ?', NULL, '20 - Les processeurs sont choisis en fonction de leur faible consommation énergétique', NULL, 'RECO', '***', NULL, NULL, '#PERF ; #VOL ;  #USE ; #VALUE ; #FIN', 'ProjectMngr ; ITMngr ;', '(X / Y ) * 100', 'Formalisés = 100 ; prévus = 75 ; Identifiés = 50 ; Non = 0', '100', 'High', 'Periodic', 'Dossier technique', NULL, NULL, NULL, NULL, NULL, NULL),
(515, 'HEBERGEMENT', 'HEB-3.C01', 'B', 'B', 'B', 'Adapter l\'utilisation des équipements techniques aux besoins', 'Le choix technique (mutualisé, dédié, collocation) est-il optimisé entre le besoin et l\'efficacité énergétique ?', 'Les différentes solutions proposées par les hébergeurs générent des allocations de matériels physiques trés différentes et donc un impact environnemental différent. Le point d\'analyse majeur est le nombre de serveurs, les puissances consommées par les serveurs, les volumes de stockage et la puissance consommée par ceux-ci. L\'utilisation de serveurs ou de stockages pour un besoin réduit en dessous de sa capacité nominale génére une déperdition environnementale.', 'Déploiement', 'Quel métrique pilote le choix : technique, sécurité , finance, évolutivité, empreinte physique ?', NULL, NULL, '71. Les calculs et les téches sont répartis sur différents serveurs dédiés en fonction de la nature de leurs téches (données, mails, vidéos etc.)', NULL, 'CONSEIL', 'N/A', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(516, 'HEBERGEMENT', 'HEB-3.05', 'B', 'B', 'B', 'Adapter l\'utilisation des équipements techniques aux besoins', 'Les coeurs inutilisés sont-ils désactivés ?', 'Les services hébergés n\'ont pas toujours les mémes besoins de performance et subissent des variations fortes de charges, au cours de la journée, de la semaine, des mois, de l\'année. Les dimensionnements sont calibrés pour accepter les charges maximales qui peuvent étre sur des plages de temps restreintes. En dehors de ces périodes, le besoin de puissance est réduit et l\'activité des systémes doit étre en mesure de se réduire dans les mémes proportions pour ne pas monopoliser d\'énergie inutilement.', 'Utilisation', 'Les périodes de sous et sur-charge sont-elles anticipées ?', NULL, NULL, NULL, NULL, 'RECO', '***', NULL, NULL, '#PERF ; #VOL ;  #USE ; #VALUE ; #FIN', 'ProjectMngr ; ITMngr ;', '(X / Y ) * 100', 'Formalisés = 100 ; prévus = 75 ; Identifiés = 50 ; Non = 0', '100', 'Low', 'Periodic', 'Dossier technique', NULL, NULL, NULL, NULL, NULL, NULL),
(517, 'HEBERGEMENT', 'HEB-3.06', 'A', 'B', 'A', 'Adapter l\'utilisation des équipements techniques aux besoins', 'Le systéme de sauvegarde est-il incrémentiel (optimisation disque) ?', 'La sécurité est un des points clé des datacenters, les sauvegardes sont l\'un des éléments participant é cette sécurisation. La volumétrie des sauvegardes est cumulative. Unitairement le volume de données é sauvegarder tend é croétre, les différents jeux de sauvegardes multiplient cet effet. Les données non modifiées n\'ont pas de raison d\'encombrer inutilement des espaces de sauvegarde. Les sauvegardes incrémentales réduisent l\'impact environnemental de ce processus. Certaines solutions sont en mesure de gérer des incréments, d\'autres ne savent gérer que des archives totales qui augmentent l\'empreinte environnementale.', 'Déploiement', 'Quel est le volume des sauvegardes quotidiennes ?', NULL, NULL, NULL, NULL, 'RECO', '*', NULL, NULL, '#PERF ; #VOL ; #SEC ; #USE ; #VALUE ; #FIN', 'ProjectMngr ; ITMngr ;', '(X / Y ) * 100', 'Formalisés = 100 ; prévus = 75 ; Identifiés = 50 ; Non = 0', '100', 'High', 'OnUpdate', 'Le dossier technique d\'hébergement permet de suivre la volumétrie des sauvegardes et leurs lieux de stockage', NULL, NULL, NULL, NULL, NULL, NULL),
(518, 'HEBERGEMENT', 'HEB-3.C02', 'B', 'B', 'B', 'Adapter l\'utilisation des équipements techniques aux besoins', 'L\'usage de containers par rapport é des machines virtuelles est-il privilégié ?', 'Les machines virtuelles et les containers sont 2 techniques de virtualisation. La technologie des machines virtuelles utilise beaucoup de duplications (systéme d\'exploitation par exemple) et créent des composants virtuels associés é des composants physiques. Le mode container repose sur les composants physiques du matériel et les exploite plus nativement en limitant les duplications de composants logiciels. D\'un point de vue NR les containers sont souvent plus efficaces mais la technologie est moins largement déployée.', 'Déploiement', 'L\'étude comparative pour le service entre VM et container est-elle disponible avec les impacts NR de chaque solution ?', NULL, NULL, NULL, NULL, 'CONSEIL', 'N/A', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(519, 'HEBERGEMENT', 'HEB-3.C03', 'C', 'B', 'A', 'Adapter l\'utilisation des équipements techniques aux besoins', 'Est-ce que les solutions Open Source sont mises en oeuvre lorsqu\'elles sont disponibles ?', 'Une solution Open Source n\'est pas un gage d\'efficacité NR et les différentes alternatives doivent étre challengées. Mais l\'Open Source permet de limiter les ambitions et mainmises de certains acteurs dont les pratiques économiques, sociales sont opaques.', 'Acquisition', 'Les solutions Open Source sont-elles aussi sélectionnées pour leurs performances NR ?', NULL, NULL, NULL, NULL, 'CONSEIL', 'N/A', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(520, 'HEBERGEMENT', 'HEB-3.07', 'B', 'B', 'B', 'Adapter l\'utilisation des équipements techniques aux besoins', 'La haute disponibilité est-elle conéue de maniére modulaire?', 'Les besoins de sécurisation sont différents en fonction des clients, des services hébergés. La méme régle générique d\'excellence dans ce domaine n\'est pas toujours nécessaire. La conception des mécanismes de haute disponibilité qui met en jeu beaucoup de composants redondants doit pouvoir s\'adapter au besoin réel afin de réduire l\'impact des services.', 'Déploiement', 'Le besoin de sécurisation est-il défini pour chaque fonction ?', NULL, NULL, NULL, NULL, 'RECO', '**', NULL, NULL, '#PERF ; #VOL ; #SEC ; #USE ; #VALUE ; #FIN', 'ProjectMngr ; ITMngr ;', '(X / Y ) * 100', 'Formalisés = 100 ; prévus = 75 ; Identifiés = 50 ; Non = 0', '100', 'High', 'OnUpdate', 'Dossier technique', NULL, NULL, NULL, NULL, NULL, NULL),
(521, 'HEBERGEMENT', 'HEB-3.08', 'B', 'B', 'B', 'Adapter l\'utilisation des équipements techniques aux besoins', 'Le provisionning / deprovisionning est-il dynamique en fonction de la charge ?', 'Lors de l\'hébergement d\'un service, chaque partie le composant peut étre amenée é avoir une durée de vie différente. Il est important que l\'ensemble des procédures de fin de vie soient exprimées pour que l\'empreinte des composants inutilisés et arrétés soient nulle. Y compris lorsque l\'arrét est temporaire suite é une baisse d\'activité.', 'Fin de Vie', 'Aprés combien de temps d\'inactivité les services sont-ils dé-provisionnés ?', NULL, NULL, NULL, NULL, 'RECO', '***', NULL, NULL, '#PERF ; #VOL ;  #USE ; #VALUE ; #FIN', 'ProjectMngr ; ITMngr ;', '(X / Y ) * 100', 'Formalisés = 100 ; prévus = 75 ; Identifiés = 50 ; Non = 0', '100', 'High', 'Periodic', 'Dossier technique', NULL, NULL, NULL, NULL, NULL, NULL),
(522, 'HEBERGEMENT', 'HEB-3.09', 'B', 'B', 'B', 'Adapter l\'utilisation des équipements techniques aux besoins', 'Est-ce que les solutions de stockage permettent de réduire le nombre de composants (par exemple: supports SSD haute densité) ?', 'La densité des espaces de stockage conditionne la consommation d\'énergie que le stockage va demander. Les technologies des disques évoluent rapidement, les parties mobiles tendent é étre remplacées par des composants électroniques qui permettent de réduire la consommation électrique globale.', 'Administration', 'Quel est le ratio entre l\'espace consommé et le volume de données ?', NULL, NULL, NULL, NULL, 'RECO', '**', NULL, NULL, '#USE ; #VALUE ; #BUY ; #FIN', 'ProjectMngr ; ITMngr ;', '(X / Y ) * 100', 'Formalisés = 100 ; prévus = 75 ; Identifiés = 50 ; Non = 0', '100', 'Medium', 'OnUpdate', 'Dossier technique', NULL, NULL, NULL, NULL, NULL, NULL),
(523, 'HEBERGEMENT', 'HEB-4', NULL, NULL, NULL, 'Suivre les impacts de l\'hébergeur', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'ODD#7 ; ODD#9 ; ODD#12 ; ODD#17', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(524, 'HEBERGEMENT', 'HEB-4.01', 'A', 'C', 'C', 'Suivre les impacts de l\'hébergeur', 'L\'électricité provient-elle d\'énergies renouvelables ?', 'L\'activité d\'un datacenter nécessite de l\'énergie. Les sources d\'énergies vont déterminer l\'empreinte environnementale. Les solutions d\'alimentation é partir d\'énergie bas carbone et renouvelables vont diminuer cet impact.', 'Utilisation', 'Les sources d\'alimentation énergétiques sont-elles connues ?', NULL, NULL, '72. Le centre de données (data center) est alimenté par des énergies renouvelables', NULL, 'RECO', '*', NULL, NULL, '#USE ; #COM ; #VALUE ; #BUY ;', 'ProjectMngr ; ITMngr ;', '(X / Y ) * 100', 'Oui = 100 ; Partiel = 50 ; Non = 0', '100', 'High', 'Periodic', 'Le dossier technique d\'hébergement permet de suivre les sources d\'approvisionnement en énergie', NULL, NULL, 'Y', '#PROCESS', '**', 'Outils Site /  base  d\'hébergeurs \"verts\"'),
(525, 'HEBERGEMENT', 'HEB-4.02', 'B', 'B', 'B', 'Suivre les impacts de l\'hébergeur', 'Les serveurs et les équipements techniques sont-ils certifiés ?', 'Les normes, labels et certifications environnementales se généralisent. Les serveurs, les espaces de stockage, les équipements réseaux affichent de plus en plus fréquemment des adhésions é certaines de ces normes de fait ou standardisées. L\'émergence de nouveaux marqueurs doit étre surveillée pour permettre la meilleure solution.', 'Acquisition', 'Les serveurs ont-ils une certification (ASHRAE, TCO8, ...) ?', NULL, NULL, '18 - Le matériel informatique choisi fait l\'objet d\'une certification via un éco-label.', NULL, 'RECO', '*', NULL, 'INCONTOURNABLE', '#COM ; #VALUE ; #BUY ;', 'ProjectMngr ; ITMngr ;', '(X / Y ) * 100', 'Oui = 100 ; Partiel = 50 ; Non = 0', '100', 'High', 'Periodic', 'Le dossier technique de l\'hébergement trace l\'éfficacité énergétique et les certifications des équipements', NULL, NULL, NULL, NULL, NULL, NULL),
(526, 'HEBERGEMENT', 'HEB-4.03', 'B', 'B', 'B', 'Suivre les impacts de l\'hébergeur', 'Le datacenter permet-il d\'avoir des informations fiables en terme de consommations d\'énergie?', 'Le total des consommations d\'énergie est utile pour estimer l\'empreinte environnementale du datacenter. Cela doit prendre en compte aussi les équipements de secours (générateurs, onduleurs,...), mais la segmentation de ces consommations par usage est beaucoup plus significative, certains indicateurs n\'étant calculés qu\'avec des données plus précises (par exemple le PUE). L\'autre intérét de cette ventilation est d\'identifier les points les plus consommateurs pour mettre en place des actions visant é les réduire.', 'Administration', 'Les données sont-elles publiées et vérifiables ?', NULL, NULL, '69. L\'hébergement des sites Internet est assuré par un prestataire qui affiche sa politique Green IT', NULL, 'RECO', '*', NULL, NULL, '#COM ; #VALUE ; #BUY ;', 'ProjectMngr ; ITMngr ;', '(X / Y ) * 100', 'Oui = 100 ; Partiel = 50 ; Non = 0', '100', 'High', 'Periodic', 'Architecture / Documentation', NULL, NULL, NULL, NULL, NULL, NULL),
(527, 'HEBERGEMENT', 'HEB-4.04', 'B', 'B', 'B', 'Suivre les impacts de l\'hébergeur', 'L\'hébergeur est-il transparent sur l\'impact carbone de l\'infrastructure et des usages pour ses clients ?', 'L\'activité d\'un datacenter génére un impact carbone qui peut étre important.', 'Utilisation', 'L\'opérateur du datacenter met-il é disposition de ses clients les informations pour connaétre l\'impact carbone ?', 'Les clients ont-ils accés é des métriques sur l\'impact carbone de leurs usages ?', NULL, NULL, NULL, 'RECO', '*', NULL, NULL, '#COM ; #VALUE ; #BUY ;', 'ProjectMngr ; ITMngr ;', '(X / Y ) * 100', 'Oui = 100 ; Partiel = 50 ; Non = 0', '100', 'High', 'Periodic', 'Dossier technique', NULL, NULL, 'Y', '#PROCESS', '**', 'Outils Site /  base  d\'hébergeurs \"verts\"'),
(528, 'HEBERGEMENT', 'HEB-4.05', 'B', 'B', 'B', 'Suivre les impacts de l\'hébergeur', 'Est-ce que les fournisseurs sont sélectionnés en fonction de leurs capacités é communiquer les indicateurs différenciants ?', 'Dans les régles de sélection des fournisseurs de services d\'hébergement, les aspects NR doivent étre pris en compte de la méme maniére que si le service était pris en charge en interne. Le cahier des charges doit exposer en plus des considérations techniques, budgétaires et de respect des normes de sécurité les principes NR que le fournisseur doit remplir.', 'Acquisition', 'Quels seuils sont définis comme limites d\'acceptabilité ?', NULL, NULL, NULL, NULL, 'RECO', '*', NULL, NULL, '#COM ; #VALUE ; #BUY ;', 'ProjectMngr ; ITMngr ;', NULL, NULL, NULL, 'High', 'Periodic', 'Procédure d\'achat', NULL, NULL, NULL, NULL, NULL, NULL),
(529, 'HEBERGEMENT', 'HEB-4.C01', 'C', 'B', 'B', 'Suivre les impacts de l\'hébergeur', 'La structure juridique, ses filiales et sa filiation sont-elles respectueuses des réglementations et de la déontologie ?', 'Les activités d\'hébergement prennent deux directions opposées : la concentration avec de gros acteurs et la multiplication d\'acteurs de proximité. Les structures juridiques de ces deux types d\'entités peuvent étre plus ou moins visibles, il est important de pouvoir en appréhender toutes les ramifications pour choisir, en connaissance de cause et dans le respect des principes NR, le fournisseur.', 'Acquisition', 'Toutes les informations légales sont-elles collectées et étudiées ?', NULL, NULL, NULL, NULL, 'CONSEIL', 'N/A', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(530, 'HEBERGEMENT', 'HEB-4.06', 'B', 'B', 'B', 'Suivre les impacts de l\'hébergeur', 'Le fournisseur expose-t-il tous ses indicateurs ?', 'Le niveau de maturité NR des opérateurs de datacenter est trés variable, lorsque les indicateurs de performances NR sont exposés cela démontre une maturité avancée. La communication d\'indicateurs ne présume pas de la pertinence et de la fiabilité des indicateurs qui doivent aussi étre exposés.', 'Administration', 'Quels indicateurs sont basés sur des données externes avec des sources fiables ?', NULL, NULL, NULL, NULL, 'RECO', '*', NULL, 'INCONTOURNABLE', '#COM ; #VALUE ; #BUY ;', 'ProjectMngr ; ITMngr ;', '(X / Y ) * 100', 'Oui = 100 ; Partiel = 50 ; Non = 0', '100', 'High', 'Periodic', 'Le dossier technique de l\'hébergement trace l\'ensemble des indicateurs mis é disposition', NULL, NULL, NULL, NULL, NULL, NULL),
(531, 'HEBERGEMENT', 'HEB-4.C02', 'A', 'B', 'B', 'Suivre les impacts de l\'hébergeur', 'Les équipements non-critiques sont-ils issus du reconditionnement ?', 'Le prolongement de la durée de vie des équipements permet de réduire les remplacements de matériels, l\'utilisation de matériels reconditionnés est une pratique intéressante dans ce sens. Toutefois les avancées technologiques et l\'efficacité des composants techniques peut rendre plus utile dans la phase d\'usage de disposer de matériels récents qui vont consommer moins d\'énergie pour une méme utilisation.', 'Déploiement', 'La source d\'approvisionnement est-elle exposée ?', NULL, NULL, NULL, NULL, 'CONSEIL', 'N/A', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Architecture / Documentation', NULL, NULL, NULL, NULL, NULL, NULL),
(532, 'HEBERGEMENT', 'HEB-4.07', 'A', 'B', 'A', 'Suivre les impacts de l\'hébergeur', 'Les équipements sont-ils réparés plutét que remplacés ?', 'La réparabilité des matériels, et le taux effectif de réparation est un critére qui permet d\'atténuer l\'impact environnemental des matériels sur l\'ensemble de leurs durées de vie.', 'Maintenance', 'Le taux de réparation est-il communiqué ?', NULL, NULL, '8. Sauf panne, le matériel informatique n\'est pas renouvelé avant 5 ans minimum d\'utilisation.', NULL, 'RECO', '*', NULL, NULL, '#MAINT ; #COM ; #VALUE ; #FIN', 'ProjectMngr ; ITMngr ;', '(X / Y ) * 100', 'Oui = 100 ; Partiel = 50 ; Non = 0', '100', 'Medium', 'Periodic', 'Le dossier technique de l\'hébergement trace la filiére de réparation / revalorisation', NULL, NULL, NULL, NULL, NULL, NULL),
(533, 'HEBERGEMENT', 'HEB-4.08', 'A', 'B', 'B', 'Suivre les impacts de l\'hébergeur', 'Les équipements en fin de vie sont-ils revalorisés ( vente / don / recyclage ... ) ?', 'Le renouvellement du parc technique est incontournable, les équipements en capacité de servir des usages moins intenses doivent étre reconditionnés et ré-injectés dans d\'autres secteurs d\'activité. Les matériels qui ne peuvent étre remis en service doivent entrer dans une filiére de recyclage fiable et chaque étape doit étre tracée pour garantir sa conformité avec les exigences NR.', 'Fin de Vie', 'La chaéne est-elle traéable ?', NULL, NULL, NULL, NULL, 'RECO', '*', NULL, 'INCONTOURNABLE', '#COM ; #VALUE ; #BUY  ; #OBSO', 'ProjectMngr ; ITMngr ;', '(X / Y ) * 100', 'Oui = 100 ; Partiel = 50 ; Non = 0', '100', 'Medium', 'Periodic', 'Le dossier technique de l\'hébergement trace la filiére de revalorisation', NULL, NULL, NULL, NULL, NULL, NULL),
(534, 'HEBERGEMENT', 'HEB-4.C03', 'B', 'B', 'B', 'Suivre les impacts de l\'hébergeur', 'Est-ce que les serveurs et routeurs sont renouvelés lorsque les technologies permettent une meilleure efficacité énergétique ?', 'Les avancées technologiques et l\'efficacité des composants techniques peut rendre plus utile dans la phase d\'usage de disposer de matériels récents qui vont consommer moins d\'énergie pour une méme utilisation.', 'Fin de Vie', 'Est-ce que les certifications des équipements sont disponibles ?', NULL, NULL, NULL, NULL, 'CONSEIL', 'N/A', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(535, 'HEBERGEMENT', 'HEB-4.09', 'B', 'B', 'B', 'Suivre les impacts de l\'hébergeur', 'Est-ce que les matériaux de construction / gaine / isolant utilisés permettent une meilleure efficacité énergétique. Le bétiment répond-t-il aux normes HQE , rt2012 ou équivalentes ?', 'En plus de la structure du bétiment qui peut étre plus ou moins efficace dans sa conception, les éléments de construction et liés é l\'activité de datacenter peuvent aussi suivre les normes environnementales les plus performantes.', 'Réalisation', 'Quelle norme est utilisée ?', NULL, NULL, NULL, NULL, 'RECO', '**', NULL, NULL, '#COM ; #VALUE ; #BUY ;', 'ProjectMngr ; ITMngr ;', NULL, NULL, NULL, 'Low', 'Once', 'Dossier technique', NULL, NULL, NULL, NULL, NULL, NULL),
(536, 'HEBERGEMENT', 'HEB-4.C04', 'B', 'B', 'B', 'Suivre les impacts de l\'hébergeur', 'Quel est l\'impact du refroidissement du datacenter sur l\'environnement ?', 'Les techniques de refroidissement font intervenir soit de la production de froid é partir d\'une source d\'énergie, soit la captation de froid sur une source naturelle. La consommation d\'énergie é un impact direct. La captation d\'une source naturelle de froid doit aussi étre évaluée par rapport é l\'impact environnemental de rejet de chaleur.', 'Utilisation', 'Quel est le type de refroidissement utilisé ?', NULL, NULL, NULL, NULL, 'CONSEIL', 'N/A', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(537, 'HEBERGEMENT', 'HEB-5', NULL, NULL, NULL, 'Garder la cohérence de positionnement entre les besoins et les choix d\'hébergement', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'ODD#7 ; ODD#8 ; ODD#12', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(538, 'HEBERGEMENT', 'HEB-5.01', 'A', 'B', 'B', 'Garder la cohérence de positionnement entre les besoins et les choix d\'hébergement', 'Les mesures carbon / water efficiency sont-elles disponibles ?', 'Les standards pour les datacenters mettent en avant les indicateurs d\'efficacité qui dépassent la partie énergétique. Les métriques d\'efficacité carbone et eau sont de plus en plus employée et permettent une meilleure évaluation des datacenter.', 'Administration', 'Les mesures d\'efficacité environnementale sont-elles disponibles ?', NULL, NULL, NULL, NULL, 'RECO', '*', NULL, NULL, '#COM ; #VALUE ; #BUY ;', 'ProjectMngr ; ITMngr ;', '(X / Y ) * 100', 'Oui = 100 ; Partiel = 50 ; Non = 0', '100', 'Medium', 'Periodic', 'Le dossier technique de l\'hébergement trace les indicateurs environnementaux', NULL, NULL, NULL, NULL, NULL, NULL),
(539, 'HEBERGEMENT', 'HEB-5.02', 'B', 'A', 'A', 'Garder la cohérence de positionnement entre les besoins et les choix d\'hébergement', 'L\'entreprise (l\'hébergeur) applique-t-elle une politique RSE / RGAA ?', 'Le respect des considérations des personnes est un critére important pour le NR', 'Acquisition', 'Les équipes de support et d\'exploitation sont-elles sous les mémes législations que le client ?', 'Un référent RSE - RGAA  est-il disponible ?', NULL, 'Schéma pluri-annuel de mise en accessibilité', NULL, 'RECO', '*', NULL, NULL, '#HR ; #FIN ; #LEG', 'RSE/NR ; HR', NULL, NULL, NULL, 'Low', 'Periodic', 'Procédure d\'achat', NULL, NULL, NULL, NULL, NULL, NULL),
(540, 'HEBERGEMENT', 'HEB-5.03', 'B', 'A', 'A', 'Garder la cohérence de positionnement entre les besoins et les choix d\'hébergement', 'L\'entreprise fournit-elle des garanties fiables en terme de RGPD ?', 'Le RGPD impose de communiquer les éléments d\'hébergement, cet aspect légal doit étre vérifié précisément.', 'Utilisation', 'Le respect des réglementations Franéaises et Européennes ainsi qu\'une indépendance vis-é-vis d\'une réglementation d\'un pays étranger sont-elles prises en compte ?', NULL, NULL, NULL, NULL, 'RECO', '*', NULL, NULL, '#SEC ; #COM ; #VALUE ; #BUY ; #LEG', 'ProjectMngr ; ITMngr ;', '(X / Y ) * 100', 'Oui = 100 ; Partiel = 50 ; Non = 0', '100', 'High', 'Periodic', 'Dossier technique', NULL, NULL, NULL, NULL, NULL, NULL),
(541, 'HEBERGEMENT', 'HEB-5.C01', 'B', 'B', 'B', 'Garder la cohérence de positionnement entre les besoins et les choix d\'hébergement', 'Les atouts de l\'hébergeur sont-ils exposés lorsqu\'ils font progresser le NR ?', 'Des outils permettent de vérifier les caractéristiques des hébergeurs pour des sites WEB. La promotion des hébergeurs utilisés qui mettent en oeuvre les meilleures solutions NR peuvent étre exposés avec des \"stickers\" sur le site WEB. Certains outils utilisent des régles opaques, dans tous les cas il est indispensable de vérifier la validité des notations données par ces outils.', 'Revalorisation', 'Quel partenariat de communication est mis en place ?', 'Un référent NR est-il disponible ?', NULL, NULL, NULL, 'CONSEIL', 'N/A', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(542, 'HEBERGEMENT', 'HEB-5.04', 'C', 'B', 'B', 'Garder la cohérence de positionnement entre les besoins et les choix d\'hébergement', 'Toutes les données soumises é une réglementation (santé, bancaire) sont-elles sécurisées sur un hébergeur avec un niveau d\'agrément compatible ?', 'Les réglementations imposent des agréments spécifiques pour certaines catégories de données (santé, bancaire). Les normes évoluent en fonction des besoins de sécurisation et de l\'état de l\'art. La validation d\'un agrément doit étre suivi réguliérement pour garantir que les utilisateurs du services ne sont pas exposés é des risques.', 'Déploiement', 'Quelles sont les certifications de l\'hébergeur vis-é-vis des données réglementées ?', NULL, NULL, NULL, NULL, 'RECO', '*', NULL, NULL, '#SEC ; #COM ; #VALUE ; #BUY ; #LEG', 'ProjectMngr ; ITMngr ;', NULL, NULL, NULL, 'High', 'Periodic', 'Analyse de risques', NULL, NULL, NULL, NULL, NULL, NULL),
(543, 'HEBERGEMENT', 'HEB-5.C02', 'C', 'B', 'B', 'Garder la cohérence de positionnement entre les besoins et les choix d\'hébergement', 'L\'hébergeur est-il agréé?', 'Les réglementations imposent des agréments spécifiques pour certaines catégories de données (santé, bancaire). Les normes évoluent en fonction des besoins de sécurisation et de l\'état de l\'art. La validation d\'un agrément doit étre suivie réguliérement pour garantir que les utilisateurs du service ne sont pas exposés é des risques.', 'Acquisition', 'L\'hébergeur est-il agréé HDS?', 'L\'hébergeur est-il agréé PCI-DSS?', NULL, NULL, NULL, 'CONSEIL', 'N/A', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Y', '#PROCESS', '**', 'Outils Site /  base  d\'hébergeurs'),
(544, 'HEBERGEMENT', 'HEB-5.C03', 'A', 'B', 'B', 'Garder la cohérence de positionnement entre les besoins et les choix d\'hébergement', 'Le fournisseur adhére-t-il é un standard / label ? S\'implique-t-il dans des réseaux liés au Numérique Responsable ?', 'L\'opérateur de datacenter peut avoir lancé une démarche NR, et étre en mesure d\'exposer son adhésion et ses actions pour le NR', 'Acquisition', 'Une charte a-t-elle été signée ? Fait-il partie d\'un réseau NR ?', NULL, NULL, NULL, NULL, 'CONSEIL', 'N/A', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Y', '#PROCESS', '**', 'Outils Site /  base  d\'hébergeurs'),
(545, 'HEBERGEMENT', 'HEB-6', NULL, NULL, NULL, 'Définir les enjeux et fonctionnalités du projet', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'ODD#12 ; ODD#13', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(546, 'HEBERGEMENT', 'HEB-6.01', 'B', 'B', 'A', 'Définir les enjeux et fonctionnalités du projet', 'Le datacenter est-il é minima Tier 3 ?', 'Le niveau de sécurisation du datacenter conditionne les éléments de disponibilité des services numériques.', 'Réalisation', 'Quel est le niveau du datacenter ?', NULL, NULL, NULL, NULL, 'RECO', '*', NULL, NULL, '#SEC ; #COM ; #VALUE ; #BUY ;', 'ProjectMngr ; ITMngr ;', '(X / Y ) * 100', 'Oui = 100 ; Partiel = 50 ; Non = 0', '100', 'High', 'Periodic', 'Architecture / Documentation', NULL, NULL, NULL, NULL, NULL, NULL),
(547, 'HEBERGEMENT', 'HEB-6.C01', 'B', 'B', 'B', 'Définir les enjeux et fonctionnalités du projet', 'L\'opérateur dispose-t-il de plusieurs datacenters interconnectés et distants de + de 100kms ?', 'Les impacts des cataclysmes naturels ou industriels peuvent rendre certains sites inopérants. Afin de garantir une continuité de service, les distances entre les différents sites hébergeant les services doivent prendre en compte ces éléments.', 'Administration', 'Est-il conforme é une norme ISO ?', NULL, NULL, NULL, NULL, 'CONSEIL', 'N/A', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(548, 'HEBERGEMENT', 'HEB-6.02', 'B', 'B', 'B', 'Définir les enjeux et fonctionnalités du projet', 'La certification du datacenter est-elle adaptée au besoin et é l\'emplacement géographique (sismique, innondation) ?', 'Les caractéristiques des zones géographiques peuvent nécessiter des dispositions spécifiques que les datacenters doivent prendre en compte.', 'Acquisition', 'Les risques physiques ont-ils été évalués sur toute la vie du projet ?', NULL, NULL, NULL, NULL, 'RECO', '*', NULL, NULL, '#SEC ; #COM ; #VALUE ; #BUY ;', 'ProjectMngr ; ITMngr ;', '(X / Y ) * 100', 'Oui = 100 ; Partiel = 50 ; Non = 0', '100', 'Low', 'Periodic', 'Architecture / Documentation', NULL, NULL, NULL, NULL, NULL, NULL),
(549, 'HEBERGEMENT', 'HEB-6.C02', 'B', 'A', 'B', 'Définir les enjeux et fonctionnalités du projet', 'Le contréle d\'accés physique au site met-il en oeuvre des données biométriques ?', 'La capture d\'information biométriques permet de sécuriser des accés, mais transmet des données personnelles importantes qui doivent étre protégées.', 'Utilisation', 'Quelles sont les garanties offertes en terme de sécurisation des données d\'accés, exploitation, conservation, durée de vie ?', NULL, NULL, NULL, NULL, 'CONSEIL', 'N/A', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(550, 'HEBERGEMENT', 'HEB-6.C03', 'B', 'B', 'B', 'Définir les enjeux et fonctionnalités du projet', 'Le datacenter fournit-il une solution locale de coffre-fort numérique sécurisé ?', 'L\'hébergement de données critiques dans un espace hautement sécurisé et crypté est parfois indispensable pour assurer la pérennité de l\'organisation', 'Utilisation', 'Quelle technologie, niveau de sécurité, cryptage est utilisée pour le coffre-fort numérique ?', NULL, NULL, NULL, NULL, 'CONSEIL', 'N/A', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
(551, 'HEBERGEMENT', 'HEB-6.03', 'A', 'B', 'B', 'Définir les enjeux et fonctionnalités du projet', 'Les processus mis en place pour la prévention des incidents prennent-ils en compte la non-dégradation des serveurs et équipements ?', 'Les procédures de réponses aux incidents doivent en premier lieu protéger les personnes et leurs données, mais la destruction d\'équipements physiques génére un besoin de renouvellement avec son impact sur l\'environnement.', 'Maintenance', 'A quelle fréquence sont testés les équipements de sécurité ?', NULL, NULL, NULL, NULL, 'RECO', '**', NULL, NULL, '#SEC ; #COM ; #VALUE ; #BUY ;', 'ProjectMngr ; ITMngr ;', '(X / Y ) * 100', 'Oui = 100 ; Partiel = 50 ; Non = 0', '100', 'Low', 'Periodic', 'Le dossier technique de l\'hébergement trace les stratégies de traitement des incidents', NULL, NULL, NULL, NULL, NULL, NULL),
(552, 'HEBERGEMENT', 'HEB-6.04', 'B', 'B', 'B', 'Définir les enjeux et fonctionnalités du projet', 'L\'impact environnemental d\'implémentation de chaque fonction est-il en rapport avec son importance ?', 'La cohérence NR pousse é s\'assurer que les fonctions secondaires n\'ont pas un impact environnemental plus important que les fonctions principales. Les spécifications doivent clairement identifier les fonctions principales et secondaires et leur impact du point de vue NR.', 'Utilisation', 'Avez-vous appliqué une démarche 80/20 pour chaque fonction en rapport avec ses impacts environnementaux et son importance ?', NULL, NULL, NULL, NULL, 'RECO', '***', NULL, NULL, '#PERF ; #VOL ; #SEC ; #USE ; #MAINT ;  #VALUE ; #DEBT ; #METH', 'ProjectMngr ; ITMngr ;', NULL, NULL, NULL, 'High', 'OnUpdate', 'Analyse d\'impact', NULL, NULL, NULL, NULL, NULL, NULL),
(553, 'HEBERGEMENT', 'HEB-6.05', 'B', 'B', 'B', 'Définir les enjeux et fonctionnalités du projet', 'Chacune des phases du cycle de vie sont-elles identifiées avec des actions spécifiques ?', 'La démarche ACV est une base de la prise en compte du NR dans la conception des services numériques, elle permet d\'associer des actions é chaque étape du cycle de vie.', 'Administration', 'A quelle fréquence ces actions sont-elles revues ?', NULL, NULL, NULL, NULL, 'RECO', '**', NULL, NULL, '#PERF ; #VOL ; #SEC ; #USE ; #MAINT ;  #VALUE ; #DEBT ; #METH', 'ProjectMngr ; ITMngr ;', NULL, NULL, NULL, 'Medium', 'Periodic', 'ACV', NULL, NULL, NULL, NULL, NULL, NULL),
(554, 'HEBERGEMENT', 'HEB-6.05', 'C', 'B', 'B', 'Définir les enjeux et fonctionnalités du projet', 'Les données des études précédentes sont-elles disponibles et tenues é jour ?', 'Les analyses ACV peuvent étre réalisées sur des services existants afin de les rendre plus efficaces dans le cas d\'une refonte ou adaptation d\'un service existant.', 'Revalorisation', 'Quelles données ACV sont produites pour permettre leur réutilisation dans d\'autres projets ?', NULL, NULL, NULL, NULL, 'RECO', '**', NULL, NULL, '#USE ;  #COM ; #VALUE ;  #METH', 'ProjectMngr ; ITMngr ;', NULL, NULL, NULL, 'Medium', 'OnUpdate', 'Documentation technique', NULL, NULL, NULL, NULL, NULL, NULL);
